/**
 * @fileoverview List installed templates command
 * @lastmodified 2025-08-22T20:00:00Z
 *
 * Features: List installed templates, show details, update status
 * Main APIs: execute(), displayTemplate(), checkUpdates()
 * Constraints: Local registry access, marketplace connectivity for updates
 * Patterns: Command pattern, template listing, status display
 */

import chalk from 'chalk';
import { BaseCommand } from '../../cli/base-command';
import { ICommand } from '../../cli/command-registry';
import { MarketplaceService } from '../../marketplace/core/marketplace.service';
import {
  TemplateRegistry,
  RegisteredTemplate,
} from '../../marketplace/core/template.registry';
import { MarketplaceCommandOptions, MarketplaceTemplate } from '../../types';
import { TemplateModel } from '../../marketplace/models/template.model';
import { logger } from '../../utils/logger';

export class ListCommand extends BaseCommand implements ICommand {
  name = 'list';

  description = 'List installed templates';

  aliases = ['ls', 'installed'];

  options = [
    {
      flags: '--detailed',
      description: 'Show detailed template information',
      defaultValue: false,
    },
    {
      flags: '--check-updates',
      description: 'Check for available updates',
      defaultValue: false,
    },
    {
      flags: '--category <category>',
      description: 'Filter by category',
    },
    {
      flags: '--author <author>',
      description: 'Filter by author',
    },
    {
      flags: '--outdated',
      description: 'Show only templates with available updates',
      defaultValue: false,
    },
    {
      flags: '--stats',
      description: 'Show installation statistics',
      defaultValue: false,
    },
  ];

  async action(args: unknown, options: unknown): Promise<void> {
    await this.execute(args as string, options as MarketplaceCommandOptions);
  }

  async execute(
    _args: string,
    options: MarketplaceCommandOptions
  ): Promise<void> {
    try {
      const registry = new TemplateRegistry();
      const marketplace = await MarketplaceService.getInstance();

      let templates = registry.listTemplates();

      if (templates.length === 0) {
        this.warn('No templates installed');
        logger.info(
          `\nüí° Install templates with: ${chalk.green('cursor-prompt install <template-name>')}`
        );
        logger.info(
          `üí° Search templates with: ${chalk.green('cursor-prompt search <query>')}`
        );
        return;
      }

      // Apply filters
      if (options.category) {
        templates = templates.filter(
          t => t.metadata.category === options.category
        );
      }

      if (options.author) {
        templates = templates.filter(t => {
          const authorName =
            typeof t.metadata.author === 'string'
              ? t.metadata.author
              : t.metadata.author?.name;
          return authorName
            ?.toLowerCase()
            .includes(options.author!.toLowerCase());
        });
      }

      // Check for updates if requested
      let updateInfo: Map<string, string> = new Map();
      if (options.checkUpdates || options.outdated) {
        this.info('Checking for updates...');
        try {
          const updates = await marketplace.checkUpdates();
          updateInfo = new Map(
            (Array.isArray(updates) ? updates : []).map((u: any) => [
              u.templateId,
              u.latestVersion,
            ])
          );
        } catch (error) {
          this.warn(
            `Failed to check updates: ${error instanceof Error ? error.message : String(error)}`
          );
        }
      }

      // Filter outdated if requested
      if (options.outdated) {
        templates = templates.filter(t => updateInfo.has(t.id));

        if (templates.length === 0) {
          this.success('All templates are up to date!');
          return;
        }
      }

      // Show statistics if requested
      if (options.stats) {
        ListCommand.displayStats(registry.getStats());
      }

      // Display templates
      logger.info(
        chalk.bold(`\nüì¶ Installed Templates (${templates.length}):\n`)
      );

      // Process templates sequentially to handle async operations properly
      // eslint-disable-next-line no-restricted-syntax
      for (const [index, template] of templates.entries()) {
        // Convert RegisteredTemplate to MarketplaceTemplate format for display
        const marketplaceTemplate = this.convertToMarketplaceTemplate(template);
        if (options.detailed) {
          // eslint-disable-next-line no-await-in-loop
          await this.displayDetailedTemplate(
            marketplaceTemplate,
            index + 1,
            updateInfo
          );
        } else {
          this.displayCompactTemplate(
            marketplaceTemplate,
            index + 1,
            updateInfo
          );
        }
      }

      // Show update summary
      if (updateInfo.size > 0) {
        const availableUpdates = templates.filter(t => updateInfo.has(t.id));
        if (availableUpdates.length > 0) {
          logger.info(
            chalk.yellow(
              `\nüîÑ ${availableUpdates.length} template(s) have updates available`
            )
          );
          logger.info(
            `üí° Update all: ${chalk.green('cursor-prompt update --all')}`
          );
        }
      }
    } catch (error) {
      this.error(
        `Failed to list templates: ${error instanceof Error ? error.message : String(error)}`
      );
    }
  }

  // eslint-disable-next-line class-methods-use-this
  private convertToMarketplaceTemplate(
    registered: RegisteredTemplate
  ): MarketplaceTemplate {
    const authorInfo =
      typeof registered.metadata.author === 'string'
        ? registered.metadata.author
        : registered.metadata.author?.name || 'Unknown Author';

    return {
      id: registered.id,
      name: registered.name,
      description: registered.metadata.description || '',
      category: registered.metadata.category || 'general',
      tags: registered.metadata.tags || [],
      author: {
        id:
          typeof registered.metadata.author === 'string'
            ? registered.metadata.author
            : registered.metadata.author?.id || 'unknown',
        name: authorInfo,
        verified:
          typeof registered.metadata.author === 'object'
            ? registered.metadata.author?.verified || false
            : false,
      },
      currentVersion: registered.version,
      versions: [],
      downloads: 0,
      rating: 0,
      reviewCount: 0,
      createdAt: registered.registered.toISOString(),
      updatedAt: registered.registered.toISOString(),
      displayName: registered.metadata.displayName || registered.name,
      registered: registered.registered.toISOString(),
      path: registered.path,
    };
  }

  // eslint-disable-next-line class-methods-use-this
  private displayCompactTemplate(
    template: MarketplaceTemplate,
    position: number,
    updateInfo: Map<string, string>
  ): void {
    const hasUpdate = updateInfo.has(template.id);
    const updateText = hasUpdate
      ? chalk.yellow(` ‚Üí ${updateInfo.get(template.id)}`)
      : '';

    const badges = ListCommand.formatBadges(template);
    const rating =
      typeof template.rating === 'number'
        ? template.rating
        : template.rating.average;
    const ratingStars = ListCommand.formatRating(rating);

    logger.info(
      `${position}. ${chalk.bold(template.displayName || template.name)} ${badges}`
    );
    logger.info(`   ${chalk.gray(template.description)}`);
    logger.info(
      `   ${chalk.yellow(`v${template.currentVersion}`)}${updateText} ${chalk.gray('‚Ä¢')} ${chalk.magenta(template.category)} ${chalk.gray('‚Ä¢')} ${ratingStars}`
    );
    logger.info(
      `   ${chalk.gray('by')} ${chalk.blue(template.author.name)} ${chalk.gray('‚Ä¢')} ${chalk.gray('installed')} ${chalk.gray(new Date(template.registered || template.createdAt).toLocaleDateString())}`
    );
    logger.info('');
  }

  // eslint-disable-next-line class-methods-use-this
  private async displayDetailedTemplate(
    template: MarketplaceTemplate,
    position: number,
    updateInfo: Map<string, string>
  ): Promise<void> {
    const hasUpdate = updateInfo.has(template.id);
    const updateText = hasUpdate
      ? chalk.yellow(` (update available: ${updateInfo.get(template.id)})`)
      : chalk.green(' (up to date)');

    const badges = ListCommand.formatBadges(template);

    logger.info(
      `${position}. ${chalk.bold.underline(template.displayName || template.name)} ${badges}`
    );
    logger.info(`   ID: ${chalk.gray(template.id)}`);
    logger.info(`   ${template.description}`);
    logger.info(
      `   Version: ${chalk.yellow(template.currentVersion)}${updateText}`
    );
    logger.info(`   Category: ${chalk.magenta(template.category)}`);
    logger.info(
      `   Author: ${chalk.blue(template.author.name)}${template.author.verified ? ' ‚úì' : ''}`
    );
    const ratingValue =
      typeof template.rating === 'number'
        ? template.rating
        : template.rating.average;
    const totalReviews =
      typeof template.rating === 'number'
        ? template.reviewCount
        : template.rating.total;
    logger.info(
      `   Rating: ${ListCommand.formatRating(ratingValue)} (${totalReviews} reviews)`
    );
    logger.info(
      `   Downloads: ${chalk.cyan(ListCommand.formatNumber(template.stats?.downloads || 0))}`
    );
    logger.info(
      `   Installed: ${chalk.gray(new Date(template.registered || template.createdAt).toLocaleDateString())}`
    );
    logger.info(`   Location: ${chalk.gray(template.path)}`);

    if (template.tags && template.tags.length > 0) {
      logger.info(
        `   Tags: ${template.tags.map((tag: string) => chalk.gray(`#${tag}`)).join(' ')}`
      );
    }

    // Show dependencies
    if (
      template.versionInfo?.dependencies &&
      template.versionInfo.dependencies.length > 0
    ) {
      logger.info(
        `   Dependencies: ${template.versionInfo.dependencies.length} required`
      );
    }

    // Show auto-update status
    const marketplace = await MarketplaceService.getInstance();
    const installation = marketplace.getInstallation(template.id);
    if (installation) {
      const autoUpdateStatus = installation.autoUpdate
        ? chalk.green('enabled')
        : chalk.gray('disabled');
      logger.info(`   Auto-update: ${autoUpdateStatus}`);
    }

    logger.info(
      `   Commands: ${chalk.green(`cursor-prompt generate ${template.name}`)}`
    );

    if (hasUpdate) {
      logger.info(
        `   Update: ${chalk.green(`cursor-prompt update ${template.name}`)}`
      );
    }

    logger.info('');
  }

  private static displayStats(stats: Record<string, unknown>): void {
    logger.info(chalk.bold('\nüìä Installation Statistics:\n'));
    logger.info(`   Total templates: ${chalk.cyan(stats.total)}`);
    logger.info(`   Active templates: ${chalk.green(stats.active)}`);

    const categories = stats.categories as Record<string, number> | undefined;
    if (categories && Object.keys(categories).length > 0) {
      logger.info('\n   Categories:');
      Object.entries(categories).forEach(([category, count]) => {
        logger.info(`     ${chalk.magenta(category)}: ${count}`);
      });
    }

    const authors = stats.authors as Record<string, number> | undefined;
    if (authors && Object.keys(authors).length > 0) {
      logger.info('\n   Top Authors:');
      Object.entries(authors)
        .sort(([, a], [, b]) => (b as number) - (a as number))
        .slice(0, 5)
        .forEach(([author, count]) => {
          logger.info(
            `     ${chalk.blue(author)}: ${count} template${(count as number) > 1 ? 's' : ''}`
          );
        });
    }
  }

  private static formatRating(rating: number): string {
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;
    const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

    let stars = '';
    stars += '‚òÖ'.repeat(fullStars);
    if (hasHalfStar) stars += '‚òÜ';
    stars += '‚òÜ'.repeat(emptyStars);

    return `${chalk.yellow(stars)} ${chalk.gray(`(${rating.toFixed(1)})`)}`;
  }

  private static formatNumber(num: number): string {
    if (num >= 1000000) {
      return `${(num / 1000000).toFixed(1)}M`;
    }
    if (num >= 1000) {
      return `${(num / 1000).toFixed(1)}K`;
    }
    return num.toString();
  }

  private static formatBadges(
    template: MarketplaceTemplate | TemplateModel
  ): string {
    const badges: string[] = [];

    if (template.featured) {
      badges.push(chalk.yellow('‚≠ê'));
    }

    if (template.verified) {
      badges.push(chalk.green('‚úì'));
    }

    if (
      template.stats &&
      ('trending' in template.stats ? template.stats.trending : false)
    ) {
      badges.push(chalk.red('üî•'));
    }

    if (template.deprecated) {
      badges.push(chalk.red('‚ö†Ô∏è'));
    }

    return badges.join(' ');
  }
}

export default new ListCommand();

/**
 * @fileoverview Template engine for cursor-prompt-template-engine
 * @lastmodified 2025-08-26T03:27:11Z
 *
 * Features: Template rendering with variable substitution, array access, conditionals with else blocks
 * Main APIs: TemplateEngine.render()
 * Constraints: Supports Handlebars-style syntax with array bracket notation, nested conditionals, else blocks
 * Patterns: Variable replacement, template parsing
 */

import * as fs from 'fs';
import * as path from 'path';
import { TemplateHelpers } from './template-helpers';
import { TemplatePartials } from './template-partials';
import { TemplateTransforms } from './template-transforms';
import { templateSanitizer, validateTemplate, SanitizationConfig } from './template-sanitizer';
import { logger } from '../utils/logger';

export interface TemplateContext {
  [key: string]: unknown;
}

export interface TemplateEngineOptions {
  enableSanitization?: boolean;
  sanitizationConfig?: Partial<SanitizationConfig>;
  maxIncludeDepth?: number;
  validateTemplates?: boolean;
  enableSecurityMode?: boolean;
}

export class TemplateEngine {
  private variablePattern = /\{\{(\s*[@\w.[\]]+\s*)\}\}/g;

  private transformPattern = /\{\{([^|}]+\|[^}]+)\}\}/g;

  private helpers: TemplateHelpers;

  private partials: TemplatePartials;

  private transforms: TemplateTransforms;

  private eachPattern = /\{\{#each\s+([\w.]+)\s*\}\}(.*?)\{\{\/each\}\}/gs;

  private ifPattern = /\{\{#if\s+([\w.@]+)\s*\}\}(.*?)\{\{\/if\}\}/gs;

  private unlessPattern =
    /\{\{#unless\s+([\w.@]+)\s*\}\}(.*?)\{\{\/unless\}\}/gs;

  private includePattern = /\{\{#include\s+["']([^"']+)["']\s*\}\}/g;

  private includedFiles = new Set<string>();

  private maxIncludeDepth = 10;

  private options: TemplateEngineOptions;

  constructor(options: TemplateEngineOptions = {}) {
    this.helpers = new TemplateHelpers();
    this.partials = new TemplatePartials();
    this.transforms = new TemplateTransforms();
    this.options = {
      enableSanitization: true,
      validateTemplates: true,
      enableSecurityMode: true,
      maxIncludeDepth: 10,
      ...options,
    };
    this.maxIncludeDepth = this.options.maxIncludeDepth || 10;
  }

  /**
   * Render a template string with variables, supporting complex expressions
   * Processes includes, conditionals with else blocks, loops, partials, helpers, and transformations
   * @param template - Template string to render
   * @param context - Context object containing variables and data
   * @returns Promise resolving to rendered template string
   * @throws Error if include files not found or processing fails
   */
  async render(template: string, context: TemplateContext): Promise<string> {
    // Security validation if enabled
    if (this.options.validateTemplates) {
      const validation = validateTemplate(template);
      if (!validation.valid) {
        logger.error('Template validation failed:', validation.errors);
        throw new Error(`Template security validation failed: ${validation.errors.join(', ')}`);
      }

      if (validation.warnings && validation.warnings.length > 0) {
        logger.warn('Template security warnings:', validation.warnings);
      }
    }

    // Sanitize template content if enabled
    let processedTemplate = template;
    if (this.options.enableSanitization) {
      const sanitizationResult = templateSanitizer.sanitize(template, this.options.sanitizationConfig);

      if (sanitizationResult.threatsRemoved.length > 0) {
        logger.warn('Template threats removed during sanitization:', sanitizationResult.threatsRemoved);
      }

      processedTemplate = sanitizationResult.sanitized;
    }

    // Sanitize context variables if security mode is enabled
    let sanitizedContext = context;
    if (this.options.enableSecurityMode) {
      sanitizedContext = templateSanitizer.sanitizeTemplateVariables(context);
    }

    // Reset included files tracking for new render
    this.includedFiles.clear();

    // First process unconditional includes (not wrapped in conditionals)
    let processed = await this.processUnconditionalIncludes(
      processedTemplate,
      sanitizedContext,
      0,
      []
    );

    // Then process conditional blocks (which will handle conditional includes internally)
    processed = await this.processConditionalBlocksWithIncludes(
      processed,
      sanitizedContext
    );

    // Then process any standalone #each blocks not inside conditionals
    processed = this.processEachBlocks(processed, sanitizedContext);

    // Process partials after conditionals and loops to ensure correct context
    processed = this.processPartials(processed, sanitizedContext);

    // Process helper functions
    processed = this.processHelpers(processed, sanitizedContext);

    // Process variable transformations
    processed = this.processTransforms(processed, sanitizedContext);

    // Finally process regular variables
    const finalResult = this.processVariables(processed, sanitizedContext);

    // Final sanitization pass if enabled
    if (this.options.enableSanitization) {
      const finalSanitization = templateSanitizer.sanitize(finalResult, {
        ...this.options.sanitizationConfig,
        enableStrictMode: false, // Less strict for final pass
      });
      return finalSanitization.sanitized;
    }

    return finalResult;
  }

  /**
   * Render template with enhanced security validation
   */
  async renderSecure(template: string, context: TemplateContext): Promise<{
    result: string;
    securityReport: {
      templateThreats: string[];
      contextThreats: string[];
      sanitizationApplied: boolean;
      validationPassed: boolean;
    };
  }> {
    const securityReport = {
      templateThreats: [] as string[],
      contextThreats: [] as string[],
      sanitizationApplied: false,
      validationPassed: false,
    };

    // Validate template
    const validation = validateTemplate(template);
    securityReport.validationPassed = validation.valid;
    securityReport.templateThreats = validation.errors.concat(validation.warnings);

    // Validate context for security threats
    const contextValidation = templateSanitizer.sanitizeTemplateVariables(context);

    // Render with security options enabled
    const secureEngine = new TemplateEngine({
      ...this.options,
      enableSanitization: true,
      validateTemplates: true,
      enableSecurityMode: true,
    });

    const result = await secureEngine.render(template, context);
    securityReport.sanitizationApplied = true;

    return { result, securityReport };
  }

  /**
   * Process variables in a template with dot notation and array bracket access
   * @param template - Template string containing variable expressions
   * @param context - Context object for variable resolution
   * @returns Template with variables replaced by their values
   * @private
   */
  private processVariables(template: string, context: TemplateContext): string {
    return template.replace(this.variablePattern, (match, variable) => {
      const key = variable.trim();
      const value = this.resolveVariable(key, context);
      return value !== undefined ? String(value) : match;
    });
  }

  /**
   * Process {{#include}} directives that are NOT wrapped in conditionals
   * @param template - Template string that may contain include directives
   * @param context - Context object for variable resolution
   * @param depth - Current include depth to prevent infinite recursion
   * @param includeStack - Stack of included files for circular dependency detection
   * @returns Promise resolving to template with unconditional includes processed
   * @throws Error if include files not found or max depth exceeded
   * @private
   */
  private async processUnconditionalIncludes(
    template: string,
    context: TemplateContext,
    depth = 0,
    includeStack: string[] = []
  ): Promise<string> {
    // Prevent infinite recursion
    if (depth > this.maxIncludeDepth) {
      // Return empty string instead of throwing to allow graceful degradation
      logger.warn(
        `Maximum include depth (${this.maxIncludeDepth}) exceeded. Stopping recursion gracefully.`
      );
      return '';
    }

    let result = template;
    let match;

    // Reset pattern index
    this.includePattern.lastIndex = 0;

    // Find all include directives
    const includes: Array<{ match: string; path: string; start: number }> = [];
    // eslint-disable-next-line no-cond-assign
    while ((match = this.includePattern.exec(template)) !== null) {
      includes.push({ match: match[0], path: match[1], start: match.index });
    }

    // Process only includes that are NOT wrapped in conditionals
    // eslint-disable-next-line no-await-in-loop
    for (let i = 0; i < includes.length; i += 1) {
      const include = includes[i];

      // Check if this include is wrapped in a conditional
      if (this.isIncludeWrappedInConditional(include, template)) {
        continue; // Skip conditional includes for now
      }

      const absolutePath = this.resolveIncludePath(include.path);

      // Check for circular dependency
      if (includeStack.includes(absolutePath)) {
        throw new Error(
          `Circular dependency detected: ${absolutePath} is already included in the call stack`
        );
      }

      // Debug logging
      // console.log(`[UNCONDITIONAL] Processing include: ${absolutePath}, stack: [${includeStack.join(', ')}], depth: ${depth}`);

      try {
        // Read the included template
        // eslint-disable-next-line no-await-in-loop
        const includedContent = await fs.promises.readFile(
          absolutePath,
          'utf-8'
        );

        // Process includes recursively in the included content
        // eslint-disable-next-line no-await-in-loop
        const processedContent = await this.processUnconditionalIncludes(
          includedContent,
          context,
          depth + 1,
          [...includeStack, absolutePath]
        );

        // Replace the include directive with the processed content
        result = result.replace(include.match, processedContent);
      } catch (error) {
        const err = error as { code?: string };
        if (err.code === 'ENOENT') {
          throw new Error(`Include file not found: ${include.path}`);
        }
        throw error;
      }
    }

    return result;
  }

  /**
   * Process {{#include}} directives to include external templates with recursion protection
   * This is the legacy method, kept for backward compatibility
   * @param template - Template string that may contain include directives
   * @param context - Context object for variable resolution
   * @param depth - Current include depth to prevent infinite recursion
   * @param includeStack - Stack of included files for circular dependency detection
   * @returns Promise resolving to template with includes processed
   * @throws Error if include files not found or max depth exceeded
   * @private
   */
  private async processIncludes(
    template: string,
    context: TemplateContext,
    depth = 0,
    includeStack: string[] = []
  ): Promise<string> {
    // Prevent infinite recursion
    if (depth > this.maxIncludeDepth) {
      // Return empty string instead of throwing to allow graceful degradation
      logger.warn(
        `Maximum include depth (${this.maxIncludeDepth}) exceeded. Stopping recursion gracefully.`
      );
      return '';
    }

    let result = template;
    let match;

    // Reset pattern index
    this.includePattern.lastIndex = 0;

    // Find all include directives
    const includes: Array<{ match: string; path: string }> = [];
    // eslint-disable-next-line no-cond-assign
    while ((match = this.includePattern.exec(template)) !== null) {
      includes.push({ match: match[0], path: match[1] });
    }

    // Process each include
    // eslint-disable-next-line no-await-in-loop
    for (let i = 0; i < includes.length; i += 1) {
      const include = includes[i];
      const absolutePath = this.resolveIncludePath(include.path);

      // Check for circular dependency
      if (includeStack.includes(absolutePath)) {
        throw new Error(
          `Circular dependency detected: ${absolutePath} is already included in the call stack`
        );
      }

      // Debug logging
      // console.log(`Processing include: ${absolutePath}, stack: [${includeStack.join(', ')}], depth: ${depth}`);

      try {
        // Read the included template
        // eslint-disable-next-line no-await-in-loop
        const includedContent = await fs.promises.readFile(
          absolutePath,
          'utf-8'
        );

        // Process includes recursively in the included content
        // Add current file to the include stack to track call chain
        // eslint-disable-next-line no-await-in-loop
        const processedContent = await this.processIncludes(
          includedContent,
          context,
          depth + 1,
          [...includeStack, absolutePath]
        );

        // Replace the include directive with the processed content
        result = result.replace(include.match, processedContent);
      } catch (error) {
        const err = error as { code?: string };
        if (err.code === 'ENOENT') {
          throw new Error(`Include file not found: ${include.path}`);
        }
        throw error;
      }
    }

    return result;
  }

  /**
   * Resolve include path relative to current working directory or absolute
   * @param includePath - Path to include file (relative or absolute)
   * @returns Absolute path to include file
   * @private
   */
  // eslint-disable-next-line class-methods-use-this
  private resolveIncludePath(includePath: string): string {
    // If absolute path, use as-is
    if (path.isAbsolute(includePath)) {
      return includePath;
    }

    // Otherwise resolve relative to current working directory
    return path.resolve(process.cwd(), includePath);
  }

  /**
   * Check if an include directive is wrapped in a conditional block
   * @param include - Include directive with position information
   * @param template - Full template string
   * @returns True if the include is wrapped in a conditional
   * @private
   */
  // eslint-disable-next-line class-methods-use-this
  private isIncludeWrappedInConditional(
    include: { match: string; path: string; start: number },
    template: string
  ): boolean {
    const includeStart = include.start;
    const includeEnd = includeStart + include.match.length;

    // Find all conditional blocks in the template
    const ifBlocks = this.findOutermostIfBlocks(template);
    const unlessBlocks = this.findOutermostUnlessBlocks(template);
    const allConditionals = [...ifBlocks, ...unlessBlocks];

    // Check if the include is inside any conditional block
    for (let i = 0; i < allConditionals.length; i += 1) {
      const conditional = allConditionals[i];
      const conditionalStart = template.indexOf(conditional.fullMatch);
      const conditionalEnd = conditionalStart + conditional.fullMatch.length;

      if (includeStart >= conditionalStart && includeEnd <= conditionalEnd) {
        return true;
      }
    }

    return false;
  }

  /**
   * Process {{#each}} blocks for array iteration with proper nesting support and else blocks
   * Supports iteration over arrays and objects with @key access
   * @param template - Template string containing #each blocks
   * @param context - Context object for variable resolution
   * @param depth - Recursion depth to prevent infinite loops
   * @returns Template with #each blocks processed
   * @private
   */
  // eslint-disable-next-line class-methods-use-this
  private processEachBlocks(
    template: string,
    context: TemplateContext,
    depth = 0
  ): string {
    // Prevent infinite recursion
    if (depth > 10) {
      return template;
    }

    let result = template;
    let changed = true;

    // Keep processing until no more changes are made (to handle nested blocks)
    while (changed) {
      changed = false;
      const eachBlocks = this.findOutermostEachBlocks(result);

      for (let i = 0; i < eachBlocks.length; i += 1) {
        const block = eachBlocks[i];
        const blockResult = this.processSingleEachBlock(block, context, depth);
        result = result.replace(block.fullMatch, blockResult.replacement);
        if (blockResult.hasChanges) {
          changed = true;
        }
      }
    }

    return result;
  }

  /**
   * Process {{#if}} and {{#unless}} blocks with includes support
   * Handles conditional includes by processing them within conditional blocks
   * @param template - Template string containing conditional blocks
   * @param context - Context object for condition evaluation
   * @param depth - Recursion depth to prevent infinite loops
   * @returns Promise resolving to template with conditional blocks processed
   */
  private async processConditionalBlocksWithIncludes(
    template: string,
    context: TemplateContext,
    depth = 0
  ): Promise<string> {
    // Prevent infinite recursion
    if (depth > 10) {
      return template;
    }

    let result = template;
    let changed = true;

    // Keep processing until no more changes are made (to handle nested blocks)
    while (changed) {
      changed = false;

      // Process #if blocks (only those not inside #each blocks at depth 0)
      const ifBlocks = this.findOutermostIfBlocks(result);
      for (let i = 0; i < ifBlocks.length; i += 1) {
        const block = ifBlocks[i];
        // Skip conditionals inside #each blocks during the top-level pass
        if (depth === 0 && this.isInsideEachBlock(block, result)) {
          // Skip this block
        } else {
          // eslint-disable-next-line no-await-in-loop
          const blockResult = await this.processSingleIfBlockWithIncludes(
            block,
            context,
            depth
          );
          result = result.replace(block.fullMatch, blockResult.replacement);
          if (blockResult.hasChanges) {
            changed = true;
          }
        }
      }

      // Process #unless blocks (only those not inside #each blocks at depth 0)
      const unlessBlocks = this.findOutermostUnlessBlocks(result);
      for (let i = 0; i < unlessBlocks.length; i += 1) {
        const block = unlessBlocks[i];
        // Skip conditionals inside #each blocks during the top-level pass
        if (depth === 0 && this.isInsideEachBlock(block, result)) {
          // Skip this block
        } else {
          // eslint-disable-next-line no-await-in-loop
          const blockResult = await this.processSingleUnlessBlockWithIncludes(
            block,
            context,
            depth
          );
          result = result.replace(block.fullMatch, blockResult.replacement);
          if (blockResult.hasChanges) {
            changed = true;
          }
        }
      }
    }

    return result;
  }

  /**
   * Process {{#if}} and {{#unless}} blocks for conditional rendering with else support
   * Handles nested conditionals and complex expressions
   * @param template - Template string containing conditional blocks
   * @param context - Context object for condition evaluation
   * @param depth - Recursion depth to prevent infinite loops
   * @returns Template with conditional blocks processed
   */
  // eslint-disable-next-line class-methods-use-this
  public processConditionalBlocks(
    template: string,
    context: TemplateContext,
    depth = 0
  ): string {
    // Prevent infinite recursion
    if (depth > 10) {
      return template;
    }

    let result = template;
    let changed = true;

    // Keep processing until no more changes are made (to handle nested blocks)
    while (changed) {
      changed = false;

      // Process #if blocks (only those not inside #each blocks at depth 0)
      const ifBlocks = this.findOutermostIfBlocks(result);
      for (let i = 0; i < ifBlocks.length; i += 1) {
        const block = ifBlocks[i];
        // Skip conditionals inside #each blocks during the top-level pass
        if (depth === 0 && this.isInsideEachBlock(block, result)) {
          // Skip this block
        } else {
          const blockResult = this.processSingleIfBlock(block, context, depth);
          result = result.replace(block.fullMatch, blockResult.replacement);
          if (blockResult.hasChanges) {
            changed = true;
          }
        }
      }

      // Process #unless blocks (only those not inside #each blocks at depth 0)
      const unlessBlocks = this.findOutermostUnlessBlocks(result);
      for (let i = 0; i < unlessBlocks.length; i += 1) {
        const block = unlessBlocks[i];
        // Skip conditionals inside #each blocks during the top-level pass
        if (depth === 0 && this.isInsideEachBlock(block, result)) {
          // Skip this block
        } else {
          const blockResult = this.processSingleUnlessBlock(
            block,
            context,
            depth
          );
          result = result.replace(block.fullMatch, blockResult.replacement);
          if (blockResult.hasChanges) {
            changed = true;
          }
        }
      }
    }

    return result;
  }

  /**
   * Check if a conditional block is inside an #each block for proper processing order
   * @param block - Conditional block to check
   * @param template - Full template string
   * @returns True if the conditional is nested inside an #each block
   * @private
   */
  // eslint-disable-next-line class-methods-use-this
  private isInsideEachBlock(
    block: { fullMatch: string; condition: string; innerTemplate: string },
    template: string
  ): boolean {
    const blockStart = template.indexOf(block.fullMatch);
    if (blockStart === -1) return false;

    // Find all #each blocks in the template
    const eachBlocks = this.findOutermostEachBlocks(template);

    // Check if our conditional block is inside any #each block
    for (let i = 0; i < eachBlocks.length; i += 1) {
      const eachBlock = eachBlocks[i];
      const eachStart = template.indexOf(eachBlock.fullMatch);
      const eachEnd = eachStart + eachBlock.fullMatch.length;

      if (blockStart > eachStart && blockStart < eachEnd) {
        return true;
      }
    }

    return false;
  }

  /**
   * Process a single #if block with else clause support
   * @param block - If block containing condition, inner template, and optional else template
   * @param context - Context object for condition evaluation
   * @param depth - Recursion depth for nested processing
   * @returns Object with replacement text and change status
   * @private
   */
  // eslint-disable-next-line class-methods-use-this
  private processSingleIfBlock(
    block: {
      fullMatch: string;
      condition: string;
      innerTemplate: string;
      elseTemplate?: string;
    },
    context: TemplateContext,
    depth: number
  ): { replacement: string; hasChanges: boolean } {
    const conditionValue = this.evaluateCondition(
      block.condition.trim(),
      context
    );
    const isTruthy = this.isTruthy(conditionValue);

    if (isTruthy) {
      // First process any #each blocks in the inner template (which will handle their own conditionals)
      let processedInner = this.processEachBlocks(
        block.innerTemplate,
        context,
        depth + 1
      );

      // Then recursively process remaining conditional blocks
      processedInner = this.processConditionalBlocks(
        processedInner,
        context,
        depth + 1
      );

      return { replacement: processedInner, hasChanges: true };
    }

    // Process else block if it exists
    if (block.elseTemplate) {
      let processedElse = this.processEachBlocks(
        block.elseTemplate,
        context,
        depth + 1
      );

      processedElse = this.processConditionalBlocks(
        processedElse,
        context,
        depth + 1
      );

      return { replacement: processedElse, hasChanges: true };
    }

    return { replacement: '', hasChanges: true };
  }

  /**
   * Process a single #unless block with else clause support
   * @param block - Unless block containing condition, inner template, and optional else template
