          enums: String,
          defaults: true,
          oneofs: true,
        }
      );

      const protoDescriptor = grpc.loadPackageDefinition(
        packageDefinition
      ) as unknown as {
        promptwizard: {
          PromptOptimizationService: new (
            url: string,
            credentials: grpc.ChannelCredentials,
            options: Record<string, unknown>
          ) => GrpcClient;
        };
      };
      const promptwizardProto = protoDescriptor.promptwizard;

      // Create client with credentials
      const credentials =
        this.config.credentials || grpc.credentials.createInsecure();

      this.client = new promptwizardProto.PromptOptimizationService(
        this.config.serviceUrl,
        credentials,
        {
          'grpc.keepalive_time_ms': this.config.keepAlive ? 10000 : 0,
          'grpc.keepalive_timeout_ms': 5000,
          'grpc.keepalive_permit_without_calls': true,
          'grpc.http2.max_pings_without_data': 0,
          'grpc.max_receive_message_length':
            this.config.maxReceiveMessageLength,
          'grpc.max_send_message_length': this.config.maxSendMessageLength,
        }
      );

      // Test connection
      await this.healthCheck();
      this.isConnected = true;

      logger.info('gRPC client initialized successfully', {
        serviceUrl: this.config.serviceUrl,
      });
    } catch (error) {
      logger.error('Failed to initialize gRPC client', error as Error);
      throw new Error(`gRPC client initialization failed: ${error}`);
    }
  }

  /**
   * Health check via gRPC
   */
  async healthCheck(): Promise<boolean> {
    return new Promise((resolve, reject) => {
      if (!this.client) {
        reject(new Error('gRPC client not initialized'));
        return;
      }

      const deadline = new Date();
      deadline.setSeconds(deadline.getSeconds() + 5);

      this.client.healthCheck(
        {},
        { deadline },
        (error: unknown, response: { healthy: boolean }) => {
          if (error) {
            logger.error('gRPC health check failed', error);
            resolve(false);
          } else {
            resolve(response.healthy === true);
          }
        }
      );
    });
  }

  /**
   * Optimize prompt via gRPC
   */
  async optimizePrompt(
    request: OptimizationRequest
  ): Promise<OptimizationResponse> {
    return this.executeWithRetry(
      'optimizePrompt',
      request
    ) as Promise<OptimizationResponse>;
  }

  /**
   * Score prompt via gRPC
   */
  async scorePrompt(request: ScoringRequest): Promise<ScoringResponse> {
    return this.executeWithRetry(
      'scorePrompt',
      request
    ) as Promise<ScoringResponse>;
  }

  /**
   * Compare prompts via gRPC
   */
  async comparePrompts(
    request: ComparisonRequest
  ): Promise<ComparisonResponse> {
    return this.executeWithRetry(
      'comparePrompts',
      request
    ) as Promise<ComparisonResponse>;
  }

  /**
   * Stream optimization updates via gRPC
   */
  streamOptimization(request: OptimizationRequest): EventEmitter {
    const streamEmitter = new EventEmitter();

    if (!this.client) {
      streamEmitter.emit('error', new Error('gRPC client not initialized'));
      return streamEmitter;
    }

    try {
      const deadline = new Date();
      deadline.setSeconds(deadline.getSeconds() + this.config.timeout / 1000);

      const stream = this.client.streamOptimization(request, { deadline });

      stream.on('data', (update: unknown) => {
        const grpcUpdate = update as GrpcStreamUpdate;
        const typedUpdate: StreamOptimizationUpdate = {
          jobId: grpcUpdate.job_id,
          progress: grpcUpdate.progress_percentage,
          currentStep: grpcUpdate.current_step,
          status: grpcUpdate.status as 'processing' | 'completed' | 'failed',
          partialResult: grpcUpdate.partial_result
            ? this.convertGrpcOptimizationResponse(grpcUpdate.partial_result)
            : undefined,
        };

        streamEmitter.emit('update', typedUpdate);

        // Emit final result if completed
        if (grpcUpdate.status === 'completed' && grpcUpdate.partial_result) {
          streamEmitter.emit('completed', typedUpdate.partialResult);
        }
      });

      stream.on('end', () => {
        streamEmitter.emit('end');
      });

      stream.on('error', (error: unknown) => {
        const grpcError = error as GrpcError;
        logger.error('gRPC stream error', error);
        streamEmitter.emit('error', this.parseGrpcError(grpcError));
      });

      // Add cancel method
      Object.defineProperty(streamEmitter, 'cancel', {
        value: () => stream.cancel(),
        enumerable: false,
        writable: false,
      });
    } catch (error) {
      logger.error('Failed to start gRPC stream', error as Error);
      streamEmitter.emit('error', error);
    }

    return streamEmitter;
  }

  /**
   * Get job status via gRPC
   */
  async getJobStatus(
    jobId: string
  ): Promise<{ status: string; progress?: number; error?: string }> {
    return this.executeWithRetry('getJobStatus', { job_id: jobId }) as Promise<{
      status: string;
      progress?: number;
      error?: string;
    }>;
  }

  /**
   * Cancel job via gRPC
   */
  async cancelJob(
    jobId: string
  ): Promise<{ cancelled: boolean; message: string }> {
    return this.executeWithRetry('cancelJob', { job_id: jobId }) as Promise<{
      cancelled: boolean;
      message: string;
    }>;
  }

  /**
   * Execute gRPC method with retry logic
   */
  private async executeWithRetry(
    method: string,
    request:
      | OptimizationRequest
      | ScoringRequest
      | ComparisonRequest
      | { job_id: string },
    retryCount: number = 0
  ): Promise<
    | OptimizationResponse
    | ScoringResponse
    | ComparisonResponse
    | { status: string; progress?: number; error?: string }
    | { cancelled: boolean; message: string }
  > {
    return new Promise((resolve, reject) => {
      if (!this.client) {
        reject(new Error('gRPC client not initialized'));
        return;
      }

      const deadline = new Date();
      deadline.setSeconds(deadline.getSeconds() + this.config.timeout / 1000);

      ((this.client as unknown) as Record<string, (...args: unknown[]) => unknown>)[method](request, { deadline }, (error: unknown, response: unknown) => {
        if (error) {
          const grpcError = this.parseGrpcError(error);

          // Retry on specific error conditions
          if (
            retryCount < this.config.retries &&
            this.isRetryableError(error)
          ) {
            logger.warn(
              `gRPC ${method} failed, retrying (${retryCount + 1}/${this.config.retries})`,
              {
                error: grpcError.message,
                retryCount: retryCount + 1,
              }
            );

            // Exponential backoff
            const backoffDelay = 2 ** retryCount * 1000;
            setTimeout(() => {
              this.executeWithRetry(method, request, retryCount + 1)
                .then(resolve)
                .catch(reject);
            }, backoffDelay);
            return;
          }

          logger.error(`gRPC ${method} failed`, grpcError);
          reject(grpcError);
        } else {
          resolve(this.convertGrpcResponse(method, response));
        }
      });
    });
  }

  /**
   * Check if error is retryable
   */
  private isRetryableError(error: unknown): boolean {
    const retryableCodes = [
      grpc.status.UNAVAILABLE,
      grpc.status.DEADLINE_EXCEEDED,
      grpc.status.RESOURCE_EXHAUSTED,
      grpc.status.INTERNAL,
    ];

    return (
      typeof error === 'object' &&
      error !== null &&
      'code' in error &&
      retryableCodes.includes((error as GrpcError).code)
    );
  }

  /**
   * Parse gRPC error into standard format
   */
  private parseGrpcError(error: unknown): Error {
    if (typeof error === 'object' && error !== null && 'code' in error) {
      const grpcError = error as GrpcError;
      const statusText =
        Object.keys(grpc.status as unknown as Record<string, number>).find(
          key =>
            (grpc.status as unknown as Record<string, number>)[key] ===
            grpcError.code
        ) || 'UNKNOWN';

      return new Error(
        `gRPC ${statusText}: ${grpcError.details || grpcError.message}`
      );
    }

    return new Error(`Unknown gRPC error: ${String(error)}`);
  }

  /**
   * Convert gRPC response to TypeScript types
   */
  private convertGrpcResponse(
    method: string,
    response: unknown
  ):
    | OptimizationResponse
    | ScoringResponse
    | ComparisonResponse
    | { status: string; progress?: number; error?: string }
    | { cancelled: boolean; message: string } {
    switch (method) {
      case 'optimizePrompt':
        return this.convertGrpcOptimizationResponse(response);
      case 'scorePrompt':
        return this.convertGrpcScoringResponse(response);
      case 'comparePrompts':
        return this.convertGrpcComparisonResponse(response);
      case 'getJobStatus':
        return this.convertGrpcJobStatusResponse(response);
      default:
        return response as
          | OptimizationResponse
          | ScoringResponse
          | ComparisonResponse
          | { status: string; progress?: number; error?: string }
          | { cancelled: boolean; message: string };
    }
  }

  /**
   * Convert gRPC optimization response
   */
  private convertGrpcOptimizationResponse(
    response: unknown
  ): OptimizationResponse {
    const grpcResponse = response as {
      job_id?: string;
      status?: string;
      original_prompt?: string;
      optimized_prompt?: string;
      metrics?: {
        accuracy_improvement?: number;
        token_reduction?: number;
        cost_reduction?: number;
        processing_time?: number;
        api_calls_used?: number;
      };
      error_message?: string;
    };

    const converted = {
      jobId: grpcResponse.job_id || '',
      status: grpcResponse.status || 'failed',
      originalPrompt: grpcResponse.original_prompt || '',
      optimizedPrompt: grpcResponse.optimized_prompt || '',
      metrics: {
        accuracyImprovement: grpcResponse.metrics?.accuracy_improvement || 0,
        tokenReduction: grpcResponse.metrics?.token_reduction || 0,
        costReduction: grpcResponse.metrics?.cost_reduction || 0,
        processingTime: grpcResponse.metrics?.processing_time || 0,
        apiCallsUsed: grpcResponse.metrics?.api_calls_used || 0,
      } as OptimizationMetrics,
      error: grpcResponse.error_message,
    };

    // Validate converted response with Zod schema
    const validationResult = safeValidateOptimizationResponse(converted);
    if (!validationResult.success) {
      logger.error(
        'Invalid OptimizationResponse from gRPC API:',
        validationResult.error.issues
      );
      throw new Error(
        `Invalid gRPC response: ${validationResult.error.issues.map((i: { message: string }) => i.message).join(', ')}`
      );
    }

    return validationResult.data;
  }

  /**
   * Convert gRPC scoring response
   */
  private convertGrpcScoringResponse(response: unknown): ScoringResponse {
    const grpcResponse = response as {
      overall_score?: number;
      component_scores?: Record<string, unknown>;
      suggestions?: string[];
      metrics?: Record<string, unknown>;
    };

    // Ensure componentScores is properly typed
    const componentScores: Record<string, number> = {};
    if (
      grpcResponse.component_scores &&
      typeof grpcResponse.component_scores === 'object'
    ) {
      Object.entries(grpcResponse.component_scores).forEach(([key, value]) => {
        componentScores[key] = typeof value === 'number' ? value : 0;
      });
    }

    const converted = {
      overallScore: grpcResponse.overall_score || 0,
      componentScores,
      suggestions: grpcResponse.suggestions || [],
      metrics: grpcResponse.metrics || {},
    };

    // Validate converted response with Zod schema
    try {
      return validateScoringResponse(converted);
    } catch (error) {
      logger.error('Invalid ScoringResponse from gRPC API:', error);
      throw new Error(
        `Invalid gRPC response: ${error instanceof Error ? error.message : String(error)}`
      );
    }
  }

  /**
   * Convert gRPC comparison response
   */
  private convertGrpcComparisonResponse(response: unknown): ComparisonResponse {
    const grpcResponse = response as {
      improvement_score?: number;
      improvements?: string[];
      potential_issues?: string[];
      metrics?: Record<string, unknown>;
    };

    const converted = {
      improvementScore: grpcResponse.improvement_score || 0,
      improvements: grpcResponse.improvements || [],
      potentialIssues: grpcResponse.potential_issues || [],
      metrics: grpcResponse.metrics || {},
    };

    // Validate converted response with Zod schema
    try {
      return validateComparisonResponse(converted);
    } catch (error) {
      logger.error('Invalid ComparisonResponse from gRPC API:', error);
      throw new Error(
        `Invalid gRPC response: ${error instanceof Error ? error.message : String(error)}`
      );
    }
  }

  /**
   * Convert gRPC job status response
   */
  private convertGrpcJobStatusResponse(response: unknown): {
    jobId: string;
    status: string;
    progressPercentage?: number;
    currentStep?: string;
    result?: OptimizationResponse | null;
    error?: string;
  } {
    const grpcResponse = response as {
      job_id?: string;
      status?: string;
      progress_percentage?: number;
      current_step?: string;
      result?: unknown;
      error_message?: string;
    };

    return {
      jobId: grpcResponse.job_id || '',
      status: grpcResponse.status || 'unknown',
      progressPercentage: grpcResponse.progress_percentage,
      currentStep: grpcResponse.current_step,
      result: grpcResponse.result
        ? this.convertGrpcOptimizationResponse(grpcResponse.result)
        : null,
      error: grpcResponse.error_message,
    };
  }

  /**
   * Close gRPC client
   */
  async close(): Promise<void> {
    if (this.client) {
      (this.client as unknown as { close: () => void }).close();
      this.isConnected = false;
      logger.info('gRPC client closed');
    }
  }

  /**
   * Get connection status
   */
  isClientConnected(): boolean {
    return this.isConnected;
  }
}

/**
 * @fileoverview Zod schemas for PromptWizard API validation
 * @lastmodified 2025-08-27T02:45:00Z
 *
 * Features: Runtime type validation using Zod schemas for PromptWizard API responses
 * Main APIs: Schema definitions for OptimizedResult, QualityScore, PromptComparison, etc.
 * Constraints: Must match PromptWizard API specification, provides type safety at runtime
 * Patterns: Schema-based validation, type inference, API contract enforcement
 */

import { z } from 'zod';

// Quality Score Schema
export const QualityScoreSchema = z.object({
  overall: z.number().min(0).max(100),
  metrics: z.object({
    clarity: z.number().min(0).max(100),
    taskAlignment: z.number().min(0).max(100),
    tokenEfficiency: z.number().min(0).max(100),
    exampleQuality: z.number().min(0).max(100).optional(),
  }),
  suggestions: z.array(z.string()),
  confidence: z.number().min(0).max(100),
});

// Optimization Metrics Schema
export const OptimizationMetricsSchema = z.object({
  accuracyImprovement: z.number(),
  tokenReduction: z.number(),
  costReduction: z.number(),
  processingTime: z.number().nonnegative(),
  apiCallsUsed: z.number().int().nonnegative(),
});

// Example Schema
export const ExampleSchema = z.object({
  input: z.string(),
  output: z.string(),
});

// Error Schema
export const ErrorSchema = z.object({
  code: z.string(),
  message: z.string(),
  details: z.unknown().optional(),
});

// Optimized Result Schema
export const OptimizedResultSchema = z.object({
  jobId: z.string(),
  originalPrompt: z.string(),
  optimizedPrompt: z.string(),
  status: z.enum(['pending', 'processing', 'completed', 'failed']),
  metrics: OptimizationMetricsSchema,
  examples: z.array(ExampleSchema).optional(),
  reasoning: z.array(z.string()).optional(),
  optimizedTemplate: z.string().optional(),
  qualityScore: z.number().min(0).max(100).optional(),
  comparison: z.unknown().optional(), // Replace any with unknown
  error: ErrorSchema.optional(),
  createdAt: z.string().transform(str => new Date(str)), // Transform string to Date
  completedAt: z
    .string()
    .transform(str => new Date(str))
    .optional(),
});

// Optimization Config Schema
export const OptimizationConfigSchema = z.object({
  prompt: z.string().min(1),
  task: z.string().min(1),
  targetModel: z
    .enum([
      'gpt-4',
      'gpt-3.5-turbo',
      'claude-3-opus',
      'claude-3-sonnet',
      'gemini-pro',
    ])
    .optional(),
  mutateRefineIterations: z.number().int().min(1).max(10).optional(),
  fewShotCount: z.number().int().min(0).max(20).optional(),
  generateReasoning: z.boolean().optional(),
  customParams: z.record(z.string(), z.unknown()).optional(),
  metadata: z
    .object({
      templateId: z.string().optional(),
      templateName: z.string().optional(),
      version: z.string().optional(),
      author: z.string().optional(),
    })
    .optional(),
  focusAreas: z.array(z.string()).optional(),
  improvementTargets: z.array(z.string()).optional(),
  constraints: z
    .object({
      maxLength: z.number().int().positive().optional(),
      preserveVariables: z.boolean().optional(),
      maintainStructure: z.boolean().optional(),
      focusAreas: z.array(z.string()).optional(),
    })
    .passthrough()
    .optional(), // Allow additional properties
});

// Prompt Comparison Schema
export const PromptComparisonSchema = z.object({
  comparisonId: z.string(),
  original: z.object({
    prompt: z.string(),
    score: QualityScoreSchema,
    estimatedTokens: z.number().int().nonnegative(),
    estimatedCost: z.number().nonnegative(),
  }),
  optimized: z.object({
    prompt: z.string(),
    score: QualityScoreSchema,
    estimatedTokens: z.number().int().nonnegative(),
    estimatedCost: z.number().nonnegative(),
  }),
  improvements: z
    .object({
      qualityImprovement: z.number(),
      tokenReduction: z.number(),
      costSavings: z.number(),
    })
    .optional(),
  analysis: z.object({
    strengthsGained: z.array(z.string()),
    potentialRisks: z.array(z.string()),
    recommendations: z.array(z.string()),
  }),
});

// Optimization Job Schema
export const OptimizationJobSchema = z.object({
  jobId: z.string(),
  status: z.enum(['queued', 'processing', 'completed', 'failed', 'cancelled']),
  progress: z.number().min(0).max(100),
  currentStep: z.string(),
  estimatedCompletion: z
    .string()
    .transform(str => new Date(str))
    .optional(),
  config: OptimizationConfigSchema,
  result: OptimizedResultSchema.optional(),
  error: z
    .object({
      code: z.string(),
      message: z.string(),
      stack: z.string().optional(),
    })
    .optional(),
});

// Service Response Schema (Generic)
export const ServiceResponseSchema = <T extends z.ZodType>(dataSchema: T): z.ZodObject<{
  success: z.ZodBoolean;
  data: T;
  error: z.ZodOptional<z.ZodObject<{
    code: z.ZodString;
    message: z.ZodString;
    details: z.ZodOptional<z.ZodUnknown>;
  }>>;
  timestamp: z.ZodString;
}> =>
  z.object({
    success: z.boolean(),
    data: dataSchema.optional(),
    error: ErrorSchema.optional(),
    metadata: z
      .object({
        requestId: z.string(),
        timestamp: z.string().transform(str => new Date(str)),
        processingTime: z.number().nonnegative(),
      })
      .optional(),
  });

// Optimization Response Schema
export const OptimizationResponseSchema = z.object({
  jobId: z.string(),
  result: OptimizedResultSchema.optional(),
  status: z.enum(['success', 'error', 'pending']),
  error: z.string().optional(),
  confidence: z.number().min(0).max(100).optional(),
  metrics: OptimizationMetricsSchema.optional(),
  originalPrompt: z.string().optional(),
  optimizedPrompt: z.string().optional(),
  metadata: z
    .object({
      processingTime: z.number().nonnegative(),
      requestId: z.string(),
      timestamp: z.string().transform(str => new Date(str)),
    })
    .optional(),
});

// Scoring Request/Response Schemas for gRPC client
export const ScoringRequestSchema = z.object({
  prompt: z.string().min(1),
  criteria: z.array(z.string()).optional(),
  model: z.string().optional(),
});

export const ScoringResponseSchema = z.object({
  overallScore: z.number().min(0).max(100),
  componentScores: z.record(z.string(), z.number()),
  suggestions: z.array(z.string()),
  metrics: z.record(z.string(), z.unknown()),
});

// Comparison Request/Response Schemas
export const ComparisonRequestSchema = z.object({
  originalPrompt: z.string().min(1),
  optimizedPrompt: z.string().min(1),
  criteria: z.array(z.string()).optional(),
});

export const ComparisonResponseSchema = z.object({
  improvementScore: z.number(),
  improvements: z.array(z.string()),
  potentialIssues: z.array(z.string()),
  metrics: z.record(z.string(), z.unknown()),
});

// Optimization Event Schema
export const OptimizationEventSchema = z.discriminatedUnion('type', [
  z.object({
    type: z.literal('started'),
    jobId: z.string(),
    config: OptimizationConfigSchema,
  }),
  z.object({
    type: z.literal('progress'),
    jobId: z.string(),
    progress: z.number().min(0).max(100),
    step: z.string(),
  }),
  z.object({
    type: z.literal('completed'),
    jobId: z.string(),
    result: OptimizedResultSchema,
  }),
  z.object({
    type: z.literal('failed'),
    jobId: z.string(),
    error: z.object({
      code: z.string(),
      message: z.string(),
    }),
  }),
  z.object({
    type: z.literal('cancelled'),
    jobId: z.string(),
  }),
]);

// Type inference helpers
export type QualityScore = z.infer<typeof QualityScoreSchema>;
export type OptimizedResult = z.infer<typeof OptimizedResultSchema>;
export type OptimizationConfig = z.infer<typeof OptimizationConfigSchema>;
export type PromptComparison = z.infer<typeof PromptComparisonSchema>;
export type OptimizationJob = z.infer<typeof OptimizationJobSchema>;
export type OptimizationResponse = z.infer<typeof OptimizationResponseSchema>;
export type ScoringRequest = z.infer<typeof ScoringRequestSchema>;
export type ScoringResponse = z.infer<typeof ScoringResponseSchema>;
export type ComparisonRequest = z.infer<typeof ComparisonRequestSchema>;
export type ComparisonResponse = z.infer<typeof ComparisonResponseSchema>;
export type OptimizationEvent = z.infer<typeof OptimizationEventSchema>;

// Validation helper functions
export const validateQualityScore = (data: unknown): QualityScore =>
  QualityScoreSchema.parse(data);

export const validateOptimizedResult = (data: unknown): OptimizedResult =>
  OptimizedResultSchema.parse(data);

export const validateOptimizationConfig = (data: unknown): OptimizationConfig =>
  OptimizationConfigSchema.parse(data);

export const validatePromptComparison = (data: unknown): PromptComparison =>
  PromptComparisonSchema.parse(data);

export const validateOptimizationJob = (data: unknown): OptimizationJob =>
  OptimizationJobSchema.parse(data);

export const validateOptimizationResponse = (
  data: unknown
): OptimizationResponse => OptimizationResponseSchema.parse(data);

export const validateScoringRequest = (data: unknown): ScoringRequest =>
  ScoringRequestSchema.parse(data);

export const validateScoringResponse = (data: unknown): ScoringResponse =>
  ScoringResponseSchema.parse(data);

export const validateComparisonRequest = (data: unknown): ComparisonRequest =>
  ComparisonRequestSchema.parse(data);

export const validateComparisonResponse = (data: unknown): ComparisonResponse =>
  ComparisonResponseSchema.parse(data);

export const validateOptimizationEvent = (data: unknown): OptimizationEvent =>
  OptimizationEventSchema.parse(data);

// Safe validation functions that return Result<T, Error>
export const safeValidateQualityScore = (
  data: unknown
):
  | { success: true; data: QualityScore }
  | { success: false; error: z.ZodError } => {
  const result = QualityScoreSchema.safeParse(data);
  if (result.success) {
    return { success: true, data: result.data };
  }
  return { success: false, error: result.error };
};

export const safeValidateOptimizedResult = (
  data: unknown
):
  | { success: true; data: OptimizedResult }
  | { success: false; error: z.ZodError } => {
  const result = OptimizedResultSchema.safeParse(data);
  if (result.success) {
    return { success: true, data: result.data };
  }
  return { success: false, error: result.error };
};

export const safeValidateOptimizationResponse = (
  data: unknown
):
  | { success: true; data: OptimizationResponse }
  | { success: false; error: z.ZodError } => {
  const result = OptimizationResponseSchema.safeParse(data);
  if (result.success) {
    return { success: true, data: result.data };
  }
  return { success: false, error: result.error };
};

export const safeValidatePromptComparison = (
  data: unknown
):
  | { success: true; data: PromptComparison }
  | { success: false; error: z.ZodError } => {
  const result = PromptComparisonSchema.safeParse(data);
  if (result.success) {
    return { success: true, data: result.data };
  }
  return { success: false, error: result.error };
};

export const safeValidateOptimizationJob = (
  data: unknown
):
  | { success: true; data: OptimizationJob }
  | { success: false; error: z.ZodError } => {
  const result = OptimizationJobSchema.safeParse(data);
  if (result.success) {
    return { success: true, data: result.data };
  }
  return { success: false, error: result.error };
};

/**
 * @fileoverview TypeScript interfaces and types for PromptWizard integration
 * @lastmodified 2025-08-26T12:00:00Z
 *
 * Features: Type-safe interfaces for PromptWizard API communication
 * Main APIs: OptimizationConfig, OptimizedResult, PromptWizardService interfaces
 * Constraints: Must align with Microsoft PromptWizard API specification
 * Patterns: Interface segregation, type safety, API contract definitions
 */

export interface OptimizationConfig {
  /** The prompt template to optimize */
  prompt: string;

  /** Task description for the prompt */
  task: string;

  /** Target model for optimization */
  targetModel?:
    | 'gpt-4'
    | 'gpt-3.5-turbo'
    | 'claude-3-opus'
    | 'claude-3-sonnet'
    | 'gemini-pro';

  /** Number of refinement iterations */
  mutateRefineIterations?: number;

  /** Number of few-shot examples to generate */
  fewShotCount?: number;

  /** Whether to generate reasoning steps */
  generateReasoning?: boolean;

  /** Custom optimization parameters */
  customParams?: Record<string, unknown>;

  /** Template metadata */
  metadata?: {
    templateId?: string;
    templateName?: string;
    version?: string;
    author?: string;
  };

  /** Focus areas for optimization */
  focusAreas?: string[];

  /** Improvement targets */
  improvementTargets?: string[];

  /** Constraints for optimization */
  constraints?: {
    maxLength?: number;
    preserveVariables?: boolean;
    maintainStructure?: boolean;
    focusAreas?: string[];
    [key: string]: unknown;
  };
}

export interface OptimizedResult {
  /** Optimization job ID */
  jobId: string;

  /** Original prompt */
  originalPrompt: string;

  /** Optimized prompt */
  optimizedPrompt: string;

  /** Optimization status */
  status: 'pending' | 'processing' | 'completed' | 'failed';

  /** Performance metrics */
  metrics: {
    /** Accuracy improvement percentage */
    accuracyImprovement: number;

    /** Token count reduction percentage */
    tokenReduction: number;

    /** Cost reduction multiplier */
    costReduction: number;

    /** Processing time in seconds */
    processingTime: number;

    /** API calls used in optimization */
    apiCallsUsed: number;
  };

  /** Generated few-shot examples */
  examples?: Array<{
    input: string;
    output: string;
  }>;

  /** Reasoning chain if generated */
  reasoning?: string[];

  /** The optimized template */
  optimizedTemplate?: string;

  /** Quality score of optimization */
  qualityScore?: number;

  /** Comparison data */
  comparison?: PromptComparison | unknown;

  /** Error information if failed */
  error?: {
    code: string;
    message: string;
    details?: unknown;
  };

  /** Timestamps */
  createdAt: Date;
  completedAt?: Date;
}

export interface QualityScore {
  /** Overall quality score (0-100) */
  overall: number;

  /** Individual scoring metrics */
  metrics: {
    /** Clarity and specificity score */
    clarity: number;

    /** Task alignment score */
    taskAlignment: number;

    /** Token efficiency score */
    tokenEfficiency: number;

    /** Example quality score */
    exampleQuality?: number;
  };

  /** Suggestions for improvement */
  suggestions: string[];

  /** Confidence level of scoring */
  confidence: number;
}

export interface PromptComparison {
  /** Comparison ID */
  comparisonId: string;

  /** Original prompt metrics */
  original: {
    prompt: string;
    score: QualityScore;
    estimatedTokens: number;
    estimatedCost: number;
  };

  /** Optimized prompt metrics */
  optimized: {
    prompt: string;
    score: QualityScore;
    estimatedTokens: number;
    estimatedCost: number;
  };

  /** Comparison results */
  improvements?: {
    qualityImprovement: number;
    tokenReduction: number;
    costSavings: number;
  };

  /** Side-by-side analysis */
  analysis: {
    strengthsGained: string[];
    potentialRisks: string[];
    recommendations: string[];
  };
}


export interface Example {
  /** Example ID */
  id: string;

  /** Input text */
  input: string;

  /** Expected output */
  output: string;

  /** Quality rating */
  quality: number;

  /** Metadata */
  metadata?: Record<string, unknown>;
}

export interface OptimizationJob {
  /** Job identifier */
  jobId: string;

  /** Current status */
  status: 'queued' | 'processing' | 'completed' | 'failed' | 'cancelled';

  /** Progress percentage */
  progress: number;

  /** Current step description */
  currentStep: string;

  /** Estimated completion time */
  estimatedCompletion?: Date;

  /** Configuration used */
  config: OptimizationConfig;

  /** Results (if completed) */
  result?: OptimizedResult;

  /** Error information (if failed) */
  error?: {
    code: string;
    message: string;
    stack?: string;
  };
}

export interface PromptWizardService {
  /**
   * Optimize a prompt using PromptWizard
   */
  optimizePrompt(config: OptimizationConfig): Promise<OptimizedResult>;

  /**
   * Score a prompt for quality
   */
  scorePrompt(prompt: string, task?: string): Promise<QualityScore>;

  /**
   * Compare two prompts
   */
  comparePrompts(
    original: string,
    optimized: string,
    task?: string
  ): Promise<PromptComparison>;

  /**
   * Generate examples for a task
   */
  generateExamples(task: string, count: number): Promise<Example[]>;

  /**
   * Get optimization job status
   */
  getJobStatus(jobId: string): Promise<OptimizationJob>;

  /**
   * Cancel an optimization job
   */
  cancelJob(jobId: string): Promise<boolean>;

  /**
   * Validate service health
   */
  healthCheck(): Promise<boolean>;
}

export interface PromptWizardConfig {
  /** Service endpoint URL */
  serviceUrl: string;

  /** API timeout in milliseconds */
  timeout: number;

  /** Maximum retry attempts */
  retries: number;

  /** Default optimization parameters */
  defaults: {
    targetModel: string;
    mutateRefineIterations: number;
    fewShotCount: number;
    generateReasoning: boolean;
  };

  /** Caching configuration */
  cache: {
    enabled: boolean;
    ttl: number;
    maxSize: number;
  };

  /** Authentication configuration */
  auth?: {
    apiKey?: string;
    tokenUrl?: string;
  };

  /** Rate limiting configuration */
  rateLimiting?: {
    maxRequests: number;
    windowMs: number;
  };
}

export interface ServiceResponse<T> {
  /** Success status */
  success: boolean;

  /** Response data */
  data?: T;

  /** Error information */
  error?: {
    code: string;
    message: string;
    details?: unknown;
  };

  /** Response metadata */
  metadata?: {
    requestId: string;
    timestamp: Date;
    processingTime: number;
  };
}

export type OptimizationEvent =
  | { type: 'started'; jobId: string; config: OptimizationConfig }
  | { type: 'progress'; jobId: string; progress: number; step: string }
  | { type: 'completed'; jobId: string; result: OptimizedResult }
  | { type: 'failed'; jobId: string; error: { code: string; message: string } }
  | { type: 'cancelled'; jobId: string };

export interface OptimizationEventHandler {
  (event: OptimizationEvent): void;
}

// Type aliases for compatibility with new integration code
export type OptimizationRequest = OptimizationConfig;
export type OptimizationResult = OptimizedResult;

export interface OptimizationResponse {
  /** Response job ID */
  jobId: string;

  /** Optimization result */
  result?: OptimizationResult;

  /** Response status */
  status: 'success' | 'error' | 'pending';

  /** Error message if failed */
  error?: string;

  /** Confidence score */
  confidence?: number;

  /** Performance metrics */
  metrics?: {
    accuracyImprovement: number;
    tokenReduction: number;
    costReduction: number;
    processingTime: number;
    apiCallsUsed: number;
  };

  /** Original prompt */
  originalPrompt?: string;

  /** Optimized prompt */
  optimizedPrompt?: string;

  /** Response metadata */
  metadata?: {
    processingTime: number;
    requestId: string;
    timestamp: Date;
  };
}

export interface OptimizationMetrics {
  /** Accuracy improvement percentage */
  accuracyImprovement: number;

  /** Token count reduction percentage */
  tokenReduction: number;

  /** Cost reduction multiplier */
  costReduction: number;

  /** Processing time in seconds */
  processingTime: number;

  /** API calls used in optimization */
  apiCallsUsed: number;

  /** Quality score */
  qualityScore?: number;
}

export interface OptimizationContext {
  /** Template ID */
  templateId: string;

  /** Target model for optimization */
  targetModel: string;

  /** Task description */
  task: string;

  /** User preferences */
  preferences?: Record<string, unknown>;

  /** Context metadata */
  metadata?: Record<string, unknown>;
}

export interface PipelineStage {
  /** Stage name */
  name: string;

  /** Stage description */
  description: string;

  /** Stage function */
  execute: (context: OptimizationContext, data: unknown) => Promise<unknown>;

  /** Stage dependencies */
  dependencies?: string[];

  /** Stage timeout in ms */
  timeout?: number;
}

export interface PipelineResult {
  /** Pipeline execution success */
  success: boolean;

  /** Pipeline result data */
  data?: OptimizationResult;

  /** Optimization result - alias for backwards compatibility */
  optimizationResult?: OptimizationResult;

  /** Pipeline error */
  error?: {
    stage: string;
    message: string;
    code: string;
  };

  /** Pipeline metrics */
  metrics: {
    totalTime: number;
    stagesCompleted: number;
    stagesFailed: number;
  };

  /** Stage results */
  stageResults: Record<string, unknown>;
}

// Additional types needed for gRPC client
export interface ScoringRequest {
  prompt: string;
  criteria?: string[];
  model?: string;
}

export interface ScoringResponse {
  overallScore: number;
  componentScores: Record<string, number>;
  suggestions: string[];
  metrics: Record<string, unknown>;
}

export interface ComparisonRequest {
  originalPrompt: string;
  optimizedPrompt: string;
  criteria?: string[];
}

export interface ComparisonResponse {
  improvementScore: number;
  improvements: string[];
  potentialIssues: string[];
  metrics: Record<string, unknown>;
}

/**
 * @fileoverview Refactored marketplace service using domain services
 * @lastmodified 2025-08-22T21:45:00Z
 *
 * Features: Orchestrates marketplace operations using specialized services
 * Main APIs: Delegates to domain services for specific operations
 * Constraints: Maintains backward compatibility
 * Patterns: Facade pattern with service composition
 */

import * as os from 'os';
import * as path from 'path';

import { MarketplaceAPI } from '../api/marketplace.api';
import {
  TemplateModel,
  TemplateSearchQuery,
  TemplateSearchResult,
  InstallationResult,
  UpdateResult,
  UpdateCheckResult,
  RatingResult,
  UserPreferences,
  TemplateReview,
} from '../models/template.model';
import { TemplateSearchService } from '../services/template-search.service';
import { TemplateInstallerService } from '../services/template-installer.service';
import { TemplateUpdaterService } from '../services/template-updater.service';
import { logger } from '../../utils/logger';
import { AuthorService } from './author.service';
import { TemplateRegistry } from './template.registry';

export class MarketplaceRefactoredService {
  private searchService: TemplateSearchService;

  private installerService: TemplateInstallerService;

  private updaterService: TemplateUpdaterService;

  private authorService: AuthorService;

  private api: MarketplaceAPI;

  private registry: TemplateRegistry;

  private preferences: UserPreferences = {
    autoUpdate: false,
    checkInterval: 86400000, // 24 hours
    includePrerelease: false,
    trustedAuthors: [],
    blockedAuthors: [],
    preferredCategories: [],
    installPath: path.join(os.homedir(), '.cursor-prompt', 'marketplace'),
    notifications: {
      newVersions: true,
      newFromAuthors: false,
      featuredTemplates: false,
      securityUpdates: true,
      weeklyDigest: false,
    },
  };

  constructor(config?: {
    apiUrl?: string;
    apiKey?: string;
    installPath?: string;
  }) {
    // Initialize core services
    this.api = new MarketplaceAPI(config);
    this.registry = new TemplateRegistry();

    // Initialize domain services
    this.searchService = new TemplateSearchService(this.api);
    this.installerService = new TemplateInstallerService(
      this.api,
      this.registry,
      config?.installPath ||
        this.preferences.installPath ||
        path.join(os.homedir(), '.cursor-prompt', 'marketplace')
    );
    this.updaterService = new TemplateUpdaterService(
      this.api,
      this.registry,
      this.installerService,
      path.join(
        config?.installPath ||
          this.preferences.installPath ||
          path.join(os.homedir(), '.cursor-prompt', 'marketplace'),
        'backups'
      )
    );
    this.authorService = AuthorService.getInstance(this.api);

    logger.info('MarketplaceRefactoredService initialized');
  }

  /**
   * Search for templates
   */
  async search(query: TemplateSearchQuery): Promise<TemplateSearchResult> {
    return this.searchService.search(query);
  }

  /**
   * Get template by ID
   */
  async getTemplate(templateId: string): Promise<TemplateModel> {
    return this.searchService.getTemplate(templateId);
  }

  /**
   * Get featured templates
   */
  async getFeatured(limit = 10): Promise<TemplateModel[]> {
    return this.searchService.getFeatured(limit);
  }

  /**
   * Get trending templates
   */
  async getTrending(limit = 10): Promise<TemplateModel[]> {
    return this.searchService.getTrending(limit);
  }

  /**
   * Install a template
   */
  async install(
    templateId: string,
    options?: {
      version?: string;
      force?: boolean;
      withDependencies?: boolean;
    }
  ): Promise<InstallationResult> {
    return this.installerService.install(templateId, options);
  }

  /**
   * Quick install
   */
  async quickInstall(templateId: string): Promise<InstallationResult> {
    return this.installerService.quickInstall(templateId);
  }

  /**
   * Batch install
   */
  async batchInstall(
    templateIds: string[],
    options?: {
      continueOnError?: boolean;
    }
  ): Promise<InstallationResult[]> {
    return this.installerService.batchInstall(templateIds, options);
  }

  /**
   * Update a template
   */
  async update(
    templateId: string,
    options?: {
      force?: boolean;
      backup?: boolean;
    }
  ): Promise<UpdateResult> {
    return this.updaterService.update(templateId, options);
  }

  /**
   * Check for updates
   */
  async checkUpdates(): Promise<UpdateCheckResult[]> {
    return this.updaterService.checkUpdates();
  }

  /**
   * Update all templates
   */
  async updateAll(options?: {
    force?: boolean;
    backup?: boolean;
  }): Promise<UpdateResult[]> {
    return this.updaterService.updateAll(options);
  }

  /**
   * Uninstall a template
   */
  async uninstall(templateId: string): Promise<void> {
    try {
      // Remove from registry
      await this.registry.unregisterTemplate(templateId);

      // Remove files
      const installPath =
        this.preferences.installPath ||
        path.join(os.homedir(), '.cursor-prompt', 'marketplace');
      const templatePath = path.join(installPath, 'templates', templateId);
      const fs = await import('fs');
      await fs.promises.rm(templatePath, { recursive: true, force: true });

      logger.info(`Template ${templateId} uninstalled successfully`);
    } catch (error) {
      logger.error(
        `Failed to uninstall template ${templateId}: ${error instanceof Error ? error.message : String(error)}`
      );
      throw error;
    }
  }

  /**
   * Rate a template
   */
  async rate(
    templateId: string,
    rating: number,
    review?: string | Partial<TemplateReview>
  ): Promise<RatingResult> {
    try {
      const reviewData =
        typeof review === 'string' ? { comment: review } : review;
      await this.api.rateTemplate(templateId, rating, reviewData);
      logger.info(`Rated template ${templateId}: ${rating} stars`);
      return {
        success: true,
        rating,
        comment: reviewData?.comment,
        templateId,
        userId: 'current-user', // Would need proper user context
      };
    } catch (error) {
      logger.error(
        `Failed to rate template ${templateId}: ${error instanceof Error ? error.message : String(error)}`
      );
      throw error;
    }
  }

  /**
   * Get installed templates
   */
  async getInstalled(): Promise<TemplateModel[]> {
    return this.registry.listTemplates().map(t => t.metadata);
  }

  /**
   * Get recommendations
   */
  async getRecommendations(limit = 10): Promise<TemplateModel[]> {
    const installed = await this.getInstalled();
    const installedIds = installed.map(t => t.id);

    // Get categories and tags from installed templates
    const categories = [
      ...new Set(installed.map(t => t.category).filter(Boolean)),
    ];
    const tags = [...new Set(installed.flatMap(t => t.tags || []))];

    return this.searchService.getRecommendations(
      {
        categories: categories as any[],
        tags,
        excludeInstalled: installedIds,
      },
      limit
    );
  }

  /**
   * Update user preferences
   */
  async updatePreferences(
    preferences: Partial<UserPreferences>
  ): Promise<UserPreferences> {
    this.preferences = {
      ...this.preferences,
      ...preferences,
    };

    // Save preferences
    const installPath =
      this.preferences.installPath ||
      path.join(os.homedir(), '.cursor-prompt', 'marketplace');
    const prefsPath = path.join(installPath, 'preferences.json');
    const fs = await import('fs');
    await fs.promises.writeFile(
      prefsPath,
      JSON.stringify(this.preferences, null, 2)
    );

    return this.preferences;
  }

  /**
   * Get author information
   */
  async getAuthor(authorId: string): Promise<unknown> {
    return this.authorService.getProfile(authorId);
  }

  /**
   * Follow an author
   */
  async followAuthor(authorId: string): Promise<unknown> {
    return this.authorService.followAuthor(authorId);
  }

  /**
   * Unfollow an author
   */
  async unfollowAuthor(authorId: string): Promise<unknown> {
    return this.authorService.unfollowAuthor(authorId);
  }
}

/**
 * @fileoverview Core marketplace service for template management
 * @lastmodified 2025-08-26T03:27:11Z
 *
 * Features: Template discovery, installation, version management, ratings, search filters, batch operations
 * Main APIs: search(), searchByTags(), install(), batchInstall(), update(), rate(), getReviews(), clearCache()
 * Constraints: Network connectivity, API rate limits, storage permissions
 * Patterns: Service layer, async operations, caching, error handling, backward compatibility
 */

import { EventEmitter } from 'events';
import * as fs from 'fs/promises';
import * as path from 'path';

import { MarketplaceAPI } from '../api/marketplace.api';
import { IMarketplaceDatabase } from '../database/database.interface';
import { getDatabase } from '../database/database.factory';
import {
  TemplateModel,
  TemplateSearchQuery,
  TemplateSearchResult,
  TemplateInstallation,
  TemplateManifest,
  TemplateVersion,
  TemplateReview,
  MarketplacePreferences,
} from '../models/template.model';
import {
  InstallationResult,
  MarketplaceTemplate,
  TemplateDependency,
  UpdateCheckResult,
  UpdateResult,
  RatingResult,
} from '../../types';
import { logger } from '../../utils/logger';
import { TemplateRegistry } from './template.registry';
// import { VersionManager } from './version.manager'; // Reserved for future use

// Interface declaration to resolve no-use-before-define
interface IMarketplaceService {
  search(query: TemplateSearchQuery): Promise<TemplateSearchResult>;
  getTemplate(templateId: string): Promise<TemplateModel>;
  install(templateId: string, version?: string): Promise<InstallationResult>;
  update(templateId: string): Promise<InstallationResult>;
  uninstall(templateId: string): Promise<void>;
  rate(
    templateId: string,
    rating: number,
    review?: Partial<TemplateReview>
  ): Promise<void>;
  publishTemplate(
    template: TemplateModel,
    options?: {
      version?: string;
      isDraft?: boolean;
      isPrivate?: boolean;
    }
  ): Promise<{
    templateId: string;
    version: string;
    url?: string;
  }>;
  // New search methods
  searchByTags(tags: string[]): Promise<TemplateSearchResult>;
  searchByCategory(category: string): Promise<TemplateSearchResult>;
  getPopularTemplates(limit: number): Promise<TemplateSearchResult>;
  getTopRated(limit: number): Promise<TemplateSearchResult>;
  getByAuthor(author: string): Promise<TemplateSearchResult>;
  getTrending(hours: number): Promise<TemplateSearchResult>;
  // Installation methods
  installTemplate(
    id: string,
    targetPath: string,
    options?: {
      version?: string;
      skipDeps?: boolean;
      enableAutoUpdate?: boolean;
    }
  ): Promise<InstallationResult>;
  batchInstall(
    templateQueries: string[],
    options?: {
      continueOnError?: boolean;
      maxConcurrency?: number;
    }
  ): Promise<
    Array<{
      templateQuery: string;
      success: boolean;
      template?: TemplateModel;
      installation?: TemplateInstallation;
      error?: Error;
    }>
  >;
  batchInstall(
    ids: string[],
    targetPath: string,
    options?: {
      maxConcurrency?: number;
      continueOnError?: boolean;
    }
  ): Promise<
    Array<{
      id: string;
      success: boolean;
      result?: InstallationResult;
      error?: Error;
    }>
  >;
  // Update methods
  checkUpdates(installedPath?: string): Promise<
    | UpdateCheckResult
    | Array<{
        templateId: string;
        currentVersion: string;
        latestVersion: string;
      }>
  >;
  updateTemplate(
    id: string,
    installedPath: string,
    version?: string
  ): Promise<InstallationResult>;
  updateAll(installedPath: string): Promise<UpdateResult>;
  rollbackTemplate(
    id: string,
    version: string,
    installedPath: string
  ): Promise<InstallationResult>;
  // Rating and review methods
  rateTemplate(
    id: string,
    rating: number,
    userId: string,
    options?: {
      comment?: string;
      title?: string;
    }
  ): Promise<RatingResult>;
  addReview(
    id: string,
    review: {
      userId: string;
      rating: number;
      title?: string;
      comment: string;
    }
  ): Promise<TemplateReview>;
  getReviews(id: string): Promise<TemplateReview[]>;
  // Analytics and cache methods
  recordDownload(id: string): Promise<void>;
  getCacheSize(): Promise<number>;
  clearCache(): Promise<void>;
}

export class MarketplaceService
  extends EventEmitter
  implements IMarketplaceService
{
  private static instance: IMarketplaceService;

  private api: MarketplaceAPI;

  private registry: TemplateRegistry;

  private database: IMarketplaceDatabase | null = null;

  // private _versionManager: VersionManager; // Reserved for future use
  private cache: Map<string, { data: unknown; expires: number }> = new Map();

  private manifestPath: string;

  private manifest: TemplateManifest | null = null;

  public constructor() {
    super();
    this.api = new MarketplaceAPI();
    this.registry = new TemplateRegistry();
    // this._versionManager = new VersionManager(); // Reserved for future use
    this.manifestPath = path.join(
      process.cwd(),
      '.cursor-prompt',
      'marketplace.json'
    );
    // Database will be initialized via async init method
  }

  static async getInstance(): Promise<MarketplaceService> {
    if (!MarketplaceService.instance) {
      MarketplaceService.instance = new MarketplaceService();
      await (MarketplaceService.instance as MarketplaceService).init();
    }
    return MarketplaceService.instance as MarketplaceService;
  }

  /**
   * Static factory method to create a new instance
   */
  static async create(): Promise<MarketplaceService> {
    const service = new MarketplaceService();
    await service.init();
    return service;
  }

  /**
   * Initialize the service (must be called after constructor)
   */
  async init(): Promise<void> {
    await this.initializeDatabase();
  }

  /**
   * Get API instance for direct access
   */
  get apiClient(): MarketplaceAPI {
    return this.api;
  }

  /**
   * Initialize database connection
   */
  private async initializeDatabase(): Promise<void> {
    try {
      this.database = await getDatabase();
      logger.info('Marketplace database initialized');

      // Initialize manifest from database or create default
      await this.initializeManifest();
    } catch (error) {
      logger.error(`Failed to initialize marketplace database: ${error}`);
      // Fallback to file-based initialization
      await this.initializeManifest();
    }
  }

  /**
   * Initialize marketplace manifest
   */
  private async initializeManifest(): Promise<void> {
    try {
      const manifestData = await fs.readFile(this.manifestPath, 'utf8');
      this.manifest = JSON.parse(manifestData);
    } catch {
      // Create default manifest if not exists
      this.manifest = {
        templates: [],
        lastSync: new Date(),
        marketplaceUrl:
          process.env.MARKETPLACE_URL ||
          'https://marketplace.cursor-prompt.com',
        preferences: {
          autoUpdate: false,
          checkInterval: 24 * 60 * 60 * 1000, // 24 hours
          includePrerelease: false,
          trustedAuthors: [],
          blockedAuthors: [],
          preferredCategories: [],
          notifications: {
            newVersions: true,
            newFromAuthors: false,
            featuredTemplates: false,
            securityUpdates: true,
            weeklyDigest: false,
          },
        },
      };
      await this.saveManifest();
    }
  }

  /**
   * Save manifest to disk
   */
  private async saveManifest(): Promise<void> {
    if (!this.manifest) return;

    const manifestDir = path.dirname(this.manifestPath);
    await fs.mkdir(manifestDir, { recursive: true });
    await fs.writeFile(
      this.manifestPath,
      JSON.stringify(this.manifest, null, 2),
      'utf8'
    );
  }

  /**
   * Search templates in marketplace
   */
  async search(query: TemplateSearchQuery): Promise<TemplateSearchResult> {
    const cacheKey = `search:${JSON.stringify(query)}`;
    const cached = this.getFromCache<TemplateSearchResult>(cacheKey);

    if (cached) {
      return cached;
    }

    try {
      let result: TemplateSearchResult;

      // Use database for local search if available, otherwise API
      if (this.database) {
        const offset = ((query.page || 1) - 1) * (query.limit || 20);
        const templates = query.query
          ? await this.database.templates.search(query.query, {
              limit: query.limit,
              offset,
              sort: query.sortBy
                ? [
                    {
                      field: query.sortBy,
                      direction: query.sortOrder || 'desc',
                    },
                  ]
                : undefined,
            })
          : await this.database.templates.findMany({
              limit: query.limit,
              offset,
              sort: query.sortBy
                ? [
                    {
                      field: query.sortBy,
                      direction: query.sortOrder || 'desc',
                    },
                  ]
                : undefined,
            });

        result = {
          templates: templates as any[], // Convert TemplateModel to MarketplaceTemplate
          total: templates.length,
          page: query.page || 1,
          limit: query.limit || 20,
          hasMore: templates.length === (query.limit || 20),
        };
      } else {
        // Fallback to API
        result = await this.api.searchTemplates(query);
      }

      this.setCache(cacheKey, result, 5 * 60 * 1000); // 5 minutes cache
      this.emit('search:completed', { query, result });
      return result;
    } catch (error) {
      logger.error(`Marketplace search failed: ${error}`);
      this.emit('search:error', { query, error });
      throw error;
    }
  }

  /**
   * Get detailed template information
   */
  async getTemplate(templateId: string): Promise<TemplateModel> {
    const cacheKey = `template:${templateId}`;
    const cached = this.getFromCache<TemplateModel>(cacheKey);

    if (cached) {
      return cached;
    }

    try {
      let template: TemplateModel;

      // Try database first, then API
      if (this.database) {
        const dbTemplate = await this.database.templates.findById(templateId);
        if (dbTemplate) {
          template = dbTemplate;
        } else {
          // Not in local database, fetch from API and cache locally
          template = await this.api.getTemplate(templateId);
          await this.database.templates.create(template);
        }
      } else {
        template = await this.api.getTemplate(templateId);
      }

      this.setCache(cacheKey, template, 10 * 60 * 1000); // 10 minutes cache

      this.emit('template:fetched', { templateId, template });
      return template;
    } catch (error) {
      logger.error(`Failed to fetch template ${templateId}: ${error}`);
      this.emit('template:error', { templateId, error });
      throw error;
    }
  }

  /**
   * Install template from marketplace
   */
  async install(
    templateId: string,
    version?: string
  ): Promise<InstallationResult> {
    const startTime = Date.now();
    try {
      this.emit('install:started', { templateId, version });

      // Get template information
      const template = await this.getTemplate(templateId);
      const targetVersion = version || template.currentVersion;
      const templateVersion = template.versions.find(
        v => v.version === targetVersion
      );

      if (!templateVersion) {
        throw new Error(
          `Version ${targetVersion} not found for template ${templateId}`
        );
      }

      // Check dependencies
      await this.checkDependencies(templateVersion);

      // Download and install template
      const installPath = await this.downloadTemplate(
        template,
        templateVersion
      );

      // Create installation record
      const installation: TemplateInstallation = {
        templateId,
        version: targetVersion,
        installPath,
        installed: new Date(),
        autoUpdate: this.manifest?.preferences.autoUpdate || false,
      };

      // Update manifest
      if (this.manifest) {
        const existingIndex = this.manifest.templates.findIndex(
          t => t.templateId === templateId
        );
        if (existingIndex >= 0) {
          this.manifest.templates[existingIndex] = installation;
        } else {
          this.manifest.templates.push(installation);
        }
        await this.saveManifest();
      }

      // Register with template registry
      await this.registry.registerTemplate(
        template,
        templateVersion,
        installPath
      );

      this.emit('install:completed', {
        templateId,
        version: targetVersion,
        installation,
      });
      logger.info(
        `Template ${templateId}@${targetVersion} installed successfully`
      );

      // Convert TemplateInstallation to InstallationResult
      const templateForResult = await this.getTemplate(templateId);
      const result: InstallationResult = {
        success: true,
        template: templateForResult as unknown as MarketplaceTemplate,
        version: installation.version,
        installPath: installation.installPath,
        duration: Date.now() - startTime,
        warnings: [],
      };
      return result;
    } catch (error) {
      logger.error(`Failed to install template ${templateId}: ${error}`);
      this.emit('install:error', { templateId, version, error });
      throw error;
    }
  }

  /**
   * Update installed template
   */
  async update(
    templateId: string,
    version?: string
  ): Promise<InstallationResult> {
    const startTime = Date.now();
    try {
      this.emit('update:started', { templateId, version });

      const installation = this.getInstallation(templateId);
      if (!installation) {
        throw new Error(`Template ${templateId} is not installed`);
      }

      // Get latest template information
      const template = await this.getTemplate(templateId);
      const targetVersion = version || template.currentVersion;

      if (installation.version === targetVersion) {
        logger.info(
          `Template ${templateId} is already at version ${targetVersion}`
        );
        const templateForResult = await this.getTemplate(templateId);
        const result: InstallationResult = {
          success: true,
          template: templateForResult as unknown as MarketplaceTemplate,
          version: installation.version,
          installPath: installation.installPath,
          duration: Date.now() - startTime,
          warnings: [],
        };
        return result;
      }

      // Backup current installation if needed
      if (installation.customizations) {
        await this.backupTemplate(installation);
      }

      // Perform update (essentially reinstall)
      const updatedInstallation = await this.install(templateId, targetVersion);

      this.emit('update:completed', {
        templateId,
        fromVersion: installation.version,
        toVersion: targetVersion,
      });
      logger.info(
        `Template ${templateId} updated from ${installation.version} to ${targetVersion}`
      );

      // Convert TemplateInstallation to InstallationResult
      const templateForResult = await this.getTemplate(templateId);
      const result: InstallationResult = {
        success: true,
        template: templateForResult as unknown as MarketplaceTemplate,
        version: updatedInstallation.version,
        installPath: updatedInstallation.installPath,
        duration: Date.now() - startTime,
        warnings: [],
      };
      return result;
    } catch (error) {
      logger.error(`Failed to update template ${templateId}: ${error}`);
      this.emit('update:error', { templateId, version, error });
      throw error;
    }
  }

  /**
   * Uninstall template
   */
  async uninstall(templateId: string): Promise<void> {
    try {
      this.emit('uninstall:started', { templateId });

      const installation = this.getInstallation(templateId);
      if (!installation) {
        throw new Error(`Template ${templateId} is not installed`);
      }

      // Remove from file system
      await fs.rm(installation.installPath, { recursive: true, force: true });

      // Remove from registry
      await this.registry.unregisterTemplate(templateId);

      // Update manifest
      if (this.manifest) {
        this.manifest.templates = this.manifest.templates.filter(
          t => t.templateId !== templateId
        );
        await this.saveManifest();
      }

      this.emit('uninstall:completed', { templateId });
      logger.info(`Template ${templateId} uninstalled successfully`);
    } catch (error) {
      logger.error(`Failed to uninstall template ${templateId}: ${error}`);
      this.emit('uninstall:error', { templateId, error });
      throw error;
    }
  }

