    `;
  }

  private static getPerformanceIndicator(
    value: number,
    type: 'responseTime' | 'tokenEfficiency' | 'cost'
  ): string {
    const thresholds = PERFORMANCE_THRESHOLDS;
    let level = 'poor';

    switch (type) {
      case 'responseTime':
        if (value < thresholds.responseTime.excellent) level = 'excellent';
        else if (value < thresholds.responseTime.good) level = 'good';
        else if (value < thresholds.responseTime.fair) level = 'fair';
        break;
      case 'tokenEfficiency':
        if (value >= thresholds.tokenEfficiency.excellent) level = 'excellent';
        else if (value >= thresholds.tokenEfficiency.good) level = 'good';
        else if (value >= thresholds.tokenEfficiency.fair) level = 'fair';
        break;
      case 'cost':
        if (value < thresholds.costEffectiveness.excellent) level = 'excellent';
        else if (value < thresholds.costEffectiveness.good) level = 'good';
        else if (value < thresholds.costEffectiveness.fair) level = 'fair';
        break;
      default:
        // For unknown metric types, default to 'poor'
        level = 'poor';
        break;
    }

    const indicators = {
      excellent: 'üü¢',
      good: 'üü°',
      fair: 'üü†',
      poor: 'üî¥',
    };

    return indicators[level as keyof typeof indicators];
  }
}

/**
 * @fileoverview Comprehensive API validation middleware with security enforcement
 * @lastmodified 2025-08-27T16:00:00Z
 *
 * Features: Request/response validation, SQL injection prevention, rate limiting
 * Main APIs: validateApiRequest(), validateApiResponse(), securityMiddleware()
 * Constraints: All API endpoints must use validation middleware
 * Patterns: Middleware pattern, security-first validation, threat detection
 */

import { z } from 'zod';
import { ValidationError } from '../errors';
import { 
  EnhancedValidator, 
  SecurityValidationResult, 
  ValidationContext,
  SecureStringSchema,
  SecurePathSchema,
  SecureUrlSchema,
  customValidators
} from '../validation/schemas';
import { securityService } from '../middleware/security.middleware';
import { logger } from '../utils/logger';

/**
 * API validation configuration
 */
export interface ApiValidationConfig {
  maxRequestSize: number;
  maxResponseSize: number;
  enableSqlInjectionProtection: boolean;
  enableXssProtection: boolean;
  enablePathTraversalProtection: boolean;
  enableRateLimiting: boolean;
  rateLimitWindow: number;
  rateLimitMaxRequests: number;
  logSecurityEvents: boolean;
  blockSuspiciousRequests: boolean;
  securityLevel: 'strict' | 'moderate' | 'permissive';
}

/**
 * Default API validation configuration
 */
export const DEFAULT_API_CONFIG: ApiValidationConfig = {
  maxRequestSize: 10 * 1024 * 1024, // 10MB
  maxResponseSize: 50 * 1024 * 1024, // 50MB
  enableSqlInjectionProtection: true,
  enableXssProtection: true,
  enablePathTraversalProtection: true,
  enableRateLimiting: true,
  rateLimitWindow: 60000, // 1 minute
  rateLimitMaxRequests: 100,
  logSecurityEvents: true,
  blockSuspiciousRequests: true,
  securityLevel: 'strict',
};

/**
 * Request validation schema with comprehensive security checks
 */
export const ApiRequestValidationSchema = z.object({
  method: z.enum(['GET', 'POST', 'PUT', 'DELETE', 'PATCH', 'OPTIONS', 'HEAD']),
  url: z.string()
    .min(1, 'URL cannot be empty')
    .max(2048, 'URL too long')
    .refine(val => !val.includes('..'), 'URL contains path traversal')
    .refine(val => !/[<>\"'&]/.test(val), 'URL contains dangerous characters'),
  headers: z.record(
    z.string()
      .max(100, 'Header name too long')
      .regex(/^[a-zA-Z0-9-_]+$/, 'Invalid header name'),
    SecureStringSchema.max(4000, 'Header value too long')
  ).refine(headers => Object.keys(headers).length <= 100, {
    message: 'Too many headers'
  }).optional(),
  query: z.record(
    z.string().max(100, 'Query parameter name too long'),
    z.union([
      SecureStringSchema.max(1000, 'Query parameter too long'),
      z.array(SecureStringSchema.max(100)).max(50, 'Too many query array values')
    ])
  ).refine(query => Object.keys(query).length <= 50, {
    message: 'Too many query parameters'
  }).optional(),
  params: z.record(
    z.string().max(100, 'Path parameter name too long'),
    SecureStringSchema.max(255, 'Path parameter too long')
  ).refine(params => Object.keys(params).length <= 20, {
    message: 'Too many path parameters'
  }).optional(),
  body: z.unknown().optional(),
  clientIp: z.string()
    .regex(/^(?:\d{1,3}\.){3}\d{1,3}$|^(?:[a-f0-9]*:+)+[a-f0-9]*$/, 'Invalid IP address format')
    .optional(),
  userAgent: SecureStringSchema.max(500, 'User agent too long').optional(),
  contentType: z.string()
    .max(100, 'Content type too long')
    .regex(/^[a-zA-Z0-9][a-zA-Z0-9!#$&\-\^]*\/[a-zA-Z0-9][a-zA-Z0-9!#$&\-\^]*(?:;.*)?$/)
    .optional(),
});

/**
 * Response validation schema
 */
export const ApiResponseValidationSchema = z.object({
  status: z.number()
    .min(100, 'Invalid HTTP status code')
    .max(599, 'Invalid HTTP status code'),
  headers: z.record(
    z.string().max(100),
    z.string().max(4000)
  ).optional(),
  body: z.unknown().optional(),
  contentType: z.string().max(100).optional(),
});

/**
 * Security event tracking
 */
interface SecurityEvent {
  timestamp: Date;
  type: 'sql_injection' | 'xss_attempt' | 'path_traversal' | 'rate_limit' | 'suspicious_content';
  severity: 'low' | 'medium' | 'high' | 'critical';
  clientIp?: string;
  userAgent?: string;
  details: string;
  blocked: boolean;
}

/**
 * API validation middleware class
 */
export class ApiValidationMiddleware {
  private config: ApiValidationConfig;
  private rateLimiter = securityService.createRateLimiter(60000, 100);
  private securityEvents: SecurityEvent[] = [];

  constructor(config: Partial<ApiValidationConfig> = {}) {
    this.config = { ...DEFAULT_API_CONFIG, ...config };
  }

  /**
   * Main middleware function for API validation
   */
  validate() {
    return async (req: any, res: any, next: any) => {
      try {
        const context: ValidationContext = {
          clientId: req.ip || 'unknown',
          userAgent: req.headers['user-agent'],
          ipAddress: req.ip,
          timestamp: new Date(),
          requestId: req.id || `req_${Date.now()}`,
          securityLevel: this.config.securityLevel,
        };

        // Rate limiting check
        if (this.config.enableRateLimiting) {
          const rateLimitResult = this.rateLimiter.isAllowed(context.clientId);
          if (!rateLimitResult.allowed) {
            this.logSecurityEvent({
              type: 'rate_limit',
              severity: 'medium',
              clientIp: context.ipAddress,
              userAgent: context.userAgent,
              details: `Rate limit exceeded: ${rateLimitResult.retryAfter}s retry`,
              blocked: true,
            });

            return res.status(429).json({
              error: 'Rate limit exceeded',
              retryAfter: rateLimitResult.retryAfter,
            });
          }
        }

        // Validate request structure
        const requestValidation = await this.validateRequest(req, context);
        if (!requestValidation.valid) {
          if (requestValidation.threatLevel === 'critical' || requestValidation.threatLevel === 'high') {
            this.logSecurityEvent({
              type: 'suspicious_content',
              severity: requestValidation.threatLevel,
              clientIp: context.ipAddress,
              userAgent: context.userAgent,
              details: `Request validation failed: ${requestValidation.errors.join(', ')}`,
              blocked: true,
            });

            return res.status(400).json({
              error: 'Request validation failed',
              details: this.config.securityLevel === 'strict' ? 'Security violation detected' : requestValidation.errors,
            });
          }
        }

        // Validate request body if present
        if (req.body && Object.keys(req.body).length > 0) {
          const bodyValidation = await this.validateRequestBody(req.body, context);
          if (!bodyValidation.valid && (bodyValidation.threatLevel === 'high' || bodyValidation.threatLevel === 'critical')) {
            this.logSecurityEvent({
              type: 'suspicious_content',
              severity: bodyValidation.threatLevel,
              clientIp: context.ipAddress,
              userAgent: context.userAgent,
              details: `Request body validation failed: ${bodyValidation.errors.join(', ')}`,
              blocked: true,
            });

            return res.status(400).json({
              error: 'Request body validation failed',
              details: this.config.securityLevel === 'strict' ? 'Invalid request data' : bodyValidation.errors,
            });
          }

          // Sanitize request body
          if (bodyValidation.sanitized) {
            req.body = bodyValidation.sanitized;
          }
        }

        // SQL injection protection
        if (this.config.enableSqlInjectionProtection) {
          const sqlThreat = this.checkSqlInjection(req);
          if (sqlThreat) {
            this.logSecurityEvent({
              type: 'sql_injection',
              severity: 'high',
              clientIp: context.ipAddress,
              userAgent: context.userAgent,
              details: sqlThreat,
              blocked: true,
            });

            return res.status(403).json({
              error: 'Forbidden: SQL injection attempt detected',
            });
          }
        }

        // XSS protection
        if (this.config.enableXssProtection) {
          const xssThreat = this.checkXssAttempt(req);
          if (xssThreat) {
            this.logSecurityEvent({
              type: 'xss_attempt',
              severity: 'high',
              clientIp: context.ipAddress,
              userAgent: context.userAgent,
              details: xssThreat,
              blocked: true,
            });

            return res.status(403).json({
              error: 'Forbidden: XSS attempt detected',
            });
          }
        }

        // Path traversal protection
        if (this.config.enablePathTraversalProtection) {
          const pathThreat = this.checkPathTraversal(req);
          if (pathThreat) {
            this.logSecurityEvent({
              type: 'path_traversal',
              severity: 'high',
              clientIp: context.ipAddress,
              userAgent: context.userAgent,
              details: pathThreat,
              blocked: true,
            });

            return res.status(403).json({
              error: 'Forbidden: Path traversal attempt detected',
            });
          }
        }

        // Add validation context to request
        req.validationContext = context;
        req.securityHeaders = securityService.getSecureHeaders({
          enableCSP: true,
          enableHSTS: true,
        });

        next();
      } catch (error) {
        logger.error('API validation middleware error:', error);
        res.status(500).json({
          error: 'Internal validation error',
        });
      }
    };
  }

  /**
   * Validate API request structure
   */
  private async validateRequest(req: any, context: ValidationContext): Promise<SecurityValidationResult> {
    const requestData = {
      method: req.method,
      url: req.originalUrl || req.url,
      headers: req.headers,
      query: req.query,
      params: req.params,
      body: req.body,
      clientIp: req.ip,
      userAgent: req.headers['user-agent'],
      contentType: req.headers['content-type'],
    };

    return EnhancedValidator.validate(ApiRequestValidationSchema, requestData, context);
  }

  /**
   * Validate request body with deep security scanning
   */
  private async validateRequestBody(body: any, context: ValidationContext): Promise<SecurityValidationResult> {
    // Check request body size
    const bodySize = JSON.stringify(body).length;
    if (bodySize > this.config.maxRequestSize) {
      return {
        valid: false,
        errors: [`Request body too large: ${bodySize} bytes (max ${this.config.maxRequestSize})`],
        warnings: [],
        threatLevel: 'medium',
      };
    }

    // Perform deep security scan
    return EnhancedValidator.validate(z.unknown(), body, context);
  }

  /**
   * Check for SQL injection patterns
   */
  private checkSqlInjection(req: any): string | null {
    const checkValue = (value: any, path: string = ''): string | null => {
      if (typeof value === 'string') {
        const sqlPatterns = [
          /(\b(union|select|insert|update|delete|drop|create|alter|exec|execute)\s)/i,
          /(--|\/\*|\*\/)/,
          /(\b(or|and)\s+['"]\d+['"]\s*=\s*['"]\d+['"])/i,
          /('.*'.*=.*'.*')/,
          /(;.*--)/,
          /(\b(cast|convert|char|varchar|nvarchar)\s*\()/i,
          /(\b(waitfor|delay|benchmark|sleep)\s*\()/i,
          /(\b(load_file|outfile|dumpfile)\s*\()/i,
        ];

        for (const pattern of sqlPatterns) {
          if (pattern.test(value)) {
            return `SQL injection pattern detected at ${path}: ${pattern.source}`;
          }
        }
      } else if (Array.isArray(value)) {
        for (let i = 0; i < value.length; i++) {
          const result = checkValue(value[i], `${path}[${i}]`);
          if (result) return result;
        }
      } else if (value && typeof value === 'object') {
        for (const [key, val] of Object.entries(value)) {
          const result = checkValue(val, path ? `${path}.${key}` : key);
          if (result) return result;
        }
      }
      return null;
    };

    return checkValue(req.query) || 
           checkValue(req.params) || 
           checkValue(req.body);
  }

  /**
   * Check for XSS attack patterns
   */
  private checkXssAttempt(req: any): string | null {
    const checkValue = (value: any, path: string = ''): string | null => {
      if (typeof value === 'string') {
        const xssPatterns = [
          /<script[^>]*>.*?<\/script>/gsi,
          /on\w+\s*=\s*["'][^"']*["']/gi,
          /(javascript|vbscript|data):\s*[^;\s]*/gi,
          /<iframe[^>]*>.*?<\/iframe>/gsi,
          /<object[^>]*>.*?<\/object>/gsi,
          /<embed[^>]*>/gi,
          /eval\s*\(/gi,
          /setTimeout\s*\(/gi,
          /setInterval\s*\(/gi,
          /Function\s*\(/gi,
        ];

        for (const pattern of xssPatterns) {
          if (pattern.test(value)) {
            return `XSS pattern detected at ${path}: ${pattern.source}`;
          }
        }
      } else if (Array.isArray(value)) {
        for (let i = 0; i < value.length; i++) {
          const result = checkValue(value[i], `${path}[${i}]`);
          if (result) return result;
        }
      } else if (value && typeof value === 'object') {
        for (const [key, val] of Object.entries(value)) {
          const result = checkValue(val, path ? `${path}.${key}` : key);
          if (result) return result;
        }
      }
      return null;
    };

    return checkValue(req.query) || 
           checkValue(req.params) || 
           checkValue(req.body);
  }

  /**
   * Check for path traversal attempts
   */
  private checkPathTraversal(req: any): string | null {
    const checkValue = (value: any, path: string = ''): string | null => {
      if (typeof value === 'string') {
        const pathTraversalPatterns = [
          /\.\.[\/\\]/,
          /[\/\\]\.\.[\/\\]/,
          /^\.\.$/,
          /^\.\.[\\/]/,
          /[\\/]\.\.$/,
          /~[\/\\]/,
          /(\.){3,}/,
        ];

        for (const pattern of pathTraversalPatterns) {
          if (pattern.test(value)) {
            return `Path traversal pattern detected at ${path}: ${pattern.source}`;
          }
        }
      } else if (Array.isArray(value)) {
        for (let i = 0; i < value.length; i++) {
          const result = checkValue(value[i], `${path}[${i}]`);
          if (result) return result;
        }
      } else if (value && typeof value === 'object') {
        for (const [key, val] of Object.entries(value)) {
          const result = checkValue(val, path ? `${path}.${key}` : key);
          if (result) return result;
        }
      }
      return null;
    };

    return checkValue(req.query) || 
           checkValue(req.params) || 
           checkValue(req.body) ||
           (req.originalUrl && checkValue(req.originalUrl, 'url'));
  }

  /**
   * Log security events
   */
  private logSecurityEvent(event: Omit<SecurityEvent, 'timestamp'>): void {
    const securityEvent: SecurityEvent = {
      ...event,
      timestamp: new Date(),
    };

    this.securityEvents.push(securityEvent);

    // Keep only last 1000 events
    if (this.securityEvents.length > 1000) {
      this.securityEvents = this.securityEvents.slice(-1000);
    }

    if (this.config.logSecurityEvents) {
      logger.warn('Security event detected:', securityEvent);
    }
  }

  /**
   * Get security event statistics
   */
  getSecurityStats(): {
    totalEvents: number;
    eventsByType: Record<string, number>;
    eventsBySeverity: Record<string, number>;
    recentEvents: SecurityEvent[];
  } {
    const eventsByType: Record<string, number> = {};
    const eventsBySeverity: Record<string, number> = {};

    for (const event of this.securityEvents) {
      eventsByType[event.type] = (eventsByType[event.type] || 0) + 1;
      eventsBySeverity[event.severity] = (eventsBySeverity[event.severity] || 0) + 1;
    }

    return {
      totalEvents: this.securityEvents.length,
      eventsByType,
      eventsBySeverity,
      recentEvents: this.securityEvents.slice(-50), // Last 50 events
    };
  }

  /**
   * Clear security events history
   */
  clearSecurityEvents(): void {
    this.securityEvents = [];
  }
}

/**
 * Global API validation middleware instance
 */
export const apiValidationMiddleware = new ApiValidationMiddleware();

/**
 * Express middleware factory for easy integration
 */
export function createApiValidationMiddleware(config?: Partial<ApiValidationConfig>) {
  const middleware = new ApiValidationMiddleware(config);
  return middleware.validate();
}

/**
 * Validation decorator for controller methods
 */
export function ValidateApiEndpoint(schema: z.ZodSchema<any>) {
  return function (target: any, propertyKey: string, descriptor: PropertyDescriptor) {
    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      const [req, res] = args;

      try {
        const validationResult = EnhancedValidator.validate(schema, {
          query: req.query,
          params: req.params,
          body: req.body,
        });

        if (!validationResult.valid) {
          return res.status(400).json({
            error: 'Validation failed',
            details: validationResult.errors,
          });
        }

        // Replace request data with sanitized version
        if (validationResult.sanitized) {
          const sanitized = validationResult.sanitized as any;
          req.query = sanitized.query || req.query;
          req.params = sanitized.params || req.params;
          req.body = sanitized.body || req.body;
        }

        return originalMethod.apply(this, args);
      } catch (error) {
        logger.error('Validation decorator error:', error);
        return res.status(500).json({
          error: 'Internal validation error',
        });
      }
    };

    return descriptor;
  };
}

/**
 * @fileoverview Enhanced file upload validation with comprehensive security checks
 * @lastmodified 2025-08-27T16:30:00Z
 *
 * Features: File validation, path traversal protection, malware detection, type verification
 * Main APIs: validateFileUpload(), createFileValidationMiddleware(), scanFileContent()
 * Constraints: All file uploads must pass security validation
 * Patterns: Security-first file handling, threat detection, sandboxed validation
 */

import { z } from 'zod';
import * as crypto from 'crypto';
import * as path from 'path';
import * as mime from 'mime-types';
import { ValidationError } from '../errors';
import { 
  SecurePathSchema, 
  SecureStringSchema, 
  customValidators,
  EnhancedValidator,
  SecurityValidationResult 
} from '../validation/schemas';
import { securityService } from '../middleware/security.middleware';
import { logger } from '../utils/logger';

/**
 * File upload configuration
 */
export interface FileUploadConfig {
  maxFileSize: number;
  maxTotalSize: number;
  allowedMimeTypes: string[];
  allowedExtensions: string[];
  blockedExtensions: string[];
  blockedMimeTypes: string[];
  enableVirusScanning: boolean;
  enableContentValidation: boolean;
  enablePathValidation: boolean;
  quarantineDirectory: string;
  uploadDirectory: string;
  enableDecompression: boolean;
  maxDecompressionRatio: number;
  enableMetadataStripping: boolean;
}

/**
 * Default file upload configuration
 */
export const DEFAULT_FILE_UPLOAD_CONFIG: FileUploadConfig = {
  maxFileSize: 10 * 1024 * 1024, // 10MB
  maxTotalSize: 100 * 1024 * 1024, // 100MB
  allowedMimeTypes: [
    'text/plain',
    'text/markdown',
    'application/json',
    'application/yaml',
    'image/jpeg',
    'image/png',
    'image/gif',
    'image/webp',
    'application/pdf'
  ],
  allowedExtensions: [
    '.txt', '.md', '.json', '.yaml', '.yml', 
    '.jpg', '.jpeg', '.png', '.gif', '.webp', '.pdf'
  ],
  blockedExtensions: [
    '.exe', '.bat', '.cmd', '.com', '.pif', '.scr', '.vbs', '.js', '.jar',
    '.msi', '.dll', '.sys', '.bin', '.deb', '.rpm', '.dmg', '.app', '.pkg',
    '.sh', '.bash', '.zsh', '.fish', '.ps1', '.psm1', '.psd1',
    '.php', '.asp', '.aspx', '.jsp', '.cfm', '.cgi', '.pl', '.py', '.rb'
  ],
  blockedMimeTypes: [
    'application/x-executable',
    'application/x-msdownload',
    'application/x-msdos-program',
    'application/x-bat',
    'application/x-sh',
    'text/x-script.python',
    'text/x-script.perl',
    'application/javascript',
    'text/javascript'
  ],
  enableVirusScanning: true,
  enableContentValidation: true,
  enablePathValidation: true,
  quarantineDirectory: './quarantine',
  uploadDirectory: './uploads',
  enableDecompression: false,
  maxDecompressionRatio: 10,
  enableMetadataStripping: true,
};

/**
 * File validation schema
 */
export const FileUploadSchema = z.object({
  filename: z.string()
    .min(1, 'Filename cannot be empty')
    .max(255, 'Filename too long')
    .refine(name => !/[\x00-\x1f\x80-\x9f]/.test(name), 'Filename contains control characters')
    .refine(name => !/[<>:"|?*\\]/.test(name), 'Filename contains invalid characters')
    .refine(name => !/(CON|PRN|AUX|NUL|COM[1-9]|LPT[1-9])(\.|$)/i.test(name), 'Filename uses reserved system name')
    .refine(name => !/^\./.test(name), 'Hidden files not allowed')
    .refine(name => !/\.$/.test(name), 'Filename cannot end with dot'),
  originalName: z.string().max(255).optional(),
  mimetype: z.string()
    .min(1, 'MIME type is required')
    .regex(/^[a-zA-Z0-9][a-zA-Z0-9!#$&\-\^]*\/[a-zA-Z0-9][a-zA-Z0-9!#$&\-\^]*$/, 'Invalid MIME type format'),
  size: z.number()
    .min(0, 'File size cannot be negative')
    .max(100 * 1024 * 1024, 'File too large'),
  buffer: z.instanceof(Buffer).optional(),
  path: SecurePathSchema.optional(),
  destination: SecurePathSchema.optional(),
});

/**
 * File validation result
 */
export interface FileValidationResult extends SecurityValidationResult {
  fileInfo: {
    filename: string;
    size: number;
    mimetype: string;
    extension: string;
    hash: string;
    isExecutable: boolean;
    containsMalware: boolean;
    isCompressed: boolean;
  };
  securityChecks: {
    pathTraversalCheck: boolean;
    mimeTypeCheck: boolean;
    extensionCheck: boolean;
    contentValidation: boolean;
    virusScan: boolean;
    sizeValidation: boolean;
  };
  sanitizedFilename?: string;
  quarantined: boolean;
}

/**
 * File upload validation middleware
 */
export class FileUploadValidationMiddleware {
  private config: FileUploadConfig;
  private uploadStats: Map<string, { count: number; totalSize: number; lastUpload: Date }> = new Map();

  constructor(config: Partial<FileUploadConfig> = {}) {
    this.config = { ...DEFAULT_FILE_UPLOAD_CONFIG, ...config };
  }

  /**
   * Validate uploaded file with comprehensive security checks
   */
  async validateFile(file: any): Promise<FileValidationResult> {
    const validationResult: FileValidationResult = {
      valid: true,
      errors: [],
      warnings: [],
      threatLevel: 'low',
      fileInfo: {
        filename: file.filename || file.originalname || 'unknown',
        size: file.size || 0,
        mimetype: file.mimetype || '',
        extension: '',
        hash: '',
        isExecutable: false,
        containsMalware: false,
        isCompressed: false,
      },
      securityChecks: {
        pathTraversalCheck: false,
        mimeTypeCheck: false,
        extensionCheck: false,
        contentValidation: false,
        virusScan: false,
        sizeValidation: false,
      },
      quarantined: false,
    };

    try {
      // Basic schema validation
      const schemaValidation = EnhancedValidator.validate(FileUploadSchema, file);
      if (!schemaValidation.valid) {
        validationResult.valid = false;
        validationResult.errors.push(...schemaValidation.errors);
        validationResult.threatLevel = 'medium';
      }

      // Extract file information
      const filename = file.filename || file.originalname;
      const extension = path.extname(filename).toLowerCase();
      const buffer = file.buffer || null;

      validationResult.fileInfo.filename = filename;
      validationResult.fileInfo.extension = extension;

      // Generate file hash
      if (buffer) {
        validationResult.fileInfo.hash = crypto
          .createHash('sha256')
          .update(buffer)
          .digest('hex');
      }

      // 1. Path traversal validation
      if (this.config.enablePathValidation) {
        const pathValidation = this.validateFilePath(filename);
        validationResult.securityChecks.pathTraversalCheck = pathValidation.safe;

        if (!pathValidation.safe) {
          validationResult.errors.push(...pathValidation.threats);
          validationResult.threatLevel = 'high';
        } else {
          validationResult.sanitizedFilename = pathValidation.sanitizedFilename;
        }
      }

      // 2. File size validation
      const sizeValidation = this.validateFileSize(file.size);
      validationResult.securityChecks.sizeValidation = sizeValidation.valid;

      if (!sizeValidation.valid) {
        validationResult.errors.push(...sizeValidation.errors);
        validationResult.threatLevel = 'medium';
      }

      // 3. MIME type validation
      const mimeValidation = this.validateMimeType(file.mimetype, extension);
      validationResult.securityChecks.mimeTypeCheck = mimeValidation.valid;

      if (!mimeValidation.valid) {
        validationResult.errors.push(...mimeValidation.errors);
        validationResult.threatLevel = 'high';
      }

      // 4. File extension validation
      const extensionValidation = this.validateFileExtension(extension);
      validationResult.securityChecks.extensionCheck = extensionValidation.valid;

      if (!extensionValidation.valid) {
        validationResult.errors.push(...extensionValidation.errors);
        validationResult.threatLevel = 'high';
      }

      // 5. Content validation (if buffer available)
      if (buffer && this.config.enableContentValidation) {
        const contentValidation = await this.validateFileContent(buffer, file.mimetype, extension);
        validationResult.securityChecks.contentValidation = contentValidation.valid;

        validationResult.fileInfo.isExecutable = contentValidation.isExecutable;
        validationResult.fileInfo.isCompressed = contentValidation.isCompressed;

        if (!contentValidation.valid) {
          validationResult.errors.push(...contentValidation.threats);
          validationResult.threatLevel = contentValidation.threatLevel;
        }
      }

      // 6. Virus scanning (if enabled)
      if (buffer && this.config.enableVirusScanning) {
        const virusValidation = await this.scanForViruses(buffer, filename);
        validationResult.securityChecks.virusScan = virusValidation.clean;
        validationResult.fileInfo.containsMalware = !virusValidation.clean;

        if (!virusValidation.clean) {
          validationResult.errors.push(...virusValidation.threats);
          validationResult.threatLevel = 'critical';
          validationResult.quarantined = true;
        }
      }

      // Final validation result
      validationResult.valid = validationResult.errors.length === 0;

      // Quarantine file if critical threats detected
      if (validationResult.threatLevel === 'critical' || validationResult.threatLevel === 'high') {
        validationResult.quarantined = true;
      }

      return validationResult;

    } catch (error) {
      logger.error('File validation error:', error);
      return {
        ...validationResult,
        valid: false,
        errors: [`File validation error: ${(error as Error).message}`],
        threatLevel: 'critical',
      };
    }
  }

  /**
   * Validate file path for traversal attacks
   */
  private validateFilePath(filename: string): {
    safe: boolean;
    threats: string[];
    sanitizedFilename?: string;
  } {
    const threats: string[] = [];
    let safe = true;

    // Check for path traversal patterns
    if (filename.includes('..')) {
      threats.push('Path traversal attempt detected (..)');
      safe = false;
    }

    if (filename.includes('/') || filename.includes('\\')) {
      threats.push('Directory separators in filename not allowed');
      safe = false;
    }

    // Check for absolute paths
    if (path.isAbsolute(filename)) {
      threats.push('Absolute paths not allowed');
      safe = false;
    }

    // Check for dangerous characters
    if (/[\x00-\x1f\x7f-\x9f]/.test(filename)) {
      threats.push('Control characters in filename');
      safe = false;
    }

    // Check for reserved names
    if (/(CON|PRN|AUX|NUL|COM[1-9]|LPT[1-9])(\.|$)/i.test(filename)) {
      threats.push('Reserved system filename');
      safe = false;
    }

    // Generate sanitized filename
    const sanitizedFilename = this.sanitizeFilename(filename);

    return { safe, threats, sanitizedFilename };
  }

  /**
   * Validate file size
   */
  private validateFileSize(size: number): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (size <= 0) {
      errors.push('File is empty');
    }

    if (size > this.config.maxFileSize) {
      errors.push(`File too large: ${size} bytes (max ${this.config.maxFileSize})`);
    }

    return { valid: errors.length === 0, errors };
  }

  /**
   * Validate MIME type
   */
  private validateMimeType(mimetype: string, extension: string): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    // Check if MIME type is allowed
    if (!this.config.allowedMimeTypes.includes(mimetype)) {
      errors.push(`MIME type not allowed: ${mimetype}`);
    }

    // Check if MIME type is explicitly blocked
    if (this.config.blockedMimeTypes.includes(mimetype)) {
      errors.push(`MIME type blocked: ${mimetype}`);
    }

    // Verify MIME type matches file extension
    const expectedMimeType = mime.lookup(extension);
    if (expectedMimeType && expectedMimeType !== mimetype) {
      errors.push(`MIME type mismatch: expected ${expectedMimeType}, got ${mimetype}`);
    }

    return { valid: errors.length === 0, errors };
  }

  /**
   * Validate file extension
   */
  private validateFileExtension(extension: string): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    // Check if extension is blocked
    if (this.config.blockedExtensions.includes(extension)) {
      errors.push(`File extension blocked: ${extension}`);
    }

    // Check if extension is allowed (if whitelist is configured)
    if (this.config.allowedExtensions.length > 0 && !this.config.allowedExtensions.includes(extension)) {
      errors.push(`File extension not allowed: ${extension}`);
    }

    return { valid: errors.length === 0, errors };
  }

  /**
   * Validate file content
   */
  private async validateFileContent(
    buffer: Buffer, 
    mimetype: string, 
    extension: string
  ): Promise<{
    valid: boolean;
    threats: string[];
    threatLevel: 'low' | 'medium' | 'high' | 'critical';
    isExecutable: boolean;
    isCompressed: boolean;
  }> {
    const threats: string[] = [];
    let threatLevel: 'low' | 'medium' | 'high' | 'critical' = 'low';
    let isExecutable = false;
    let isCompressed = false;

    // Check file magic bytes
    const magicBytes = buffer.subarray(0, 16);
    const magicValidation = this.validateFileMagicBytes(magicBytes, mimetype, extension);

    if (!magicValidation.valid) {
      threats.push(...magicValidation.threats);
      threatLevel = 'high';
    }

    isExecutable = magicValidation.isExecutable;
    isCompressed = magicValidation.isCompressed;

    // Check for embedded scripts in text files
    if (mimetype.startsWith('text/') || mimetype === 'application/json') {
      const textContent = buffer.toString('utf8', 0, Math.min(buffer.length, 10000));
      const scriptValidation = this.validateTextContent(textContent);

      if (!scriptValidation.safe) {
        threats.push(...scriptValidation.threats);
        threatLevel = scriptValidation.threatLevel;
      }
    }

    // Check for dangerous binary patterns
    if (!mimetype.startsWith('text/')) {
      const binaryValidation = this.validateBinaryContent(buffer);

      if (!binaryValidation.safe) {
        threats.push(...binaryValidation.threats);
        threatLevel = binaryValidation.threatLevel;
      }
    }

    return {
      valid: threats.length === 0,
      threats,
      threatLevel,
      isExecutable,
      isCompressed,
    };
  }

  /**
   * Validate file magic bytes
   */
  private validateFileMagicBytes(
    magicBytes: Buffer, 
    mimetype: string, 
    extension: string
  ): {
    valid: boolean;
    threats: string[];
    isExecutable: boolean;
    isCompressed: boolean;
  } {
    const threats: string[] = [];
    let isExecutable = false;
    let isCompressed = false;

    // Common executable signatures
    const executableSignatures = [
      [0x4D, 0x5A], // PE/DOS executable (MZ)
      [0x7F, 0x45, 0x4C, 0x46], // ELF executable
      [0xFE, 0xED, 0xFA, 0xCE], // Mach-O executable
      [0xCA, 0xFE, 0xBA, 0xBE], // Java class file
      [0x50, 0x4B, 0x03, 0x04], // ZIP/JAR (potentially executable)
    ];

    // Compression signatures
    const compressionSignatures = [
      [0x1F, 0x8B], // GZIP
      [0x50, 0x4B], // ZIP
      [0x52, 0x61, 0x72, 0x21], // RAR
      [0x42, 0x5A, 0x68], // BZIP2
    ];

    // Check for executable signatures
    for (const signature of executableSignatures) {
      if (this.matchesSignature(magicBytes, signature)) {
        isExecutable = true;
        threats.push('Executable file signature detected');
        break;
      }
    }

    // Check for compression signatures
    for (const signature of compressionSignatures) {
      if (this.matchesSignature(magicBytes, signature)) {
        isCompressed = true;
        if (!this.config.enableDecompression) {
          threats.push('Compressed files not allowed');
        }
        break;
      }
    }

    return {
      valid: threats.length === 0,
      threats,
      isExecutable,
      isCompressed,
    };
  }

  /**
   * Validate text content for embedded scripts
   */
  private validateTextContent(content: string): {
    safe: boolean;
    threats: string[];
    threatLevel: 'low' | 'medium' | 'high' | 'critical';
  } {
    const threats: string[] = [];
    let threatLevel: 'low' | 'medium' | 'high' | 'critical' = 'low';

    // Check for script tags
    if (/<script[^>]*>.*?<\/script>/gsi.test(content)) {
      threats.push('Embedded script tags detected');
      threatLevel = 'high';
    }

    // Check for JavaScript functions
    const jsPatterns = [
      /eval\s*\(/gi,
      /setTimeout\s*\(/gi,
      /setInterval\s*\(/gi,
      /Function\s*\(/gi,
      /new\s+Function\s*\(/gi,
    ];

    for (const pattern of jsPatterns) {
      if (pattern.test(content)) {
        threats.push('Dangerous JavaScript functions detected');
        threatLevel = 'high';
        break;
      }
    }

    // Check for shell commands
    const shellPatterns = [
      /system\s*\(/gi,
      /exec\s*\(/gi,
      /shell_exec\s*\(/gi,
      /passthru\s*\(/gi,
    ];

    for (const pattern of shellPatterns) {
      if (pattern.test(content)) {
        threats.push('Shell command injection patterns detected');
        threatLevel = 'critical';
        break;
      }
    }

    return { safe: threats.length === 0, threats, threatLevel };
  }

  /**
   * Validate binary content
   */
  private validateBinaryContent(buffer: Buffer): {
    safe: boolean;
    threats: string[];
    threatLevel: 'low' | 'medium' | 'high' | 'critical';
  } {
    const threats: string[] = [];
    let threatLevel: 'low' | 'medium' | 'high' | 'critical' = 'low';

    // Check for embedded PE headers
    if (buffer.includes(Buffer.from('MZ'))) {
      threats.push('PE executable header found in binary data');
      threatLevel = 'high';
    }

    // Check for script languages in binary
    const scriptMarkers = ['#!/bin/sh', '#!/bin/bash', '<?php', '<%', '<script'];
    for (const marker of scriptMarkers) {
      if (buffer.includes(Buffer.from(marker))) {
        threats.push(`Script marker found in binary: ${marker}`);
        threatLevel = 'medium';
      }
    }

    return { safe: threats.length === 0, threats, threatLevel };
  }

  /**
   * Virus scanning (placeholder - would integrate with actual antivirus)
   */
  private async scanForViruses(buffer: Buffer, filename: string): Promise<{
    clean: boolean;
    threats: string[];
  }> {
    // This would integrate with actual antivirus scanning
    // For now, implement basic signature detection
    const threats: string[] = [];

    // Basic malware signatures (simplified)
    const malwareSignatures = [
      'EICAR-STANDARD-ANTIVIRUS-TEST-FILE',
      '\x4d\x5a\x90\x00\x03\x00\x00\x00', // PE header
    ];

    const content = buffer.toString('binary');
    for (const signature of malwareSignatures) {
      if (content.includes(signature)) {
        threats.push(`Malware signature detected: ${signature.substring(0, 20)}...`);
      }
    }

    return {
      clean: threats.length === 0,
      threats,
    };
  }

  /**
   * Sanitize filename
   */
  private sanitizeFilename(filename: string): string {
    return filename
      .replace(/[^\w\-_.]/g, '_') // Replace non-alphanumeric with underscore
      .replace(/_{2,}/g, '_') // Multiple underscores to single
      .substring(0, 255) // Limit length
      .replace(/^_+|_+$/g, ''); // Remove leading/trailing underscores
  }

  /**
   * Check if magic bytes match signature
   */
  private matchesSignature(buffer: Buffer, signature: number[]): boolean {
    if (buffer.length < signature.length) return false;

    for (let i = 0; i < signature.length; i++) {
      if (buffer[i] !== signature[i]) return false;
    }

    return true;
  }

  /**
   * Express middleware factory
   */
  createMiddleware() {
    return async (req: any, res: any, next: any) => {
      if (!req.files && !req.file) {
        return next(); // No files to validate
      }

      try {
        const files = req.files ? (Array.isArray(req.files) ? req.files : [req.files]) : [req.file];
        const validationResults: FileValidationResult[] = [];

        for (const file of files) {
          if (file) {
            const result = await this.validateFile(file);
            validationResults.push(result);

            if (!result.valid) {
              logger.warn('File validation failed:', {
                filename: result.fileInfo.filename,
                errors: result.errors,
                threatLevel: result.threatLevel,
              });

              if (result.quarantined) {
                // Move file to quarantine (implementation would depend on file storage)
                logger.error('File quarantined due to security threats:', result.fileInfo.filename);
              }

              if (result.threatLevel === 'critical' || result.threatLevel === 'high') {
                return res.status(400).json({
                  error: 'File validation failed',
                  details: result.errors,
                  threatLevel: result.threatLevel,
                });
              }
            }
          }
        }

        req.fileValidationResults = validationResults;
        next();
      } catch (error) {
        logger.error('File validation middleware error:', error);
        res.status(500).json({
          error: 'File validation error',
          message: (error as Error).message,
        });
      }
    };
  }
}

/**
 * Global file upload validation middleware
 */
export const fileUploadValidationMiddleware = new FileUploadValidationMiddleware();

/**
 * Express middleware factory
 */
export function createFileUploadValidationMiddleware(config?: Partial<FileUploadConfig>) {
  const middleware = new FileUploadValidationMiddleware(config);
  return middleware.createMiddleware();
}

/**
 * @fileoverview Rate limiting middleware for API calls and service operations
 * @lastmodified 2025-08-25T21:44:14-05:00
 *
 * Features: Flexible rate limiting with multiple algorithms and storage backends
 * Main APIs: RateLimiter class, withRateLimit decorator, rate limiting middleware
 * Constraints: Configurable limits, multiple algorithms, persistent storage
 * Patterns: Middleware pattern, decorator pattern, sliding window, token bucket
 */

import { EventEmitter } from 'events';
import { logger } from '../utils/logger';

/**
 * Rate limiting algorithms
 */
export type RateLimitAlgorithm =
  | 'sliding-window'
  | 'token-bucket'
  | 'fixed-window'
  | 'leaky-bucket';

/**
 * Rate limit data structure
 */
export interface RateLimitData {
  count: number;
  resetTime: number;
  firstHit: number;

  // Token bucket specific
  tokens?: number;
  lastRefill?: number;

  // Sliding window specific
  hits?: Array<{ timestamp: number; count: number }>;
}

/**
 * Rate limit storage interface
 */
export interface IRateLimitStore {
  get(key: string): Promise<RateLimitData | null>;
  set(key: string, data: RateLimitData): Promise<void>;
  increment(key: string, ttl: number): Promise<number>;
  reset(key: string): Promise<void>;
  cleanup(): Promise<void>;
}

/**
 * Rate limit configuration
 */
export interface RateLimitConfig {
  // Basic configuration
  windowMs: number; // Time window in milliseconds
  maxRequests: number; // Maximum requests per window
  max?: number; // Alias for maxRequests (backward compatibility)
  algorithm: RateLimitAlgorithm;

  // Advanced configuration
  keyGenerator?: (identifier: string) => string;
  skipSuccessfulRequests?: boolean;
  skipFailedRequests?: boolean;

  // Behavior on limit exceeded
  delayAfterHit?: number; // Delay in ms after hit
  blockAfterHit?: boolean; // Block further requests

  // Custom messages
  message?: string;

  // Storage backend
  store?: IRateLimitStore;

  // Whitelist/blacklist
  whitelist?: string[];
  blacklist?: string[];
}

/**
 * Rate limit result
 */
export interface RateLimitResult {
  allowed: boolean;
  remaining: number;
  resetTime: number;
  retryAfter?: number;
  error?: string;
}

/**
 * Default rate limit configuration
 */
export const DEFAULT_RATE_LIMIT_CONFIG: RateLimitConfig = {
  windowMs: 60 * 1000, // 1 minute
  maxRequests: 100,
  algorithm: 'sliding-window',
  keyGenerator: (id: string) => id,
  skipSuccessfulRequests: false,
  skipFailedRequests: false,
  delayAfterHit: 0,
  blockAfterHit: true,
  message: 'Rate limit exceeded',
};

/**
 * In-memory rate limit store
 */
export class MemoryRateLimitStore implements IRateLimitStore {
  private store = new Map<string, RateLimitData>();

  private cleanupInterval: ReturnType<typeof setInterval>;

  constructor(cleanupIntervalMs = 60000) {
    // Clean up expired entries every minute
    this.cleanupInterval = setInterval(() => {
      this.cleanup();
    }, cleanupIntervalMs);
  }

  async get(key: string): Promise<RateLimitData | null> {
    const data = this.store.get(key);
    if (!data) return null;

    // Check if expired
    if (Date.now() > data.resetTime) {
      this.store.delete(key);
      return null;
    }

    return data;
  }

  async set(key: string, data: RateLimitData): Promise<void> {
    this.store.set(key, data);
  }

  async increment(key: string, ttl: number): Promise<number> {
    const existing = await this.get(key);
    const now = Date.now();

    if (!existing) {
      const data: RateLimitData = {
        count: 1,
        resetTime: now + ttl,
        firstHit: now,
      };
      await this.set(key, data);
      return 1;
    }

    existing.count += 1;
    await this.set(key, existing);
    return existing.count;
  }

  async reset(key: string): Promise<void> {
    this.store.delete(key);
  }

  async cleanup(): Promise<void> {
    const now = Date.now();
    for (const [key, data] of this.store.entries()) {
      if (now > data.resetTime) {
        this.store.delete(key);
      }
    }
  }

  dispose(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
    }
    this.store.clear();
  }
}

/**
 * Main rate limiter class
 */
export class RateLimiter extends EventEmitter {
  private config: Required<RateLimitConfig>;

  private store: IRateLimitStore;

  constructor(config: Partial<RateLimitConfig> = {}) {
    super();

    // Handle 'max' option alias for backward compatibility
    const processedConfig = { ...config };
    if (config.max !== undefined) {
      processedConfig.maxRequests = config.max;
    }

    this.config = {
      ...DEFAULT_RATE_LIMIT_CONFIG,
      ...processedConfig,
      store: processedConfig.store || new MemoryRateLimitStore(),
      keyGenerator:
        processedConfig.keyGenerator || DEFAULT_RATE_LIMIT_CONFIG.keyGenerator!,
      message: processedConfig.message || DEFAULT_RATE_LIMIT_CONFIG.message!,
    } as Required<RateLimitConfig>;

    this.store = this.config.store;
  }

  /**
   * Check if request is allowed and update counters
   */
  async checkLimit(identifier: string): Promise<RateLimitResult> {
    try {
      const key = this.config.keyGenerator(identifier);

      // Check whitelist
      if (this.config.whitelist?.includes(identifier)) {
        return {
          allowed: true,
          remaining: this.config.maxRequests,
          resetTime: Date.now() + this.config.windowMs,
        };
      }

      // Check blacklist
      if (this.config.blacklist?.includes(identifier)) {
        return {
          allowed: false,
          remaining: 0,
          resetTime: Date.now() + this.config.windowMs,
          error: 'Identifier is blacklisted',
        };
      }

      switch (this.config.algorithm) {
        case 'sliding-window':
          return this.slidingWindowCheck(key);
        case 'token-bucket':
          return this.tokenBucketCheck(key);
        case 'fixed-window':
          return this.fixedWindowCheck(key);
        case 'leaky-bucket':
          return this.leakyBucketCheck(key);
        default:
          throw new Error(`Unknown algorithm: ${this.config.algorithm}`);
      }
    } catch (error: unknown) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      logger.error(`Rate limit check failed: ${errorMessage}`);
      return {
        allowed: false,
        remaining: 0,
        resetTime: Date.now() + this.config.windowMs,
        error: error.message,
      };
    }
  }

  /**
   * Sliding window rate limiting
   */
  private async slidingWindowCheck(key: string): Promise<RateLimitResult> {
    const now = Date.now();
    const windowStart = now - this.config.windowMs;

    let data = await this.store.get(key);
    if (!data) {
      data = {
        count: 0,
        resetTime: now + this.config.windowMs,
        firstHit: now,
        hits: [],
      };
    }

    // Remove old hits outside the window
    if (data.hits) {
      data.hits = data.hits.filter(hit => hit.timestamp > windowStart);
    }

    // Calculate current count
    const currentCount =
      data.hits?.reduce((sum, hit) => sum + hit.count, 0) || 0;

    if (currentCount >= this.config.maxRequests) {
      const oldestHit = data.hits?.[0];
      const resetTime = oldestHit
        ? oldestHit.timestamp + this.config.windowMs
        : now + this.config.windowMs;

      this.emit('limitExceeded', {
        key,
        identifier: key,
        currentCount,
        limit: this.config.maxRequests,
      });

      return {
        allowed: false,
        remaining: 0,
        resetTime,
        retryAfter: Math.max(0, resetTime - now),
      };
    }

    // Add current hit
    if (!data.hits) data.hits = [];
    data.hits.push({ timestamp: now, count: 1 });
    data.count = currentCount + 1;

    await this.store.set(key, data);

    return {
      allowed: true,
      remaining: Math.max(0, this.config.maxRequests - (currentCount + 1)),
      resetTime: now + this.config.windowMs,
    };
  }

  /**
   * Token bucket rate limiting
   */
  private async tokenBucketCheck(key: string): Promise<RateLimitResult> {
    const now = Date.now();

    let data = await this.store.get(key);
    if (!data) {
      data = {
        count: 0,
        resetTime: now + this.config.windowMs,
        firstHit: now,
        tokens: this.config.maxRequests,
        lastRefill: now,
      };
    }

    // Refill tokens based on time passed
    const timePassed = now - (data.lastRefill || now);
    const refillRate = this.config.maxRequests / this.config.windowMs; // tokens per ms
    const tokensToAdd = Math.floor(timePassed * refillRate);

    data.tokens = Math.min(
      this.config.maxRequests,
      (data.tokens || 0) + tokensToAdd
    );
    data.lastRefill = now;

    if ((data.tokens || 0) < 1) {
      const timeToNextToken = 1 / refillRate;

      this.emit('limitExceeded', {
        key,
        identifier: key,
        tokens: data.tokens,
        limit: this.config.maxRequests,
      });

      return {
        allowed: false,
        remaining: 0,
        resetTime: now + timeToNextToken,
        retryAfter: timeToNextToken,
      };
    }

    // Consume one token
    data.tokens = (data.tokens || 0) - 1;
    data.count += 1;

    await this.store.set(key, data);

    return {
      allowed: true,
      remaining: data.tokens,
      resetTime: now + this.config.windowMs,
    };
  }

  /**
   * Fixed window rate limiting
   */
  private async fixedWindowCheck(key: string): Promise<RateLimitResult> {
    const now = Date.now();
    const windowStart =
      Math.floor(now / this.config.windowMs) * this.config.windowMs;

    let data = await this.store.get(key);
    if (!data || data.firstHit < windowStart) {
      data = {
        count: 0,
        resetTime: windowStart + this.config.windowMs,
        firstHit: now,
      };
    }

    if (data.count >= this.config.maxRequests) {
      this.emit('limitExceeded', {
        key,
        identifier: key,
        currentCount: data.count,
        limit: this.config.maxRequests,
      });

      return {
        allowed: false,
        remaining: 0,
        resetTime: data.resetTime,
        retryAfter: data.resetTime - now,
      };
    }

    data.count += 1;
    await this.store.set(key, data);

    return {
      allowed: true,
      remaining: this.config.maxRequests - data.count,
      resetTime: data.resetTime,
    };
  }

  /**
   * Leaky bucket rate limiting
   */
  private async leakyBucketCheck(key: string): Promise<RateLimitResult> {
    const now = Date.now();

    let data = await this.store.get(key);
    if (!data) {
      data = {
        count: 1,
        resetTime: now + this.config.windowMs,
        firstHit: now,
      };
    } else {
      // Leak tokens based on time
      const timePassed = now - data.firstHit;
      const leakRate = this.config.maxRequests / this.config.windowMs; // requests per ms
      const leaked = Math.floor(timePassed * leakRate);

      data.count = Math.max(0, data.count - leaked);
      data.firstHit = now;
    }

    if (data.count >= this.config.maxRequests) {
      const drainTime =
        (data.count - this.config.maxRequests + 1) /
        (this.config.maxRequests / this.config.windowMs);

      this.emit('limitExceeded', {
        key,
        identifier: key,
        currentCount: data.count,
        limit: this.config.maxRequests,
      });

      return {
        allowed: false,
        remaining: 0,
        resetTime: now + drainTime,
        retryAfter: drainTime,
      };
    }

    data.count += 1;
    await this.store.set(key, data);

    return {
      allowed: true,
      remaining: this.config.maxRequests - data.count,
      resetTime: data.resetTime,
    };
  }

  /**
   * Reset rate limit for identifier
   */
  async reset(identifier: string): Promise<void> {
    const key = this.config.keyGenerator(identifier);
    await this.store.reset(key);
    this.emit('limitReset', { identifier, key });
  }

  /**
   * Get current status for identifier
   */
  async getStatus(identifier: string): Promise<RateLimitData | null> {
    const key = this.config.keyGenerator(identifier);
    return this.store.get(key);
  }

  /**
   * Cleanup expired entries
   */
  async cleanup(): Promise<void> {
    await this.store.cleanup();
  }

  /**
   * Dispose resources
   */
  dispose(): void {
    if (this.store instanceof MemoryRateLimitStore) {
      this.store.dispose();
    }
  }
}

/**
 * Rate limiting decorator
 */
export function withRateLimit(config: Partial<RateLimitConfig> = {}) {
  const limiter = new RateLimiter(config);

  return function (
    _target: unknown,
    _propertyName: string,
    descriptor: PropertyDescriptor
  ) {
    const method = descriptor.value;
    const newDescriptor = { ...descriptor };

    newDescriptor.value = async function (...args: unknown[]) {
      // Use 'this' context or first argument as identifier
      const identifier =
        this?.constructor?.name || args[0]?.toString() || 'anonymous';

      const result = await limiter.checkLimit(identifier);

      if (!result.allowed) {
        const error = new Error(config.message || 'Rate limit exceeded');
        (error as any).rateLimitInfo = result;
        throw error;
      }

      // Apply delay if configured
      if (config.delayAfterHit && config.delayAfterHit > 0) {
        await new Promise(resolve => {
          setTimeout(resolve, config.delayAfterHit);
        });
      }

      return method.apply(this, args);
    };

    return newDescriptor;
  };
}

/**
 * Create rate limiter middleware for different contexts
 */
export function createRateLimitMiddleware(
  config: Partial<RateLimitConfig> = {}
) {
  const limiter = new RateLimiter(config);

  return async (
    identifier: string,
    operation: () => Promise<any>
  ): Promise<any> => {
    const result = await limiter.checkLimit(identifier);

    if (!result.allowed) {
      const error = new Error(config.message || 'Rate limit exceeded');
      (error as any).rateLimitInfo = result;
      throw error;
    }

    // Apply delay if configured
    if (config.delayAfterHit && config.delayAfterHit > 0) {
      await new Promise(resolve => {
        setTimeout(resolve, config.delayAfterHit);
      });
    }

    return operation();
  };
}

/**
 * Pre-configured rate limiters for common use cases
 */
export const rateLimiters = {
  // Strict rate limiting for sensitive operations
  strict: new RateLimiter({
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 10, // 10 requests per minute
    algorithm: 'sliding-window',
    blockAfterHit: true,
  }),

  // Moderate rate limiting for general APIs
  moderate: new RateLimiter({
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 100, // 100 requests per minute
    algorithm: 'token-bucket',
    delayAfterHit: 100, // 100ms delay after each request
  }),

  // Lenient rate limiting for bulk operations
  lenient: new RateLimiter({
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 1000, // 1000 requests per minute
    algorithm: 'fixed-window',
    skipSuccessfulRequests: true, // Only count failed requests
  }),

  // Burst-friendly rate limiting
  burst: new RateLimiter({
    windowMs: 15 * 60 * 1000, // 15 minutes
    maxRequests: 200, // 200 requests per 15 minutes
    algorithm: 'leaky-bucket',
  }),
};

