/**
 * @fileoverview Comprehensive security headers middleware for enterprise-grade protection
 * @lastmodified 2025-08-27T16:45:00Z
 *
 * Features: OWASP-compliant headers, CSP with nonces, HSTS, security policies, threat protection
 * Main APIs: securityHeadersMiddleware(), generateCSPNonce(), setSecurityHeaders()
 * Constraints: Compatible with Express.js, requires proper configuration
 * Patterns: Middleware pattern, security-first design, configurable policies
 */

import * as crypto from 'crypto';
import { logger } from '../utils/logger';
import { cryptoService } from '../security/cryptographic.service';

export interface SecurityHeadersConfig {
  // Content Security Policy
  csp?: {
    enabled: boolean;
    reportOnly?: boolean;
    directives?: CSPDirectives;
    nonce?: boolean;
    reportUri?: string;
  };

  // HTTP Strict Transport Security
  hsts?: {
    enabled: boolean;
    maxAge: number;
    includeSubDomains: boolean;
    preload: boolean;
  };

  // Cross-Origin policies
  crossOrigin?: {
    embedderPolicy: 'unsafe-none' | 'require-corp';
    openerPolicy: 'unsafe-none' | 'same-origin-allow-popups' | 'same-origin';
    resourcePolicy: 'cross-origin' | 'same-site' | 'same-origin';
  };

  // Feature/Permissions Policy
  permissionsPolicy?: {
    [feature: string]: string[];
  };

  // Additional security headers
  additionalHeaders?: {
    [header: string]: string;
  };

  // Environment-specific overrides
  development?: Partial<SecurityHeadersConfig>;
  production?: Partial<SecurityHeadersConfig>;
}

export interface CSPDirectives {
  'default-src'?: string[];
  'script-src'?: string[];
  'style-src'?: string[];
  'img-src'?: string[];
  'font-src'?: string[];
  'connect-src'?: string[];
  'media-src'?: string[];
  'object-src'?: string[];
  'child-src'?: string[];
  'worker-src'?: string[];
  'frame-src'?: string[];
  'form-action'?: string[];
  'frame-ancestors'?: string[];
  'base-uri'?: string[];
  'manifest-src'?: string[];
  'prefetch-src'?: string[];
  'navigate-to'?: string[];
  'report-uri'?: string[];
  'report-to'?: string[];
  'require-trusted-types-for'?: string[];
  'trusted-types'?: string[];
  'upgrade-insecure-requests'?: boolean;
  'block-all-mixed-content'?: boolean;
}

export interface SecurityContext {
  nonce?: string;
  requestId?: string;
  userAgent?: string;
  origin?: string;
  referer?: string;
}

/**
 * Enterprise security headers middleware service
 */
export class SecurityHeadersMiddleware {
  private readonly config: SecurityHeadersConfig;

  private readonly nonceCache = new Map<string, { nonce: string; expires: Date }>();

  constructor(config: SecurityHeadersConfig = {}) {
    this.config = this.mergeWithDefaults(config);
    this.setupNonceCleanup();
    logger.info('Security headers middleware initialized');
  }

  /**
   * Express middleware for setting security headers
   */
  middleware() {
    return (req: any, res: any, next: any) => {
      try {
        const context = this.extractSecurityContext(req);
        const headers = this.generateSecurityHeaders(context);

        // Set all security headers
        Object.entries(headers).forEach(([name, value]) => {
          if (value) {
            res.setHeader(name, value);
          }
        });

        // Store nonce in request for use in templates
        if (context.nonce) {
          req.cspNonce = context.nonce;
        }

        next();
      } catch (error) {
        logger.error('Security headers middleware error', error as Error);
        // Continue processing even if security headers fail
        next();
      }
    };
  }

  /**
   * Generate all security headers
   */
  generateSecurityHeaders(context: SecurityContext = {}): Record<string, string> {
    const headers: Record<string, string> = {};

    // Content Security Policy
    if (this.config.csp?.enabled) {
      const cspHeader = this.config.csp.reportOnly 
        ? 'Content-Security-Policy-Report-Only' 
        : 'Content-Security-Policy';

      headers[cspHeader] = this.buildCSP(context);
    }

    // HTTP Strict Transport Security
    if (this.config.hsts?.enabled) {
      headers['Strict-Transport-Security'] = this.buildHSTS();
    }

    // X-Frame-Options (fallback for older browsers)
    headers['X-Frame-Options'] = 'DENY';

    // X-Content-Type-Options
    headers['X-Content-Type-Options'] = 'nosniff';

    // X-XSS-Protection (for older browsers)
    headers['X-XSS-Protection'] = '1; mode=block';

    // Referrer Policy
    headers['Referrer-Policy'] = 'strict-origin-when-cross-origin';

    // Cross-Origin Policies
    if (this.config.crossOrigin) {
      headers['Cross-Origin-Embedder-Policy'] = this.config.crossOrigin.embedderPolicy;
      headers['Cross-Origin-Opener-Policy'] = this.config.crossOrigin.openerPolicy;
      headers['Cross-Origin-Resource-Policy'] = this.config.crossOrigin.resourcePolicy;
    }

    // Permissions Policy
    if (this.config.permissionsPolicy) {
      headers['Permissions-Policy'] = this.buildPermissionsPolicy();
    }

    // Additional custom headers
    if (this.config.additionalHeaders) {
      Object.assign(headers, this.config.additionalHeaders);
    }

    // Security-related headers for API protection
    headers['X-Permitted-Cross-Domain-Policies'] = 'none';
    headers['X-Download-Options'] = 'noopen';
    headers['X-DNS-Prefetch-Control'] = 'off';
    headers['Expect-CT'] = 'max-age=86400, enforce';

    return headers;
  }

  /**
   * Generate CSP nonce for inline scripts/styles
   */
  generateCSPNonce(requestId?: string): string {
    const nonce = crypto.randomBytes(16).toString('base64');
    const expires = new Date(Date.now() + 5 * 60 * 1000); // 5 minutes

    if (requestId) {
      this.nonceCache.set(requestId, { nonce, expires });
    }

    return nonce;
  }

  /**
   * Build Content Security Policy header
   */
  private buildCSP(context: SecurityContext): string {
    const directives = { ...this.getDefaultCSPDirectives() };

    // Merge with custom directives
    if (this.config.csp?.directives) {
      Object.assign(directives, this.config.csp.directives);
    }

    // Add nonce if enabled
    if (this.config.csp?.nonce && context.nonce) {
      const nonceValue = `'nonce-${context.nonce}'`;

      if (directives['script-src']) {
        directives['script-src'] = [...directives['script-src'], nonceValue];
      }

      if (directives['style-src']) {
        directives['style-src'] = [...directives['style-src'], nonceValue];
      }
    }

    // Build CSP string
    const cspParts: string[] = [];

    Object.entries(directives).forEach(([directive, sources]) => {
      if (typeof sources === 'boolean') {
        if (sources) {
          cspParts.push(directive);
        }
      } else if (Array.isArray(sources) && sources.length > 0) {
        cspParts.push(`${directive} ${sources.join(' ')}`);
      }
    });

    // Add report URI if configured
    if (this.config.csp?.reportUri) {
      cspParts.push(`report-uri ${this.config.csp.reportUri}`);
    }

    return cspParts.join('; ');
  }

  /**
   * Build HSTS header
   */
  private buildHSTS(): string {
    if (!this.config.hsts) return '';

    const parts = [`max-age=${this.config.hsts.maxAge}`];

    if (this.config.hsts.includeSubDomains) {
      parts.push('includeSubDomains');
    }

    if (this.config.hsts.preload) {
      parts.push('preload');
    }

    return parts.join('; ');
  }

  /**
   * Build Permissions Policy header
   */
  private buildPermissionsPolicy(): string {
    if (!this.config.permissionsPolicy) return '';

    const policies: string[] = [];

    Object.entries(this.config.permissionsPolicy).forEach(([feature, allowlist]) => {
      if (allowlist.length === 0) {
        policies.push(`${feature}=()`);
      } else {
        const origins = allowlist.map(origin => 
          origin === '*' ? '*' : `"${origin}"`
        ).join(' ');
        policies.push(`${feature}=(${origins})`);
      }
    });

    return policies.join(', ');
  }

  /**
   * Get default CSP directives
   */
  private getDefaultCSPDirectives(): CSPDirectives {
    return {
      'default-src': ["'self'"],
      'script-src': [
        "'self'",
        "'strict-dynamic'",
        // Remove unsafe-inline and unsafe-eval for maximum security
      ],
      'style-src': [
        "'self'",
        'https://fonts.googleapis.com',
        // Only allow specific trusted sources
      ],
      'img-src': [
        "'self'",
        'data:',
        'blob:',
        'https:',
      ],
      'font-src': [
        "'self'",
        'https://fonts.gstatic.com',
      ],
      'connect-src': [
        "'self'",
        'https://api.promptwizard.com',
        'wss://api.promptwizard.com',
      ],
      'media-src': ["'self'"],
      'object-src': ["'none'"],
      'child-src': ["'none'"],
      'worker-src': ["'self'"],
      'frame-src': ["'none'"],
      'form-action': ["'self'"],
      'frame-ancestors': ["'none'"],
      'base-uri': ["'self'"],
      'manifest-src': ["'self'"],
      'upgrade-insecure-requests': true,
      'block-all-mixed-content': true,
      'require-trusted-types-for': ["'script'"],
      'trusted-types': ['default'],
    };
  }

  /**
   * Extract security context from request
   */
  private extractSecurityContext(req: any): SecurityContext {
    const context: SecurityContext = {};

    // Generate nonce if CSP nonce is enabled
    if (this.config.csp?.nonce) {
      context.nonce = this.generateCSPNonce(req.id || req.requestId);
    }

    // Extract request metadata
    context.requestId = req.id || req.requestId;
    context.userAgent = req.get('User-Agent');
    context.origin = req.get('Origin');
    context.referer = req.get('Referer');

    return context;
  }

  /**
   * Merge configuration with secure defaults
   */
  private mergeWithDefaults(config: SecurityHeadersConfig): SecurityHeadersConfig {
    const environment = process.env.NODE_ENV || 'development';

    const defaults: SecurityHeadersConfig = {
      csp: {
        enabled: true,
        reportOnly: environment === 'development',
        nonce: true,
        reportUri: process.env.CSP_REPORT_URI || '/api/csp-report',
      },
      hsts: {
        enabled: environment === 'production',
        maxAge: 31536000, // 1 year
        includeSubDomains: true,
        preload: true,
      },
      crossOrigin: {
        embedderPolicy: 'require-corp',
        openerPolicy: 'same-origin',
        resourcePolicy: 'same-origin',
      },
      permissionsPolicy: {
        geolocation: [],
        microphone: [],
        camera: [],
        payment: [],
        usb: [],
        magnetometer: [],
        accelerometer: [],
        gyroscope: [],
        'picture-in-picture': [],
        'fullscreen': ['self'],
        'display-capture': [],
      },
    };

    // Deep merge configuration
    const merged = this.deepMerge(defaults, config);

    // Apply environment-specific overrides
    if (environment === 'development' && merged.development) {
      return this.deepMerge(merged, merged.development);
    } else if (environment === 'production' && merged.production) {
      return this.deepMerge(merged, merged.production);
    }

    return merged;
  }

  /**
   * Deep merge configuration objects
   */
  private deepMerge(target: any, source: any): any {
    const result = { ...target };

    Object.keys(source).forEach(key => {
      if (source[key] && typeof source[key] === 'object' && !Array.isArray(source[key])) {
        result[key] = this.deepMerge(target[key] || {}, source[key]);
      } else {
        result[key] = source[key];
      }
    });

    return result;
  }

  /**
   * Setup nonce cache cleanup
   */
  private setupNonceCleanup(): void {
    // Clean expired nonces every 10 minutes
    setInterval(() => {
      const now = new Date();
      for (const [requestId, { expires }] of this.nonceCache.entries()) {
        if (expires < now) {
          this.nonceCache.delete(requestId);
        }
      }
    }, 10 * 60 * 1000);
  }

  /**
   * Get security headers statistics
   */
  getStats() {
    return {
      config: {
        cspEnabled: this.config.csp?.enabled,
        hstsEnabled: this.config.hsts?.enabled,
        nonceEnabled: this.config.csp?.nonce,
        reportOnly: this.config.csp?.reportOnly,
      },
      runtime: {
        activenonces: this.nonceCache.size,
        environment: process.env.NODE_ENV || 'development',
      },
      headers: Object.keys(this.generateSecurityHeaders()).length,
    };
  }
}

/**
 * CSP report endpoint handler
 */
export function cspReportHandler() {
  return (req: any, res: any) => {
    try {
      const report = req.body;
      logger.warn('CSP Violation Report', {
        report,
        userAgent: req.get('User-Agent'),
        origin: req.get('Origin'),
        timestamp: new Date().toISOString(),
      });

      res.status(204).send();
    } catch (error) {
      logger.error('CSP report handler error', error as Error);
      res.status(400).json({ error: 'Invalid report format' });
    }
  };
}

/**
 * Security headers testing utility
 */
export function testSecurityHeaders(url: string): Promise<{
  grade: 'A+' | 'A' | 'B' | 'C' | 'D' | 'F';
  headers: Record<string, string>;
  missing: string[];
  issues: string[];
}> {
  return new Promise((resolve) => {
    // This is a simplified implementation
    // In a real scenario, you'd make an HTTP request and analyze headers

    const mockHeaders = {
      'Content-Security-Policy': "default-src 'self'",
      'Strict-Transport-Security': 'max-age=31536000',
      'X-Frame-Options': 'DENY',
      'X-Content-Type-Options': 'nosniff',
    };

    const requiredHeaders = [
      'Content-Security-Policy',
      'Strict-Transport-Security',
      'X-Frame-Options',
      'X-Content-Type-Options',
      'X-XSS-Protection',
      'Referrer-Policy',
    ];

    const missing = requiredHeaders.filter(header => !mockHeaders[header]);
    const grade = missing.length === 0 ? 'A+' : missing.length <= 2 ? 'A' : 'B';

    resolve({
      grade,
      headers: mockHeaders,
      missing,
      issues: [],
    });
  });
}

/**
 * Global security headers middleware instance
 */
export const securityHeadersMiddleware = new SecurityHeadersMiddleware();

/**
 * @fileoverview Security middleware for data protection and secure headers
 * @lastmodified 2025-08-27T15:45:00Z
 *
 * Features: Encryption at rest, secure headers, CSP, secrets management
 * Main APIs: encryptData(), decryptData(), setSecureHeaders(), createCSP()
 * Constraints: Requires ENCRYPTION_KEY environment variable
 * Patterns: Middleware pattern, encryption, security headers, CSP directives
 */

import * as crypto from 'crypto';
import { logger } from '../utils/logger';

export interface SecurityConfig {
  encryptionKey?: string;
  algorithm?: string;
  keyDerivationIterations?: number;
  enableCSP?: boolean;
  cspDirectives?: Record<string, string[]>;
  enableHSTS?: boolean;
  hstsMaxAge?: number;
}

export interface EncryptedData {
  data: string;
  iv: string;
  authTag: string;
  salt: string;
}

/**
 * Security service for data protection and secure headers
 */
export class SecurityService {
  private encryptionKey: Buffer;

  private algorithm: string;

  private keyDerivationIterations: number;

  constructor(config: SecurityConfig = {}) {
    this.algorithm = config.algorithm || 'aes-256-gcm';
    this.keyDerivationIterations = config.keyDerivationIterations || 100000;

    const keyString =
      config.encryptionKey ||
      process.env.ENCRYPTION_KEY ||
      'default-dev-key-not-secure';
    this.encryptionKey = crypto.scryptSync(keyString, 'salt', 32);

    if (keyString === 'default-dev-key-not-secure') {
      logger.warn('Using default encryption key - not secure for production');
    }
  }

  /**
   * Encrypt sensitive data for storage using AEAD (Authenticated Encryption with Associated Data)
   */
  encryptData(plaintext: string): EncryptedData {
    try {
      const iv = crypto.randomBytes(16); // 128-bit IV for AES-GCM
      const salt = crypto.randomBytes(32); // 256-bit salt
      const key = crypto.scryptSync(this.encryptionKey, salt, 32);

      // Use createCipherGCM instead of deprecated createCipher
      const cipher = crypto.createCipherGCM(this.algorithm, key, iv);
      cipher.setAAD(Buffer.from('encrypted-data'));

      let encrypted = cipher.update(plaintext, 'utf8', 'hex');
      encrypted += cipher.final('hex');

      const authTag = cipher.getAuthTag();

      return {
        data: encrypted,
        iv: iv.toString('hex'),
        authTag: authTag.toString('hex'),
        salt: salt.toString('hex'),
      };
    } catch (error) {
      logger.error('Encryption failed', error as Error);
      throw new Error('Failed to encrypt data');
    }
  }

  /**
   * Decrypt stored sensitive data using AEAD (Authenticated Encryption with Associated Data)
   */
  decryptData(encryptedData: EncryptedData): string {
    try {
      const iv = Buffer.from(encryptedData.iv, 'hex');
      const salt = Buffer.from(encryptedData.salt, 'hex');
      const authTag = Buffer.from(encryptedData.authTag, 'hex');
      const key = crypto.scryptSync(this.encryptionKey, salt, 32);

      // Use createDecipherGCM instead of deprecated createDecipher
      const decipher = crypto.createDecipherGCM(this.algorithm, key, iv);
      decipher.setAAD(Buffer.from('encrypted-data'));
      decipher.setAuthTag(authTag);

      let decrypted = decipher.update(encryptedData.data, 'hex', 'utf8');
      decrypted += decipher.final('utf8');

      return decrypted;
    } catch (error) {
      logger.error('Decryption failed', error as Error);
      throw new Error('Failed to decrypt data');
    }
  }

  /**
   * Generate secure random token
   */
  generateSecureToken(length: number = 32): string {
    return crypto.randomBytes(length).toString('hex');
  }

  /**
   * Hash password with salt
   */
  hashPassword(
    password: string,
    salt?: string
  ): { hash: string; salt: string } {
    const saltBuffer = salt ? Buffer.from(salt, 'hex') : crypto.randomBytes(32);
    const hash = crypto.pbkdf2Sync(
      password,
      saltBuffer,
      this.keyDerivationIterations,
      64,
      'sha512'
    );

    return {
      hash: hash.toString('hex'),
      salt: saltBuffer.toString('hex'),
    };
  }

  /**
   * Verify password against hash
   */
  verifyPassword(password: string, storedHash: string, salt: string): boolean {
    const { hash } = this.hashPassword(password, salt);
    return crypto.timingSafeEqual(
      Buffer.from(storedHash, 'hex'),
      Buffer.from(hash, 'hex')
    );
  }

  /**
   * Set secure HTTP headers
   */
  getSecureHeaders(
    config: {
      enableHSTS?: boolean;
      hstsMaxAge?: number;
      enableCSP?: boolean;
      cspDirectives?: Record<string, string[]>;
      enableXFrameOptions?: boolean;
      enableXContentTypeOptions?: boolean;
      enableReferrerPolicy?: boolean;
    } = {}
  ): Record<string, string> {
    const headers: Record<string, string> = {};

    // Strict Transport Security
    if (config.enableHSTS !== false) {
      const maxAge = config.hstsMaxAge || 31536000; // 1 year
      headers['Strict-Transport-Security'] =
        `max-age=${maxAge}; includeSubDomains; preload`;
    }

    // Content Security Policy
    if (config.enableCSP !== false) {
      const csp = this.buildCSP(config.cspDirectives);
      headers['Content-Security-Policy'] = csp;
    }

    // X-Frame-Options
    if (config.enableXFrameOptions !== false) {
      headers['X-Frame-Options'] = 'DENY';
    }

    // X-Content-Type-Options
    if (config.enableXContentTypeOptions !== false) {
      headers['X-Content-Type-Options'] = 'nosniff';
    }

    // X-XSS-Protection
    headers['X-XSS-Protection'] = '1; mode=block';

    // Referrer Policy
    if (config.enableReferrerPolicy !== false) {
      headers['Referrer-Policy'] = 'strict-origin-when-cross-origin';
    }

    // Permissions Policy (formerly Feature Policy)
    headers['Permissions-Policy'] = [
      'geolocation=()',
      'microphone=()',
      'camera=()',
      'fullscreen=(self)',
      'payment=()',
    ].join(', ');

    return headers;
  }

  /**
   * Build Content Security Policy header value with strict security
   */
  private buildCSP(customDirectives?: Record<string, string[]>): string {
    // Generate a nonce for inline scripts (should be passed from request context)
    const nonce = crypto.randomBytes(16).toString('base64');

    const defaultDirectives = {
      'default-src': ["'self'"],
      'script-src': [
        "'self'",
        `'nonce-${nonce}'`,
        "'strict-dynamic'",
        // Remove unsafe-inline and unsafe-eval for security
      ],
      'style-src': [
        "'self'",
        `'nonce-${nonce}'`,
        'https://fonts.googleapis.com',
        // Only allow hashed styles, no unsafe-inline
      ],
      'font-src': ["'self'", 'https://fonts.gstatic.com'],
      'img-src': ["'self'", 'data:', 'blob:', 'https:'],
      'connect-src': ["'self'", 'https://api.promptwizard.com', 'wss://api.promptwizard.com'],
      'frame-src': ["'none'"],
      'object-src': ["'none'"],
      'base-uri': ["'self'"],
      'form-action': ["'self'"],
      'frame-ancestors': ["'none'"],
      'worker-src': ["'self'"],
      'manifest-src': ["'self'"],
      'media-src': ["'self'"],
      'child-src': ["'none'"],
      'upgrade-insecure-requests': [],
      'block-all-mixed-content': [],
      'trusted-types': ['default'],
      'require-trusted-types-for': ["'script'"],
    };

    const directives = { ...defaultDirectives, ...customDirectives };

    return Object.entries(directives)
      .map(([directive, sources]) =>
        sources.length > 0 ? `${directive} ${sources.join(' ')}` : directive
      )
      .join('; ');
  }

  /**
   * Sanitize file upload
   */
  sanitizeFileUpload(
    filename: string,
    content: Buffer,
    allowedTypes: string[]
  ): {
    safe: boolean;
    sanitizedFilename?: string;
    issues: string[];
  } {
    const issues: string[] = [];

    // Check filename
    if (
      filename.includes('..') ||
      filename.includes('/') ||
      filename.includes('\\')
    ) {
      issues.push('Filename contains path traversal characters');
    }

    if (filename.length > 255) {
      issues.push('Filename too long');
    }

    if (!/^[a-zA-Z0-9._-]+$/.test(filename)) {
      issues.push('Filename contains unsafe characters');
    }

    // Check file extension
    const extension = filename.split('.').pop()?.toLowerCase();
    if (!extension || !allowedTypes.includes(extension)) {
      issues.push(`File type .${extension} not allowed`);
    }

    // Check for embedded executable content
    const contentStr = content.toString(
      'utf8',
      0,
      Math.min(content.length, 1024)
    );
    if (/<%|<script|javascript:|data:/i.test(contentStr)) {
      issues.push('File contains potentially executable content');
    }

    // Check file size (example: 10MB limit)
    if (content.length > 10 * 1024 * 1024) {
      issues.push('File too large (max 10MB)');
    }

    // Sanitize filename
    const sanitizedFilename = filename
      .replace(/[^a-zA-Z0-9._-]/g, '_')
      .substring(0, 255);

    return {
      safe: issues.length === 0,
      sanitizedFilename,
      issues,
    };
  }

  /**
   * Rate limiting with sliding window
   */
  createRateLimiter(windowMs: number, maxRequests: number) {
    const requests = new Map<string, number[]>();

    return {
      isAllowed: (
        identifier: string
      ): { allowed: boolean; retryAfter?: number } => {
        const now = Date.now();
        const userRequests = requests.get(identifier) || [];

        // Remove expired requests
        const validRequests = userRequests.filter(
          time => now - time < windowMs
        );

        if (validRequests.length >= maxRequests) {
          const oldestRequest = Math.min(...validRequests);
          const retryAfter = Math.ceil((oldestRequest + windowMs - now) / 1000);
          return { allowed: false, retryAfter };
        }

        validRequests.push(now);
        requests.set(identifier, validRequests);

        return { allowed: true };
      },

      reset: (identifier: string): void => {
        requests.delete(identifier);
      },

      getStats: () => ({
        totalClients: requests.size,
        requests: Array.from(requests.entries()).map(([id, times]) => ({
          identifier: id,
          requestCount: times.length,
          lastRequest: Math.max(...times),
        })),
      }),
    };
  }

  /**
   * Input sanitization for different contexts
   */
  sanitizeInput(
    input: string,
    context: 'html' | 'sql' | 'javascript' | 'shell'
  ): string {
    switch (context) {
      case 'html':
        return input
          .replace(/&/g, '&amp;')
          .replace(/</g, '&lt;')
          .replace(/>/g, '&gt;')
          .replace(/"/g, '&quot;')
          .replace(/'/g, '&#x27;')
          .replace(/\//g, '&#x2F;');

      case 'sql':
        return input.replace(/'/g, "''").replace(/"/g, '""');

      case 'javascript':
        return input
          .replace(/\\/g, '\\\\')
          .replace(/'/g, "\\'")
          .replace(/"/g, '\\"')
          .replace(/\n/g, '\\n')
          .replace(/\r/g, '\\r')
          .replace(/\t/g, '\\t');

      case 'shell':
        return input.replace(/[;&|`$(){}[\]<>]/g, '\\$&');

      default:
        return input;
    }
  }

  /**
   * Generate integrity hash for files
   */
  generateIntegrityHash(content: string | Buffer): string {
    const hash = crypto.createHash('sha384');
    hash.update(content);
    return `sha384-${hash.digest('base64')}`;
  }

  /**
   * Verify file integrity
   */
  verifyIntegrity(content: string | Buffer, expectedHash: string): boolean {
    const actualHash = this.generateIntegrityHash(content);
    return crypto.timingSafeEqual(
      Buffer.from(actualHash),
      Buffer.from(expectedHash)
    );
  }
}

/**
 * Global security service instance
 */
export const securityService = new SecurityService();

/**
 * Middleware to set secure headers
 */
export function setSecureHeaders(config?: {
  enableHSTS?: boolean;
  hstsMaxAge?: number;
  enableCSP?: boolean;
  cspDirectives?: Record<string, string[]>;
}) {
  const headers = securityService.getSecureHeaders(config);

  return (request: { headers?: Record<string, string> }) => ({
    ...request,
    securityHeaders: headers,
  });
}

/**
 * Middleware for file upload security
 */
export function secureFileUpload(
  allowedTypes: string[],
  maxSizeBytes: number = 10 * 1024 * 1024
) {
  return (request: {
    filename?: string;
    fileContent?: Buffer;
    uploadSafe?: boolean;
    uploadIssues?: string[];
  }) => {
    if (!request.filename || !request.fileContent) {
      return request;
    }

    const result = securityService.sanitizeFileUpload(
      request.filename,
      request.fileContent,
      allowedTypes
    );

    return {
      ...request,
      filename: result.sanitizedFilename || request.filename,
      uploadSafe: result.safe,
      uploadIssues: result.issues,
    };
  };
}

/**
 * Secrets management utilities
 */
export class SecretsManager {
  private secrets = new Map<string, EncryptedData>();

  /**
   * Store encrypted secret
   */
  setSecret(key: string, value: string): void {
    const encrypted = securityService.encryptData(value);
    this.secrets.set(key, encrypted);
    logger.info(`Secret stored: ${key}`);
  }

  /**
   * Retrieve decrypted secret
   */
  getSecret(key: string): string | null {
    const encrypted = this.secrets.get(key);
    if (!encrypted) {
      return null;
    }

    try {
      return securityService.decryptData(encrypted);
    } catch (error) {
      logger.error(`Failed to decrypt secret: ${key}`, error as Error);
      return null;
    }
  }

  /**
   * Remove secret
   */
  deleteSecret(key: string): boolean {
    const existed = this.secrets.has(key);
    this.secrets.delete(key);
    if (existed) {
      logger.info(`Secret deleted: ${key}`);
    }
    return existed;
  }

  /**
   * List available secret keys (not values)
   */
  listSecrets(): string[] {
    return Array.from(this.secrets.keys());
  }

  /**
   * Rotate secret with new value
   */
  rotateSecret(key: string, newValue: string): boolean {
    if (!this.secrets.has(key)) {
      return false;
    }

    this.setSecret(key, newValue);
    logger.info(`Secret rotated: ${key}`);
    return true;
  }
}

/**
 * Global secrets manager instance
 */
export const secretsManager = new SecretsManager();

/**
 * @fileoverview Validation middleware for command handlers and API endpoints
 * @lastmodified 2025-08-23T05:00:00Z
 *
 * Features: Input validation, sanitization, error handling
 * Main APIs: validateInput(), createValidator(), withValidation()
 * Constraints: Uses Zod for schema validation
 * Patterns: Middleware pattern, functional composition
 */

import { z } from 'zod';
import { ValidationError } from '../errors';
import {
  SecureStringSchema,
  SecurePathSchema,
  SecureCommandArgSchema,
  SecureUrlSchema,
} from '../validation/schemas';
import { logger } from '../utils/logger';

// Type definition for command handler
type CommandHandler<TInput, TOutput> = (input: TInput) => Promise<TOutput>;

/**
 * Validation middleware result
 */
export interface ValidationResult<T> {
  success: boolean;
  data?: T;
  errors?: ValidationError[];
}

/**
 * Create a validation middleware for a specific schema
 */
export function createValidator<T>(
  schema: z.ZodSchema<T>
): (input: unknown) => ValidationResult<T> {
  return (input: unknown): ValidationResult<T> => {
    try {
      const data = schema.parse(input);
      return { success: true, data };
    } catch (error) {
      if (error instanceof z.ZodError) {
        const errors = error.issues.map(err => {
          const path = err.path.join('.');
          return new ValidationError(err.message, path, input, {
            type: err.code,
          });
        });
        return { success: false, errors };
      }

      return {
        success: false,
        errors: [new ValidationError('Validation failed', undefined, input)],
      };
    }
  };
}

/**
 * Validate input against a schema
 */
export async function validateInput<T>(
  schema: z.ZodSchema<T>,
  input: unknown
): Promise<T> {
  const validator = createValidator(schema);
  const result = validator(input);

  if (!result.success) {
    const errorMessages =
      result.errors?.map(e => e.message).join(', ') || 'Validation failed';
    throw new ValidationError(errorMessages);
  }

  return result.data!; // We know data exists if success is true
}

/**
 * Wrap a command handler with validation
 */
export function withValidation<TInput, TOutput>(
  schema: z.ZodSchema<TInput>,
  handler: CommandHandler<TInput, TOutput>
): CommandHandler<unknown, TOutput> {
  return async (input: unknown): Promise<TOutput> => {
    const validatedInput = await validateInput(schema, input);
    return handler(validatedInput);
  };
}

/**
 * Sanitize data by removing potentially dangerous content
 */
function sanitizeData<T>(data: T): T {
  if (typeof data === 'string') {
    // Remove script tags and other dangerous patterns
    return data
      .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
      .replace(/javascript:/gi, '')
      .replace(/on[a-z]+\s*=\s*["'][^"']*["']/gi, '') // Remove event handlers with quotes
      .replace(/on[a-z]+\s*=\s*[^\s>]*/gi, '') as T; // Remove event handlers without quotes
  }

  if (Array.isArray(data)) {
    return data.map(item => sanitizeData(item)) as T;
  }

  if (data && typeof data === 'object') {
    const sanitized: Record<string, unknown> = {};
    for (const [key, value] of Object.entries(data)) {
      sanitized[key] = sanitizeData(value);
    }
    return sanitized as T;
  }

  return data;
}

/**
 * Create a middleware chain with validation
 */
export function createValidationMiddleware<T>(
  schema: z.ZodSchema<T>,
  options?: {
    transform?: (data: T) => T | Promise<T>;
    sanitize?: boolean;
    strict?: boolean;
  }
): (input: unknown) => Promise<T> {
  return async (input: unknown): Promise<T> => {
    // Note: strict mode not available in base ZodSchema type
    // Parse and validate
    let data = await validateInput(schema, input);

    // Apply transformation if provided
    if (options?.transform) {
      data = await options.transform(data);
    }

    // Additional sanitization if needed
    if (options?.sanitize) {
      data = sanitizeData(data) as any;
    }

    return data;
  };
}

/**
 * Batch validation for multiple inputs
 */
export async function validateBatch<T>(
  schema: z.ZodSchema<T>,
  inputs: unknown[]
): Promise<ValidationResult<T>[]> {
  const validator = createValidator(schema);
  return inputs.map(input => validator(input));
}

/**
 * Conditional validation based on input
 */
export function conditionalValidator<T>(
  condition: (input: unknown) => boolean,
  trueSchema: z.ZodSchema<T>,
  falseSchema: z.ZodSchema<T>
): (input: unknown) => ValidationResult<T> {
  return (input: unknown): ValidationResult<T> => {
    const schema = condition(input) ? trueSchema : falseSchema;
    return createValidator(schema)(input);
  };
}

/**
 * Compose multiple validators
 */
export function composeValidators<T>(
  ...validators: Array<(input: unknown) => ValidationResult<any>>
): (input: unknown) => ValidationResult<T> {
  return (input: unknown): ValidationResult<T> => {
    let currentData = input;

    for (const validator of validators) {
      const result = validator(currentData);
      if (!result.success) {
        return result;
      }
      currentData = result.data;
    }

    return { success: true, data: currentData as T };
  };
}

/**
 * Create a type-safe validator with custom error messages
 */
export function createTypedValidator<T>(
  schema: z.ZodSchema<T>,
  errorMessages?: Record<string, string>
): {
  validate: (input: unknown) => ValidationResult<T>;
  validateAsync: (input: unknown) => Promise<ValidationResult<T>>;
  validateOrThrow: (input: unknown) => T;
  isValid: (input: unknown) => input is T;
} {
  const validator = createValidator(schema);

  return {
    validate: (input: unknown) => {
      const result = validator(input);
      if (!result.success && errorMessages && result.errors) {
        result.errors = result.errors.map(err => {
          const customMessage = errorMessages[err.field || ''] || err.message;
          return new ValidationError(
            customMessage,
            err.field,
            err.value,
            err.constraints
          );
        });
      }
      return result;
    },

    validateAsync: async (input: unknown) => validator(input),

    validateOrThrow: (input: unknown): T => {
      const result = validator(input);
      if (!result.success) {
        const errors =
          result.errors?.map(e => e.message).join(', ') || 'Validation failed';
        throw new ValidationError(errors);
      }
      return result.data!; // We know data exists if success is true
    },

    isValid: (input: unknown): input is T => {
      const result = validator(input);
      return result.success;
    },
  };
}

/**
 * Environment variable validation
 */
export function validateEnv<T extends Record<string, unknown>>(
  schema: z.ZodSchema<T>
): T {
  const result = createValidator(schema)(process.env);

  if (!result.success) {
    const missing = result.errors
      ?.map(e => e.field)
      .filter(Boolean)
      .join(', ');
    throw new ValidationError(
      `Missing required environment variables: ${missing}`,
      undefined,
      undefined,
      { required: missing?.split(', ') }
    );
  }

  return result.data!; // We know data exists if success is true
}

/**
 * File path validation
 */
export function validateFilePath(
  path: string,
  options?: {
    mustExist?: boolean;
    allowTraversal?: boolean;
    extensions?: string[];
  }
): boolean {
  // Check for path traversal
  if (!options?.allowTraversal && path.includes('..')) {
    throw new ValidationError('Path traversal detected', 'path', path);
  }

  // Check file extension if specified
  if (options?.extensions) {
    const hasValidExtension = options.extensions.some(ext =>
      path.endsWith(ext)
    );
    if (!hasValidExtension) {
      throw new ValidationError(
        `Invalid file extension. Allowed: ${options.extensions.join(', ')}`,
        'path',
        path
      );
    }
  }

  return true;
}

