/**
 * Deep security scan for any input
 */
export function performSecurityScan(input: unknown): {
  safe: boolean;
  threats: string[];
  sanitized?: unknown;
} {
  const threats: string[] = [];

  function scanValue(value: unknown, path: string = ''): void {
    if (typeof value === 'string') {
      // Check for XSS patterns
      if (/<script|javascript:|on\w+\s*=|data:/i.test(value)) {
        threats.push(`XSS pattern detected at ${path}`);
      }

      // Check for SQL injection patterns
      if (
        /(union|select|insert|delete|update|drop|create|alter|exec|execute)\s/i.test(
          value
        )
      ) {
        threats.push(`SQL injection pattern detected at ${path}`);
      }

      // Check for path traversal
      if (/\.\.\/|\.\.\\/g.test(value)) {
        threats.push(`Path traversal detected at ${path}`);
      }

      // Check for command injection
      if (/[;&|`$(){}\[\]<>]/.test(value)) {
        threats.push(`Command injection pattern detected at ${path}`);
      }

      // Check for null bytes
      if (value.includes('\x00')) {
        threats.push(`Null byte detected at ${path}`);
      }
    } else if (Array.isArray(value)) {
      value.forEach((item, index) => scanValue(item, `${path}[${index}]`));
    } else if (value && typeof value === 'object') {
      Object.entries(value).forEach(([key, val]) =>
        scanValue(val, path ? `${path}.${key}` : key)
      );
    }
  }

  scanValue(input);

  return {
    safe: threats.length === 0,
    threats,
    sanitized: threats.length > 0 ? sanitizeData(input) : input,
  };
}

/**
 * Rate limiting validation
 */
export function createRateLimitValidator(
  maxRequests: number,
  windowMs: number
) {
  const requests = new Map<string, number[]>();

  return (clientId: string): { allowed: boolean; retryAfter?: number } => {
    const now = Date.now();
    const clientRequests = requests.get(clientId) || [];

    // Clean old requests
    const validRequests = clientRequests.filter(time => now - time < windowMs);

    if (validRequests.length >= maxRequests) {
      const oldestRequest = Math.min(...validRequests);
      const retryAfter = Math.ceil((oldestRequest + windowMs - now) / 1000);
      return { allowed: false, retryAfter };
    }

    validRequests.push(now);
    requests.set(clientId, validRequests);

    return { allowed: true };
  };
}

/**
 * @fileoverview Chain-of-thought optimizer for enhanced reasoning in prompts
 * @lastmodified 2025-08-26T12:45:00Z
 *
 * Features: Reasoning chain generation, step validation, effectiveness measurement
 * Main APIs: generateReasoningChain(), optimizeSteps(), validateChain()
 * Constraints: Model-specific reasoning patterns, logical consistency requirements
 * Patterns: Builder pattern for chain construction, validation pipeline
 */

import { logger } from '../utils/logger';
import { Template } from '../types';

export interface ReasoningStep {
  id: string;
  content: string;
  type: 'premise' | 'inference' | 'conclusion' | 'example' | 'validation';
  dependencies: string[];
  confidence: number;
  metadata?: {
    source?: string;
    evidence?: string[];
    alternatives?: string[];
  };
}

export interface ReasoningChain {
  steps: ReasoningStep[];
  goal: string;
  context: string;
  effectiveness: number;
  validated: boolean;
}

export interface ChainOptimizationConfig {
  maxSteps?: number;
  minConfidence?: number;
  includeExamples?: boolean;
  verbosity?: 'minimal' | 'moderate' | 'detailed';
  targetModel?: string;
  optimizationGoal?: 'clarity' | 'accuracy' | 'efficiency' | 'balanced';
}

export interface ValidationIssue {
  stepId: string;
  type: 'logical_error' | 'missing_connection' | 'redundancy' | 'ambiguity';
  severity: 'low' | 'medium' | 'high';
  description: string;
}

export interface ChainValidationResult {
  valid: boolean;
  issues: ValidationIssue[];
  suggestions: string[];
  overallScore: number;
}

export class ChainOfThoughtOptimizer {
  private readonly reasoningPatterns = {
    deductive: ['premise', 'premise', 'inference', 'conclusion'],
    inductive: ['example', 'example', 'inference', 'conclusion'],
    abductive: ['observation', 'hypothesis', 'validation', 'conclusion'],
    analogical: ['source', 'mapping', 'inference', 'conclusion'],
  };

  private readonly modelSpecificPatterns = {
    'gpt-4': {
      preferredStyle: 'step-by-step',
      maxSteps: 10,
      requiresExplicit: true,
    },
    'claude-3-opus': {
      preferredStyle: 'structured',
      maxSteps: 15,
      requiresExplicit: false,
    },
    'gemini-pro': {
      preferredStyle: 'hierarchical',
      maxSteps: 12,
      requiresExplicit: true,
    },
  };

  /**
   * Generate a reasoning chain for a prompt
   */
  async generateReasoningChain(
    prompt: string,
    template: Template,
    config: ChainOptimizationConfig = {}
  ): Promise<ReasoningChain> {
    logger.info(`Generating reasoning chain for template: ${template.name}`);

    const goal = this.extractGoal(prompt, template);
    const context = this.extractContext(prompt, template);

    const steps = await this.buildReasoningSteps(prompt, goal, context, config);

    const optimizedSteps = await this.optimizeSteps(steps, config);
    const effectiveness = this.measureEffectiveness(optimizedSteps, goal);
    const validated = await this.validateChain({
      steps: optimizedSteps,
      goal,
      context,
      effectiveness,
      validated: false,
    });

    const chain: ReasoningChain = {
      steps: optimizedSteps,
      goal,
      context,
      effectiveness,
      validated: validated.valid,
    };

    logger.info(
      `Generated reasoning chain with ${chain.steps.length} steps, ` +
        `effectiveness: ${effectiveness.toFixed(2)}`
    );

    return chain;
  }

  /**
   * Optimize reasoning steps for clarity and efficiency
   */
  async optimizeSteps(
    steps: ReasoningStep[],
    config: ChainOptimizationConfig = {}
  ): Promise<ReasoningStep[]> {
    logger.info(`Optimizing ${steps.length} reasoning steps`);

    let optimized = [...steps];

    // Remove redundant steps
    optimized = this.removeRedundantSteps(optimized);

    // Consolidate similar steps
    optimized = this.consolidateSimilarSteps(optimized);

    // Reorder for logical flow
    optimized = this.reorderSteps(optimized);

    // Apply model-specific optimizations
    if (config.targetModel) {
      optimized = this.applyModelSpecificOptimizations(
        optimized,
        config.targetModel
      );
    }

    // Ensure confidence thresholds
    if (config.minConfidence !== undefined) {
      optimized = optimized.filter(
        step => step.confidence >= config.minConfidence!
      );
    }

    // Apply verbosity settings
    optimized = this.applyVerbositySettings(
      optimized,
      config.verbosity || 'moderate'
    );

    // Limit to max steps if specified
    if (config.maxSteps && optimized.length > config.maxSteps) {
      optimized = this.selectMostImportantSteps(optimized, config.maxSteps);
    }

    logger.info(
      `Optimization complete: ${steps.length} ‚Üí ${optimized.length} steps`
    );

    return optimized;
  }

  /**
   * Validate a reasoning chain for logical consistency
   */
  async validateChain(chain: ReasoningChain): Promise<ChainValidationResult> {
    logger.info('Validating reasoning chain');

    const issues: ValidationIssue[] = [];
    const suggestions: string[] = [];

    // Check logical consistency
    const logicalIssues = this.checkLogicalConsistency(chain.steps);
    issues.push(...logicalIssues);

    // Check for missing connections
    const connectionIssues = this.checkConnections(chain.steps);
    issues.push(...connectionIssues);

    // Check for redundancies
    const redundancyIssues = this.checkRedundancies(chain.steps);
    issues.push(...redundancyIssues);

    // Check for ambiguities
    const ambiguityIssues = this.checkAmbiguities(chain.steps);
    issues.push(...ambiguityIssues);

    // Generate improvement suggestions
    if (issues.length > 0) {
      suggestions.push(...this.generateImprovementSuggestions(issues, chain));
    }

    // Calculate overall score
    const overallScore = this.calculateValidationScore(issues, chain);

    const result: ChainValidationResult = {
      valid: issues.filter(i => i.severity === 'high').length === 0,
      issues,
      suggestions,
      overallScore,
    };

    logger.info(
      `Validation complete: ${result.valid ? 'VALID' : 'INVALID'}, ` +
        `score: ${overallScore.toFixed(2)}, ${issues.length} issues found`
    );

    return result;
  }

  /**
   * Generate an optimized chain for specific reasoning patterns
   */
  generatePatternBasedChain(
    pattern: keyof typeof this.reasoningPatterns,
    content: string,
    _config: ChainOptimizationConfig = {}
  ): ReasoningChain {
    const patternSteps = this.reasoningPatterns[pattern];
    const steps: ReasoningStep[] = [];

    patternSteps.forEach((stepType, index) => {
      steps.push({
        id: `step-${index}`,
        content: this.generateStepContent(stepType, content, index),
        type: stepType as ReasoningStep['type'],
        dependencies: index > 0 ? [`step-${index - 1}`] : [],
        confidence: 0.8 + index * 0.02, // Confidence increases with progression
        metadata: {
          source: pattern,
        },
      });
    });

    return {
      steps,
      goal: `Apply ${pattern} reasoning to: ${content.substring(0, 50)}...`,
      context: content,
      effectiveness: 0.75,
      validated: false,
    };
  }

  // Private helper methods

  private extractGoal(prompt: string, template: Template): string {
    // Extract explicit goal statements
    const goalPatterns = [
      /goal:\s*([^.]+)/i,
      /objective:\s*([^.]+)/i,
      /purpose:\s*([^.]+)/i,
      /aim:\s*([^.]+)/i,
    ];

    for (const pattern of goalPatterns) {
      const match = prompt.match(pattern);
      if (match) return match[1].trim();
    }

    // Fall back to template description
    return template.description || 'Process the given input';
  }

  private extractContext(prompt: string, template: Template): string {
    // Extract context from prompt and template
    const contextMarkers = ['context:', 'background:', 'given:', 'assuming:'];
    let context = '';

    contextMarkers.forEach(marker => {
      const index = prompt.toLowerCase().indexOf(marker);
      if (index !== -1) {
        const endIndex = prompt.indexOf('\n', index);
        context += `${prompt.substring(
          index + marker.length,
          endIndex > 0 ? endIndex : undefined
        )} `;
      }
    });

    if (!context && template.content) {
      context = template.content.substring(0, 200);
    }

    return context.trim() || 'General context';
  }

  private async buildReasoningSteps(
    prompt: string,
    goal: string,
    context: string,
    config: ChainOptimizationConfig
  ): Promise<ReasoningStep[]> {
    const steps: ReasoningStep[] = [];

    // Add initial premise
    steps.push({
      id: 'step-0',
      content: `Given: ${context}`,
      type: 'premise',
      dependencies: [],
      confidence: 1.0,
    });

    // Add goal clarification
    steps.push({
      id: 'step-1',
      content: `Goal: ${goal}`,
      type: 'premise',
      dependencies: ['step-0'],
      confidence: 1.0,
    });

    // Build logical progression
    const numIntermediateSteps = Math.min(
      config.maxSteps ? config.maxSteps - 3 : 5,
      this.calculateRequiredSteps(prompt, goal)
    );

    for (let i = 0; i < numIntermediateSteps; i++) {
      steps.push({
        id: `step-${i + 2}`,
        content: this.generateIntermediateStep(prompt, i, numIntermediateSteps),
        type: 'inference',
        dependencies: [`step-${i + 1}`],
        confidence: 0.8 - i * 0.05,
      });
    }

    // Add conclusion
    steps.push({
      id: `step-${steps.length}`,
      content: `Therefore: ${this.generateConclusion(goal, steps)}`,
      type: 'conclusion',
      dependencies: [`step-${steps.length - 1}`],
      confidence: 0.85,
    });

    return steps;
  }

  private removeRedundantSteps(steps: ReasoningStep[]): ReasoningStep[] {
    const uniqueSteps: ReasoningStep[] = [];
    const seenContent = new Set<string>();

    steps.forEach(step => {
      const normalized = this.normalizeStepContent(step.content);
      if (!seenContent.has(normalized)) {
        seenContent.add(normalized);
        uniqueSteps.push(step);
      }
    });

    return uniqueSteps;
  }

  private consolidateSimilarSteps(steps: ReasoningStep[]): ReasoningStep[] {
    const consolidated: ReasoningStep[] = [];
    const processed = new Set<number>();

    for (let i = 0; i < steps.length; i++) {
      if (processed.has(i)) continue;

      const similar: ReasoningStep[] = [steps[i]];
      for (let j = i + 1; j < steps.length; j++) {
        if (this.areSimilarSteps(steps[i], steps[j])) {
          similar.push(steps[j]);
          processed.add(j);
        }
      }

      if (similar.length > 1) {
        consolidated.push(this.mergeSteps(similar));
      } else {
        consolidated.push(steps[i]);
      }
    }

    return consolidated;
  }

  private reorderSteps(steps: ReasoningStep[]): ReasoningStep[] {
    // Topological sort based on dependencies
    const sorted: ReasoningStep[] = [];
    const visited = new Set<string>();
    const visiting = new Set<string>();

    const visit = (step: ReasoningStep) => {
      if (visited.has(step.id)) return;
      if (visiting.has(step.id)) {
        logger.warn(`Circular dependency detected at step: ${step.id}`);
        return;
      }

      visiting.add(step.id);

      step.dependencies.forEach(depId => {
        const depStep = steps.find(s => s.id === depId);
        if (depStep) visit(depStep);
      });

      visiting.delete(step.id);
      visited.add(step.id);
      sorted.push(step);
    };

    steps.forEach(step => visit(step));

    return sorted;
  }

  private applyModelSpecificOptimizations(
    steps: ReasoningStep[],
    model: string
  ): ReasoningStep[] {
    const pattern =
      this.modelSpecificPatterns[
        model as keyof typeof this.modelSpecificPatterns
      ];
    if (!pattern) return steps;

    let optimized = [...steps];

    // Apply max steps limit
    if (optimized.length > pattern.maxSteps) {
      optimized = this.selectMostImportantSteps(optimized, pattern.maxSteps);
    }

    // Add explicit markers if required
    if (pattern.requiresExplicit) {
      optimized = optimized.map((step, index) => ({
        ...step,
        content: `Step ${index + 1}: ${step.content}`,
      }));
    }

    // Apply preferred style
    if (pattern.preferredStyle === 'hierarchical') {
      optimized = this.organizeHierarchically(optimized);
    }

    return optimized;
  }

  private applyVerbositySettings(
    steps: ReasoningStep[],
    verbosity: ChainOptimizationConfig['verbosity']
  ): ReasoningStep[] {
    switch (verbosity) {
      case 'minimal':
        return steps.map(step => ({
          ...step,
          content: this.summarizeContent(step.content, 20),
        }));
      case 'detailed':
        return steps.map(step => ({
          ...step,
          content: this.expandContent(step.content),
          metadata: {
            ...step.metadata,
            evidence: [`Supporting detail for: ${step.content}`],
          },
        }));
      default:
        return steps;
    }
  }

  private selectMostImportantSteps(
    steps: ReasoningStep[],
    maxSteps: number
  ): ReasoningStep[] {
    // Keep premises and conclusions, select best intermediate steps
    const premises = steps.filter(s => s.type === 'premise');
    const conclusions = steps.filter(s => s.type === 'conclusion');
    const intermediate = steps.filter(
      s => s.type !== 'premise' && s.type !== 'conclusion'
    );

    // Sort intermediate steps by confidence
    intermediate.sort((a, b) => b.confidence - a.confidence);

    const keepIntermediate = maxSteps - premises.length - conclusions.length;

    return [
      ...premises,
      ...intermediate.slice(0, Math.max(0, keepIntermediate)),
      ...conclusions,
    ];
  }

  private checkLogicalConsistency(steps: ReasoningStep[]): ValidationIssue[] {
    const issues: ValidationIssue[] = [];

    steps.forEach((step, index) => {
      // Check if dependencies exist
      step.dependencies.forEach(depId => {
        if (!steps.find(s => s.id === depId)) {
          issues.push({
            stepId: step.id,
            type: 'missing_connection',
            severity: 'high',
            description: `Missing dependency: ${depId}`,
          });
        }
      });

      // Check for logical gaps
      if (
        index > 0 &&
        step.dependencies.length === 0 &&
        step.type !== 'premise'
      ) {
        issues.push({
          stepId: step.id,
          type: 'missing_connection',
          severity: 'medium',
          description: 'Step has no dependencies but is not a premise',
        });
      }
    });

    return issues;
  }

  private checkConnections(steps: ReasoningStep[]): ValidationIssue[] {
    const issues: ValidationIssue[] = [];
    const referenced = new Set<string>();

    steps.forEach(step => {
      step.dependencies.forEach(dep => referenced.add(dep));
    });

    // Check for orphaned steps
    steps.forEach(step => {
      if (
        !referenced.has(step.id) &&
        step.type !== 'conclusion' &&
        steps[steps.length - 1].id !== step.id
      ) {
        issues.push({
          stepId: step.id,
          type: 'missing_connection',
          severity: 'low',
          description: 'Step is not referenced by any other step',
        });
      }
    });

    return issues;
  }

  private checkRedundancies(steps: ReasoningStep[]): ValidationIssue[] {
    const issues: ValidationIssue[] = [];

    for (let i = 0; i < steps.length - 1; i++) {
      for (let j = i + 1; j < steps.length; j++) {
        if (this.areSimilarSteps(steps[i], steps[j])) {
          issues.push({
            stepId: steps[j].id,
            type: 'redundancy',
            severity: 'low',
            description: `Similar to step ${steps[i].id}`,
          });
        }
      }
    }

    return issues;
  }

  private checkAmbiguities(steps: ReasoningStep[]): ValidationIssue[] {
    const issues: ValidationIssue[] = [];
    const ambiguousTerms = ['it', 'this', 'that', 'they', 'these', 'those'];

    steps.forEach(step => {
      const words = step.content.toLowerCase().split(/\s+/);
      const ambiguousCount = words.filter(w =>
        ambiguousTerms.includes(w)
      ).length;

      if (ambiguousCount > 2) {
        issues.push({
          stepId: step.id,
          type: 'ambiguity',
          severity: 'low',
          description: 'Contains ambiguous pronouns without clear antecedents',
        });
      }
    });

    return issues;
  }

  private generateImprovementSuggestions(
    issues: ValidationIssue[],
    chain: ReasoningChain
  ): string[] {
    const suggestions: string[] = [];

    const issueTypes = new Set(issues.map(i => i.type));

    if (issueTypes.has('missing_connection')) {
      suggestions.push('Add explicit connections between reasoning steps');
    }

    if (issueTypes.has('redundancy')) {
      suggestions.push('Consolidate or remove redundant reasoning steps');
    }

    if (issueTypes.has('ambiguity')) {
      suggestions.push('Replace ambiguous pronouns with specific references');
    }

    if (issueTypes.has('logical_error')) {
      suggestions.push('Review logical progression and fix inconsistencies');
    }

    if (chain.steps.length > 10) {
      suggestions.push('Consider simplifying the reasoning chain for clarity');
    }

    return suggestions;
  }

  private calculateValidationScore(
    issues: ValidationIssue[],
    chain: ReasoningChain
  ): number {
    let score = 1.0;

    issues.forEach(issue => {
      switch (issue.severity) {
        case 'high':
          score -= 0.3;
          break;
        case 'medium':
          score -= 0.15;
          break;
        case 'low':
          score -= 0.05;
          break;
        default:
          // Unknown severity level, treat as low impact
          score -= 0.05;
          break;
      }
    });

    // Bonus for well-structured chains
    if (chain.steps.length >= 3 && chain.steps.length <= 10) {
      score += 0.1;
    }

    // Penalty for very long chains
    if (chain.steps.length > 15) {
      score -= 0.2;
    }

    return Math.max(0, Math.min(1, score));
  }

  private measureEffectiveness(steps: ReasoningStep[], goal: string): number {
    let effectiveness = 0.5; // Base score

    // Check if conclusion addresses goal
    const conclusion = steps.find(s => s.type === 'conclusion');
    if (conclusion && this.addressesGoal(conclusion.content, goal)) {
      effectiveness += 0.3;
    }

    // Check logical flow
    const hasLogicalFlow = this.checkLogicalFlow(steps);
    if (hasLogicalFlow) {
      effectiveness += 0.1;
    }

    // Check confidence levels
    const avgConfidence =
      steps.reduce((sum, s) => sum + s.confidence, 0) / steps.length;
    effectiveness += avgConfidence * 0.1;

    return Math.min(1.0, effectiveness);
  }

  // Utility methods

  private normalizeStepContent(content: string): string {
    return content
      .toLowerCase()
      .replace(/[^a-z0-9\s]/g, '')
      .replace(/\s+/g, ' ')
      .trim();
  }

  private areSimilarSteps(step1: ReasoningStep, step2: ReasoningStep): boolean {
    const normalized1 = this.normalizeStepContent(step1.content);
    const normalized2 = this.normalizeStepContent(step2.content);

    // Calculate similarity
    const words1 = new Set(normalized1.split(' '));
    const words2 = new Set(normalized2.split(' '));

    const intersection = new Set([...words1].filter(x => words2.has(x)));
    const union = new Set([...words1, ...words2]);

    const similarity = intersection.size / union.size;

    return similarity > 0.7;
  }

  private mergeSteps(steps: ReasoningStep[]): ReasoningStep {
    // Merge similar steps into one comprehensive step
    const allDependencies = new Set<string>();
    steps.forEach(s => s.dependencies.forEach(d => allDependencies.add(d)));

    const avgConfidence =
      steps.reduce((sum, s) => sum + s.confidence, 0) / steps.length;

    return {
      id: steps[0].id,
      content: steps[0].content, // Use first step's content as primary
      type: steps[0].type,
      dependencies: Array.from(allDependencies),
      confidence: avgConfidence,
      metadata: {
        alternatives: steps.slice(1).map(s => s.content),
      },
    };
  }

  private generateStepContent(
    stepType: string,
    content: string,
    index: number
  ): string {
    const templates = {
      premise: `Given information #${index + 1}: ${content.substring(0, 100)}`,
      inference: `Based on the above, we can infer: ${content.substring(0, 100)}`,
      conclusion: `Therefore, we conclude: ${content.substring(0, 100)}`,
      example: `For example: ${content.substring(0, 100)}`,
      validation: `This is validated by: ${content.substring(0, 100)}`,
      observation: `We observe that: ${content.substring(0, 100)}`,
      hypothesis: `We hypothesize that: ${content.substring(0, 100)}`,
      source: `From the source: ${content.substring(0, 100)}`,
      mapping: `Mapping this concept: ${content.substring(0, 100)}`,
    };

    return templates[stepType as keyof typeof templates] || content;
  }

  private calculateRequiredSteps(prompt: string, goal: string): number {
    // Estimate complexity based on prompt length and goal
    const promptComplexity = Math.min(10, Math.floor(prompt.length / 100));
    const goalComplexity = goal.split(' ').length > 10 ? 2 : 1;

    return promptComplexity + goalComplexity;
  }

  private generateIntermediateStep(
    _prompt: string,
    index: number,
    total: number
  ): string {
    const progress = (index + 1) / total;

    if (progress < 0.3) {
      return `Analyzing the initial conditions and constraints`;
    }
    if (progress < 0.6) {
      return `Applying logical reasoning to derive intermediate results`;
    }
    return `Synthesizing findings to approach the conclusion`;
  }

  private generateConclusion(goal: string, steps: ReasoningStep[]): string {
    const keyPoints = steps
      .filter(s => s.confidence > 0.7)
      .map(s => this.extractKeyPoint(s.content))
      .slice(0, 3)
      .join(', ');

    return `${goal} is achieved through ${keyPoints}`;
  }

  private extractKeyPoint(content: string): string {
    // Extract the most important phrase from content
    const words = content.split(' ');
    if (words.length <= 5) return content;

    return words.slice(0, 5).join(' ');
  }

  private summarizeContent(content: string, maxWords: number): string {
    const words = content.split(' ');
    if (words.length <= maxWords) return content;

    return `${words.slice(0, maxWords).join(' ')}...`;
  }

  private expandContent(content: string): string {
    return `${content}. This step is critical for maintaining logical consistency and ensuring comprehensive reasoning.`;
  }

  private organizeHierarchically(steps: ReasoningStep[]): ReasoningStep[] {
    // Group steps by type and organize hierarchically
    const grouped = {
      premises: steps.filter(s => s.type === 'premise'),
      inferences: steps.filter(s => s.type === 'inference'),
      validations: steps.filter(s => s.type === 'validation'),
      conclusions: steps.filter(s => s.type === 'conclusion'),
    };

    return [
      ...grouped.premises,
      ...grouped.inferences,
      ...grouped.validations,
      ...grouped.conclusions,
    ];
  }

  private addressesGoal(conclusionContent: string, goal: string): boolean {
    const goalKeywords = goal
      .toLowerCase()
      .split(' ')
      .filter(w => w.length > 3);
    const conclusionWords = conclusionContent.toLowerCase().split(' ');

    const matches = goalKeywords.filter(keyword =>
      conclusionWords.includes(keyword)
    );

    return matches.length > goalKeywords.length * 0.5;
  }

  private checkLogicalFlow(steps: ReasoningStep[]): boolean {
    // Check if steps form a logical progression
    for (let i = 1; i < steps.length; i++) {
      const currentStep = steps[i];
      const hasDependency = currentStep.dependencies.some(depId =>
        steps.slice(0, i).some(s => s.id === depId)
      );

      if (!hasDependency && currentStep.type !== 'premise') {
        return false;
      }
    }

    return true;
  }
}

/**
 * @fileoverview Synthetic example generator for improving template robustness
 * @lastmodified 2025-08-26T12:15:00Z
 *
 * Features: Diverse example generation, edge case coverage, validation
 * Main APIs: generateExamples(), generateEdgeCases(), validateExamples()
 * Constraints: Requires template context, language models for generation
 * Patterns: Factory pattern, template method pattern, validation pipeline
 */

import { logger } from '../utils/logger';
import { Template } from '../types';

export interface ExampleGenerationConfig {
  count: number;
  includeEdgeCases: boolean;
  diversityLevel: 'low' | 'medium' | 'high';
  targetDomain?: string;
  constraints?: {
    maxLength?: number;
    minLength?: number;
    requiredElements?: string[];
    forbiddenElements?: string[];
  };
}

export interface GeneratedExample {
  input: string;
  expectedOutput?: string;
  category: 'normal' | 'edge_case' | 'adversarial' | 'boundary';
  confidence: number;
  metadata: {
    generationMethod: string;
    diversity: number;
    complexity: number;
    coverage: string[];
  };
}

export interface ValidationResult {
  valid: boolean;
  errors: string[];
  warnings: string[];
  score: number;
}

export class ExampleGenerator {
  private readonly diversityStrategies = {
    low: 0.3,
    medium: 0.6,
    high: 0.9,
  };

  private readonly edgeCasePatterns = [
    { name: 'empty_input', pattern: () => '' },
    { name: 'minimal_input', pattern: () => this.generateMinimalInput() },
    { name: 'maximum_length', pattern: () => this.generateMaxLengthInput() },
    { name: 'special_characters', pattern: () => this.generateSpecialChars() },
    { name: 'unicode_characters', pattern: () => this.generateUnicode() },
    { name: 'repeated_patterns', pattern: () => this.generateRepeated() },
    { name: 'nested_structures', pattern: () => this.generateNested() },
    { name: 'mixed_formats', pattern: () => this.generateMixedFormat() },
  ];

  /**
   * Generate synthetic examples for a template
   */
  async generateExamples(
    template: Template,
    config: ExampleGenerationConfig
  ): Promise<GeneratedExample[]> {
    logger.info(
      `Generating ${config.count} examples for template: ${template.name}`
    );

    const examples: GeneratedExample[] = [];
    const diversityThreshold = this.diversityStrategies[config.diversityLevel];

    const normalCount = Math.floor(config.count * (1 - diversityThreshold));
    const edgeCaseCount = config.includeEdgeCases
      ? Math.floor(config.count * diversityThreshold * 0.5)
      : 0;
    const adversarialCount = Math.floor(
      config.count * diversityThreshold * 0.3
    );
    const boundaryCount =
      config.count - normalCount - edgeCaseCount - adversarialCount;

    examples.push(
      ...(await this.generateNormalExamples(template, normalCount, config))
    );

    if (config.includeEdgeCases) {
      examples.push(
        ...(await this.generateEdgeCases(template, edgeCaseCount, config))
      );
    }

    examples.push(
      ...(await this.generateAdversarialExamples(
        template,
        adversarialCount,
        config
      ))
    );
    examples.push(
      ...(await this.generateBoundaryExamples(template, boundaryCount, config))
    );

    const validatedExamples = await this.validateAndFilter(examples, template);

    logger.info(`Generated ${validatedExamples.length} valid examples`);
    return validatedExamples;
  }

  /**
   * Generate normal examples based on template patterns
   */
  private async generateNormalExamples(
    template: Template,
    count: number,
    config: ExampleGenerationConfig
  ): Promise<GeneratedExample[]> {
    const examples: GeneratedExample[] = [];

    for (let i = 0; i < count; i++) {
      const input = this.generateTemplateBasedInput(template, config);
      const example: GeneratedExample = {
        input,
        category: 'normal',
        confidence: 0.9,
        metadata: {
          generationMethod: 'template_based',
          diversity: this.calculateDiversity(
            input,
            examples.map(e => e.input)
          ),
          complexity: this.calculateComplexity(input),
          coverage: this.analyzeCoverage(input, template),
        },
      };

      if (template.variables) {
        example.expectedOutput = this.generateExpectedOutput(template, input);
      }

      examples.push(example);
    }

    return examples;
  }

  /**
   * Generate edge case examples
   */
  async generateEdgeCases(
    _template: Template,
    count: number,
    _config: ExampleGenerationConfig
  ): Promise<GeneratedExample[]> {
    const examples: GeneratedExample[] = [];

    for (let i = 0; i < count; i++) {
      const patternIndex = i % this.edgeCasePatterns.length;
      const pattern = this.edgeCasePatterns[patternIndex];
      const input = pattern.pattern();

      examples.push({
        input,
        category: 'edge_case',
        confidence: 0.8,
        metadata: {
          generationMethod: `edge_case_${pattern.name}`,
          diversity: 1.0,
          complexity: this.calculateComplexity(input),
          coverage: [pattern.name],
        },
      });
    }

    return examples;
  }

  /**
   * Generate adversarial examples to test robustness
   */
  private async generateAdversarialExamples(
    template: Template,
    count: number,
    config: ExampleGenerationConfig
  ): Promise<GeneratedExample[]> {
    const examples: GeneratedExample[] = [];

    for (let i = 0; i < count; i++) {
      const input = this.generateAdversarialInput(template, config);
      examples.push({
        input,
        category: 'adversarial',
        confidence: 0.6,
        metadata: {
          generationMethod: 'adversarial',
          diversity: 0.9,
          complexity: this.calculateComplexity(input),
          coverage: ['adversarial_testing'],
        },
      });
    }

    return examples;
  }

  /**
   * Generate boundary condition examples
   */
  private async generateBoundaryExamples(
    template: Template,
    count: number,
    config: ExampleGenerationConfig
  ): Promise<GeneratedExample[]> {
    const examples: GeneratedExample[] = [];

    for (let i = 0; i < count; i++) {
      const input = this.generateBoundaryInput(template, config);
      examples.push({
        input,
        category: 'boundary',
        confidence: 0.7,
        metadata: {
          generationMethod: 'boundary',
          diversity: 0.7,
          complexity: this.calculateComplexity(input),
          coverage: ['boundary_testing'],
        },
      });
    }

    return examples;
  }

  /**
   * Validate generated examples
   */
  async validateExamples(
    examples: GeneratedExample[],
    template: Template
  ): Promise<ValidationResult[]> {
    return examples.map(example => this.validateExample(example, template));
  }

  /**
   * Validate a single example
   */
  private validateExample(
    example: GeneratedExample,
    template: Template
  ): ValidationResult {
    const errors: string[] = [];
    const warnings: string[] = [];
    let score = 1.0;

    if (!example.input) {
      errors.push('Example has no input');
      score = 0;
    }

    if (example.input.length === 0 && example.category !== 'edge_case') {
      warnings.push('Empty input for non-edge-case example');
      score *= 0.5;
    }

    if (example.confidence < 0.5) {
      warnings.push('Low confidence example');
      score *= 0.7;
    }

    const requiredVars = Object.entries(template.variables || {})
      .filter(([, config]) => (config as any).required)
      .map(([key]) => key);

    const missingVars = requiredVars.filter(
      varName => !example.input.includes(`{{${varName}}}`)
    );

    if (missingVars.length > 0 && example.category === 'normal') {
      warnings.push(`Missing required variables: ${missingVars.join(', ')}`);
      score *= 0.8;
    }

    return {
      valid: errors.length === 0,
      errors,
      warnings,
      score,
    };
  }

  /**
   * Validate and filter examples
   */
  private async validateAndFilter(
    examples: GeneratedExample[],
    template: Template
  ): Promise<GeneratedExample[]> {
    const validationResults = await this.validateExamples(examples, template);

    return examples.filter((_example, index) => {
      const validation = validationResults[index];
      if (!validation.valid) {
        logger.warn(
          `Filtering out invalid example: ${validation.errors.join(', ')}`
        );
        return false;
      }
      if (validation.score < 0.3) {
        logger.warn(`Filtering out low-score example: ${validation.score}`);
        return false;
      }
      return true;
    });
  }

  // Helper methods for generation

  private generateTemplateBasedInput(
    template: Template,
    config: ExampleGenerationConfig
  ): string {
    const variables = template.variables || {};
    let input = template.content || '';

    Object.entries(variables).forEach(([key, varConfig]) => {
      const value = this.generateValueForType((varConfig as any).type, config);
      input = input.replace(new RegExp(`{{${key}}}`, 'g'), value);
    });

    return input;
  }

  private generateValueForType(
    type: string,
    config: ExampleGenerationConfig
  ): string {
    switch (type) {
      case 'string':
        return this.generateString(
          config.constraints?.minLength || 10,
          config.constraints?.maxLength || 100
        );
      case 'number':
        return Math.floor(Math.random() * 1000).toString();
      case 'boolean':
        return Math.random() > 0.5 ? 'true' : 'false';
      case 'array':
        return JSON.stringify(this.generateArray());
      case 'object':
        return JSON.stringify(this.generateObject());
      default:
        return 'example_value';
    }
  }

  private generateString(minLength: number, maxLength: number): string {
    const length =
      Math.floor(Math.random() * (maxLength - minLength)) + minLength;
    const chars =
      'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789 ';
    let result = '';
    for (let i = 0; i < length; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
  }

  private generateArray(): unknown[] {
    const length = Math.floor(Math.random() * 5) + 1;
    return Array(length)
      .fill(0)
      .map(() => this.generateString(5, 15));
  }

  private generateObject(): Record<string, unknown> {
    return {
      key1: this.generateString(5, 20),
      key2: Math.floor(Math.random() * 100),
      key3: Math.random() > 0.5,
    };
  }

  private generateMinimalInput(): string {
    return 'a';
  }

  private generateMaxLengthInput(): string {
    return this.generateString(10000, 10000);
  }

  private generateSpecialChars(): string {
    const specialChars = '!@#$%^&*()_+-=[]{}|;:,.<>?/\\`~"\' ';
    let result = '';
    for (let i = 0; i < 50; i++) {
      result += specialChars.charAt(
        Math.floor(Math.random() * specialChars.length)
      );
    }
    return result;
  }

  private generateUnicode(): string {
    const unicodeRanges = [
      [0x0041, 0x005a], // Latin uppercase
      [0x0061, 0x007a], // Latin lowercase
      [0x4e00, 0x4e20], // CJK
      [0x0600, 0x0620], // Arabic
      [0x0400, 0x0420], // Cyrillic
    ];

    let result = '';
    for (let i = 0; i < 20; i++) {
      const range =
        unicodeRanges[Math.floor(Math.random() * unicodeRanges.length)];
      const codePoint =
        Math.floor(Math.random() * (range[1] - range[0])) + range[0];
      result += String.fromCodePoint(codePoint);
    }
    return result;
  }

  private generateRepeated(): string {
    const pattern = this.generateString(5, 10);
    return pattern.repeat(Math.floor(Math.random() * 10) + 2);
  }

  private generateNested(): string {
    const depth = Math.floor(Math.random() * 5) + 1;
    let result = '';
    for (let i = 0; i < depth; i++) {
      result = `{${result}}`;
    }
    return result;
  }

  private generateMixedFormat(): string {
    const formats = [
      () => JSON.stringify({ data: this.generateString(10, 20) }),
      () => `<xml>${this.generateString(10, 20)}</xml>`,
      () => `key: ${this.generateString(10, 20)}`,
      () => `${this.generateString(5, 10)}|${this.generateString(5, 10)}`,
    ];

    return formats[Math.floor(Math.random() * formats.length)]();
  }

  private generateAdversarialInput(
    _template: Template,
    _config: ExampleGenerationConfig
  ): string {
    const adversarialPatterns = [
      'DROP TABLE users;',
      '<script>alert("XSS")</script>',
      `${String.fromCharCode(36)}{__import__("os").system("ls")}`, // Avoid template literal detection
      '../../etc/passwd',
      '\x00\x01\x02\x03',
      'A'.repeat(100000),
    ];

    return adversarialPatterns[
      Math.floor(Math.random() * adversarialPatterns.length)
    ];
  }

  private generateBoundaryInput(
    _template: Template,
    config: ExampleGenerationConfig
  ): string {
    const maxLength = config.constraints?.maxLength || 1000;
    const minLength = config.constraints?.minLength || 1;

    const boundaries = [
      this.generateString(minLength, minLength),
      this.generateString(maxLength, maxLength),
      this.generateString(maxLength - 1, maxLength - 1),
      this.generateString(minLength + 1, minLength + 1),
    ];

    return boundaries[Math.floor(Math.random() * boundaries.length)];
  }

  private generateExpectedOutput(template: Template, input: string): string {
    return `Processed: ${template.name} with input length ${input.length}`;
  }

  private calculateDiversity(input: string, existingInputs: string[]): number {
    if (existingInputs.length === 0) return 1.0;

    const similarities = existingInputs.map(existing =>
      this.calculateSimilarity(input, existing)
    );

    const avgSimilarity =
      similarities.reduce((a, b) => a + b, 0) / similarities.length;
    return 1.0 - avgSimilarity;
  }

  private calculateSimilarity(str1: string, str2: string): number {
    const longer = str1.length > str2.length ? str1 : str2;
    const shorter = str1.length > str2.length ? str2 : str1;

    if (longer.length === 0) return 1.0;

    const editDistance = this.levenshteinDistance(longer, shorter);
    return (longer.length - editDistance) / longer.length;
  }

  private levenshteinDistance(str1: string, str2: string): number {
    const matrix: number[][] = [];

    for (let i = 0; i <= str2.length; i++) {
      matrix[i] = [i];
    }

    for (let j = 0; j <= str1.length; j++) {
      matrix[0][j] = j;
    }

    for (let i = 1; i <= str2.length; i++) {
      for (let j = 1; j <= str1.length; j++) {
        if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
          matrix[i][j] = matrix[i - 1][j - 1];
        } else {
          matrix[i][j] = Math.min(
            matrix[i - 1][j - 1] + 1,
            matrix[i][j - 1] + 1,
            matrix[i - 1][j] + 1
          );
        }
      }
    }

    return matrix[str2.length][str1.length];
  }

  private calculateComplexity(input: string): number {
    const factors = {
      length: Math.min(input.length / 1000, 1.0),
      uniqueChars: new Set(input).size / input.length,
      specialChars:
        (input.match(/[^a-zA-Z0-9\s]/g) || []).length / input.length,
      nesting: (input.match(/[{}[\]()]/g) || []).length / 10,
    };

    return (
      Object.values(factors).reduce((a, b) => a + b, 0) /
      Object.keys(factors).length
    );
  }

  private analyzeCoverage(input: string, template: Template): string[] {
    const coverage: string[] = [];

    const variables = Object.keys(template.variables || {});
    variables.forEach(varName => {
      if (input.includes(`{{${varName}}}`)) {
        coverage.push(`var:${varName}`);
      }
    });

    if (input.length < 10) coverage.push('short_input');
    if (input.length > 1000) coverage.push('long_input');
    if (/[^a-zA-Z0-9\s]/.test(input)) coverage.push('special_chars');
    if (/\d/.test(input)) coverage.push('numeric');

    return coverage;
  }
}

/**
 * @fileoverview Self-evolving template system with continuous learning capabilities
 * @lastmodified 2025-08-26T12:00:00Z
 *
 * Features: Performance tracking, automatic re-optimization, version management
 * Main APIs: trackPerformance(), triggerEvolution(), rollbackVersion()
 * Constraints: Requires PromptWizard service, Redis for metrics storage
 * Patterns: Observer pattern, event-driven architecture, continuous feedback loop
 */

import { EventEmitter } from 'events';
import { logger } from '../utils/logger';
import { PromptOptimizationService } from '../services/prompt-optimization.service';
import { TemplateService } from '../services/template.service';
import { Template } from '../types/index';
import { CacheService } from '../services/cache.service';
import { ConfigManager } from '../config/config-manager';

export interface PerformanceMetric {
  templateId: string;
  version: string;
  timestamp: Date;
  metrics: {
    executionTime: number;
    tokenUsage: number;
    accuracy?: number;
    userSatisfaction?: number;
    errorRate: number;
    successRate: number;
  };
  feedback?: {
    rating: number;
    comments?: string;
  };
}

export interface EvolutionTrigger {
  type: 'threshold' | 'schedule' | 'manual' | 'feedback';
  condition: {
    metric?: string;
    threshold?: number;
    schedule?: string;
    feedbackThreshold?: number;
  };
}

export interface EvolutionResult {
  templateId: string;
  previousVersion: string;
  newVersion: string;
  improvements: {
    tokenReduction: number;
    accuracyGain: number;
    performanceGain: number;
  };
  evolutionTime: number;
  success: boolean;
}

export interface TemplateVersion {
  version: string;
  template: Template;
  performance: PerformanceMetric[];
  createdAt: Date;
  reason: string;
}

export class SelfEvolvingSystem extends EventEmitter {
  private optimizationService: PromptOptimizationService;

  private templateService: TemplateService;

  private metricsCache: CacheService<PerformanceMetric[]>;

  private versionHistory: Map<string, TemplateVersion[]>;

  private evolutionTriggers: Map<string, EvolutionTrigger[]>;

  private performanceThresholds: {
    minAccuracy: number;
    maxTokenUsage: number;
    minSuccessRate: number;
    maxErrorRate: number;
  };

  constructor(
    optimizationService: PromptOptimizationService,
    templateService: TemplateService,
    cacheService: CacheService
  ) {
    super();
    this.optimizationService = optimizationService;
    this.templateService = templateService;
    this.metricsCache = cacheService;
    this.versionHistory = new Map();
    this.evolutionTriggers = new Map();

    const config = ConfigManager.getInstance();
    this.performanceThresholds = {
      minAccuracy: config.get('promptwizard.evolution.minAccuracy', 0.8),
      maxTokenUsage: config.get('promptwizard.evolution.maxTokenUsage', 1000),
      minSuccessRate: config.get('promptwizard.evolution.minSuccessRate', 0.9),
      maxErrorRate: config.get('promptwizard.evolution.maxErrorRate', 0.1),
    };

    this.initializeScheduledEvolutions();
  }

  /**
   * Track template performance metrics
   */
  async trackPerformance(
    templateId: string,
    metric: Omit<PerformanceMetric, 'templateId' | 'timestamp'>
  ): Promise<void> {
    const performanceMetric: PerformanceMetric = {
      templateId,
      timestamp: new Date(),
      ...metric,
    };

    logger.info(`Tracking performance for template: ${templateId}`);

    const cacheKey = `metrics:${templateId}`;
    const existingMetrics = (await this.metricsCache.get(cacheKey)) || [];
    existingMetrics.push(performanceMetric);

    const maxMetrics = 100;
    if (existingMetrics.length > maxMetrics) {
      existingMetrics.splice(0, existingMetrics.length - maxMetrics);
    }

    await this.metricsCache.set(cacheKey, existingMetrics);

    await this.evaluatePerformance(templateId, existingMetrics);

    this.emit('performance:tracked', performanceMetric);
  }

  /**
   * Evaluate template performance and trigger evolution if needed
   */
  private async evaluatePerformance(
    templateId: string,
    metrics: PerformanceMetric[]
  ): Promise<void> {
    if (metrics.length < 10) {
      return;
    }

    const recentMetrics = metrics.slice(-20);
    const avgMetrics = this.calculateAverageMetrics(recentMetrics);

    const needsEvolution =
      avgMetrics.accuracy < this.performanceThresholds.minAccuracy ||
      avgMetrics.tokenUsage > this.performanceThresholds.maxTokenUsage ||
      avgMetrics.successRate < this.performanceThresholds.minSuccessRate ||
      avgMetrics.errorRate > this.performanceThresholds.maxErrorRate;

    if (needsEvolution) {
      logger.info(
        `Template ${templateId} needs evolution based on performance`
      );
      await this.triggerEvolution(templateId, 'threshold');
    }
  }

  /**
   * Calculate average metrics from performance data
   */
  private calculateAverageMetrics(metrics: PerformanceMetric[]): {
    accuracy: number;
    tokenUsage: number;
    successRate: number;
    errorRate: number;
    executionTime: number;
  } {
    const sum = metrics.reduce(
      (acc, m) => ({
        accuracy: acc.accuracy + (m.metrics.accuracy || 0),
        tokenUsage: acc.tokenUsage + m.metrics.tokenUsage,
        successRate: acc.successRate + m.metrics.successRate,
        errorRate: acc.errorRate + m.metrics.errorRate,
        executionTime: acc.executionTime + m.metrics.executionTime,
      }),
      {
        accuracy: 0,
        tokenUsage: 0,
        successRate: 0,
        errorRate: 0,
        executionTime: 0,
      }
    );

    const count = metrics.length;
    return {
      accuracy: sum.accuracy / count,
      tokenUsage: sum.tokenUsage / count,
      successRate: sum.successRate / count,
      errorRate: sum.errorRate / count,
      executionTime: sum.executionTime / count,
    };
  }

  /**
   * Trigger template evolution
   */
  async triggerEvolution(
    templateId: string,
    triggerType: EvolutionTrigger['type']
  ): Promise<EvolutionResult> {
    logger.info(
      `Triggering evolution for template: ${templateId} (${triggerType})`
    );
    this.emit('evolution:started', { templateId, triggerType });

    const startTime = Date.now();

    try {
      const template = await this.templateService.loadTemplate(templateId);

      const currentVersion = this.getCurrentVersion(templateId);
      const newVersion = this.generateVersion();

      const metrics =
        (await this.metricsCache.get(`metrics:${templateId}`)) || [];
      const examples = this.generateExamplesFromMetrics(metrics);

      const optimizationResult =
        await this.optimizationService.optimizeTemplate({
          templateId,
          template: template as any,
          config: {
            mutateRefineIterations: 5,
            fewShotCount: examples.length,
            generateReasoning: true,
          },
          options: {
            skipCache: true,
            priority: 'high',
          },
        });

      this.saveVersion(templateId, {
        version: newVersion,
        template: optimizationResult.optimizedTemplate as any,
        performance: [],
        createdAt: new Date(),
        reason: `Evolution triggered by ${triggerType}`,
      });

      const evolutionResult: EvolutionResult = {
        templateId,
        previousVersion: currentVersion,
        newVersion,
        improvements: {
          tokenReduction: optimizationResult.metrics.tokenReduction,
          accuracyGain: optimizationResult.metrics.accuracyImprovement,
          performanceGain: this.calculatePerformanceGain(metrics),
        },
        evolutionTime: Date.now() - startTime,
        success: true,
      };

      logger.info(`Evolution completed for template: ${templateId}`);
      this.emit('evolution:completed', evolutionResult);

      return evolutionResult;
    } catch (error) {
      logger.error(`Evolution failed for template: ${templateId} - ${error}`);
      this.emit('evolution:failed', { templateId, error });

      return {
        templateId,
        previousVersion: this.getCurrentVersion(templateId),
        newVersion: '',
        improvements: {
          tokenReduction: 0,
          accuracyGain: 0,
          performanceGain: 0,
        },
        evolutionTime: Date.now() - startTime,
        success: false,
      };
    }
  }

  /**
   * Generate examples from performance metrics
   */
  private generateExamplesFromMetrics(metrics: PerformanceMetric[]): string[] {
    const successfulExecutions = metrics
      .filter(m => m.metrics.successRate > 0.8)
      .slice(-10);

    return successfulExecutions.map(
      m =>
        `Version: ${m.version}, Tokens: ${m.metrics.tokenUsage}, Success: ${m.metrics.successRate}`
    );
  }

  /**
   * Calculate performance gain from metrics
   */
  private calculatePerformanceGain(metrics: PerformanceMetric[]): number {
    if (metrics.length < 2) return 0;

    const oldMetrics = metrics.slice(0, Math.floor(metrics.length / 2));
    const newMetrics = metrics.slice(Math.floor(metrics.length / 2));

    const oldAvg = this.calculateAverageMetrics(oldMetrics);
    const newAvg = this.calculateAverageMetrics(newMetrics);

    const executionTimeGain =
      (oldAvg.executionTime - newAvg.executionTime) / oldAvg.executionTime;
    const tokenGain =
      (oldAvg.tokenUsage - newAvg.tokenUsage) / oldAvg.tokenUsage;
    const accuracyGain = (newAvg.accuracy - oldAvg.accuracy) / oldAvg.accuracy;

    return (executionTimeGain + tokenGain + accuracyGain) / 3;
  }

  /**
   * Rollback template to a previous version
   */
  async rollbackVersion(
    templateId: string,
    targetVersion?: string
  ): Promise<void> {
    const versions = this.versionHistory.get(templateId);
    if (!versions || versions.length === 0) {
      throw new Error(`No version history for template: ${templateId}`);
    }

    let targetVersionObj: TemplateVersion | undefined;

    if (targetVersion) {
      targetVersionObj = versions.find(v => v.version === targetVersion);
    } else {
      targetVersionObj = versions[versions.length - 2];
    }

    if (!targetVersionObj) {
      throw new Error(
        `Version ${targetVersion} not found for template: ${templateId}`
      );
    }

    logger.info(
      `Rolling back template ${templateId} to version ${targetVersionObj.version}`
    );

    await this.metricsCache.delete(`metrics:${templateId}`);

    this.emit('version:rollback', {
      templateId,
      fromVersion: this.getCurrentVersion(templateId),
      toVersion: targetVersionObj.version,
    });
  }

  /**
   * Get version history for a template
   */
  getVersionHistory(templateId: string): TemplateVersion[] {
    return this.versionHistory.get(templateId) || [];
  }

  /**
   * Get current version of a template
   */
  private getCurrentVersion(templateId: string): string {
    const versions = this.versionHistory.get(templateId);
    if (!versions || versions.length === 0) {
      return '1.0.0';
    }
    return versions[versions.length - 1].version;
  }

  /**
   * Generate a new version number
   */
  private generateVersion(): string {
    const timestamp = Date.now();
    const random = Math.random().toString(36).substring(2, 7);
    return `${timestamp}-${random}`;
  }

  /**
   * Save a template version
   */
  private saveVersion(templateId: string, version: TemplateVersion): void {
    if (!this.versionHistory.has(templateId)) {
      this.versionHistory.set(templateId, []);
    }

    const versions = this.versionHistory.get(templateId)!;
    versions.push(version);

    const maxVersions = 10;
    if (versions.length > maxVersions) {
      versions.shift();
    }
  }

  /**
   * Initialize scheduled evolutions
   */
  private initializeScheduledEvolutions(): void {
    const config = ConfigManager.getInstance();
    const scheduleEnabled = config.get(
      'promptwizard.evolution.scheduleEnabled',
      false
    );

    if (scheduleEnabled) {
      const intervalMs = config.get(
        'promptwizard.evolution.intervalMs',
        86400000
      );

      setInterval(async () => {
        logger.info('Running scheduled evolution check');

        for (const [templateId] of this.versionHistory) {
          const metrics =
            (await this.metricsCache.get(`metrics:${templateId}`)) || [];
          if (metrics.length > 50) {
            await this.triggerEvolution(templateId, 'schedule');
          }
        }
      }, intervalMs);
    }
  }

  /**
   * Add evolution trigger for a template
   */
  addEvolutionTrigger(templateId: string, trigger: EvolutionTrigger): void {
    if (!this.evolutionTriggers.has(templateId)) {
      this.evolutionTriggers.set(templateId, []);
    }

    this.evolutionTriggers.get(templateId)!.push(trigger);
    logger.info(`Added evolution trigger for template: ${templateId}`);
  }

  /**
   * Remove evolution triggers for a template
   */
  removeEvolutionTriggers(templateId: string): void {
    this.evolutionTriggers.delete(templateId);
    logger.info(`Removed evolution triggers for template: ${templateId}`);
  }
}

/**
 * @fileoverview Claude-specific prompt optimization strategies and patterns
 * @lastmodified 2025-08-26T14:10:00Z
 *
 * Features: Claude-optimized prompt patterns, constitutional AI principles, reasoning
 * Main APIs: ClaudeOptimizer class with Anthropic-specific optimization strategies
 * Constraints: Optimized for Claude's constitutional AI and helpfulness principles
 * Patterns: Strategy pattern, constitutional AI alignment, ethical reasoning
 */

import { logger } from '../../utils/logger';

export interface ClaudeOptimizationConfig {
  /** Maximum tokens for Claude (varies by model: Claude-3 has 200k context) */
  maxTokens: number;

  /** Claude model variant */
  model: 'claude-3-opus' | 'claude-3-sonnet' | 'claude-3-haiku' | 'claude-2';

  /** Enable constitutional AI principles */
  enableConstitutionalAI: boolean;

  /** Optimize for helpfulness, harmlessness, honesty */
  optimizeForHHH: boolean;

  /** Enable XML-style structured reasoning */
  enableStructuredReasoning: boolean;

  /** Optimize for long-form reasoning */
  optimizeForLongForm: boolean;

  /** Enable Claude's thinking process */
  enableThinkingProcess: boolean;
}

export interface ClaudeOptimizationResult {
  /** Optimized prompt */
  optimizedPrompt: string;

  /** Optimization techniques applied */
  techniquesApplied: string[];

  /** Estimated token usage */
  estimatedTokens: number;

  /** Performance improvements */
  improvements: {
    helpfulnessScore: number;
    clarityScore: number;
    structureScore: number;
    reasoningScore: number;
  };

  /** Claude-specific enhancements */
  claudeEnhancements: {
    constitutionalPrinciplesAdded: boolean;
    xmlStructuringUsed: boolean;
    thinkingProcessAdded: boolean;
    hhhOptimized: boolean;
  };
}

export class ClaudeOptimizer {
  private readonly defaultConfig: ClaudeOptimizationConfig = {
    maxTokens: 180000, // Leave room for response
    model: 'claude-3-sonnet',
    enableConstitutionalAI: true,
    optimizeForHHH: true,
    enableStructuredReasoning: true,
    optimizeForLongForm: true,
    enableThinkingProcess: true,
  };

  constructor(
    private config: ClaudeOptimizationConfig = {} as ClaudeOptimizationConfig
  ) {
    this.config = { ...this.defaultConfig, ...config };
  }

  /**
   * Optimize prompt specifically for Claude's capabilities and principles
   */
  async optimizeForClaude(
    prompt: string,
    task: string,
    context?: {
      expectedFormat?: 'xml' | 'markdown' | 'json' | 'text';
      reasoning?: 'simple' | 'complex' | 'analytical';
      domain?: string;
      ethicalConsiderations?: boolean;
    }
  ): Promise<ClaudeOptimizationResult> {
    logger.info("Optimizing prompt for Claude's constitutional AI principles");

    let optimizedPrompt = prompt;
    const techniquesApplied: string[] = [];
    const claudeEnhancements = {
      constitutionalPrinciplesAdded: false,
      xmlStructuringUsed: false,
      thinkingProcessAdded: false,
      hhhOptimized: false,
    };

    // Apply Claude-specific optimizations
    if (this.config.enableConstitutionalAI) {
      const constitutionalResult = this.addConstitutionalPrinciples(
        optimizedPrompt,
        context
      );
      optimizedPrompt = constitutionalResult.prompt;
      if (constitutionalResult.applied) {
        techniquesApplied.push('Constitutional AI principles');
        claudeEnhancements.constitutionalPrinciplesAdded = true;
      }
    }

    if (this.config.optimizeForHHH) {
      const hhhResult = this.optimizeForHHH(optimizedPrompt, task);
      optimizedPrompt = hhhResult.prompt;
      if (hhhResult.applied) {
        techniquesApplied.push(
          'Helpfulness, Harmlessness, Honesty optimization'
        );
        claudeEnhancements.hhhOptimized = true;
      }
    }

    if (this.config.enableStructuredReasoning) {
      const structureResult = this.addXMLStructuring(optimizedPrompt, context);
      optimizedPrompt = structureResult.prompt;
      if (structureResult.applied) {
        techniquesApplied.push('XML-structured reasoning');
        claudeEnhancements.xmlStructuringUsed = true;
      }
    }

    if (this.config.enableThinkingProcess) {
      const thinkingResult = this.addThinkingProcess(optimizedPrompt, context);
      optimizedPrompt = thinkingResult.prompt;
      if (thinkingResult.applied) {
        techniquesApplied.push('Structured thinking process');
        claudeEnhancements.thinkingProcessAdded = true;
      }
    }

    // Apply Claude-specific prompt patterns
    optimizedPrompt = this.applyClaudePatterns(optimizedPrompt, context);
    techniquesApplied.push('Claude-specific patterns');

    // Optimize for Claude's preferred communication style
    optimizedPrompt = this.optimizeCommunicationStyle(optimizedPrompt);
    techniquesApplied.push('Communication style optimization');

    // Ensure token limits for specific Claude model
    const tokenOptimizedResult = this.optimizeTokenUsage(optimizedPrompt);
    optimizedPrompt = tokenOptimizedResult.prompt;
    if (tokenOptimizedResult.tokensReduced > 0) {
      techniquesApplied.push(
        `Token optimization (-${tokenOptimizedResult.tokensReduced} tokens)`
      );
    }

    // Calculate improvements
    const improvements = this.calculateImprovements(
      prompt,
      optimizedPrompt,
      context
    );

    const result: ClaudeOptimizationResult = {
      optimizedPrompt,
      techniquesApplied,
      estimatedTokens: this.estimateTokens(optimizedPrompt),
      improvements,
      claudeEnhancements,
    };

    logger.info(
      `Claude optimization completed: ${techniquesApplied.length} techniques applied`
    );
    return result;
  }

  /**
   * Add constitutional AI principles to prompt
   */
  private addConstitutionalPrinciples(
    prompt: string,
    context?: { ethicalConsiderations?: boolean; domain?: string }
  ): { prompt: string; applied: boolean } {
    // Check if constitutional principles already mentioned
    if (
      prompt.toLowerCase().includes('helpful') ||
      prompt.toLowerCase().includes('harmless')
    ) {
      return { prompt, applied: false };
    }

    let constitutionalPrompt = prompt;

    // Add appropriate constitutional framing
    if (context?.ethicalConsiderations) {
      constitutionalPrompt = `Please respond in a way that is helpful, harmless, and honest. Consider ethical implications and potential impacts of your response.\n\n${constitutionalPrompt}`;
    } else {
      // Add lighter constitutional framing
      constitutionalPrompt = `Please provide a helpful and accurate response.\n\n${constitutionalPrompt}`;
    }

    // Add domain-specific constitutional considerations
    if (context?.domain === 'medical') {
      constitutionalPrompt +=
        '\n\nNote: This should not be considered medical advice. Please consult healthcare professionals for medical decisions.';
    } else if (context?.domain === 'legal') {
      constitutionalPrompt +=
        '\n\nNote: This should not be considered legal advice. Please consult qualified legal professionals for legal matters.';
    } else if (context?.domain === 'financial') {
      constitutionalPrompt +=
        '\n\nNote: This should not be considered financial advice. Please consult financial advisors for investment decisions.';
    }

    return { prompt: constitutionalPrompt, applied: true };
  }

  /**
   * Optimize for Helpfulness, Harmlessness, and Honesty
   */
  private optimizeForHHH(
    prompt: string,
    task: string
  ): { prompt: string; applied: boolean } {
    let hhhPrompt = prompt;
    let applied = false;

    // Enhance helpfulness
    if (
      !prompt.toLowerCase().includes('specific') &&
      !prompt.toLowerCase().includes('detailed')
    ) {
      hhhPrompt +=
        '\n\nPlease be specific and thorough in your response to be as helpful as possible.';
      applied = true;
    }

    // Enhance honesty/accuracy
    if (
      task.toLowerCase().includes('fact') ||
      task.toLowerCase().includes('information')
    ) {
      hhhPrompt +=
        "\n\nIf you're uncertain about any facts, please indicate this clearly and suggest where to verify the information.";
      applied = true;
    }

    // Enhance harmlessness for sensitive topics
    const sensitiveTriggers = [
      'harm',
      'danger',
      'risk',
      'illegal',
      'unethical',
    ];
    if (
      sensitiveTriggers.some(trigger => prompt.toLowerCase().includes(trigger))
    ) {
      hhhPrompt +=
        '\n\nPlease ensure your response promotes safety and well-being, and avoid any potentially harmful advice.';
      applied = true;
    }

    return { prompt: hhhPrompt, applied };
  }

  /**
   * Add XML structuring for Claude's reasoning
   */
  private addXMLStructuring(
    prompt: string,
    context?: { expectedFormat?: string; reasoning?: string }
  ): { prompt: string; applied: boolean } {
    // Check if XML structure already present
    if (prompt.includes('<') || prompt.includes('/>')) {
      return { prompt, applied: false };
    }

    let xmlPrompt = prompt;

    // Add XML structuring based on reasoning complexity
    if (
      context?.reasoning === 'complex' ||
      context?.reasoning === 'analytical'
    ) {
      xmlPrompt +=
        '\n\nPlease structure your response using the following format:';
      xmlPrompt += '\n<analysis>';
      xmlPrompt += '\n<problem>Identify the key problem or question</problem>';
      xmlPrompt += '\n<approach>Describe your analytical approach</approach>';
      xmlPrompt +=
        '\n<reasoning>Work through the reasoning step by step</reasoning>';
      xmlPrompt += '\n<conclusion>Provide your final conclusion</conclusion>';
      xmlPrompt += '\n</analysis>';
    } else if (context?.reasoning === 'simple') {
      xmlPrompt += '\n\nPlease structure your response as:';
      xmlPrompt += '\n<response>';
      xmlPrompt += '\n<answer>Your main answer</answer>';
      xmlPrompt += '\n<explanation>Brief explanation</explanation>';
      xmlPrompt += '\n</response>';
    }

    // Add format-specific XML
    if (context?.expectedFormat === 'xml') {
      xmlPrompt +=
        '\n\nPlease use appropriate XML tags to structure your content clearly.';
    }

    return { prompt: xmlPrompt, applied: xmlPrompt !== prompt };
  }

  /**
   * Add structured thinking process for Claude
   */
  private addThinkingProcess(
    prompt: string,
    context?: { reasoning?: string; domain?: string }
  ): { prompt: string; applied: boolean } {
    // Check if thinking process already present
    if (
      prompt.includes('think') &&
      (prompt.includes('step') || prompt.includes('process'))
    ) {
      return { prompt, applied: false };
    }

    let thinkingPrompt = prompt;

    // Add thinking structure based on context
    if (context?.reasoning === 'complex') {
      thinkingPrompt += '\n\nPlease work through this systematically:';
      thinkingPrompt += '\n1. Break down the problem into components';
      thinkingPrompt += '\n2. Analyze each component carefully';
      thinkingPrompt += '\n3. Consider relationships and dependencies';
      thinkingPrompt += '\n4. Synthesize insights into a cohesive response';
      thinkingPrompt += '\n\nShow your thinking process clearly.';
    } else if (context?.reasoning === 'analytical') {
      thinkingPrompt += '\n\nPlease approach this analytically:';
      thinkingPrompt += '\n- Examine the evidence or information provided';
      thinkingPrompt += '\n- Consider multiple perspectives';
      thinkingPrompt += '\n- Identify key insights and implications';
      thinkingPrompt += '\n- Draw well-reasoned conclusions';
    } else {
      thinkingPrompt +=
        '\n\nPlease think through this carefully and explain your reasoning.';
    }

    return { prompt: thinkingPrompt, applied: true };
  }

  /**
   * Apply Claude-specific prompt patterns and preferences
   */
  private applyClaudePatterns(
    prompt: string,
    context?: { domain?: string; expectedFormat?: string }
  ): string {
    let patterned = prompt;

    // Claude responds well to polite, conversational language
    if (!patterned.toLowerCase().includes('please')) {
      patterned = patterned.replace(/^([A-Z])/, 'Please $1');
    }

    // Claude likes explicit structure and organization
    if (context?.domain && !patterned.includes(context.domain)) {
      patterned = `Regarding ${context.domain}: ${patterned}`;
    }

    // Claude excels with clear expectations
    if (!patterned.includes('I need') && !patterned.includes('help me')) {
      patterned = patterned.replace(
        /\?$/,
        '? Please help me understand this clearly.'
      );
    }

    // Add conversational elements that Claude responds well to
    patterned = patterned.replace(/\bgenerate\b/gi, 'help me create');
    patterned = patterned.replace(/\bmake\b/gi, 'help me make');

    return patterned;
  }

  /**
   * Optimize communication style for Claude
   */
  private optimizeCommunicationStyle(prompt: string): string {
    let styled = prompt;

    // Claude prefers natural, conversational language
    styled = styled.replace(/\byou must\b/gi, 'please');
    styled = styled.replace(/\brequired\b/gi, 'needed');
    styled = styled.replace(/\bdemand\b/gi, 'request');

    // Add collaborative framing
    if (!styled.includes('we') && !styled.includes('together')) {
      styled = styled.replace(/\bI want\b/gi, "I'd like us to work on");
    }

    // Ensure respectful tone
    styled = styled.replace(/\bdo this\b/gi, 'help with this');
    styled = styled.replace(/\bgive me\b/gi, 'please provide');

    return styled;
  }

  /**
   * Optimize token usage for Claude models
   */
  private optimizeTokenUsage(prompt: string): {
    prompt: string;
    tokensReduced: number;
  } {
    const originalTokens = this.estimateTokens(prompt);

    if (originalTokens <= this.config.maxTokens) {
      return { prompt, tokensReduced: 0 };
    }

    let optimized = prompt;

    // Claude-specific token optimizations
    // Remove redundant politeness (Claude is naturally polite)
    optimized = optimized.replace(/\bplease please\b/gi, 'please');
    optimized = optimized.replace(/\bthank you very much\b/gi, 'thank you');

    // Compress while maintaining conversational tone
    optimized = optimized.replace(/\s+/g, ' ').trim();
    optimized = optimized.replace(/(\n\s*){3,}/g, '\n\n');

    // Simplify verbose expressions while keeping Claude's preferred style
    const replacements = [
      [/I would like you to please/gi, 'please'],
      [/if you could please/gi, 'please'],
      [/would you be able to/gi, 'can you'],
      [/it would be great if you could/gi, 'please'],
    ];

    for (const [pattern, replacement] of replacements) {
      optimized = optimized.replace(pattern as RegExp, replacement as string);
    }

    const newTokens = this.estimateTokens(optimized);
    const tokensReduced = originalTokens - newTokens;

    return { prompt: optimized, tokensReduced };
  }

  /**
   * Estimate token count for Claude (similar to GPT but slightly different)
   */
  private estimateTokens(text: string): number {
    // Claude tokenization is roughly 3.8 characters per token for English
    return Math.ceil(text.length / 3.8);
  }

  /**
   * Calculate improvement scores specific to Claude's strengths
   */
  private calculateImprovements(
    _original: string,
    optimized: string,
    context?: { reasoning?: string }
  ) {
    // Helpfulness score based on Claude's helpfulness patterns
    const helpfulnessScore = this.calculateHelpfulnessScore(optimized);

    // Clarity score with Claude's preference for clear communication
    const clarityScore = this.calculateClarityScore(optimized);

    // Structure score considering XML and organized thinking
    const structureScore = this.calculateStructureScore(optimized);

    // Reasoning score for analytical thinking
    const reasoningScore = this.calculateReasoningScore(optimized, context);

    return {
      helpfulnessScore: Math.min(100, helpfulnessScore),
      clarityScore: Math.min(100, clarityScore),
      structureScore: Math.min(100, structureScore),
      reasoningScore: Math.min(100, reasoningScore),
    };
  }

  private calculateHelpfulnessScore(text: string): number {
    let score = 60; // Base score

    // Award points for helpfulness indicators
    if (text.toLowerCase().includes('please')) score += 10;
    if (text.toLowerCase().includes('help')) score += 10;
    if (text.toLowerCase().includes('specific')) score += 5;
    if (text.toLowerCase().includes('detailed')) score += 5;
    if (text.toLowerCase().includes('thorough')) score += 5;
    if (text.toLowerCase().includes('clear')) score += 5;

    return score;
  }

  private calculateClarityScore(text: string): number {
    let score = 60; // Base score

    // Award points for clarity
    if (text.includes('\n\n')) score += 10; // Clear paragraphs
    if (text.includes(':')) score += 5; // Structure
    if (text.includes('example')) score += 5;
    if (text.includes('specifically')) score += 5;

    // Deduct for unclear patterns
    if (text.includes('...')) score -= 5;
    if (text.length > 3000 && !text.includes('<')) score -= 10; // Too verbose without structure

    return score;
  }

  private calculateStructureScore(text: string): number {
    let score = 60; // Base score

    // Award points for structure
    if (text.includes('<') && text.includes('>')) score += 20; // XML structure
    if (/\d+\./.test(text)) score += 10; // Numbered lists
    if (text.includes('- ')) score += 5; // Bullet points
    if (text.includes('\n\n')) score += 10; // Proper spacing

    return score;
  }

  private calculateReasoningScore(
    text: string,
    context?: { reasoning?: string }
  ): number {
    let score = 50; // Base score

    // Award points for reasoning patterns
    if (text.toLowerCase().includes('because')) score += 10;
    if (text.toLowerCase().includes('therefore')) score += 10;
    if (text.toLowerCase().includes('analysis')) score += 10;
    if (text.toLowerCase().includes('consider')) score += 10;
    if (text.toLowerCase().includes('reasoning')) score += 15;

    // Bonus for complex reasoning structure
    if (context?.reasoning === 'complex' && text.includes('<')) score += 15;

    return score;
  }

  /**
   * Get Claude-specific recommendations
   */
  getClaudeRecommendations(): {
    bestPractices: string[];
    constitutionalPrinciples: string[];
    communicationTips: string[];
  } {
    return {
      bestPractices: [
        'Use conversational, polite language - Claude responds well to natural communication',
        'Structure complex requests with XML tags for clarity',
        'Ask Claude to show its thinking process for complex reasoning',
        'Be specific about the type of analysis or reasoning you want',
        'Include context about ethics or safety when relevant',
        'Use collaborative language ("help me" vs "do this")',
      ],
      constitutionalPrinciples: [
        'Helpfulness: Claude aims to be maximally helpful while being safe',
        'Harmlessness: Claude avoids harmful, dangerous, or illegal content',
        'Honesty: Claude strives for accuracy and admits uncertainty',
        'Constitutional AI: Claude follows built-in safety guidelines',
      ],
      communicationTips: [
        'Claude excels at long-form reasoning and analysis',
        'Use XML structuring for complex multi-part requests',
        'Ask for step-by-step thinking when you need detailed reasoning',
        'Claude can handle very long contexts (200k tokens for Claude-3)',
        'Conversational tone works better than overly formal instructions',
        'Claude is particularly good at ethical reasoning and nuanced analysis',
      ],
    };
  }

  /**
   * Get model-specific configuration recommendations
   */
  getModelSpecificRecommendations(): Record<
    string,
    {
      contextWindow: number;
      strengths: string[];
      bestUseCase: string;
    }
  > {
    return {
      'claude-3-opus': {
        contextWindow: 200000,
        strengths: [
          'Complex reasoning',
          'Long-form analysis',
          'Creative tasks',
        ],
        bestUseCase:
          'High-complexity analytical tasks requiring deep reasoning',
      },
      'claude-3-sonnet': {
        contextWindow: 200000,
        strengths: ['Balanced performance', 'Good reasoning', 'Efficiency'],
        bestUseCase:
          'General-purpose tasks with good balance of speed and capability',
      },
      'claude-3-haiku': {
        contextWindow: 200000,
        strengths: ['Speed', 'Efficiency', 'Simple tasks'],
        bestUseCase: 'Fast responses for straightforward tasks',
      },
      'claude-2': {
        contextWindow: 100000,
        strengths: ['Solid reasoning', 'Good safety', 'Reliability'],
        bestUseCase: 'Reliable performance for standard prompting tasks',
      },
    };
  }
}

export default ClaudeOptimizer;

/**
 * @fileoverview Gemini-specific prompt optimization strategies and patterns
 * @lastmodified 2025-08-26T14:15:00Z
 *
 * Features: Gemini-optimized prompt patterns, multimodal capabilities, efficiency
 * Main APIs: GeminiOptimizer class with Google-specific optimization strategies
 * Constraints: Optimized for Gemini's multimodal and efficiency characteristics
 * Patterns: Strategy pattern, multimodal optimization, efficiency-focused tuning
 */

import { logger } from '../../utils/logger';

export interface GeminiOptimizationConfig {
  maxTokens: number;
  model: 'gemini-pro' | 'gemini-pro-vision' | 'gemini-ultra';
  enableMultimodal: boolean;
  optimizeForEfficiency: boolean;
  enableStructuredPrompts: boolean;
  optimizeForCode: boolean;
}

export interface GeminiOptimizationResult {
  optimizedPrompt: string;
  techniquesApplied: string[];
  estimatedTokens: number;
  improvements: {
    efficiencyScore: number;
    clarityScore: number;
    structureScore: number;
    multimodalScore: number;
  };
  geminiEnhancements: {
    multimodalOptimized: boolean;
    efficiencyImproved: boolean;
    structuredFormat: boolean;
    codeOptimized: boolean;
  };
}

export class GeminiOptimizer {
  private readonly defaultConfig: GeminiOptimizationConfig = {
    maxTokens: 30000, // Gemini Pro has ~32k context
    model: 'gemini-pro',
    enableMultimodal: false,
    optimizeForEfficiency: true,
    enableStructuredPrompts: true,
    optimizeForCode: true,
  };

  constructor(
    private config: GeminiOptimizationConfig = {} as GeminiOptimizationConfig
  ) {
    this.config = { ...this.defaultConfig, ...config };
  }

  async optimizeForGemini(
    prompt: string,
    _task: string,
    context?: {
      hasImages?: boolean;
      codeRelated?: boolean;
      expectedFormat?: string;
    }
  ): Promise<GeminiOptimizationResult> {
    logger.info('Optimizing prompt for Gemini capabilities');

    let optimizedPrompt = prompt;
    const techniquesApplied: string[] = [];
    const geminiEnhancements = {
      multimodalOptimized: false,
      efficiencyImproved: false,
      structuredFormat: false,
      codeOptimized: false,
    };

    // Apply Gemini-specific optimizations
    if (this.config.optimizeForEfficiency) {
      const efficiencyResult = this.optimizeForEfficiency(optimizedPrompt);
      optimizedPrompt = efficiencyResult.prompt;
      if (efficiencyResult.applied) {
        techniquesApplied.push('Efficiency optimization');
        geminiEnhancements.efficiencyImproved = true;
      }
    }

    if (this.config.optimizeForCode && context?.codeRelated) {
      const codeResult = this.optimizeForCode(optimizedPrompt);
      optimizedPrompt = codeResult.prompt;
      if (codeResult.applied) {
        techniquesApplied.push('Code optimization');
        geminiEnhancements.codeOptimized = true;
      }
    }

    if (this.config.enableMultimodal && context?.hasImages) {
      const multimodalResult = this.optimizeForMultimodal(optimizedPrompt);
      optimizedPrompt = multimodalResult.prompt;
      if (multimodalResult.applied) {
        techniquesApplied.push('Multimodal optimization');
        geminiEnhancements.multimodalOptimized = true;
      }
    }

    if (this.config.enableStructuredPrompts) {
      const structureResult = this.addStructuredFormat(
        optimizedPrompt,
        context?.expectedFormat
      );
      optimizedPrompt = structureResult.prompt;
      if (structureResult.applied) {
        techniquesApplied.push('Structured formatting');
        geminiEnhancements.structuredFormat = true;
      }
    }

    // Apply Gemini patterns
    optimizedPrompt = this.applyGeminiPatterns(optimizedPrompt);
    techniquesApplied.push('Gemini-specific patterns');

    const improvements = this.calculateImprovements(
      prompt,
      optimizedPrompt,
      context
    );

    return {
      optimizedPrompt,
      techniquesApplied,
      estimatedTokens: this.estimateTokens(optimizedPrompt),
      improvements,
      geminiEnhancements,
    };
  }

  private optimizeForEfficiency(prompt: string): {
    prompt: string;
    applied: boolean;
  } {
    let optimized = prompt;
    let applied = false;

    // Remove redundant words
    const redundancies = [
      [/very very/gi, 'very'],
      [/really really/gi, 'really'],
      [/quite quite/gi, 'quite'],
      [/please please/gi, 'please'],
    ];

    for (const [pattern, replacement] of redundancies) {
      const original = optimized;
      optimized = optimized.replace(pattern as RegExp, replacement as string);
      if (optimized !== original) applied = true;
    }

    // Simplify verbose expressions
    const simplifications = [
      [/in order to/gi, 'to'],
      [/for the purpose of/gi, 'to'],
      [/with regard to/gi, 'regarding'],
      [/a large number of/gi, 'many'],
    ];

    for (const [pattern, replacement] of simplifications) {
      const original = optimized;
      optimized = optimized.replace(pattern as RegExp, replacement as string);
      if (optimized !== original) applied = true;
    }

    return { prompt: optimized, applied };
  }

  private optimizeForCode(prompt: string): {
    prompt: string;
    applied: boolean;
  } {
    if (
      !prompt.toLowerCase().includes('code') &&
      !prompt.toLowerCase().includes('programming')
    ) {
      return { prompt, applied: false };
    }

    let codePrompt = prompt;
    codePrompt += '\n\nFor code examples:';
    codePrompt += '\n- Use clear, readable formatting';
    codePrompt += '\n- Include relevant comments';
    codePrompt += '\n- Follow best practices for the language';
    codePrompt += '\n- Provide complete, working examples when possible';

    return { prompt: codePrompt, applied: true };
  }

  private optimizeForMultimodal(prompt: string): {
    prompt: string;
    applied: boolean;
  } {
    let multimodalPrompt = prompt;
    multimodalPrompt += '\n\nWhen analyzing any images:';
    multimodalPrompt += '\n- Describe relevant visual elements clearly';
    multimodalPrompt += '\n- Connect visual information to the text context';
    multimodalPrompt += '\n- Be specific about what you observe';

    return { prompt: multimodalPrompt, applied: true };
  }

  private addStructuredFormat(
    prompt: string,
    expectedFormat?: string
  ): { prompt: string; applied: boolean } {
    if (prompt.includes('format:') || prompt.includes('structure:')) {
      return { prompt, applied: false };
    }

    let structured = prompt;

    if (expectedFormat) {
      structured += `\n\nPlease format your response as ${expectedFormat}.`;
    } else {
      structured += '\n\nPlease structure your response clearly with:';
      structured += '\n1. Main points organized logically';
      structured += '\n2. Clear headings or sections where appropriate';
      structured += '\n3. Concise and actionable information';
    }

    return { prompt: structured, applied: true };
  }

  private applyGeminiPatterns(prompt: string): string {
    let patterned = prompt;

    // Gemini works well with direct, clear instructions
    patterned = patterned.replace(/could you please/gi, 'please');
    patterned = patterned.replace(/would you mind/gi, 'please');
    patterned = patterned.replace(/if possible/gi, '');

    // Gemini prefers specific over vague requests
    patterned = patterned.replace(/some information/gi, 'specific information');
    patterned = patterned.replace(/a bit about/gi, 'details about');

    return patterned;
  }

  private estimateTokens(text: string): number {
    // Gemini tokenization approximation
    return Math.ceil(text.length / 4);
  }

  private calculateImprovements(
    _original: string,
    optimized: string,
    context?: Record<string, unknown>
  ) {
    const efficiencyScore = this.calculateEfficiencyScore(optimized);
    const clarityScore = this.calculateClarityScore(optimized);
    const structureScore = this.calculateStructureScore(optimized);
    const multimodalScore = context?.hasImages
      ? this.calculateMultimodalScore(optimized)
      : 0;

    return {
      efficiencyScore: Math.min(100, efficiencyScore),
      clarityScore: Math.min(100, clarityScore),
      structureScore: Math.min(100, structureScore),
      multimodalScore: Math.min(100, multimodalScore),
    };
  }

  private calculateEfficiencyScore(text: string): number {
    let score = 70;

    // Award for conciseness
    if (text.length < 500) score += 15;
    else if (text.length < 1000) score += 10;

    // Award for direct language
    if (!text.includes('perhaps') && !text.includes('maybe')) score += 10;

    return score;
  }

  private calculateClarityScore(text: string): number {
    let score = 60;

    if (text.includes('specific')) score += 10;
    if (text.includes('clear')) score += 10;
    if (text.includes(':')) score += 5;

    return score;
  }

  private calculateStructureScore(text: string): number {
    let score = 60;

    if (/\d+\./.test(text)) score += 15;
    if (text.includes('- ')) score += 10;
    if (text.includes('\n\n')) score += 10;

    return score;
  }

  private calculateMultimodalScore(text: string): number {
    let score = 50;

    if (text.includes('image')) score += 20;
    if (text.includes('visual')) score += 15;
    if (text.includes('describe')) score += 15;

    return score;
  }

  getGeminiRecommendations(): {
    bestPractices: string[];
    efficiencyTips: string[];
    multimodalTips: string[];
  } {
    return {
      bestPractices: [
        'Use direct, clear instructions for best results',
        'Structure prompts with numbered lists or bullet points',
        'Be specific about desired output format',
        'Keep prompts concise while maintaining necessary context',
        'Use Gemini Pro Vision for multimodal tasks',
      ],
      efficiencyTips: [
        'Remove redundant words and phrases',
        'Use simple, direct language over complex expressions',
        'Combine related requests into single prompts',
        'Specify output length requirements clearly',
      ],
      multimodalTips: [
        'Describe what you want analyzed in images',
        'Ask specific questions about visual elements',
        'Combine text and image context effectively',
        'Use Gemini Pro Vision for image analysis tasks',
      ],
    };
  }
}

export default GeminiOptimizer;

/**
 * @fileoverview GPT-4 specific prompt optimization strategies and patterns
 * @lastmodified 2025-08-26T14:00:00Z
 *
 * Features: GPT-4 optimized prompt patterns, token management, context utilization
 * Main APIs: GPT4Optimizer class with model-specific optimization strategies
 * Constraints: Optimized for GPT-4 context window and reasoning capabilities
 * Patterns: Strategy pattern, model-specific optimization, performance tuning
 */

import { logger } from '../../utils/logger';

export interface GPT4OptimizationConfig {
  /** Maximum tokens to use (GPT-4 has 128k context) */
  maxTokens: number;

  /** Temperature for optimization requests */
  temperature: number;

  /** Enable chain-of-thought optimization */
  enableChainOfThought: boolean;

  /** Use GPT-4's advanced reasoning capabilities */
  enableAdvancedReasoning: boolean;

  /** Optimize for structured outputs */
  optimizeForStructuredOutput: boolean;

  /** Enable system message optimization */
  optimizeSystemMessages: boolean;
}

export interface GPT4OptimizationResult {
  /** Optimized prompt */
  optimizedPrompt: string;

  /** Optimization techniques applied */
  techniquesApplied: string[];

  /** Estimated token usage */
  estimatedTokens: number;

  /** Performance improvements */
  improvements: {
    clarityScore: number;
    reasoningScore: number;
    structureScore: number;
    efficiencyScore: number;
  };

  /** GPT-4 specific enhancements */
  gpt4Enhancements: {
    chainOfThoughtAdded: boolean;
    systemMessageOptimized: boolean;
    structuredOutputEnabled: boolean;
    reasoningStepsAdded: number;
  };
}

export class GPT4Optimizer {
  private readonly defaultConfig: GPT4OptimizationConfig = {
    maxTokens: 120000, // Leave some room for response
    temperature: 0.1, // Low temperature for optimization tasks
    enableChainOfThought: true,
    enableAdvancedReasoning: true,
    optimizeForStructuredOutput: true,
    optimizeSystemMessages: true,
  };

  constructor(
    private config: GPT4OptimizationConfig = {} as GPT4OptimizationConfig
  ) {
    this.config = { ...this.defaultConfig, ...config };
  }

  /**
   * Optimize prompt specifically for GPT-4's capabilities
   */
  async optimizeForGPT4(
    prompt: string,
    task: string,
    context?: {
      expectedFormat?: 'json' | 'markdown' | 'code' | 'text';
      complexity?: 'simple' | 'medium' | 'complex';
      domain?: string;
    }
  ): Promise<GPT4OptimizationResult> {
    logger.info('Optimizing prompt for GPT-4 specific capabilities');

    let optimizedPrompt = prompt;
    const techniquesApplied: string[] = [];
    const gpt4Enhancements = {
      chainOfThoughtAdded: false,
      systemMessageOptimized: false,
      structuredOutputEnabled: false,
      reasoningStepsAdded: 0,
    };

    // Apply GPT-4 specific optimizations
    if (this.config.enableChainOfThought) {
      const cotResult = this.addChainOfThought(optimizedPrompt, context);
      optimizedPrompt = cotResult.prompt;
      if (cotResult.applied) {
        techniquesApplied.push('Chain-of-thought reasoning');
        gpt4Enhancements.chainOfThoughtAdded = true;
        gpt4Enhancements.reasoningStepsAdded = cotResult.stepsAdded;
      }
    }

    if (this.config.optimizeSystemMessages) {
      const systemResult = this.optimizeSystemMessage(optimizedPrompt, task);
      optimizedPrompt = systemResult.prompt;
      if (systemResult.applied) {
        techniquesApplied.push('System message optimization');
        gpt4Enhancements.systemMessageOptimized = true;
      }
    }

    if (this.config.optimizeForStructuredOutput && context?.expectedFormat) {
      const structureResult = this.addStructuredOutput(
        optimizedPrompt,
        context.expectedFormat
      );
      optimizedPrompt = structureResult.prompt;
      if (structureResult.applied) {
        techniquesApplied.push('Structured output formatting');
        gpt4Enhancements.structuredOutputEnabled = true;
      }
    }

    if (this.config.enableAdvancedReasoning) {
      const reasoningResult = this.enhanceReasoning(optimizedPrompt, context);
      optimizedPrompt = reasoningResult.prompt;
      if (reasoningResult.applied) {
        techniquesApplied.push('Advanced reasoning patterns');
      }
    }

    // Apply GPT-4 specific prompt patterns
    optimizedPrompt = this.applyGPT4Patterns(optimizedPrompt, context);
    techniquesApplied.push('GPT-4 specific patterns');

    // Ensure token limits
    const tokenOptimizedResult = this.optimizeTokenUsage(optimizedPrompt);
    optimizedPrompt = tokenOptimizedResult.prompt;
    if (tokenOptimizedResult.tokensReduced > 0) {
      techniquesApplied.push(
        `Token optimization (-${tokenOptimizedResult.tokensReduced} tokens)`
      );
    }

    // Calculate improvements
    const improvements = this.calculateImprovements(prompt, optimizedPrompt);

    const result: GPT4OptimizationResult = {
      optimizedPrompt,
      techniquesApplied,
      estimatedTokens: this.estimateTokens(optimizedPrompt),
      improvements,
      gpt4Enhancements,
    };

    logger.info(
      `GPT-4 optimization completed: ${techniquesApplied.length} techniques applied`
    );
    return result;
  }

  /**
   * Add chain-of-thought reasoning patterns optimized for GPT-4
   */
  private addChainOfThought(
    prompt: string,
    context?: { complexity?: string; domain?: string }
  ): { prompt: string; applied: boolean; stepsAdded: number } {
    // Check if chain-of-thought is already present
    if (this.hasChainOfThought(prompt)) {
      return { prompt, applied: false, stepsAdded: 0 };
    }

    let cotPrompt = prompt;
    let stepsAdded = 0;

    // Add appropriate CoT based on complexity
    if (context?.complexity === 'complex') {
      cotPrompt += "\n\nLet's approach this step-by-step:\n";
      cotPrompt += '1. First, analyze the key requirements\n';
      cotPrompt += '2. Break down the problem into components\n';
      cotPrompt += '3. Consider potential challenges and solutions\n';
      cotPrompt += '4. Synthesize a comprehensive response\n';
      cotPrompt +=
        '\nPlease work through each step explicitly before providing your final answer.';
      stepsAdded = 4;
    } else if (context?.complexity === 'medium') {
      cotPrompt += "\n\nLet's think through this systematically:\n";
      cotPrompt += '1. Identify the main objective\n';
      cotPrompt += '2. Consider the key factors involved\n';
      cotPrompt += '3. Formulate the best approach\n';
      stepsAdded = 3;
    } else {
      // Simple complexity or default
      cotPrompt += '\n\nPlease think step-by-step before responding:';
      stepsAdded = 1;
    }

    return { prompt: cotPrompt, applied: true, stepsAdded };
  }

  /**
   * Optimize system message for GPT-4
   */
  private optimizeSystemMessage(
    prompt: string,
    task: string
  ): { prompt: string; applied: boolean } {
    // Check if system message pattern exists
    if (prompt.includes('You are') || prompt.includes('System:')) {
      return { prompt, applied: false };
    }

    // Add optimized system message for GPT-4
    let systemMessage =
      'You are an expert assistant designed to provide accurate, helpful, and comprehensive responses. ';

    // Customize based on task
    if (
      task.toLowerCase().includes('code') ||
      task.toLowerCase().includes('programming')
    ) {
      systemMessage +=
        'You excel at code analysis, debugging, and software development guidance. ';
    } else if (
      task.toLowerCase().includes('analysis') ||
      task.toLowerCase().includes('research')
    ) {
      systemMessage +=
        'You excel at analytical thinking, research, and providing well-reasoned insights. ';
    } else if (
      task.toLowerCase().includes('creative') ||
      task.toLowerCase().includes('writing')
    ) {
      systemMessage +=
        'You excel at creative thinking and effective communication. ';
    }

    systemMessage +=
      'Always strive for clarity, accuracy, and usefulness in your responses.';

    const optimizedPrompt = `${systemMessage}\n\n${prompt}`;
    return { prompt: optimizedPrompt, applied: true };
  }

  /**
   * Add structured output formatting for GPT-4
   */
  private addStructuredOutput(
    prompt: string,
    format: 'json' | 'markdown' | 'code' | 'text'
  ): { prompt: string; applied: boolean } {
    // Check if structured output already specified
    if (
      prompt.toLowerCase().includes('format') &&
      prompt.toLowerCase().includes(format)
    ) {
      return { prompt, applied: false };
    }

    let structurePrompt = prompt;

    switch (format) {
      case 'json':
        structurePrompt +=
          '\n\nPlease provide your response in valid JSON format with appropriate structure and field names.';
        break;
      case 'markdown':
        structurePrompt +=
          '\n\nPlease format your response using Markdown with appropriate headers, lists, and formatting for clarity.';
        break;
      case 'code':
        structurePrompt +=
          '\n\nPlease provide code examples with appropriate syntax highlighting and clear explanations.';
        break;
      default:
        structurePrompt +=
          '\n\nPlease structure your response clearly with logical organization and appropriate formatting.';
    }

    return { prompt: structurePrompt, applied: true };
  }

  /**
   * Enhance reasoning patterns for GPT-4's advanced capabilities
   */
  private enhanceReasoning(
    prompt: string,
    context?: { domain?: string; complexity?: string }
  ): { prompt: string; applied: boolean } {
    let enhancedPrompt = prompt;

    // Add domain-specific reasoning patterns
    if (context?.domain) {
      enhancedPrompt += `\n\nWhen addressing this ${context.domain} question, please:`;
      enhancedPrompt += '\n- Draw upon relevant domain expertise';
      enhancedPrompt += '\n- Consider multiple perspectives and approaches';
      enhancedPrompt += '\n- Provide evidence-based reasoning';
      enhancedPrompt += '\n- Acknowledge any limitations or assumptions';
    }

    // Add metacognitive prompting for complex tasks
    if (context?.complexity === 'complex') {
      enhancedPrompt += '\n\nBefore responding, please:';
      enhancedPrompt += '\n1. Assess the complexity and scope of the question';
      enhancedPrompt += '\n2. Identify potential edge cases or complications';
      enhancedPrompt += '\n3. Consider alternative solutions or viewpoints';
      enhancedPrompt += '\n4. Verify your reasoning for logical consistency';
    }

    return { prompt: enhancedPrompt, applied: enhancedPrompt !== prompt };
  }

  /**
   * Apply GPT-4 specific prompt patterns and best practices
   */
  private applyGPT4Patterns(
    prompt: string,
    _context?: { expectedFormat?: string; domain?: string }
  ): string {
    let patterned = prompt;

    // Add GPT-4 specific instruction clarity
    if (!patterned.includes('Please') && !patterned.includes('I need')) {
      patterned = `Please ${patterned.charAt(0).toLowerCase()}${patterned.slice(1)}`;
    }

    // Optimize for GPT-4's instruction following
    patterned = patterned.replace(
      /\b(do|make|create|write|generate)\b/gi,
      match => `please ${match.toLowerCase()}`
    );

    // Add context preservation for long conversations
    if (patterned.length > 1000) {
      patterned +=
        '\n\n(Please maintain context from this entire prompt when responding.)';
    }

    return patterned;
  }

  /**
   * Optimize token usage for GPT-4's context window
   */
  private optimizeTokenUsage(prompt: string): {
    prompt: string;
    tokensReduced: number;
  } {
    const originalTokens = this.estimateTokens(prompt);

    if (originalTokens <= this.config.maxTokens) {
      return { prompt, tokensReduced: 0 };
    }

    let optimized = prompt;

    // Remove redundant whitespace
    optimized = optimized.replace(/\s+/g, ' ').trim();

    // Compress repeated patterns
    optimized = optimized.replace(/(\n\s*){3,}/g, '\n\n');

    // Simplify verbose expressions while maintaining meaning
    const replacements = [
      [/in order to/gi, 'to'],
      [/due to the fact that/gi, 'because'],
      [/it is important to note that/gi, 'note that'],
      [/please be aware that/gi, 'note that'],
      [/it should be mentioned that/gi, ''],
      [/as previously mentioned/gi, ''],
    ];

    for (const [pattern, replacement] of replacements) {
      optimized = optimized.replace(pattern as RegExp, replacement as string);
    }

    const newTokens = this.estimateTokens(optimized);
    const tokensReduced = originalTokens - newTokens;

    return { prompt: optimized, tokensReduced };
  }

  /**
   * Check if chain-of-thought is already present
   */
  private hasChainOfThought(prompt: string): boolean {
    const cotPatterns = [
      /step.by.step/i,
      /think through/i,
      /let's approach/i,
      /first.*second.*third/i,
      /\d+\.\s/g, // Numbered lists
    ];

    return cotPatterns.some(pattern => pattern.test(prompt));
  }

  /**
   * Estimate token count (rough approximation)
   */
  private estimateTokens(text: string): number {
    // GPT-4 tokenization is roughly 4 characters per token for English
    return Math.ceil(text.length / 4);
  }

  /**
   * Calculate improvement scores
   */
  private calculateImprovements(original: string, optimized: string) {
    // This is a simplified scoring system - in production, you'd want more sophisticated metrics
    const originalLength = original.length;
    const optimizedLength = optimized.length;

    // Structure score based on formatting and organization
    const structureScore = this.calculateStructureScore(optimized);

    // Clarity score based on specific phrases and patterns
    const clarityScore = this.calculateClarityScore(optimized);

    // Reasoning score based on CoT and reasoning patterns
    const reasoningScore = this.calculateReasoningScore(optimized);

    // Efficiency score based on token usage
    const efficiencyScore = Math.max(
      0,
      100 - ((optimizedLength - originalLength) / originalLength) * 100
    );

    return {
      clarityScore: Math.min(100, clarityScore),
      reasoningScore: Math.min(100, reasoningScore),
      structureScore: Math.min(100, structureScore),
      efficiencyScore: Math.min(100, Math.max(0, efficiencyScore)),
    };
  }

  private calculateStructureScore(text: string): number {
    let score = 60; // Base score

    // Award points for good structure
    if (text.includes('\n\n')) score += 10; // Paragraphs
    if (/\d+\./.test(text)) score += 10; // Numbered lists
    if (/[-*]/.test(text)) score += 5; // Bullet points
    if (text.includes(':')) score += 5; // Colons for structure

    return score;
  }

  private calculateClarityScore(text: string): number {
    let score = 60; // Base score

    // Award points for clarity indicators
    if (text.toLowerCase().includes('please')) score += 5;
    if (text.toLowerCase().includes('specific')) score += 5;
    if (text.toLowerCase().includes('clear')) score += 5;
    if (text.toLowerCase().includes('detailed')) score += 5;

    // Deduct for unclear patterns
    if (text.includes('...')) score -= 5;
    if (text.length > 2000) score -= 10; // Too verbose

    return score;
  }

  private calculateReasoningScore(text: string): number {
    let score = 50; // Base score

    // Award points for reasoning patterns
    if (text.toLowerCase().includes('step')) score += 15;
    if (text.toLowerCase().includes('because')) score += 10;
    if (text.toLowerCase().includes('therefore')) score += 10;
    if (text.toLowerCase().includes('consider')) score += 10;
    if (text.toLowerCase().includes('analyze')) score += 10;

    return score;
  }

  /**
   * Get GPT-4 specific recommendations
   */
  getGPT4Recommendations(): {
    bestPractices: string[];
    avoidances: string[];
    tips: string[];
  } {
    return {
      bestPractices: [
        'Use clear, specific instructions for better results',
        'Leverage chain-of-thought reasoning for complex tasks',
        'Structure prompts with numbered steps when appropriate',
        'Include context and constraints explicitly',
        'Use system messages to set the right tone and expertise level',
        'Specify desired output format clearly',
      ],
      avoidances: [
        'Avoid overly verbose or redundant instructions',
        "Don't use ambiguous language or unclear references",
        'Avoid assuming GPT-4 knows your specific context without explaining',
        "Don't overload with too many simultaneous requests",
      ],
      tips: [
        'GPT-4 excels at following structured, step-by-step instructions',
        'Use temperature 0.1-0.3 for focused, deterministic outputs',
        'Break complex tasks into smaller, manageable components',
        'Provide examples when the desired format is specific',
        'Use metacognitive prompting for complex reasoning tasks',
      ],
    };
  }
}

export default GPT4Optimizer;

/**
 * @fileoverview Llama and open-source model optimization strategies
 * @lastmodified 2025-08-26T14:30:00Z
 *
 * Features: Open-source model optimization, fine-tuning support, efficiency patterns
 * Main APIs: LlamaOptimizer class with open-source model optimization strategies
 * Constraints: Optimized for varying context windows and model capabilities
 * Patterns: Strategy pattern, model-agnostic optimization, efficiency-focused tuning
 */

import { logger } from '../../utils/logger';

export interface LlamaOptimizationConfig {
  maxTokens: number;
  model:
    | 'llama-2-7b'
    | 'llama-2-13b'
    | 'llama-2-70b'
    | 'codellama'
    | 'mistral'
    | 'custom';
  enableInstructionTuning: boolean;
  optimizeForEfficiency: boolean;
  enableFewShotPrompting: boolean;
  customModelConfig?: {
    contextWindow: number;
    instructionFormat?: string;
    stopTokens?: string[];
  };
}

export interface LlamaOptimizationResult {
  optimizedPrompt: string;
  techniquesApplied: string[];
  estimatedTokens: number;
  improvements: {
    efficiencyScore: number;
    clarityScore: number;
    instructionScore: number;
    compatibilityScore: number;
  };
  llamaEnhancements: {
    instructionFormatOptimized: boolean;
    fewShotExamplesAdded: boolean;
    efficiencyImproved: boolean;
    modelSpecificTuning: boolean;
  };
}

export class LlamaOptimizer {
  private readonly defaultConfig: LlamaOptimizationConfig = {
    maxTokens: 4096, // Conservative default for smaller models
    model: 'llama-2-7b',
    enableInstructionTuning: true,
    optimizeForEfficiency: true,
    enableFewShotPrompting: true,
  };

  constructor(
    private config: LlamaOptimizationConfig = {} as LlamaOptimizationConfig
  ) {
    this.config = { ...this.defaultConfig, ...config };
  }

  async optimizeForLlama(
    prompt: string,
    task: string,
    context?: {
      examples?: Array<{ input: string; output: string }>;
      domain?: string;
      complexity?: 'simple' | 'medium' | 'complex';
    }
  ): Promise<LlamaOptimizationResult> {
    logger.info(`Optimizing prompt for ${this.config.model} capabilities`);

    let optimizedPrompt = prompt;
    const techniquesApplied: string[] = [];
    const llamaEnhancements = {
      instructionFormatOptimized: false,
      fewShotExamplesAdded: false,
      efficiencyImproved: false,
      modelSpecificTuning: false,
    };

    // Apply instruction format optimization
    if (this.config.enableInstructionTuning) {
      const instructionResult = this.optimizeInstructionFormat(
        optimizedPrompt,
        task
      );
      optimizedPrompt = instructionResult.prompt;
      if (instructionResult.applied) {
        techniquesApplied.push('Instruction format optimization');
        llamaEnhancements.instructionFormatOptimized = true;
      }
    }

    // Add few-shot examples if available
    if (this.config.enableFewShotPrompting && context?.examples) {
      const fewShotResult = this.addFewShotExamples(
        optimizedPrompt,
        context.examples
      );
      optimizedPrompt = fewShotResult.prompt;
      if (fewShotResult.applied) {
        techniquesApplied.push('Few-shot example integration');
        llamaEnhancements.fewShotExamplesAdded = true;
      }
    }

    // Apply efficiency optimizations
    if (this.config.optimizeForEfficiency) {
      const efficiencyResult = this.optimizeForEfficiency(optimizedPrompt);
      optimizedPrompt = efficiencyResult.prompt;
      if (efficiencyResult.applied) {
        techniquesApplied.push('Efficiency optimization');
        llamaEnhancements.efficiencyImproved = true;
      }
    }

    // Apply model-specific tuning
    const tuningResult = this.applyModelSpecificTuning(optimizedPrompt);
    optimizedPrompt = tuningResult.prompt;
    if (tuningResult.applied) {
      techniquesApplied.push('Model-specific tuning');
      llamaEnhancements.modelSpecificTuning = true;
    }

    // Apply general open-source patterns
    optimizedPrompt = this.applyOpenSourcePatterns(optimizedPrompt);
    techniquesApplied.push('Open-source model patterns');

    const improvements = this.calculateImprovements(
      prompt,
      optimizedPrompt,
      context
    );

    return {
      optimizedPrompt,
      techniquesApplied,
      estimatedTokens: this.estimateTokens(optimizedPrompt),
      improvements,
      llamaEnhancements,
    };
  }

  private optimizeInstructionFormat(
    prompt: string,
    _task: string
  ): { prompt: string; applied: boolean } {
    // Check if instruction format already present
    if (this.hasInstructionFormat(prompt)) {
      return { prompt, applied: false };
    }

    let formatted = '';

    // Llama/Alpaca instruction format
    if (this.config.model.includes('llama') || this.config.model === 'custom') {
      formatted = '### Instruction:\n';
      formatted += `${prompt}\n\n`;
      formatted += '### Response:\n';
    } else if (this.config.model.includes('mistral')) {
      formatted = `[INST] ${prompt} [/INST]`;
    } else {
      // Generic instruction format
      formatted = `Instructions: ${prompt}\n\nResponse:`;
    }

    return { prompt: formatted, applied: true };
  }

  private addFewShotExamples(
    prompt: string,
    examples: Array<{ input: string; output: string }>
  ): { prompt: string; applied: boolean } {
    if (examples.length === 0) {
      return { prompt, applied: false };
    }

    // Limit examples to avoid token overflow
    const maxExamples = Math.min(examples.length, 3);
    const selectedExamples = examples.slice(0, maxExamples);

    let fewShotPrompt = prompt;

    // Add examples before the main instruction
    if (this.hasInstructionFormat(prompt)) {
      // Insert examples before the main instruction
      const instructionIndex = prompt.indexOf('### Instruction:');
      if (instructionIndex !== -1) {
        let exampleSection = '### Examples:\n';
        selectedExamples.forEach((example, index) => {
          exampleSection += `Example ${index + 1}:\n`;
          exampleSection += `Input: ${example.input}\n`;
          exampleSection += `Output: ${example.output}\n\n`;
        });

        fewShotPrompt =
          prompt.slice(0, instructionIndex) +
          exampleSection +
          prompt.slice(instructionIndex);
      }
    } else {
      // Add examples at the beginning
      let exampleSection = 'Here are some examples:\n\n';
      selectedExamples.forEach((example, index) => {
        exampleSection += `Example ${index + 1}:\n`;
        exampleSection += `Input: ${example.input}\n`;
        exampleSection += `Output: ${example.output}\n\n`;
      });

      fewShotPrompt = exampleSection + prompt;
    }

    return { prompt: fewShotPrompt, applied: true };
  }

  private optimizeForEfficiency(prompt: string): {
    prompt: string;
    applied: boolean;
  } {
    let optimized = prompt;
    let applied = false;

    // Remove verbose language that doesn't add value
    const redundancies = [
      [/please note that/gi, ''],
      [/it is important to/gi, ''],
      [/you should understand that/gi, ''],
      [/keep in mind that/gi, ''],
      [/as you can see/gi, ''],
    ];

    for (const [pattern, replacement] of redundancies) {
      const original = optimized;
      optimized = optimized.replace(pattern as RegExp, replacement as string);
      if (optimized !== original) applied = true;
    }

    // Simplify complex phrases
    const simplifications = [
      [/in order to accomplish/gi, 'to'],
      [/for the purpose of/gi, 'to'],
      [/with the intention of/gi, 'to'],
      [/due to the fact that/gi, 'because'],
    ];

    for (const [pattern, replacement] of simplifications) {
      const original = optimized;
      optimized = optimized.replace(pattern as RegExp, replacement as string);
      if (optimized !== original) applied = true;
    }

    // Clean up extra whitespace
    const original = optimized;
    optimized = optimized.replace(/\s+/g, ' ').trim();
    if (optimized !== original) applied = true;

    return { prompt: optimized, applied };
  }

  private applyModelSpecificTuning(prompt: string): {
    prompt: string;
    applied: boolean;
  } {
    let tuned = prompt;
    let applied = false;

    // Model-specific optimizations
    switch (this.config.model) {
      case 'codellama':
        if (
          prompt.toLowerCase().includes('code') ||
          prompt.toLowerCase().includes('programming')
        ) {
          tuned += '\n\nProvide complete, working code with comments.';
          applied = true;
        }
        break;

      case 'llama-2-70b':
        // Leverage larger model's reasoning capabilities
        if (!prompt.includes('step-by-step') && !prompt.includes('reasoning')) {
          tuned += '\n\nExplain your reasoning step-by-step.';
          applied = true;
        }
        break;

      case 'mistral':
        // Mistral works well with direct, concise instructions
        tuned = tuned.replace(/could you please/gi, '');
        tuned = tuned.replace(/if possible/gi, '');
        applied = true;
        break;

      case 'custom':
        if (this.config.customModelConfig?.instructionFormat) {
          // Apply custom instruction format if specified
          tuned = this.config.customModelConfig.instructionFormat.replace(
            '{prompt}',
            tuned
          );
          applied = true;
        }
        break;

      default:
        // For unrecognized models, apply general optimizations
        break;
    }

    return { prompt: tuned, applied };
  }

  private applyOpenSourcePatterns(prompt: string): string {
    let patterned = prompt;

    // Open-source models often work better with explicit instructions
    patterned = patterned.replace(/maybe/gi, '');
    patterned = patterned.replace(/perhaps/gi, '');
    patterned = patterned.replace(/if you can/gi, '');

    // Add explicit output formatting if not present
    if (!patterned.includes('format') && !patterned.includes('structure')) {
      patterned += '\n\nProvide a clear, well-structured response.';
    }

    return patterned;
  }

  private hasInstructionFormat(prompt: string): boolean {
    const instructionPatterns = [
      /### Instruction:/i,
      /### Response:/i,
      /\[INST\]/i,
      /\[\/INST\]/i,
      /Instructions:/i,
      /Response:/i,
    ];

    return instructionPatterns.some(pattern => pattern.test(prompt));
  }

  private estimateTokens(text: string): number {
    // Open-source tokenization approximation (varies by model)
    return Math.ceil(text.length / 3.5);
  }

  private calculateImprovements(
    _original: string,
    optimized: string,
    _context?: Record<string, unknown>
  ) {
    const efficiencyScore = this.calculateEfficiencyScore(optimized);
    const clarityScore = this.calculateClarityScore(optimized);
    const instructionScore = this.calculateInstructionScore(optimized);
    const compatibilityScore = this.calculateCompatibilityScore(optimized);

    return {
      efficiencyScore: Math.min(100, efficiencyScore),
      clarityScore: Math.min(100, clarityScore),
      instructionScore: Math.min(100, instructionScore),
      compatibilityScore: Math.min(100, compatibilityScore),
    };
  }

  private calculateEfficiencyScore(text: string): number {
    let score = 60;

    // Award for conciseness
    if (text.length < 300) score += 20;
    else if (text.length < 600) score += 10;

    // Award for direct language
    if (!text.includes('perhaps') && !text.includes('maybe')) score += 15;

    return score;
  }

  private calculateClarityScore(text: string): number {
    let score = 60;

    if (text.includes('clear')) score += 10;
    if (text.includes('specific')) score += 10;
    if (text.includes(':')) score += 5;
    if (text.includes('\n')) score += 5;

    return score;
  }

  private calculateInstructionScore(text: string): number {
    let score = 50;

    if (this.hasInstructionFormat(text)) score += 20;
    if (text.includes('Example')) score += 15;
    if (text.includes('step-by-step')) score += 10;
    if (text.includes('Response:')) score += 5;

    return score;
  }

  private calculateCompatibilityScore(text: string): number {
    let score = 70;

    // Award for open-source model compatibility
    if (text.includes('### Instruction:')) score += 15;
    if (text.includes('[INST]')) score += 15;
    if (!text.includes('maybe') && !text.includes('perhaps')) score += 10;

    return score;
  }

  getLlamaRecommendations(): {
    bestPractices: string[];
    modelSpecific: Record<string, string[]>;
    efficiencyTips: string[];
  } {
    return {
      bestPractices: [
        'Use clear instruction formats (### Instruction: / ### Response:)',
        'Provide few-shot examples for complex tasks',
        'Be explicit and direct in instructions',
        'Structure prompts with clear sections',
        'Test with different context lengths',
      ],
      modelSpecific: {
        'llama-2-7b': [
          'Keep prompts concise due to smaller context window',
          'Use simple, direct language',
          'Provide clear examples for better performance',
        ],
        'llama-2-70b': [
          'Leverage reasoning capabilities with step-by-step requests',
          'Can handle more complex, multi-part instructions',
          'Benefits from detailed context and examples',
        ],
        codellama: [
          'Specify programming language clearly',
          'Request complete, working code examples',
          'Include relevant context about code requirements',
        ],
        mistral: [
          'Use [INST] instruction format for best results',
          'Keep instructions clear and concise',
          'Works well with direct, imperative statements',
        ],
      },
      efficiencyTips: [
        'Remove unnecessary filler words and phrases',
        'Use direct imperative statements',
        'Limit few-shot examples to 3 or fewer',
        'Structure content with clear delimiters',
        'Test token usage with your specific model',
      ],
    };
  }
}

export default LlamaOptimizer;

/**
 * @fileoverview Google AI (Gemini) platform adapter for prompt optimization
 * @lastmodified 2025-08-26T14:45:00Z
 *
 * Features: Google AI API format adaptation, multimodal support, efficiency optimization
 * Main APIs: GoogleAdapter class for converting optimized prompts to Google AI format
 * Constraints: Google AI API specifications and token limits
 * Patterns: Adapter pattern, multimodal integration, efficiency-focused formatting
