    temperature?: number;
    topK?: number;
    topP?: number;
    maxOutputTokens?: number;
    stopSequences?: string[];
    candidateCount?: number;
  };
  safetySettings?: Array<{
    category: string;
    threshold: string;
  }>;
  systemInstruction?: {
    parts: Array<{ text: string }>;
  };
}

export interface GoogleAdapterConfig {
  model:
    | 'gemini-pro'
    | 'gemini-pro-vision'
    | 'gemini-1.5-pro'
    | 'gemini-1.5-flash';
  maxOutputTokens: number;
  temperature: number;
  topK: number;
  topP: number;
  enableMultimodal: boolean;
  enableSafetySettings: boolean;
  optimizeForEfficiency: boolean;
}

export interface GoogleAdaptationResult {
  request: GoogleRequest;
  tokenEstimate: number;
  adaptationNotes: string[];
  warnings: string[];
  multimodalFeatures?: {
    imageSupport: boolean;
    fileSupport: boolean;
    optimizedForVision: boolean;
  };
}

export class GoogleAdapter {
  private readonly defaultConfig: GoogleAdapterConfig = {
    model: 'gemini-1.5-pro',
    maxOutputTokens: 8192,
    temperature: 0.4,
    topK: 40,
    topP: 0.95,
    enableMultimodal: true,
    enableSafetySettings: true,
    optimizeForEfficiency: true,
  };

  private readonly modelLimits = {
    'gemini-pro': 32000,
    'gemini-pro-vision': 16000,
    'gemini-1.5-pro': 2000000,
    'gemini-1.5-flash': 1000000,
  };

  constructor(private config: GoogleAdapterConfig = {} as GoogleAdapterConfig) {
    this.config = { ...this.defaultConfig, ...config };
  }

  /**
   * Convert optimized prompt to Google AI API format
   */
  adaptToGoogle(
    optimizedPrompt: string,
    context?: {
      systemMessage?: string;
      conversationHistory?: Array<{ role: string; content: string }>;
      images?: Array<{ data: string; mimeType: string }>;
      files?: Array<{ data: string; mimeType: string }>;
      safetyLevel?: 'strict' | 'moderate' | 'permissive';
    }
  ): GoogleAdaptationResult {
    logger.info(`Adapting prompt for Google ${this.config.model}`);

    const contents: GoogleContent[] = [];
    const adaptationNotes: string[] = [];
    const warnings: string[] = [];
    let multimodalFeatures: Record<string, unknown> = {};

    // Add conversation history if available
    if (context?.conversationHistory) {
      const historyContents = this.formatConversationHistory(
        context.conversationHistory
      );
      contents.push(...historyContents);
      adaptationNotes.push(
        `Added ${historyContents.length} conversation history entries`
      );
    }

    // Process the main prompt
    const processedPrompt = this.processPromptForGoogle(optimizedPrompt);

    // Create main user content
    const userContent: GoogleContent = {
      parts: [{ text: processedPrompt }],
      role: 'user',
    };

    // Add multimodal content if available and supported
    if (this.config.enableMultimodal && this.supportsMultimodal()) {
      const multimodalResult = this.addMultimodalContent(userContent, context);
      if (multimodalResult.added) {
        multimodalFeatures = multimodalResult.features;
        adaptationNotes.push(...multimodalResult.notes);
      }
    }

    contents.push(userContent);

    // Check token limits
    const totalTokens = this.estimateTokens(contents);
    const modelLimit = this.modelLimits[this.config.model];

    if (totalTokens > modelLimit * 0.8) {
      // Leave room for response
      warnings.push(
        `Prompt approaching model limit (${totalTokens}/${modelLimit} tokens)`
      );
    }

    // Build the request
    const request: GoogleRequest = {
      model: this.config.model,
      contents,
      generationConfig: {
        temperature: this.config.temperature,
        topK: this.config.topK,
        topP: this.config.topP,
        maxOutputTokens: this.config.maxOutputTokens,
      },
    };

    // Add system instruction if available
    if (context?.systemMessage) {
      const systemInstruction = this.formatSystemInstruction(
        context.systemMessage
      );
      request.systemInstruction = systemInstruction;
      adaptationNotes.push('System instruction formatted for Google AI');
    }

    // Add safety settings if enabled
    if (this.config.enableSafetySettings) {
      request.safetySettings = this.createSafetySettings(context?.safetyLevel);
      adaptationNotes.push('Safety settings configured');
    }

    return {
      request,
      tokenEstimate: totalTokens,
      adaptationNotes,
      warnings,
      multimodalFeatures:
        Object.keys(multimodalFeatures).length > 0
          ? multimodalFeatures
          : undefined,
    };
  }

  /**
   * Process prompt for Google AI optimization
   */
  private processPromptForGoogle(prompt: string): string {
    let processed = prompt;

    if (this.config.optimizeForEfficiency) {
      // Remove verbose language
      processed = processed.replace(/please note that/gi, '');
      processed = processed.replace(/it is important to understand that/gi, '');
      processed = processed.replace(/you should know that/gi, '');

      // Simplify complex phrases
      processed = processed.replace(/in order to/gi, 'to');
      processed = processed.replace(/for the purpose of/gi, 'to');
      processed = processed.replace(/due to the fact that/gi, 'because');
    }

    // Optimize for Gemini's instruction following
    if (!processed.includes('Please') && !processed.startsWith('I need')) {
      processed = `Please ${processed.charAt(0).toLowerCase()}${processed.slice(1)}`;
    }

    // Clean up whitespace
    processed = processed.replace(/\s+/g, ' ').trim();

    return processed;
  }

  /**
   * Format conversation history for Google AI
   */
  private formatConversationHistory(
    history: Array<{ role: string; content: string }>
  ): GoogleContent[] {
    const contents: GoogleContent[] = [];

    for (const msg of history) {
      const role = this.normalizeRole(msg.role);
      if (role) {
        contents.push({
          parts: [{ text: msg.content }],
          role,
        });
      }
    }

    // Ensure alternating pattern (Google AI requirement)
    return this.ensureAlternatingPattern(contents);
  }

  /**
   * Add multimodal content (images, files)
   */
  private addMultimodalContent(
    userContent: GoogleContent,
    context?: {
      images?: Array<{ data: string; mimeType: string }>;
      files?: Array<{ data: string; mimeType: string }>;
    }
  ): { added: boolean; features: Record<string, unknown>; notes: string[] } {
    const features = {
      imageSupport: false,
      fileSupport: false,
      optimizedForVision: false,
    };
    const notes: string[] = [];
    let added = false;

    // Add images if available
    if (context?.images && context.images.length > 0) {
      for (const image of context.images) {
        userContent.parts.push({
          inlineData: {
            mimeType: image.mimeType,
            data: image.data,
          },
        });
      }
      features.imageSupport = true;
      features.optimizedForVision = this.config.model.includes('vision');
      notes.push(`Added ${context.images.length} images`);
      added = true;
    }

    // Add files if available (for supported models)
    if (context?.files && context.files.length > 0) {
      const supportedMimeTypes = [
        'application/pdf',
        'text/plain',
        'text/csv',
        'application/json',
      ];

      for (const file of context.files) {
        if (supportedMimeTypes.includes(file.mimeType)) {
          userContent.parts.push({
            inlineData: {
              mimeType: file.mimeType,
              data: file.data,
            },
          });
          features.fileSupport = true;
          added = true;
        }
      }

      if (features.fileSupport) {
        notes.push(`Added ${context.files.length} files`);
      }
    }

    return { added, features, notes };
  }

  /**
   * Format system instruction for Google AI
   */
  private formatSystemInstruction(systemMessage: string): {
    parts: Array<{ text: string }>;
  } {
    // Enhance system message for Google AI
    let enhanced = systemMessage;

    // Add Google AI specific guidance
    if (!enhanced.includes('accurate') && !enhanced.includes('helpful')) {
      enhanced +=
        ' Provide accurate, helpful responses based on the given instructions.';
    }

    // Add efficiency guidance for Gemini
    if (this.config.optimizeForEfficiency) {
      enhanced +=
        ' Be concise and direct in your responses while maintaining completeness.';
    }

    return {
      parts: [{ text: enhanced }],
    };
  }

  /**
   * Create safety settings based on safety level
   */
  private createSafetySettings(
    safetyLevel?: 'strict' | 'moderate' | 'permissive'
  ) {
    const level = safetyLevel || 'moderate';

    const thresholds = {
      strict: 'BLOCK_LOW_AND_ABOVE',
      moderate: 'BLOCK_MEDIUM_AND_ABOVE',
      permissive: 'BLOCK_ONLY_HIGH',
    };

    const threshold = thresholds[level];

    return [
      {
        category: 'HARM_CATEGORY_HARASSMENT',
        threshold,
      },
      {
        category: 'HARM_CATEGORY_HATE_SPEECH',
        threshold,
      },
      {
        category: 'HARM_CATEGORY_SEXUALLY_EXPLICIT',
        threshold,
      },
      {
        category: 'HARM_CATEGORY_DANGEROUS_CONTENT',
        threshold,
      },
    ];
  }

  /**
   * Check if current model supports multimodal input
   */
  private supportsMultimodal(): boolean {
    return (
      this.config.model.includes('vision') || this.config.model.includes('1.5')
    );
  }

  /**
   * Normalize role names for Google AI
   */
  private normalizeRole(role: string): 'user' | 'model' | null {
    const normalizedRole = role.toLowerCase();
    if (normalizedRole === 'user' || normalizedRole === 'human') return 'user';
    if (
      normalizedRole === 'assistant' ||
      normalizedRole === 'ai' ||
      normalizedRole === 'model'
    )
      return 'model';
    return null;
  }

  /**
   * Ensure alternating user/model pattern
   */
  private ensureAlternatingPattern(contents: GoogleContent[]): GoogleContent[] {
    const result: GoogleContent[] = [];
    let lastRole: string | undefined;

    for (const content of contents) {
      if (content.role !== lastRole) {
        result.push(content);
        lastRole = content.role;
      } else if (
        result.length > 0 &&
        result[result.length - 1].role === content.role
      ) {
        // Combine with previous content of same role
        const lastContent = result[result.length - 1];
        const newText = content.parts.map(p => p.text).join(' ');
        lastContent.parts[0].text += ` ${newText}`;
      }
    }

    return result;
  }

  /**
   * Estimate token count for Google AI models
   */
  private estimateTokens(contents: GoogleContent[]): number {
    let totalTokens = 0;

    for (const content of contents) {
      for (const part of content.parts) {
        if (part.text) {
          // Google AI tokenization approximation
          totalTokens += Math.ceil(part.text.length / 4);
        }
        if (part.inlineData) {
          // Estimate for multimodal content
          totalTokens += 1000; // Rough estimate for images/files
        }
      }
    }

    return totalTokens;
  }

  /**
   * Get Google AI specific recommendations
   */
  getGoogleRecommendations(): {
    bestPractices: string[];
    multimodal: string[];
    efficiency: string[];
    modelSpecific: Record<string, string[]>;
  } {
    return {
      bestPractices: [
        'Use clear, direct instructions for best results',
        'Structure prompts with logical flow',
        'Provide specific examples when needed',
        'Use appropriate safety settings for your use case',
        'Consider multimodal capabilities for vision models',
      ],
      multimodal: [
        'Use Gemini Pro Vision or 1.5 models for image analysis',
        'Provide clear context about what to analyze in images',
        'Combine text and visual information effectively',
        'Consider file upload capabilities for document analysis',
      ],
      efficiency: [
        'Remove redundant words and phrases',
        'Use direct, imperative language',
        'Structure requests with clear objectives',
        "Leverage Gemini's efficiency optimizations",
      ],
      modelSpecific: {
        'gemini-pro': [
          'Best for text-only tasks',
          'Fast and efficient responses',
          'Good for general conversations and analysis',
        ],
        'gemini-pro-vision': [
          'Designed for image analysis tasks',
          'Can process images alongside text',
          'Good for visual question answering',
        ],
        'gemini-1.5-pro': [
          'Largest context window (2M tokens)',
          'Best for complex, long-form tasks',
          'Supports multimodal input (text, images, files)',
        ],
        'gemini-1.5-flash': [
          'Fastest response times',
          'Good balance of speed and capability',
          'Supports multimodal with 1M token context',
        ],
      },
    };
  }

  /**
   * Validate Google AI request format
   */
  validateRequest(request: GoogleRequest): {
    valid: boolean;
    errors: string[];
  } {
    const errors: string[] = [];

    if (!request.model) {
      errors.push('Model is required');
    }

    if (!request.contents || request.contents.length === 0) {
      errors.push('At least one content entry is required');
    }

    if (request.contents) {
      for (const content of request.contents) {
        if (!content.parts || content.parts.length === 0) {
          errors.push('Each content entry must have at least one part');
        }

        for (const part of content.parts) {
          if (!part.text && !part.inlineData) {
            errors.push('Each part must have either text or inlineData');
          }
        }
      }

      // Check alternating pattern
      for (let i = 1; i < request.contents.length; i++) {
        if (request.contents[i].role === request.contents[i - 1].role) {
          errors.push('Contents should alternate between user and model roles');
          break;
        }
      }
    }

    if (request.generationConfig) {
      const config = request.generationConfig;

      if (
        config.temperature &&
        (config.temperature < 0 || config.temperature > 2)
      ) {
        errors.push('Temperature must be between 0 and 2');
      }

      if (config.topK && config.topK < 1) {
        errors.push('topK must be a positive integer');
      }

      if (config.topP && (config.topP < 0 || config.topP > 1)) {
        errors.push('topP must be between 0 and 1');
      }
    }

    return {
      valid: errors.length === 0,
      errors,
    };
  }
}

export default GoogleAdapter;

/**
 * @fileoverview xAI (Grok) platform adapter for prompt optimization
 * @lastmodified 2025-08-26T14:50:00Z
 *
 * Features: xAI API format adaptation, reasoning optimization, real-time capabilities
 * Main APIs: xAIAdapter class for converting optimized prompts to xAI format
 * Constraints: xAI API specifications and token limits
 * Patterns: Adapter pattern, reasoning-focused optimization, real-time integration
 */

import { logger } from '../../utils/logger';

export interface xAIMessage {
  role: 'system' | 'user' | 'assistant';
  content: string;
}

export interface xAIRequest {
  model: string;
  messages: xAIMessage[];
  temperature?: number;
  top_p?: number;
  max_tokens?: number;
  stream?: boolean;
  stop?: string[];
  frequency_penalty?: number;
  presence_penalty?: number;
  tools?: Array<{
    type: 'function';
    function: {
      name: string;
      description: string;
      parameters: object;
    };
  }>;
}

export interface xAIAdapterConfig {
  model: 'grok-beta' | 'grok-vision-beta';
  maxTokens: number;
  temperature: number;
  enableRealtimeData: boolean;
  enableReasoningOptimization: boolean;
  enableToolCalling: boolean;
  optimizeForHumor: boolean;
}

export interface xAIAdaptationResult {
  request: xAIRequest;
  tokenEstimate: number;
  adaptationNotes: string[];
  warnings: string[];
  xaiFeatures?: {
    realtimeEnabled: boolean;
    reasoningOptimized: boolean;
    toolsConfigured: boolean;
    humorEnhanced: boolean;
  };
}

export class xAIAdapter {
  private readonly defaultConfig: xAIAdapterConfig = {
    model: 'grok-beta',
    maxTokens: 4096,
    temperature: 0.7,
    enableRealtimeData: true,
    enableReasoningOptimization: true,
    enableToolCalling: false,
    optimizeForHumor: false,
  };

  private readonly modelLimits = {
    'grok-beta': 128000,
    'grok-vision-beta': 128000,
  };

  constructor(private config: xAIAdapterConfig = {} as xAIAdapterConfig) {
    this.config = { ...this.defaultConfig, ...config };
  }

  /**
   * Convert optimized prompt to xAI API format
   */
  adaptToXAI(
    optimizedPrompt: string,
    context?: {
      systemMessage?: string;
      conversationHistory?: Array<{ role: string; content: string }>;
      enableRealtime?: boolean;
      tools?: Array<{ name: string; description: string; parameters: object }>;
      personality?: 'standard' | 'witty' | 'serious' | 'technical';
    }
  ): xAIAdaptationResult {
    logger.info(`Adapting prompt for xAI ${this.config.model}`);

    const messages: xAIMessage[] = [];
    const adaptationNotes: string[] = [];
    const warnings: string[] = [];
    const xaiFeatures = {
      realtimeEnabled: false,
      reasoningOptimized: false,
      toolsConfigured: false,
      humorEnhanced: false,
    };

    // Add system message with xAI-specific enhancements
    const systemMessage = this.createSystemMessage(optimizedPrompt, context);
    if (systemMessage) {
      messages.push({
        role: 'system',
        content: systemMessage,
      });
      adaptationNotes.push('System message optimized for xAI/Grok');
    }

    // Add conversation history if available
    if (context?.conversationHistory) {
      const historyMessages = this.formatConversationHistory(
        context.conversationHistory
      );
      messages.push(...historyMessages);
      adaptationNotes.push(
        `Added ${historyMessages.length} conversation history messages`
      );
    }

    // Process the main prompt for xAI optimization
    let processedPrompt = this.optimizePromptForXAI(optimizedPrompt, context);

    // Add real-time context if enabled
    if (this.config.enableRealtimeData && context?.enableRealtime !== false) {
      const realtimeResult = this.addRealtimeContext(processedPrompt);
      processedPrompt = realtimeResult.prompt;
      if (realtimeResult.applied) {
        xaiFeatures.realtimeEnabled = true;
        adaptationNotes.push('Real-time data context added');
      }
    }

    // Apply reasoning optimization
    if (this.config.enableReasoningOptimization) {
      const reasoningResult = this.optimizeForReasoning(processedPrompt);
      processedPrompt = reasoningResult.prompt;
      if (reasoningResult.applied) {
        xaiFeatures.reasoningOptimized = true;
        adaptationNotes.push('Reasoning optimization applied');
      }
    }

    // Add humor optimization if enabled
    if (this.config.optimizeForHumor || context?.personality === 'witty') {
      const humorResult = this.addHumorOptimization(processedPrompt);
      processedPrompt = humorResult.prompt;
      if (humorResult.applied) {
        xaiFeatures.humorEnhanced = true;
        adaptationNotes.push('Humor optimization applied');
      }
    }

    // Create user message
    messages.push({
      role: 'user',
      content: processedPrompt,
    });

    // Check token limits
    const totalTokens = this.estimateTokens(messages);
    const modelLimit = this.modelLimits[this.config.model];

    if (totalTokens > modelLimit * 0.8) {
      // Leave room for response
      warnings.push(
        `Prompt approaching model limit (${totalTokens}/${modelLimit} tokens)`
      );
    }

    // Build the request
    const request: xAIRequest = {
      model: this.config.model,
      messages,
      temperature: this.config.temperature,
      max_tokens: this.config.maxTokens,
    };

    // Add tools if configured
    if (this.config.enableToolCalling && context?.tools) {
      request.tools = this.formatTools(context.tools);
      xaiFeatures.toolsConfigured = true;
      adaptationNotes.push(`Configured ${context.tools.length} tools`);
    }

    // Add xAI-specific parameters
    if (this.config.model === 'grok-vision-beta') {
      request.top_p = 0.9;
      adaptationNotes.push('Vision model parameters applied');
    }

    return {
      request,
      tokenEstimate: totalTokens,
      adaptationNotes,
      warnings,
      xaiFeatures: Object.values(xaiFeatures).some(Boolean)
        ? xaiFeatures
        : undefined,
    };
  }

  /**
   * Create xAI-optimized system message
   */
  private createSystemMessage(
    prompt: string,
    context?: { systemMessage?: string; personality?: string }
  ): string {
    let systemMessage = context?.systemMessage || '';

    // Extract existing system message from prompt if not provided
    if (!systemMessage) {
      const systemPatterns = [
        /^You are (.*?)(?:\n\n|\.\s)/i,
        /^System: (.*?)(?:\n\n|\.\s)/i,
        /^Instructions: (.*?)(?:\n\n|\.\s)/i,
      ];

      for (const pattern of systemPatterns) {
        const match = prompt.match(pattern);
        if (match) {
          systemMessage = match[0].trim();
          break;
        }
      }
    }

    // Create base system message if none found
    if (!systemMessage) {
      systemMessage = 'You are Grok, an AI assistant created by xAI.';
    }

    // Enhance with xAI-specific characteristics
    systemMessage +=
      ' You have access to real-time information and excel at providing current, relevant responses.';

    // Add personality-specific enhancements
    switch (context?.personality) {
      case 'witty':
        systemMessage +=
          ' You have a witty, engaging personality and can use appropriate humor in your responses.';
        break;
      case 'serious':
        systemMessage +=
          ' You maintain a professional, serious tone focused on accuracy and thoroughness.';
        break;
      case 'technical':
        systemMessage +=
          ' You excel at technical explanations and detailed analysis with precision and clarity.';
        break;
      default:
        systemMessage +=
          ' You are helpful, informative, and engaging while maintaining accuracy.';
    }

    // Add reasoning guidance
    if (this.config.enableReasoningOptimization) {
      systemMessage +=
        ' When tackling complex problems, show your reasoning process clearly.';
    }

    return systemMessage;
  }

  /**
   * Format conversation history for xAI
   */
  private formatConversationHistory(
    history: Array<{ role: string; content: string }>
  ): xAIMessage[] {
    return history.map(msg => ({
      role: this.normalizeRole(msg.role),
      content: msg.content,
    }));
  }

  /**
   * Optimize prompt specifically for xAI/Grok
   */
  private optimizePromptForXAI(
    prompt: string,
    _context?: { personality?: string }
  ): string {
    let optimized = prompt;

    // Remove system message content if it was extracted
    const systemPatterns = [
      /^You are .*?(?:\n\n|\.\s)/i,
      /^System: .*?(?:\n\n|\.\s)/i,
      /^Instructions: .*?(?:\n\n|\.\s)/i,
    ];

    for (const pattern of systemPatterns) {
      optimized = optimized.replace(pattern, '').trim();
    }

    // Optimize for Grok's conversational style
    optimized = optimized.replace(/could you please/gi, 'please');
    optimized = optimized.replace(/would you be able to/gi, 'can you');
    optimized = optimized.replace(/if possible/gi, '');

    // Add specificity for better results
    optimized = optimized.replace(/some information/gi, 'detailed information');
    optimized = optimized.replace(
      /a bit about/gi,
      'comprehensive details about'
    );

    return optimized.trim();
  }

  /**
   * Add real-time data context
   */
  private addRealtimeContext(prompt: string): {
    prompt: string;
    applied: boolean;
  } {
    // Check if the prompt would benefit from real-time data
    const realtimeIndicators = [
      /current/i,
      /recent/i,
      /latest/i,
      /today/i,
      /now/i,
      /up.to.date/i,
    ];

    const needsRealtime = realtimeIndicators.some(pattern =>
      pattern.test(prompt)
    );

    if (needsRealtime) {
      const realtimePrompt = `${prompt}\n\nPlease use the most current and up-to-date information available to you.`;
      return { prompt: realtimePrompt, applied: true };
    }

    return { prompt, applied: false };
  }

  /**
   * Optimize for reasoning capabilities
   */
  private optimizeForReasoning(prompt: string): {
    prompt: string;
    applied: boolean;
  } {
    // Check if prompt involves complex reasoning
    const reasoningIndicators = [
      /analyze/i,
      /compare/i,
      /evaluate/i,
      /explain why/i,
      /reasoning/i,
      /logic/i,
      /step.by.step/i,
    ];

    const needsReasoning = reasoningIndicators.some(pattern =>
      pattern.test(prompt)
    );

    if (needsReasoning && !prompt.includes('reasoning')) {
      const reasoningPrompt = `${prompt}\n\nPlease show your reasoning process and explain your thought process clearly.`;
      return { prompt: reasoningPrompt, applied: true };
    }

    return { prompt, applied: false };
  }

  /**
   * Add humor optimization (Grok's specialty)
   */
  private addHumorOptimization(prompt: string): {
    prompt: string;
    applied: boolean;
  } {
    // Check if humor would be appropriate
    const seriousTopics = [
      /medical/i,
      /legal/i,
      /emergency/i,
      /crisis/i,
      /serious/i,
      /important.*matter/i,
    ];

    const isSeriousTopic = seriousTopics.some(pattern => pattern.test(prompt));

    if (
      !isSeriousTopic &&
      !prompt.includes('humor') &&
      !prompt.includes('funny')
    ) {
      const humorPrompt = `${prompt}\n\nFeel free to add appropriate wit or humor to make your response engaging, while maintaining accuracy.`;
      return { prompt: humorPrompt, applied: true };
    }

    return { prompt, applied: false };
  }

  /**
   * Format tools for xAI function calling
   */
  private formatTools(
    tools: Array<{ name: string; description: string; parameters: object }>
  ) {
    return tools.map(tool => ({
      type: 'function' as const,
      function: {
        name: tool.name,
        description: tool.description,
        parameters: tool.parameters,
      },
    }));
  }

  /**
   * Normalize role names for xAI
   */
  private normalizeRole(role: string): 'system' | 'user' | 'assistant' {
    const normalizedRole = role.toLowerCase();
    if (normalizedRole === 'system') return 'system';
    if (
      normalizedRole === 'assistant' ||
      normalizedRole === 'ai' ||
      normalizedRole === 'grok'
    )
      return 'assistant';
    return 'user';
  }

  /**
   * Estimate token count for xAI models
   */
  private estimateTokens(messages: xAIMessage[]): number {
    let totalTokens = 0;

    for (const message of messages) {
      // xAI tokenization approximation (similar to OpenAI)
      totalTokens += Math.ceil(message.content.length / 4);
      totalTokens += 4; // Overhead per message
    }

    return totalTokens;
  }

  /**
   * Get xAI-specific recommendations
   */
  getXAIRecommendations(): {
    bestPractices: string[];
    realtimeUsage: string[];
    reasoning: string[];
    personality: Record<string, string[]>;
  } {
    return {
      bestPractices: [
        'Leverage real-time data capabilities for current information',
        'Use clear, direct instructions for best results',
        "Take advantage of Grok's reasoning capabilities",
        'Consider personality settings for appropriate tone',
        'Use function calling for complex workflows',
      ],
      realtimeUsage: [
        'Great for current events and recent information',
        'Excellent for time-sensitive queries',
        'Can provide up-to-date facts and figures',
        'Useful for trending topics and current analysis',
      ],
      reasoning: [
        'Excels at step-by-step reasoning',
        'Shows clear thought processes',
        'Good at explaining complex concepts',
        'Can break down multi-step problems',
      ],
      personality: {
        witty: [
          'Adds appropriate humor to responses',
          'Engaging and entertaining communication style',
          'Good for creative and casual interactions',
        ],
        serious: [
          'Professional and focused responses',
          'Best for business and formal contexts',
          'Emphasizes accuracy and thoroughness',
        ],
        technical: [
          'Detailed technical explanations',
          'Precise and comprehensive analysis',
          'Great for engineering and scientific topics',
        ],
        standard: [
          'Balanced approach to all topics',
          'Helpful and informative responses',
          'Good general-purpose setting',
        ],
      },
    };
  }

  /**
   * Validate xAI request format
   */
  validateRequest(request: xAIRequest): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!request.model) {
      errors.push('Model is required');
    }

    if (!request.messages || request.messages.length === 0) {
      errors.push('At least one message is required');
    }

    if (request.messages) {
      for (const message of request.messages) {
        if (!['system', 'user', 'assistant'].includes(message.role)) {
          errors.push(`Invalid role: ${message.role}`);
        }
        if (!message.content) {
          errors.push('Message content cannot be empty');
        }
      }
    }

    if (
      request.max_tokens &&
      (request.max_tokens < 1 || request.max_tokens > 32768)
    ) {
      errors.push('max_tokens must be between 1 and 32768');
    }

    if (
      request.temperature &&
      (request.temperature < 0 || request.temperature > 2)
    ) {
      errors.push('Temperature must be between 0 and 2');
    }

    if (request.top_p && (request.top_p < 0 || request.top_p > 1)) {
      errors.push('top_p must be between 0 and 1');
    }

    if (request.tools) {
      for (const tool of request.tools) {
        if (tool.type !== 'function') {
          errors.push('Only function tools are supported');
        }
        if (!tool.function.name || !tool.function.description) {
          errors.push('Function tools must have name and description');
        }
      }
    }

    return {
      valid: errors.length === 0,
      errors,
    };
  }
}

export default xAIAdapter;

/**
 * @fileoverview Plugin registry for managing PromptWizard and other optimization plugins
 * @lastmodified 2025-08-26T10:30:00Z
 *
 * Features: Plugin discovery, registration, lifecycle management, dependency resolution
 * Main APIs: PluginRegistry class for centralized plugin management
 * Constraints: Integrates with secure plugin manager and marketplace
 * Patterns: Registry pattern, plugin architecture, lifecycle management
 */

import * as fs from 'fs/promises';
import * as path from 'path';
import { logger } from '../utils/logger';
import { SecurePluginManager } from './secure-plugin-manager';

/**
 * Plugin types
 */
export enum PluginType {
  OPTIMIZATION = 'optimization',
  ANALYSIS = 'analysis',
  INTEGRATION = 'integration',
  UI = 'ui',
  UTILITY = 'utility',
  EXTENSION = 'extension',
}

/**
 * Plugin capabilities
 */
export enum PluginCapability {
  TEMPLATE_OPTIMIZATION = 'template-optimization',
  QUALITY_ANALYSIS = 'quality-analysis',
  PERFORMANCE_TRACKING = 'performance-tracking',
  MARKETPLACE_INTEGRATION = 'marketplace-integration',
  CLI_EXTENSION = 'cli-extension',
  UI_COMPONENTS = 'ui-components',
  API_INTEGRATION = 'api-integration',
  DATA_EXPORT = 'data-export',
  REAL_TIME_FEEDBACK = 'real-time-feedback',
  BATCH_PROCESSING = 'batch-processing',
}

/**
 * Plugin status
 */
export enum PluginStatus {
  REGISTERED = 'registered',
  ENABLED = 'enabled',
  DISABLED = 'disabled',
  ERROR = 'error',
  UPDATING = 'updating',
  UNINSTALLING = 'uninstalling',
}

/**
 * Plugin dependency
 */
export interface PluginDependency {
  /** Dependency name */
  name: string;

  /** Version requirement */
  version: string;

  /** Whether dependency is optional */
  optional: boolean;

  /** Dependency type */
  type: 'plugin' | 'package' | 'service';
}

/**
 * Plugin metadata
 */
export interface PluginMetadata {
  /** Installation source */
  source: 'marketplace' | 'local' | 'git' | 'npm';

  /** Installation method */
  installMethod: string;

  /** Last updated timestamp */
  lastUpdated?: Date;

  /** Usage statistics */
  usageStats?: {
    activationCount: number;
    lastActivated?: Date;
    errorCount: number;
    lastError?: Date;
  };

  /** User preferences */
  userPreferences?: Record<string, unknown>;

  /** Plugin tags */
  tags?: string[];

  /** Plugin rating */
  rating?: number;

  /** Update availability */
  updateAvailable?: boolean;

  /** Available version */
  availableVersion?: string;
}

/**
 * Plugin registration information
 */
export interface PluginRegistration {
  /** Plugin name */
  name: string;

  /** Plugin display name */
  displayName: string;

  /** Plugin version */
  version: string;

  /** Plugin description */
  description: string;

  /** Plugin author */
  author: string;

  /** Plugin type/category */
  type: PluginType;

  /** Plugin capabilities */
  capabilities: PluginCapability[];

  /** Plugin configuration schema */
  configSchema?: Record<string, unknown>;

  /** Plugin dependencies */
  dependencies: PluginDependency[];

  /** Plugin installation path */
  installPath: string;

  /** Plugin manifest path */
  manifestPath: string;

  /** Registration timestamp */
  registeredAt: Date;

  /** Plugin status */
  status: PluginStatus;

  /** Plugin metadata */
  metadata?: PluginMetadata;
}

/**
 * Plugin search criteria
 */
export interface PluginSearchCriteria {
  /** Plugin name pattern */
  name?: string;

  /** Plugin type */
  type?: PluginType;

  /** Required capabilities */
  capabilities?: PluginCapability[];

  /** Plugin status */
  status?: PluginStatus;

  /** Plugin tags */
  tags?: string[];

  /** Minimum rating */
  minRating?: number;

  /** Sort criteria */
  sortBy?: 'name' | 'rating' | 'updated' | 'usage';

  /** Sort order */
  sortOrder?: 'asc' | 'desc';
}

/**
 * Plugin registry for centralized plugin management
 */
export class PluginRegistry {
  private registrations = new Map<string, PluginRegistration>();

  private pluginManager: SecurePluginManager;

  private registryPath: string;

  private watchForChanges: boolean;

  private changeWatcher?: NodeJS.FSWatcher; // File system watcher

  constructor(
    pluginManager: SecurePluginManager,
    registryPath: string = './plugins',
    options: { watchForChanges?: boolean } = {}
  ) {
    this.pluginManager = pluginManager;
    this.registryPath = registryPath;
    this.watchForChanges = options.watchForChanges ?? true;
  }

  /**
   * Initialize the plugin registry
   * @returns Promise resolving when registry is initialized
   */
  async initialize(): Promise<void> {
    try {
      // Ensure registry directory exists
      await fs.mkdir(this.registryPath, { recursive: true });

      // Load existing plugin registrations
      await this.loadRegistrations();

      // Discover and register plugins
      await this.discoverPlugins();

      // Start watching for changes if enabled
      if (this.watchForChanges) {
        await this.startWatching();
      }

      logger.info(
        `Plugin registry initialized with ${this.registrations.size} plugins`
      );
    } catch (error) {
      logger.error('Failed to initialize plugin registry:', error);
      throw error;
    }
  }

  /**
   * Register a plugin in the registry
   * @param pluginPath - Path to plugin directory
   * @returns Promise resolving to plugin registration
   */
  async registerPlugin(pluginPath: string): Promise<PluginRegistration> {
    try {
      // Validate plugin path
      const normalizedPath = path.resolve(pluginPath);
      const manifestPath = path.join(normalizedPath, 'plugin.json');

      // Check if manifest exists
      try {
        await fs.access(manifestPath);
      } catch {
        throw new Error(`Plugin manifest not found: ${manifestPath}`);
      }

      // Load and validate manifest
      const manifestContent = await fs.readFile(manifestPath, 'utf8');
      const manifest = JSON.parse(manifestContent);

      // Validate required fields
      this.validateManifest(manifest);

      // Create registration
      const registration: PluginRegistration = {
        name: manifest.name,
        displayName: manifest.displayName || manifest.name,
        version: manifest.version,
        description: manifest.description || '',
        author: manifest.author || 'Unknown',
        type: this.parsePluginType(manifest.type || manifest.category),
        capabilities: this.parseCapabilities(manifest.capabilities || []),
        configSchema: manifest.configuration,
        dependencies: this.parseDependencies(manifest.dependencies || {}),
        installPath: normalizedPath,
        manifestPath,
        registeredAt: new Date(),
        status: PluginStatus.REGISTERED,
        metadata: this.extractMetadata(manifest, normalizedPath),
      };

      // Check for conflicts
      await this.checkPluginConflicts(registration);

      // Validate dependencies
      await this.validateDependencies(registration);

      // Register with plugin manager
      await this.pluginManager.loadPlugin(
        path.join(normalizedPath, manifest.main || 'index.js')
      );

      // Store registration
      this.registrations.set(registration.name, registration);

      // Save registry state
      await this.saveRegistrations();

      logger.info(
        `Plugin registered: ${registration.name} v${registration.version}`
      );
      return registration;
    } catch (error) {
      logger.error(`Failed to register plugin ${pluginPath}:`, error);
      throw error;
    }
  }

  /**
   * Unregister a plugin from the registry
   * @param pluginName - Name of plugin to unregister
   * @returns Promise resolving when plugin is unregistered
   */
  async unregisterPlugin(pluginName: string): Promise<void> {
    try {
      const registration = this.registrations.get(pluginName);
      if (!registration) {
        throw new Error(`Plugin not found: ${pluginName}`);
      }

      // Update status
      registration.status = PluginStatus.UNINSTALLING;

      // Unload from plugin manager
      await this.pluginManager.unloadPlugin(pluginName);

      // Remove registration
      this.registrations.delete(pluginName);

      // Save registry state
      await this.saveRegistrations();

      logger.info(`Plugin unregistered: ${pluginName}`);
    } catch (error) {
      logger.error(`Failed to unregister plugin ${pluginName}:`, error);
      throw error;
    }
  }

  /**
   * Enable a registered plugin
   * @param pluginName - Name of plugin to enable
   * @returns Promise resolving when plugin is enabled
   */
  async enablePlugin(pluginName: string): Promise<void> {
    try {
      const registration = this.registrations.get(pluginName);
      if (!registration) {
        throw new Error(`Plugin not found: ${pluginName}`);
      }

      if (registration.status === PluginStatus.ENABLED) {
        return; // Already enabled
      }

      // Enable in plugin manager
      const success = await this.pluginManager.enablePlugin(pluginName);
      if (!success) {
        throw new Error(`Failed to enable plugin: ${pluginName}`);
      }

      // Update status
      registration.status = PluginStatus.ENABLED;

      // Update usage stats
      if (registration.metadata?.usageStats) {
        registration.metadata.usageStats.activationCount += 1;
        registration.metadata.usageStats.lastActivated = new Date();
      }

      await this.saveRegistrations();

      logger.info(`Plugin enabled: ${pluginName}`);
    } catch (error) {
      const registration = this.registrations.get(pluginName);
      if (registration) {
        registration.status = PluginStatus.ERROR;
        if (registration.metadata?.usageStats) {
          registration.metadata.usageStats.errorCount += 1;
          registration.metadata.usageStats.lastError = new Date();
        }
      }

      logger.error(`Failed to enable plugin ${pluginName}:`, error);
      throw error;
    }
  }

  /**
   * Disable a registered plugin
   * @param pluginName - Name of plugin to disable
   * @returns Promise resolving when plugin is disabled
   */
  async disablePlugin(pluginName: string): Promise<void> {
    try {
      const registration = this.registrations.get(pluginName);
      if (!registration) {
        throw new Error(`Plugin not found: ${pluginName}`);
      }

      if (registration.status === PluginStatus.DISABLED) {
        return; // Already disabled
      }

      // Disable in plugin manager
      const success = await this.pluginManager.disablePlugin(pluginName);
      if (!success) {
        throw new Error(`Failed to disable plugin: ${pluginName}`);
      }

      // Update status
      registration.status = PluginStatus.DISABLED;

      await this.saveRegistrations();

      logger.info(`Plugin disabled: ${pluginName}`);
    } catch (error) {
      logger.error(`Failed to disable plugin ${pluginName}:`, error);
      throw error;
    }
  }

  /**
   * Get plugin registration by name
   * @param pluginName - Plugin name
   * @returns Plugin registration or undefined
   */
  getPlugin(pluginName: string): PluginRegistration | undefined {
    return this.registrations.get(pluginName);
  }

  /**
   * Get all registered plugins
   * @returns Array of plugin registrations
   */
  getAllPlugins(): PluginRegistration[] {
    return Array.from(this.registrations.values());
  }

  /**
   * Search for plugins by criteria
   * @param criteria - Search criteria
   * @returns Matching plugin registrations
   */
  searchPlugins(criteria: PluginSearchCriteria): PluginRegistration[] {
    let results = Array.from(this.registrations.values());

    // Filter by name
    if (criteria.name) {
      const namePattern = new RegExp(criteria.name, 'i');
      results = results.filter(
        p => namePattern.test(p.name) || namePattern.test(p.displayName)
      );
    }

    // Filter by type
    if (criteria.type) {
      results = results.filter(p => p.type === criteria.type);
    }

    // Filter by capabilities
    if (criteria.capabilities && criteria.capabilities.length > 0) {
      results = results.filter(p =>
        criteria.capabilities!.every(cap => p.capabilities.includes(cap))
      );
    }

    // Filter by status
    if (criteria.status) {
      results = results.filter(p => p.status === criteria.status);
    }

    // Filter by tags
    if (criteria.tags && criteria.tags.length > 0) {
      results = results.filter(p =>
        p.metadata?.tags?.some(tag => criteria.tags!.includes(tag))
      );
    }

    // Filter by rating
    if (criteria.minRating) {
      results = results.filter(
        p => (p.metadata?.rating || 0) >= criteria.minRating!
      );
    }

    // Sort results
    const sortBy = criteria.sortBy || 'name';
    const sortOrder = criteria.sortOrder || 'asc';

    results.sort((a, b) => {
      let aValue: unknown;
      let bValue: unknown;

      switch (sortBy) {
        case 'name':
          aValue = a.name;
          bValue = b.name;
          break;
        case 'rating':
          aValue = a.metadata?.rating || 0;
          bValue = b.metadata?.rating || 0;
          break;
        case 'updated':
          aValue = a.metadata?.lastUpdated || a.registeredAt;
          bValue = b.metadata?.lastUpdated || b.registeredAt;
          break;
        case 'usage':
          aValue = a.metadata?.usageStats?.activationCount || 0;
          bValue = b.metadata?.usageStats?.activationCount || 0;
          break;
        default:
          aValue = a.name;
          bValue = b.name;
      }

      if (sortOrder === 'desc') {
        [aValue, bValue] = [bValue, aValue];
      }

      if (aValue < bValue) return -1;
      if (aValue > bValue) return 1;
      return 0;
    });

    return results;
  }

  /**
   * Get plugins by capability
   * @param capability - Required capability
   * @returns Plugins with the specified capability
   */
  getPluginsByCapability(capability: PluginCapability): PluginRegistration[] {
    return this.searchPlugins({ capabilities: [capability] });
  }

  /**
   * Get enabled optimization plugins
   * @returns Enabled optimization plugins
   */
  getOptimizationPlugins(): PluginRegistration[] {
    return this.searchPlugins({
      type: PluginType.OPTIMIZATION,
      status: PluginStatus.ENABLED,
    });
  }

  /**
   * Check if a plugin is registered
   * @param pluginName - Plugin name
   * @returns Whether plugin is registered
   */
  isPluginRegistered(pluginName: string): boolean {
    return this.registrations.has(pluginName);
  }

  /**
   * Check if a plugin is enabled
   * @param pluginName - Plugin name
   * @returns Whether plugin is enabled
   */
  isPluginEnabled(pluginName: string): boolean {
    const registration = this.registrations.get(pluginName);
    return registration?.status === PluginStatus.ENABLED;
  }

  /**
   * Update plugin metadata
   * @param pluginName - Plugin name
   * @param metadata - Metadata to update
   * @returns Promise resolving when metadata is updated
   */
  async updatePluginMetadata(
    pluginName: string,
    metadata: Partial<PluginMetadata>
  ): Promise<void> {
    const registration = this.registrations.get(pluginName);
    if (!registration) {
      throw new Error(`Plugin not found: ${pluginName}`);
    }

    // Merge metadata
    registration.metadata = {
      source: 'local' as const, // Provide default required value
      installMethod: 'unknown',
      ...registration.metadata,
      ...metadata,
      lastUpdated: new Date(),
    };

    await this.saveRegistrations();
  }

  /**
   * Get plugin statistics
   * @returns Registry statistics
   */
  getStatistics(): {
    totalPlugins: number;
    enabledPlugins: number;
    disabledPlugins: number;
    errorPlugins: number;
    pluginsByType: Record<PluginType, number>;
    pluginsByCapability: Record<PluginCapability, number>;
  } {
    const plugins = this.getAllPlugins();

    const stats = {
      totalPlugins: plugins.length,
      enabledPlugins: plugins.filter(p => p.status === PluginStatus.ENABLED)
        .length,
      disabledPlugins: plugins.filter(p => p.status === PluginStatus.DISABLED)
        .length,
      errorPlugins: plugins.filter(p => p.status === PluginStatus.ERROR).length,
      pluginsByType: {} as Record<PluginType, number>,
      pluginsByCapability: {} as Record<PluginCapability, number>,
    };

    // Count by type
    for (const type of Object.values(PluginType)) {
      stats.pluginsByType[type] = plugins.filter(p => p.type === type).length;
    }

    // Count by capability
    for (const capability of Object.values(PluginCapability)) {
      stats.pluginsByCapability[capability] = plugins.filter(p =>
        p.capabilities.includes(capability)
      ).length;
    }

    return stats;
  }

  /**
   * Cleanup registry resources
   * @returns Promise resolving when cleanup is complete
   */
  async cleanup(): Promise<void> {
    if (this.changeWatcher) {
      this.changeWatcher.close();
      this.changeWatcher = undefined;
    }

    await this.saveRegistrations();
  }

  // Private methods

  /**
   * Load plugin registrations from storage
   * @private
   */
  private async loadRegistrations(): Promise<void> {
    try {
      const registryFile = path.join(this.registryPath, 'registry.json');
      const content = await fs.readFile(registryFile, 'utf8');
      const data = JSON.parse(content);

      // Restore registrations with proper types
      for (const [name, regData] of Object.entries(data.registrations || {})) {
        const registration = regData as PluginRegistration;
        registration.registeredAt = new Date(registration.registeredAt);
        if (registration.metadata?.lastUpdated) {
          registration.metadata.lastUpdated = new Date(
            registration.metadata.lastUpdated
          );
        }
        this.registrations.set(name, registration);
      }
    } catch (_error) {
      // Registry file doesn't exist or is invalid - start fresh
      logger.debug('No existing plugin registry found, starting fresh');
    }
  }

  /**
   * Save plugin registrations to storage
   * @private
   */
  private async saveRegistrations(): Promise<void> {
    try {
      const registryFile = path.join(this.registryPath, 'registry.json');
      const data = {
        version: '1.0.0',
        lastUpdated: new Date().toISOString(),
        registrations: Object.fromEntries(this.registrations),
      };

      await fs.writeFile(registryFile, JSON.stringify(data, null, 2));
    } catch (error) {
      logger.error('Failed to save plugin registry:', error);
    }
  }

  /**
   * Discover plugins in the registry directory
   * @private
   */
  private async discoverPlugins(): Promise<void> {
    try {
      const entries = await fs.readdir(this.registryPath, {
        withFileTypes: true,
      });
      const pluginDirs = entries.filter(entry => entry.isDirectory());

      for (const dir of pluginDirs) {
        const pluginPath = path.join(this.registryPath, dir.name);
        const manifestPath = path.join(pluginPath, 'plugin.json');

        try {
          await fs.access(manifestPath);

          // Check if already registered
          const manifest = JSON.parse(await fs.readFile(manifestPath, 'utf8'));
          if (!this.registrations.has(manifest.name)) {
            await this.registerPlugin(pluginPath);
          }
        } catch {
          // No manifest or invalid plugin - skip
          continue;
        }
      }
    } catch (error) {
      logger.error('Failed to discover plugins:', error);
    }
  }

  /**
   * Start watching for plugin changes
   * @private
   */
  private async startWatching(): Promise<void> {
    try {
      this.changeWatcher = fs.watch(this.registryPath, { recursive: true });

      this.changeWatcher.on(
        'change',
        async (_eventType: string, filename?: string) => {
          if (filename && filename.endsWith('plugin.json')) {
            // Plugin manifest changed - refresh registration
            const pluginDir = path.dirname(
              path.join(this.registryPath, filename)
            );
            await this.refreshPlugin(pluginDir);
          }
        }
      );
    } catch (error) {
      logger.warn('Failed to start plugin watching:', error);
    }
  }

  /**
   * Refresh a plugin registration
   * @param pluginPath - Plugin path
   * @private
   */
  private async refreshPlugin(pluginPath: string): Promise<void> {
    try {
      const manifestPath = path.join(pluginPath, 'plugin.json');
      const manifest = JSON.parse(await fs.readFile(manifestPath, 'utf8'));

      const existingReg = this.registrations.get(manifest.name);
      if (existingReg && existingReg.version !== manifest.version) {
        // Version changed - re-register
        await this.unregisterPlugin(manifest.name);
        await this.registerPlugin(pluginPath);
      }
    } catch (error) {
      logger.debug('Failed to refresh plugin registration:', error);
    }
  }

  /**
   * Validate plugin manifest
   * @param manifest - Plugin manifest
   * @private
   */
  private validateManifest(manifest: Record<string, unknown>): void {
    const required = ['name', 'version', 'description'];
    for (const field of required) {
      if (!manifest[field]) {
        throw new Error(`Missing required field in manifest: ${field}`);
      }
    }

    // Validate version format
    if (!/^\d+\.\d+\.\d+/.test(manifest.version)) {
      throw new Error(`Invalid version format: ${manifest.version}`);
    }
  }

  /**
   * Parse plugin type from manifest
   * @param type - Type string from manifest
   * @returns Parsed plugin type
   * @private
   */
  private parsePluginType(type: string): PluginType {
    const typeMap: Record<string, PluginType> = {
      optimization: PluginType.OPTIMIZATION,
      'ai-optimization': PluginType.OPTIMIZATION,
      analysis: PluginType.ANALYSIS,
      integration: PluginType.INTEGRATION,
      ui: PluginType.UI,
      utility: PluginType.UTILITY,
      extension: PluginType.EXTENSION,
    };

    return typeMap[type.toLowerCase()] || PluginType.UTILITY;
  }

  /**
   * Parse plugin capabilities from manifest
   * @param capabilities - Capabilities array from manifest
   * @returns Parsed capabilities
   * @private
   */
  private parseCapabilities(capabilities: string[]): PluginCapability[] {
    const capabilityMap: Record<string, PluginCapability> = {
      'template-optimization': PluginCapability.TEMPLATE_OPTIMIZATION,
      'quality-analysis': PluginCapability.QUALITY_ANALYSIS,
      'performance-tracking': PluginCapability.PERFORMANCE_TRACKING,
      'marketplace-integration': PluginCapability.MARKETPLACE_INTEGRATION,
      'cli-extension': PluginCapability.CLI_EXTENSION,
      'ui-components': PluginCapability.UI_COMPONENTS,
      'api-integration': PluginCapability.API_INTEGRATION,
      'data-export': PluginCapability.DATA_EXPORT,
      'real-time-feedback': PluginCapability.REAL_TIME_FEEDBACK,
      'batch-processing': PluginCapability.BATCH_PROCESSING,
    };

    return capabilities
      .map(cap => capabilityMap[cap.toLowerCase()])
      .filter(Boolean);
  }

  /**
   * Parse plugin dependencies from manifest
   * @param dependencies - Dependencies object from manifest
   * @returns Parsed dependencies
   * @private
   */
  private parseDependencies(
    dependencies: Record<string, string>
  ): PluginDependency[] {
    return Object.entries(dependencies).map(([name, version]) => ({
      name,
      version,
      optional: false,
      type: 'package' as const,
    }));
  }

  /**
   * Extract plugin metadata from manifest and path
   * @param manifest - Plugin manifest
   * @param installPath - Installation path
   * @returns Plugin metadata
   * @private
   */
  private extractMetadata(manifest: Record<string, unknown>, _installPath: string): PluginMetadata {
    return {
      source: 'local',
      installMethod: 'manual',
      usageStats: {
        activationCount: 0,
        errorCount: 0,
      },
      tags: manifest.keywords || [],
      rating: 0,
    };
  }

  /**
   * Check for plugin conflicts
   * @param registration - Plugin registration to check
   * @private
   */
  private async checkPluginConflicts(
    registration: PluginRegistration
  ): Promise<void> {
    // Check for name conflicts
    if (this.registrations.has(registration.name)) {
      const existing = this.registrations.get(registration.name)!;
      if (existing.version !== registration.version) {
        logger.warn(
          `Plugin version conflict: ${registration.name} ${existing.version} vs ${registration.version}`
        );
      }
    }

    // Check for capability conflicts (if needed)
    // This could be extended to prevent conflicting optimization plugins
  }

  /**
   * Validate plugin dependencies
   * @param registration - Plugin registration to validate
   * @private
   */
  private async validateDependencies(
    registration: PluginRegistration
  ): Promise<void> {
    for (const dep of registration.dependencies) {
      if (dep.type === 'plugin') {
        // Check if required plugin is registered
        const depPlugin = this.registrations.get(dep.name);
        if (!depPlugin && !dep.optional) {
          throw new Error(`Missing required plugin dependency: ${dep.name}`);
        }

        // Version compatibility check could be added here
      }
    }
  }
}

/**
 * Create and initialize a plugin registry
 * @param pluginManager - Secure plugin manager instance
 * @param registryPath - Path to plugin registry
 * @param options - Registry options
 * @returns Initialized plugin registry
 */
export async function createPluginRegistry(
  pluginManager: SecurePluginManager,
  registryPath?: string,
  options?: { watchForChanges?: boolean }
): Promise<PluginRegistry> {
  const registry = new PluginRegistry(pluginManager, registryPath, options);
  await registry.initialize();
  return registry;
}

