/**
 * @fileoverview Real-time security monitoring and behavior analysis system
 * @lastmodified 2025-08-27T13:00:00Z
 *
 * Features: Real-time monitoring, anomaly detection, behavioral analysis, threat scoring
 * Main APIs: BehaviorMonitor class for comprehensive security event analysis
 * Constraints: Machine learning patterns, statistical analysis, real-time processing
 * Patterns: Observer pattern, event stream processing, anomaly detection, threat intelligence
 */

import { EventEmitter } from 'events';
import * as crypto from 'crypto';
import { logger } from '../../utils/logger';

/**
 * Security event types
 */
export type SecurityEventType = 
  | 'resource-violation'
  | 'code-execution' 
  | 'network-request'
  | 'file-access'
  | 'permission-escalation'
  | 'suspicious-behavior'
  | 'authentication-failure'
  | 'data-exfiltration'
  | 'timing-attack'
  | 'injection-attempt';

/**
 * Security event severity
 */
export type EventSeverity = 'info' | 'warning' | 'high' | 'critical';

/**
 * Security event data
 */
export interface SecurityEvent {
  id: string;
  type: SecurityEventType;
  severity: EventSeverity;
  timestamp: number;
  executionId: string;
  pluginName: string;
  description: string;
  metadata: {
    source?: string;
    target?: string;
    method?: string;
    parameters?: unknown[];
    stackTrace?: string;
    userAgent?: string;
    ip?: string;
    duration?: number;
    size?: number;
  };
  context?: {
    previousEvents: string[];
    userSession?: string;
    systemLoad?: number;
    memoryUsage?: number;
  };
}

/**
 * Behavioral pattern analysis
 */
export interface BehaviorPattern {
  id: string;
  name: string;
  description: string;
  eventSequence: SecurityEventType[];
  timeWindowMs: number;
  minimumOccurrences: number;
  riskScore: number; // 0-100
  indicators: string[];
}

/**
 * Anomaly detection result
 */
export interface AnomalyDetection {
  detected: boolean;
  confidence: number; // 0-100
  type: 'statistical' | 'pattern' | 'frequency' | 'temporal';
  description: string;
  baseline?: number;
  current?: number;
  threshold?: number;
}

/**
 * Threat assessment result
 */
export interface ThreatAssessment {
  executionId: string;
  pluginName: string;
  overallRiskScore: number; // 0-100
  threatLevel: 'none' | 'low' | 'medium' | 'high' | 'critical';
  confidence: number;
  indicators: ThreatIndicator[];
  recommendations: string[];
  actions: RecommendedAction[];
}

/**
 * Threat indicator
 */
export interface ThreatIndicator {
  type: 'behavioral' | 'statistical' | 'pattern' | 'signature';
  name: string;
  description: string;
  severity: EventSeverity;
  evidence: unknown[];
  confidence: number;
}

/**
 * Recommended security action
 */
export interface RecommendedAction {
  action: 'monitor' | 'throttle' | 'isolate' | 'terminate' | 'quarantine' | 'alert';
  priority: 'low' | 'medium' | 'high' | 'immediate';
  description: string;
  automated: boolean;
}

/**
 * Monitoring configuration
 */
export interface MonitoringConfig {
  // Event collection
  enableEventCollection: boolean;
  maxEventHistory: number;
  eventRetentionMs: number;

  // Behavioral analysis
  enableBehaviorAnalysis: boolean;
  behaviorAnalysisIntervalMs: number;
  minimumEventsForAnalysis: number;

  // Anomaly detection
  enableAnomalyDetection: boolean;
  anomalyDetectionSensitivity: number; // 0-1
  baselineWindowMs: number;

  // Threat assessment
  enableThreatAssessment: boolean;
  threatAssessmentIntervalMs: number;
  riskThreshold: number;

  // Actions
  enableAutomatedResponse: boolean;
  maxAutomatedActions: number;
  actionCooldownMs: number;

  // Alerting
  enableAlerting: boolean;
  alertThreshold: number;
  alertCooldownMs: number;
}

/**
 * Default monitoring configuration
 */
export const DEFAULT_MONITORING_CONFIG: MonitoringConfig = {
  // Event collection
  enableEventCollection: true,
  maxEventHistory: 10000,
  eventRetentionMs: 24 * 60 * 60 * 1000, // 24 hours

  // Behavioral analysis
  enableBehaviorAnalysis: true,
  behaviorAnalysisIntervalMs: 30000, // 30 seconds
  minimumEventsForAnalysis: 5,

  // Anomaly detection
  enableAnomalyDetection: true,
  anomalyDetectionSensitivity: 0.7,
  baselineWindowMs: 10 * 60 * 1000, // 10 minutes

  // Threat assessment
  enableThreatAssessment: true,
  threatAssessmentIntervalMs: 60000, // 1 minute
  riskThreshold: 70,

  // Actions
  enableAutomatedResponse: true,
  maxAutomatedActions: 5,
  actionCooldownMs: 60000, // 1 minute

  // Alerting
  enableAlerting: true,
  alertThreshold: 80,
  alertCooldownMs: 300000, // 5 minutes
};

/**
 * Statistical baseline for anomaly detection
 */
interface StatisticalBaseline {
  mean: number;
  standardDeviation: number;
  min: number;
  max: number;
  count: number;
  lastUpdated: number;
}

/**
 * Real-time security monitoring and behavior analysis system
 */
export class BehaviorMonitor extends EventEmitter {
  private config: MonitoringConfig;
  private eventHistory = new Map<string, SecurityEvent[]>(); // executionId -> events
  private behaviorPatterns: BehaviorPattern[];
  private statisticalBaselines = new Map<string, StatisticalBaseline>();
  private threatAssessments = new Map<string, ThreatAssessment>();
  private recentActions = new Map<string, { action: RecommendedAction; timestamp: number }[]>();

  private analysisTimer?: NodeJS.Timeout;
  private cleanupTimer?: NodeJS.Timeout;

  constructor(config: Partial<MonitoringConfig> = {}) {
    super();
    this.config = { ...DEFAULT_MONITORING_CONFIG, ...config };
    this.behaviorPatterns = this.initializeBehaviorPatterns();

    this.startPeriodicAnalysis();
    this.startPeriodicCleanup();

    logger.info('Security behavior monitor initialized');
  }

  /**
   * Record a security event for analysis
   */
  recordEvent(event: Omit<SecurityEvent, 'id' | 'timestamp'>): void {
    const fullEvent: SecurityEvent = {
      ...event,
      id: this.generateEventId(),
      timestamp: Date.now(),
    };

    // Add to history
    if (!this.eventHistory.has(event.executionId)) {
      this.eventHistory.set(event.executionId, []);
    }

    const events = this.eventHistory.get(event.executionId)!;
    events.push(fullEvent);

    // Limit history size
    if (events.length > this.config.maxEventHistory) {
      events.splice(0, events.length - this.config.maxEventHistory);
    }

    // Emit event for real-time processing
    this.emit('securityEvent', fullEvent);

    // Immediate analysis for critical events
    if (event.severity === 'critical') {
      this.performImmediateAnalysis(event.executionId);
    }

    logger.debug(`Security event recorded: ${event.type} (${event.severity}) for ${event.pluginName}`);
  }

  /**
   * Get current threat assessment for an execution
   */
  getThreatAssessment(executionId: string): ThreatAssessment | null {
    return this.threatAssessments.get(executionId) || null;
  }

  /**
   * Get all threat assessments
   */
  getAllThreatAssessments(): Map<string, ThreatAssessment> {
    return new Map(this.threatAssessments);
  }

  /**
   * Get event history for an execution
   */
  getEventHistory(executionId: string): SecurityEvent[] {
    return this.eventHistory.get(executionId)?.slice() || [];
  }

  /**
   * Perform immediate analysis for critical events
   */
  private async performImmediateAnalysis(executionId: string): Promise<void> {
    try {
      const events = this.eventHistory.get(executionId) || [];
      if (events.length === 0) return;

      const assessment = await this.analyzeThreatLevel(executionId, events);
      this.threatAssessments.set(executionId, assessment);

      // Take immediate action if necessary
      if (assessment.threatLevel === 'critical' && this.config.enableAutomatedResponse) {
        await this.takeAutomatedAction(assessment);
      }

      // Emit assessment for external handlers
      this.emit('threatAssessment', assessment);

    } catch (error: any) {
      logger.error(`Immediate analysis error for ${executionId}: ${error.message}`);
    }
  }

  /**
   * Start periodic analysis timer
   */
  private startPeriodicAnalysis(): void {
    if (!this.config.enableBehaviorAnalysis && !this.config.enableThreatAssessment) {
      return;
    }

    this.analysisTimer = setInterval(async () => {
      await this.performPeriodicAnalysis();
    }, Math.min(this.config.behaviorAnalysisIntervalMs, this.config.threatAssessmentIntervalMs));
  }

  /**
   * Start periodic cleanup timer
   */
  private startPeriodicCleanup(): void {
    this.cleanupTimer = setInterval(() => {
      this.cleanupExpiredData();
    }, 60000); // Clean up every minute
  }

  /**
   * Perform periodic analysis of all active executions
   */
  private async performPeriodicAnalysis(): Promise<void> {
    try {
      const activeExecutions = Array.from(this.eventHistory.keys());

      for (const executionId of activeExecutions) {
        const events = this.eventHistory.get(executionId) || [];

        if (events.length < this.config.minimumEventsForAnalysis) {
          continue;
        }

        // Behavioral analysis
        if (this.config.enableBehaviorAnalysis) {
          await this.analyzeBehaviorPatterns(executionId, events);
        }

        // Anomaly detection
        if (this.config.enableAnomalyDetection) {
          await this.detectAnomalies(executionId, events);
        }

        // Threat assessment
        if (this.config.enableThreatAssessment) {
          const assessment = await this.analyzeThreatLevel(executionId, events);
          this.threatAssessments.set(executionId, assessment);

          // Automated response
          if (assessment.overallRiskScore > this.config.riskThreshold && 
              this.config.enableAutomatedResponse) {
            await this.takeAutomatedAction(assessment);
          }

          // Alerting
          if (assessment.overallRiskScore > this.config.alertThreshold && 
              this.config.enableAlerting) {
            this.sendAlert(assessment);
          }
        }
      }

    } catch (error: any) {
      logger.error(`Periodic analysis error: ${error.message}`);
    }
  }

  /**
   * Analyze behavior patterns in event sequence
   */
  private async analyzeBehaviorPatterns(executionId: string, events: SecurityEvent[]): Promise<void> {
    const recentEvents = events.filter(e => 
      Date.now() - e.timestamp < Math.max(...this.behaviorPatterns.map(p => p.timeWindowMs))
    );

    for (const pattern of this.behaviorPatterns) {
      const matches = this.findPatternMatches(pattern, recentEvents);

      if (matches.length >= pattern.minimumOccurrences) {
        const lastEvent = events[events.length - 1];
        this.recordEvent({
          type: 'suspicious-behavior',
          severity: pattern.riskScore > 70 ? 'critical' : pattern.riskScore > 50 ? 'high' : 'warning',
          executionId,
          pluginName: lastEvent.pluginName,
          description: `Behavior pattern detected: ${pattern.name}`,
          metadata: {
            pattern: pattern.id,
            matches: matches.length,
            indicators: pattern.indicators,
          },
        });

        logger.warn(`Behavior pattern detected: ${pattern.name} in ${executionId} (${matches.length} matches)`);
      }
    }
  }

  /**
   * Find pattern matches in event sequence
   */
  private findPatternMatches(pattern: BehaviorPattern, events: SecurityEvent[]): SecurityEvent[][] {
    const matches: SecurityEvent[][] = [];
    const windowStart = Date.now() - pattern.timeWindowMs;
    const relevantEvents = events.filter(e => e.timestamp > windowStart);

    // Simple pattern matching - look for event type sequences
    for (let i = 0; i <= relevantEvents.length - pattern.eventSequence.length; i++) {
      const sequence = relevantEvents.slice(i, i + pattern.eventSequence.length);

      const sequenceTypes = sequence.map(e => e.type);
      const patternMatches = pattern.eventSequence.every((expectedType, index) => 
        expectedType === sequenceTypes[index]
      );

      if (patternMatches) {
        matches.push(sequence);
      }
    }

    return matches;
  }

  /**
   * Detect anomalies in event patterns
   */
  private async detectAnomalies(executionId: string, events: SecurityEvent[]): Promise<void> {
    const recentEvents = events.filter(e => 
      Date.now() - e.timestamp < this.config.baselineWindowMs
    );

    if (recentEvents.length < 5) return; // Need minimum events for statistical analysis

    // Frequency anomaly detection
    const eventTypes = recentEvents.map(e => e.type);
    const typeFrequency = this.calculateFrequency(eventTypes);

    for (const [type, frequency] of typeFrequency.entries()) {
      const baseline = this.getStatisticalBaseline(executionId, type);
      const anomaly = this.detectFrequencyAnomaly(frequency, baseline);

      if (anomaly.detected) {
        const lastEvent = events[events.length - 1];
        this.recordEvent({
          type: 'suspicious-behavior',
          severity: anomaly.confidence > 0.8 ? 'high' : 'warning',
          executionId,
          pluginName: lastEvent.pluginName,
          description: `Frequency anomaly detected for ${type}: ${anomaly.description}`,
          metadata: {
            anomaly: anomaly,
            baseline: baseline,
          },
        });
      }
    }

    // Temporal anomaly detection
    const timingAnomalies = this.detectTimingAnomalies(recentEvents);
    for (const anomaly of timingAnomalies) {
      if (anomaly.detected) {
        const lastEvent = events[events.length - 1];
        this.recordEvent({
          type: 'timing-attack',
          severity: 'high',
          executionId,
          pluginName: lastEvent.pluginName,
          description: `Timing anomaly detected: ${anomaly.description}`,
          metadata: { anomaly },
        });
      }
    }
  }

  /**
   * Calculate frequency distribution
   */
  private calculateFrequency<T>(items: T[]): Map<T, number> {
    const frequency = new Map<T, number>();
    for (const item of items) {
      frequency.set(item, (frequency.get(item) || 0) + 1);
    }
    return frequency;
  }

  /**
   * Get or create statistical baseline
   */
  private getStatisticalBaseline(executionId: string, eventType: string): StatisticalBaseline {
    const key = `${executionId}:${eventType}`;
    let baseline = this.statisticalBaselines.get(key);

    if (!baseline) {
      baseline = {
        mean: 0,
        standardDeviation: 0,
        min: 0,
        max: 0,
        count: 0,
        lastUpdated: Date.now(),
      };
      this.statisticalBaselines.set(key, baseline);
    }

    return baseline;
  }

  /**
   * Detect frequency anomalies using statistical analysis
   */
  private detectFrequencyAnomaly(frequency: number, baseline: StatisticalBaseline): AnomalyDetection {
    if (baseline.count < 10) {
      // Update baseline
      this.updateBaseline(baseline, frequency);
      return { detected: false, confidence: 0, type: 'frequency', description: 'Insufficient baseline data' };
    }

    const zScore = Math.abs(frequency - baseline.mean) / Math.max(baseline.standardDeviation, 0.1);
    const threshold = 2.0 / this.config.anomalyDetectionSensitivity; // Higher sensitivity = lower threshold

    this.updateBaseline(baseline, frequency);

    if (zScore > threshold) {
      return {
        detected: true,
        confidence: Math.min(zScore / threshold, 1.0),
        type: 'frequency',
        description: `Unusual frequency: ${frequency} (baseline: ${baseline.mean.toFixed(2)} ¬± ${baseline.standardDeviation.toFixed(2)})`,
        baseline: baseline.mean,
        current: frequency,
        threshold,
      };
    }

    return { detected: false, confidence: 0, type: 'frequency', description: 'Within normal range' };
  }

  /**
   * Update statistical baseline with new data point
   */
  private updateBaseline(baseline: StatisticalBaseline, newValue: number): void {
    baseline.count++;
    const delta = newValue - baseline.mean;
    baseline.mean += delta / baseline.count;

    if (baseline.count === 1) {
      baseline.standardDeviation = 0;
    } else {
      const delta2 = newValue - baseline.mean;
      baseline.standardDeviation = Math.sqrt(
        ((baseline.count - 2) * Math.pow(baseline.standardDeviation, 2) + delta * delta2) / (baseline.count - 1)
      );
    }

    baseline.min = Math.min(baseline.min, newValue);
    baseline.max = Math.max(baseline.max, newValue);
    baseline.lastUpdated = Date.now();
  }

  /**
   * Detect timing anomalies
   */
  private detectTimingAnomalies(events: SecurityEvent[]): AnomalyDetection[] {
    const anomalies: AnomalyDetection[] = [];

    if (events.length < 3) return anomalies;

    // Check for rapid-fire events (potential automated attack)
    const intervals: number[] = [];
    for (let i = 1; i < events.length; i++) {
      intervals.push(events[i].timestamp - events[i - 1].timestamp);
    }

    const avgInterval = intervals.reduce((sum, interval) => sum + interval, 0) / intervals.length;
    const rapidFireThreshold = 100; // 100ms

    const rapidFireCount = intervals.filter(interval => interval < rapidFireThreshold).length;
    if (rapidFireCount > intervals.length * 0.5) {
      anomalies.push({
        detected: true,
        confidence: rapidFireCount / intervals.length,
        type: 'temporal',
        description: `Rapid-fire event pattern detected (${rapidFireCount}/${intervals.length} events < ${rapidFireThreshold}ms apart)`,
        baseline: avgInterval,
        current: Math.min(...intervals),
      });
    }

    return anomalies;
  }

  /**
   * Analyze overall threat level for an execution
   */
  private async analyzeThreatLevel(executionId: string, events: SecurityEvent[]): Promise<ThreatAssessment> {
    const pluginName = events.length > 0 ? events[0].pluginName : 'unknown';
    let overallRiskScore = 0;
    const indicators: ThreatIndicator[] = [];
    const recommendations: string[] = [];
    const actions: RecommendedAction[] = [];

    // Event severity scoring
    const severityScores = { info: 1, warning: 5, high: 20, critical: 50 };
    const severityScore = events.reduce((score, event) => 
      score + severityScores[event.severity], 0);

    overallRiskScore += Math.min(severityScore, 100);

    // Event frequency scoring
    const recentEvents = events.filter(e => Date.now() - e.timestamp < 300000); // 5 minutes
    const frequencyScore = Math.min(recentEvents.length * 2, 50);
    overallRiskScore += frequencyScore;

    // Event type diversity scoring (more types = higher risk)
    const eventTypes = new Set(events.map(e => e.type));
    const diversityScore = Math.min(eventTypes.size * 5, 30);
    overallRiskScore += diversityScore;

    // Critical event patterns
    const criticalEvents = events.filter(e => e.severity === 'critical');
    if (criticalEvents.length > 0) {
      indicators.push({
        type: 'behavioral',
        name: 'Critical Security Events',
        description: `${criticalEvents.length} critical security events detected`,
        severity: 'critical',
        evidence: criticalEvents.map(e => e.description),
        confidence: 95,
      });
      recommendations.push('Immediate investigation required for critical security events');
      actions.push({
        action: 'isolate',
        priority: 'immediate',
        description: 'Isolate plugin execution due to critical security events',
        automated: true,
      });
    }

    // Suspicious behavior patterns
    const suspiciousBehavior = events.filter(e => e.type === 'suspicious-behavior');
    if (suspiciousBehavior.length > 0) {
      indicators.push({
        type: 'pattern',
        name: 'Suspicious Behavior Detected',
        description: `${suspiciousBehavior.length} suspicious behavior patterns identified`,
        severity: 'high',
        evidence: suspiciousBehavior.map(e => e.description),
        confidence: 80,
      });
      recommendations.push('Monitor plugin behavior closely for malicious activity');
    }

    // Resource violations
    const resourceViolations = events.filter(e => e.type === 'resource-violation');
    if (resourceViolations.length > 3) {
      indicators.push({
        type: 'behavioral',
        name: 'Excessive Resource Violations',
        description: `${resourceViolations.length} resource limit violations`,
        severity: 'high',
        evidence: resourceViolations.slice(-3).map(e => e.description),
        confidence: 70,
      });
      recommendations.push('Review resource limits and plugin resource usage');
      actions.push({
        action: 'throttle',
        priority: 'high',
        description: 'Throttle plugin execution due to resource violations',
        automated: true,
      });
    }

    // Network access attempts
    const networkEvents = events.filter(e => e.type === 'network-request');
    if (networkEvents.length > 0) {
      indicators.push({
        type: 'behavioral',
        name: 'Network Activity',
        description: `${networkEvents.length} network access attempts`,
        severity: networkEvents.length > 10 ? 'high' : 'warning',
        evidence: networkEvents.map(e => `${e.metadata.method} ${e.metadata.target}`),
        confidence: 60,
      });

      if (networkEvents.length > 10) {
        recommendations.push('Investigate excessive network activity for data exfiltration');
        actions.push({
          action: 'monitor',
          priority: 'medium',
          description: 'Monitor network activity for suspicious patterns',
          automated: false,
        });
      }
    }

    // Cap the risk score
    overallRiskScore = Math.min(overallRiskScore, 100);

    // Determine threat level
    let threatLevel: ThreatAssessment['threatLevel'];
    if (overallRiskScore >= 80) threatLevel = 'critical';
    else if (overallRiskScore >= 60) threatLevel = 'high';
    else if (overallRiskScore >= 40) threatLevel = 'medium';
    else if (overallRiskScore >= 20) threatLevel = 'low';
    else threatLevel = 'none';

    // Calculate confidence based on data quality
    const confidence = Math.min(95, 50 + events.length * 2); // More events = higher confidence

    return {
      executionId,
      pluginName,
      overallRiskScore,
      threatLevel,
      confidence,
      indicators,
      recommendations,
      actions,
    };
  }

  /**
   * Take automated action based on threat assessment
   */
  private async takeAutomatedAction(assessment: ThreatAssessment): Promise<void> {
    const recentActions = this.recentActions.get(assessment.executionId) || [];
    const now = Date.now();

    // Clean up old actions
    const validActions = recentActions.filter(a => 
      now - a.timestamp < this.config.actionCooldownMs
    );

    // Check if we've exceeded the maximum automated actions
    if (validActions.length >= this.config.maxAutomatedActions) {
      logger.warn(`Maximum automated actions reached for ${assessment.executionId}`);
      return;
    }

    // Find the highest priority automated action
    const automatedActions = assessment.actions
      .filter(a => a.automated)
      .sort((a, b) => {
        const priorities = { immediate: 4, high: 3, medium: 2, low: 1 };
        return priorities[b.priority] - priorities[a.priority];
      });

    if (automatedActions.length === 0) return;

    const action = automatedActions[0];

    // Record the action
    validActions.push({ action, timestamp: now });
    this.recentActions.set(assessment.executionId, validActions);

    // Emit action event for external handlers
    this.emit('automatedAction', {
      executionId: assessment.executionId,
      action,
      assessment,
    });

    logger.warn(`Automated action taken for ${assessment.executionId}: ${action.action} (${action.priority})`);
  }

  /**
   * Send security alert
   */
  private sendAlert(assessment: ThreatAssessment): void {
    const alertId = crypto.randomUUID();

    const alert = {
      id: alertId,
      timestamp: Date.now(),
      type: 'security-threat',
      severity: assessment.threatLevel,
      executionId: assessment.executionId,
      pluginName: assessment.pluginName,
      riskScore: assessment.overallRiskScore,
      indicators: assessment.indicators,
      recommendations: assessment.recommendations,
    };

    this.emit('securityAlert', alert);

    logger.error(`Security alert generated: ${alertId} - ${assessment.pluginName} (risk: ${assessment.overallRiskScore}/100)`);
  }

  /**
   * Clean up expired data
   */
  private cleanupExpiredData(): void {
    const cutoff = Date.now() - this.config.eventRetentionMs;
    let cleanedEvents = 0;

    // Clean up event history
    for (const [executionId, events] of this.eventHistory.entries()) {
      const filteredEvents = events.filter(e => e.timestamp > cutoff);

      if (filteredEvents.length === 0) {
        this.eventHistory.delete(executionId);
        this.threatAssessments.delete(executionId);
        this.recentActions.delete(executionId);
      } else {
        this.eventHistory.set(executionId, filteredEvents);
        cleanedEvents += events.length - filteredEvents.length;
      }
    }

    // Clean up baselines
    for (const [key, baseline] of this.statisticalBaselines.entries()) {
      if (Date.now() - baseline.lastUpdated > this.config.eventRetentionMs) {
        this.statisticalBaselines.delete(key);
      }
    }

    if (cleanedEvents > 0) {
      logger.debug(`Cleaned up ${cleanedEvents} expired security events`);
    }
  }

  /**
   * Initialize default behavior patterns
   */
  private initializeBehaviorPatterns(): BehaviorPattern[] {
    return [
      {
        id: 'data-exfiltration',
        name: 'Data Exfiltration Pattern',
        description: 'Multiple file reads followed by network requests',
        eventSequence: ['file-access', 'file-access', 'network-request'],
        timeWindowMs: 30000, // 30 seconds
        minimumOccurrences: 1,
        riskScore: 85,
        indicators: ['Multiple file reads', 'Network transmission', 'Data access pattern'],
      },
      {
        id: 'privilege-escalation',
        name: 'Privilege Escalation Attempt',
        description: 'Permission violations followed by resource access',
        eventSequence: ['permission-escalation', 'resource-violation'],
        timeWindowMs: 10000, // 10 seconds
        minimumOccurrences: 1,
        riskScore: 90,
        indicators: ['Permission bypass attempt', 'Resource access violation'],
      },
      {
        id: 'reconnaissance',
        name: 'System Reconnaissance',
        description: 'Multiple resource probing attempts',
        eventSequence: ['resource-violation', 'resource-violation', 'resource-violation'],
        timeWindowMs: 60000, // 1 minute
        minimumOccurrences: 1,
        riskScore: 70,
        indicators: ['Multiple resource probes', 'System exploration'],
      },
      {
        id: 'timing-attack',
        name: 'Timing Attack Pattern',
        description: 'Rapid repeated operations for timing analysis',
        eventSequence: ['code-execution', 'code-execution', 'code-execution'],
        timeWindowMs: 5000, // 5 seconds
        minimumOccurrences: 5,
        riskScore: 75,
        indicators: ['Rapid execution pattern', 'Potential timing analysis'],
      },
      {
        id: 'resource-exhaustion',
        name: 'Resource Exhaustion Attack',
        description: 'Sustained resource violations',
        eventSequence: ['resource-violation', 'resource-violation'],
        timeWindowMs: 120000, // 2 minutes
        minimumOccurrences: 10,
        riskScore: 80,
        indicators: ['Sustained resource abuse', 'DoS attempt'],
      },
    ];
  }

  /**
   * Generate unique event ID
   */
  private generateEventId(): string {
    return crypto.randomUUID();
  }

  /**
   * Add custom behavior pattern
   */
  addBehaviorPattern(pattern: BehaviorPattern): void {
    this.behaviorPatterns.push(pattern);
    logger.info(`Added custom behavior pattern: ${pattern.name}`);
  }

  /**
   * Remove behavior pattern
   */
  removeBehaviorPattern(patternId: string): boolean {
    const initialLength = this.behaviorPatterns.length;
    this.behaviorPatterns = this.behaviorPatterns.filter(p => p.id !== patternId);

    if (this.behaviorPatterns.length < initialLength) {
      logger.info(`Removed behavior pattern: ${patternId}`);
      return true;
    }
    return false;
  }

  /**
   * Get monitoring statistics
   */
  getMonitoringStats(): object {
    const totalEvents = Array.from(this.eventHistory.values())
      .reduce((total, events) => total + events.length, 0);

    const activeExecutions = this.eventHistory.size;
    const threatAssessments = this.threatAssessments.size;
    const statisticalBaselines = this.statisticalBaselines.size;

    return {
      totalEvents,
      activeExecutions,
      threatAssessments,
      statisticalBaselines,
      behaviorPatterns: this.behaviorPatterns.length,
      config: this.config,
    };
  }

  /**
   * Cleanup and stop monitoring
   */
  cleanup(): void {
    if (this.analysisTimer) {
      clearInterval(this.analysisTimer);
    }

    if (this.cleanupTimer) {
      clearInterval(this.cleanupTimer);
    }

    this.eventHistory.clear();
    this.statisticalBaselines.clear();
    this.threatAssessments.clear();
    this.recentActions.clear();

    logger.info('Behavior monitor cleanup completed');
  }
}

/**
 * @fileoverview Advanced code analysis and filtering engine for plugin security
 * @lastmodified 2025-08-27T12:45:00Z
 *
 * Features: Static analysis, dynamic pattern detection, obfuscation detection, security scoring
 * Main APIs: CodeAnalyzer class for comprehensive code security analysis
 * Constraints: AST-based analysis, pattern matching, heuristic detection
 * Patterns: Static analysis, visitor pattern, security rule engine, threat classification
 */

import { parse } from '@babel/parser';
import traverse from '@babel/traverse';
import * as t from '@babel/types';
import { IPlugin } from '../../types';
import { logger } from '../../utils/logger';

/**
 * Security threat classification
 */
export type ThreatLevel = 'none' | 'low' | 'medium' | 'high' | 'critical';

/**
 * Code analysis threat
 */
export interface SecurityThreat {
  type: 'dangerous-function' | 'global-access' | 'eval-usage' | 'network-access' | 
        'file-access' | 'process-access' | 'obfuscation' | 'complexity' | 'injection';
  severity: ThreatLevel;
  description: string;
  line?: number;
  column?: number;
  code?: string;
  suggestion?: string;
  evidence?: string[];
}

/**
 * Code complexity metrics
 */
export interface ComplexityMetrics {
  cyclomatic: number;
  cognitive: number;
  nestingDepth: number;
  functionCount: number;
  variableCount: number;
  lineCount: number;
  branchCount: number;
  loopCount: number;
}

/**
 * Obfuscation analysis result
 */
export interface ObfuscationAnalysis {
  detected: boolean;
  confidence: number; // 0-100
  patterns: string[];
  evidence: {
    minifiedVariables: number;
    hexStrings: number;
    unicodeEscapes: number;
    evalUsage: number;
    dynamicPropertyAccess: number;
  };
}

/**
 * Code analysis result
 */
export interface CodeAnalysisResult {
  safe: boolean;
  score: number; // 0-100, higher is safer
  threatLevel: ThreatLevel;
  threats: SecurityThreat[];
  warnings: string[];
  complexity: ComplexityMetrics;
  obfuscation: ObfuscationAnalysis;
  metadata: {
    pluginName: string;
    analysisTime: number;
    rulesApplied: number;
    bytesAnalyzed: number;
  };
}

/**
 * Analysis configuration
 */
export interface AnalysisConfig {
  // Threat detection
  enableDangerousFunctionDetection: boolean;
  enableGlobalAccessDetection: boolean;
  enableEvalDetection: boolean;
  enableObfuscationDetection: boolean;

  // Complexity analysis
  maxCyclomaticComplexity: number;
  maxCognitiveComplexity: number;
  maxNestingDepth: number;
  maxFunctionCount: number;

  // File size limits
  maxCodeSize: number;
  maxLineCount: number;

  // Security rules
  strictMode: boolean;
  allowUnsafePatterns: boolean;
  customRules: SecurityRule[];

  // Performance
  analysisTimeoutMs: number;
  enableCaching: boolean;
}

/**
 * Custom security rule
 */
export interface SecurityRule {
  id: string;
  name: string;
  description: string;
  severity: ThreatLevel;
  pattern: RegExp | ((node: t.Node, path: any) => boolean);
  message: string;
  suggestion?: string;
}

/**
 * Default analysis configuration
 */
export const DEFAULT_ANALYSIS_CONFIG: AnalysisConfig = {
  // Threat detection
  enableDangerousFunctionDetection: true,
  enableGlobalAccessDetection: true,
  enableEvalDetection: true,
  enableObfuscationDetection: true,

  // Complexity limits
  maxCyclomaticComplexity: 20,
  maxCognitiveComplexity: 30,
  maxNestingDepth: 6,
  maxFunctionCount: 50,

  // Size limits
  maxCodeSize: 1024 * 1024, // 1MB
  maxLineCount: 10000,

  // Security
  strictMode: true,
  allowUnsafePatterns: false,
  customRules: [],

  // Performance
  analysisTimeoutMs: 30000,
  enableCaching: true,
};

/**
 * Advanced code analysis and filtering engine
 */
export class CodeAnalyzer {
  private config: AnalysisConfig;
  private analysisCache = new Map<string, CodeAnalysisResult>();
  private securityRules: SecurityRule[];

  constructor(config: Partial<AnalysisConfig> = {}) {
    this.config = { ...DEFAULT_ANALYSIS_CONFIG, ...config };
    this.securityRules = this.initializeSecurityRules();
    logger.info('Advanced code analyzer initialized');
  }

  /**
   * Analyze plugin code for security threats
   */
  async analyzePlugin(plugin: IPlugin): Promise<CodeAnalysisResult> {
    const startTime = Date.now();

    try {
      // Generate cache key
      const cacheKey = this.generateCacheKey(plugin);
      if (this.config.enableCaching && this.analysisCache.has(cacheKey)) {
        logger.debug(`Using cached analysis for plugin ${plugin.name}`);
        return this.analysisCache.get(cacheKey)!;
      }

      logger.info(`Starting code analysis for plugin: ${plugin.name}`);

      // Extract code to analyze
      const codeToAnalyze = this.extractPluginCode(plugin);

      // Basic validation
      const basicValidation = this.validateCodeBasics(codeToAnalyze);
      if (!basicValidation.valid) {
        const result: CodeAnalysisResult = {
          safe: false,
          score: 0,
          threatLevel: 'critical',
          threats: [{
            type: 'eval-usage',
            severity: 'critical',
            description: basicValidation.error!,
          }],
          warnings: [],
          complexity: this.createEmptyComplexityMetrics(),
          obfuscation: this.createEmptyObfuscationAnalysis(),
          metadata: {
            pluginName: plugin.name,
            analysisTime: Date.now() - startTime,
            rulesApplied: 0,
            bytesAnalyzed: codeToAnalyze.length,
          },
        };
        return result;
      }

      // Parse code into AST with timeout
      const ast = await this.parseCodeWithTimeout(codeToAnalyze);

      // Perform comprehensive analysis
      const threats = await this.detectThreats(ast, codeToAnalyze);
      const complexity = this.calculateComplexityMetrics(ast, codeToAnalyze);
      const obfuscation = this.analyzeObfuscation(ast, codeToAnalyze);
      const warnings = this.generateWarnings(complexity, obfuscation);

      // Calculate overall safety score
      const score = this.calculateSafetyScore(threats, complexity, obfuscation);
      const threatLevel = this.determineThreatLevel(threats, score);

      const result: CodeAnalysisResult = {
        safe: threatLevel === 'none' || threatLevel === 'low',
        score,
        threatLevel,
        threats,
        warnings,
        complexity,
        obfuscation,
        metadata: {
          pluginName: plugin.name,
          analysisTime: Date.now() - startTime,
          rulesApplied: this.securityRules.length,
          bytesAnalyzed: codeToAnalyze.length,
        },
      };

      // Cache result
      if (this.config.enableCaching) {
        this.analysisCache.set(cacheKey, result);
      }

      logger.info(`Code analysis completed for ${plugin.name}: ${result.safe ? 'SAFE' : 'UNSAFE'} (score: ${result.score}/100, threats: ${result.threats.length})`);
      return result;

    } catch (error: any) {
      logger.error(`Code analysis error for ${plugin.name}: ${error.message}`);

      return {
        safe: false,
        score: 0,
        threatLevel: 'critical',
        threats: [{
          type: 'eval-usage',
          severity: 'critical',
          description: `Analysis error: ${error.message}`,
        }],
        warnings: [`Analysis failed: ${error.message}`],
        complexity: this.createEmptyComplexityMetrics(),
        obfuscation: this.createEmptyObfuscationAnalysis(),
        metadata: {
          pluginName: plugin.name,
          analysisTime: Date.now() - startTime,
          rulesApplied: 0,
          bytesAnalyzed: 0,
        },
      };
    }
  }

  /**
   * Extract code from plugin for analysis
   */
  private extractPluginCode(plugin: IPlugin): string {
    const codeParts: string[] = [];

    if (plugin.execute) codeParts.push(plugin.execute.toString());
    if (plugin.init) codeParts.push(plugin.init.toString());
    if (plugin.activate) codeParts.push(plugin.activate.toString());
    if (plugin.deactivate) codeParts.push(plugin.deactivate.toString());
    if (plugin.dispose) codeParts.push(plugin.dispose.toString());

    // Include hooks if available
    if (plugin.hooks && typeof plugin.hooks === 'object') {
      for (const [hookName, hookFn] of Object.entries(plugin.hooks)) {
        if (typeof hookFn === 'function') {
          codeParts.push(`// Hook: ${hookName}\n${hookFn.toString()}`);
        }
      }
    }

    return codeParts.join('\n\n');
  }

  /**
   * Validate basic code properties
   */
  private validateCodeBasics(code: string): { valid: boolean; error?: string } {
    // Check size limits
    if (code.length > this.config.maxCodeSize) {
      return {
        valid: false,
        error: `Code size exceeds limit: ${code.length} > ${this.config.maxCodeSize} bytes`,
      };
    }

    // Check line count
    const lineCount = code.split('\n').length;
    if (lineCount > this.config.maxLineCount) {
      return {
        valid: false,
        error: `Line count exceeds limit: ${lineCount} > ${this.config.maxLineCount}`,
      };
    }

    // Basic security checks
    if (this.config.enableEvalDetection) {
      const evalPattern = /\beval\s*\(/gi;
      const functionPattern = /new\s+Function\s*\(/gi;

      if (evalPattern.test(code)) {
        return {
          valid: false,
          error: 'Code contains eval() usage - strictly forbidden',
        };
      }

      if (functionPattern.test(code)) {
        return {
          valid: false,
          error: 'Code contains Function constructor - strictly forbidden',
        };
      }
    }

    return { valid: true };
  }

  /**
   * Parse code with timeout protection
   */
  private async parseCodeWithTimeout(code: string): Promise<t.File> {
    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => {
        reject(new Error('Code parsing timeout'));
      }, this.config.analysisTimeoutMs);

      try {
        const ast = parse(code, {
          sourceType: 'module',
          plugins: [
            'objectRestSpread',
            'functionBind',
            'exportDefaultFrom',
            'decorators-legacy',
            'asyncGenerators',
            'optionalChaining',
            'nullishCoalescingOperator',
          ],
          allowImportExportEverywhere: true,
          allowAwaitOutsideFunction: true,
          allowReturnOutsideFunction: true,
          allowUndeclaredExports: true,
        });

        clearTimeout(timeout);
        resolve(ast);
      } catch (error) {
        clearTimeout(timeout);
        reject(error);
      }
    });
  }

  /**
   * Detect security threats in AST
   */
  private async detectThreats(ast: t.File, code: string): Promise<SecurityThreat[]> {
    const threats: SecurityThreat[] = [];

    try {
      traverse(ast, {
        enter: (path) => {
          const node = path.node;

          // Apply built-in security rules
          if (this.config.enableDangerousFunctionDetection) {
            this.detectDangerousFunctions(node, path, threats);
          }

          if (this.config.enableGlobalAccessDetection) {
            this.detectGlobalAccess(node, path, threats);
          }

          this.detectNetworkAccess(node, path, threats);
          this.detectFileAccess(node, path, threats);
          this.detectProcessAccess(node, path, threats);
          this.detectInjectionPatterns(node, path, threats);

          // Apply custom rules
          this.applyCustomRules(node, path, threats);
        },
      });

      // Detect regex-based patterns
      this.detectRegexPatterns(code, threats);

    } catch (error: any) {
      threats.push({
        type: 'eval-usage',
        severity: 'high',
        description: `AST traversal error: ${error.message}`,
      });
    }

    return threats;
  }

  /**
   * Detect dangerous function calls
   */
  private detectDangerousFunctions(node: t.Node, path: any, threats: SecurityThreat[]): void {
    if (node.type === 'CallExpression' && node.callee.type === 'Identifier') {
      const dangerousFunctions = [
        { name: 'eval', severity: 'critical' as ThreatLevel, message: 'eval() allows arbitrary code execution' },
        { name: 'Function', severity: 'critical' as ThreatLevel, message: 'Function constructor allows code generation' },
        { name: 'setTimeout', severity: 'high' as ThreatLevel, message: 'setTimeout with string argument allows code execution' },
        { name: 'setInterval', severity: 'high' as ThreatLevel, message: 'setInterval with string argument allows code execution' },
        { name: 'setImmediate', severity: 'medium' as ThreatLevel, message: 'setImmediate can be used for timing attacks' },
        { name: 'execSync', severity: 'critical' as ThreatLevel, message: 'execSync allows command execution' },
        { name: 'spawn', severity: 'critical' as ThreatLevel, message: 'spawn allows process creation' },
        { name: 'fork', severity: 'critical' as ThreatLevel, message: 'fork allows process forking' },
        { name: 'exec', severity: 'critical' as ThreatLevel, message: 'exec allows command execution' },
      ];

      const found = dangerousFunctions.find(fn => fn.name === node.callee.name);
      if (found) {
        threats.push({
          type: 'dangerous-function',
          severity: found.severity,
          description: `Dangerous function call: ${found.name} - ${found.message}`,
          line: node.loc?.start.line,
          column: node.loc?.start.column,
          suggestion: `Remove ${found.name} call or use safer alternatives`,
        });
      }
    }
  }

  /**
   * Detect global object access
   */
  private detectGlobalAccess(node: t.Node, path: any, threats: SecurityThreat[]): void {
    if (node.type === 'MemberExpression' && node.object.type === 'Identifier') {
      const dangerousGlobals = [
        { name: 'process', severity: 'critical' as ThreatLevel, message: 'Access to process object' },
        { name: 'global', severity: 'high' as ThreatLevel, message: 'Access to global object' },
        { name: 'globalThis', severity: 'high' as ThreatLevel, message: 'Access to globalThis object' },
        { name: '__dirname', severity: 'medium' as ThreatLevel, message: 'Access to __dirname' },
        { name: '__filename', severity: 'medium' as ThreatLevel, message: 'Access to __filename' },
        { name: 'module', severity: 'high' as ThreatLevel, message: 'Access to module object' },
        { name: 'exports', severity: 'medium' as ThreatLevel, message: 'Access to exports object' },
        { name: 'Buffer', severity: 'medium' as ThreatLevel, message: 'Access to Buffer constructor' },
      ];

      const found = dangerousGlobals.find(glob => glob.name === node.object.name);
      if (found) {
        threats.push({
          type: 'global-access',
          severity: found.severity,
          description: `Global object access: ${found.name} - ${found.message}`,
          line: node.loc?.start.line,
          column: node.loc?.start.column,
          suggestion: 'Avoid accessing global objects directly',
        });
      }
    }
  }

  /**
   * Detect network access patterns
   */
  private detectNetworkAccess(node: t.Node, path: any, threats: SecurityThreat[]): void {
    const networkPatterns = [
      { pattern: 'fetch', severity: 'medium' as ThreatLevel },
      { pattern: 'XMLHttpRequest', severity: 'medium' as ThreatLevel },
      { pattern: 'WebSocket', severity: 'high' as ThreatLevel },
      { pattern: 'request', severity: 'medium' as ThreatLevel },
      { pattern: 'axios', severity: 'medium' as ThreatLevel },
    ];

    if (node.type === 'CallExpression' && node.callee.type === 'Identifier') {
      const found = networkPatterns.find(p => p.pattern === node.callee.name);
      if (found) {
        threats.push({
          type: 'network-access',
          severity: found.severity,
          description: `Network access detected: ${found.pattern}`,
          line: node.loc?.start.line,
          column: node.loc?.start.column,
          suggestion: 'Network access should be explicitly approved',
        });
      }
    }
  }

  /**
   * Detect file access patterns
   */
  private detectFileAccess(node: t.Node, path: any, threats: SecurityThreat[]): void {
    if (node.type === 'MemberExpression' && 
        node.object.type === 'Identifier' && 
        node.object.name === 'fs') {

      threats.push({
        type: 'file-access',
        severity: 'high',
        description: 'File system access detected',
        line: node.loc?.start.line,
        column: node.loc?.start.column,
        suggestion: 'File access should be sandboxed and limited',
      });
    }
  }

  /**
   * Detect process access patterns
   */
  private detectProcessAccess(node: t.Node, path: any, threats: SecurityThreat[]): void {
    if (node.type === 'MemberExpression' && 
        node.object.type === 'Identifier' && 
        node.object.name === 'process') {

      const property = node.property.type === 'Identifier' ? node.property.name : 'unknown';
      const dangerousProps = ['exit', 'kill', 'abort', 'env', 'argv', 'cwd'];
      const severity = dangerousProps.includes(property) ? 'critical' : 'high';

      threats.push({
        type: 'process-access',
        severity: severity as ThreatLevel,
        description: `Process object property access: ${property}`,
        line: node.loc?.start.line,
        column: node.loc?.start.column,
        suggestion: 'Process access should be strictly controlled',
      });
    }
  }

  /**
   * Detect injection attack patterns
   */
  private detectInjectionPatterns(node: t.Node, path: any, threats: SecurityThreat[]): void {
    if (node.type === 'TemplateLiteral') {
      // Check for potential SQL injection in template literals
      const quasi = node.quasis.map(q => q.value.raw).join('');
      const sqlKeywords = /\b(SELECT|INSERT|UPDATE|DELETE|DROP|CREATE|ALTER)\b/gi;

      if (sqlKeywords.test(quasi)) {
        threats.push({
          type: 'injection',
          severity: 'high',
          description: 'Potential SQL injection pattern in template literal',
          line: node.loc?.start.line,
          column: node.loc?.start.column,
          suggestion: 'Use parameterized queries to prevent SQL injection',
        });
      }
    }
  }

  /**
   * Apply custom security rules
   */
  private applyCustomRules(node: t.Node, path: any, threats: SecurityThreat[]): void {
    for (const rule of this.securityRules) {
      try {
        let matches = false;

        if (rule.pattern instanceof RegExp) {
          // For regex patterns, check the node's source code
          const code = path.getSource?.() || '';
          matches = rule.pattern.test(code);
        } else if (typeof rule.pattern === 'function') {
          matches = rule.pattern(node, path);
        }

        if (matches) {
          threats.push({
            type: 'eval-usage', // Generic type for custom rules
            severity: rule.severity,
            description: `${rule.name}: ${rule.message}`,
            line: node.loc?.start.line,
            column: node.loc?.start.column,
            suggestion: rule.suggestion,
          });
        }
      } catch (error: any) {
        logger.warn(`Error applying custom rule ${rule.id}: ${error.message}`);
      }
    }
  }

  /**
   * Detect regex-based patterns in code
   */
  private detectRegexPatterns(code: string, threats: SecurityThreat[]): void {
    const patterns = [
      {
        regex: /document\./gi,
        type: 'global-access' as const,
        severity: 'medium' as ThreatLevel,
        description: 'DOM access detected - not available in Node.js environment',
      },
      {
        regex: /window\./gi,
        type: 'global-access' as const,
        severity: 'medium' as ThreatLevel,
        description: 'Window object access - not available in Node.js environment',
      },
      {
        regex: /navigator\./gi,
        type: 'global-access' as const,
        severity: 'low' as ThreatLevel,
        description: 'Navigator object access detected',
      },
      {
        regex: /location\./gi,
        type: 'global-access' as const,
        severity: 'medium' as ThreatLevel,
        description: 'Location object access detected',
      },
    ];

    for (const pattern of patterns) {
      const matches = code.match(pattern.regex);
      if (matches) {
        threats.push({
          type: pattern.type,
          severity: pattern.severity,
          description: pattern.description,
          evidence: matches.slice(0, 5), // Limit evidence
        });
      }
    }
  }

  /**
   * Calculate code complexity metrics
   */
  private calculateComplexityMetrics(ast: t.File, code: string): ComplexityMetrics {
    let cyclomaticComplexity = 1; // Start with 1
    let cognitiveComplexity = 0;
    let maxNestingDepth = 0;
    let currentNestingDepth = 0;
    let functionCount = 0;
    let variableCount = 0;
    let branchCount = 0;
    let loopCount = 0;

    traverse(ast, {
      enter: (path) => {
        const node = path.node;

        // Count functions
        if (t.isFunction(node)) {
          functionCount++;
          currentNestingDepth++;
          maxNestingDepth = Math.max(maxNestingDepth, currentNestingDepth);
        }

        // Count variables
        if (t.isVariableDeclarator(node)) {
          variableCount++;
        }

        // Count complexity-contributing structures
        if (t.isIfStatement(node) || t.isConditionalExpression(node)) {
          cyclomaticComplexity++;
          cognitiveComplexity++;
          branchCount++;
          currentNestingDepth++;
          maxNestingDepth = Math.max(maxNestingDepth, currentNestingDepth);
        }

        if (t.isLoop(node)) {
          cyclomaticComplexity++;
          cognitiveComplexity += 1 + currentNestingDepth; // Nesting penalty
          loopCount++;
          currentNestingDepth++;
          maxNestingDepth = Math.max(maxNestingDepth, currentNestingDepth);
        }

        if (t.isSwitchStatement(node)) {
          cyclomaticComplexity += node.cases.length;
          cognitiveComplexity++;
          branchCount += node.cases.length;
        }

        if (t.isTryStatement(node)) {
          cyclomaticComplexity++;
          cognitiveComplexity++;
        }
      },

      exit: (path) => {
        const node = path.node;

        if (t.isFunction(node) || t.isIfStatement(node) || 
            t.isConditionalExpression(node) || t.isLoop(node)) {
          currentNestingDepth--;
        }
      },
    });

    const lineCount = code.split('\n').length;

    return {
      cyclomatic: cyclomaticComplexity,
      cognitive: cognitiveComplexity,
      nestingDepth: maxNestingDepth,
      functionCount,
      variableCount,
      lineCount,
      branchCount,
      loopCount,
    };
  }

  /**
   * Analyze code for obfuscation patterns
   */
  private analyzeObfuscation(ast: t.File, code: string): ObfuscationAnalysis {
    let minifiedVariables = 0;
    let hexStrings = 0;
    let unicodeEscapes = 0;
    let evalUsage = 0;
    let dynamicPropertyAccess = 0;
    const patterns: string[] = [];

    // Count short variable names (potential minification)
    const shortVarNames = code.match(/\b[a-zA-Z_$][a-zA-Z0-9_$]{0,2}\b/g) || [];
    minifiedVariables = shortVarNames.length;

    // Count hex strings
    const hexMatches = code.match(/\\x[0-9a-fA-F]{2}/g) || [];
    hexStrings = hexMatches.length;

    // Count unicode escapes
    const unicodeMatches = code.match(/\\u[0-9a-fA-F]{4}/g) || [];
    unicodeEscapes = unicodeMatches.length;

    // Check for eval usage
    const evalMatches = code.match(/\beval\s*\(/g) || [];
    evalUsage = evalMatches.length;

    // AST-based analysis
    traverse(ast, {
      enter: (path) => {
        const node = path.node;

        // Dynamic property access (obj['prop'] instead of obj.prop)
        if (t.isMemberExpression(node) && node.computed && t.isStringLiteral(node.property)) {
          dynamicPropertyAccess++;
        }
      },
    });

    // Analyze patterns
    if (minifiedVariables > code.length / 50) {
      patterns.push('High ratio of short variable names');
    }

    if (hexStrings > 10) {
      patterns.push('Excessive hex-encoded strings');
    }

    if (unicodeEscapes > 5) {
      patterns.push('Unicode escape sequences detected');
    }

    if (evalUsage > 0) {
      patterns.push('Dynamic code execution detected');
    }

    if (dynamicPropertyAccess > 20) {
      patterns.push('Excessive dynamic property access');
    }

    // Calculate confidence score
    let confidence = 0;
    if (patterns.length > 0) confidence += patterns.length * 20;
    if (minifiedVariables > 100) confidence += 30;
    if (hexStrings > 20) confidence += 25;
    if (unicodeEscapes > 10) confidence += 20;
    confidence = Math.min(confidence, 100);

    return {
      detected: patterns.length > 0,
      confidence,
      patterns,
      evidence: {
        minifiedVariables,
        hexStrings,
        unicodeEscapes,
        evalUsage,
        dynamicPropertyAccess,
      },
    };
  }

  /**
   * Generate warnings based on analysis
   */
  private generateWarnings(complexity: ComplexityMetrics, obfuscation: ObfuscationAnalysis): string[] {
    const warnings: string[] = [];

    if (complexity.cyclomatic > this.config.maxCyclomaticComplexity) {
      warnings.push(`High cyclomatic complexity: ${complexity.cyclomatic} > ${this.config.maxCyclomaticComplexity}`);
    }

    if (complexity.cognitive > this.config.maxCognitiveComplexity) {
      warnings.push(`High cognitive complexity: ${complexity.cognitive} > ${this.config.maxCognitiveComplexity}`);
    }

    if (complexity.nestingDepth > this.config.maxNestingDepth) {
      warnings.push(`Deep nesting detected: ${complexity.nestingDepth} > ${this.config.maxNestingDepth}`);
    }

    if (complexity.functionCount > this.config.maxFunctionCount) {
      warnings.push(`Many functions detected: ${complexity.functionCount} > ${this.config.maxFunctionCount}`);
    }

    if (obfuscation.detected) {
      warnings.push(`Possible obfuscation detected (${obfuscation.confidence}% confidence)`);
    }

    return warnings;
  }

  /**
   * Calculate overall safety score
   */
  private calculateSafetyScore(
    threats: SecurityThreat[],
    complexity: ComplexityMetrics,
    obfuscation: ObfuscationAnalysis
  ): number {
    let score = 100;

    // Deduct points for threats
    for (const threat of threats) {
      switch (threat.severity) {
        case 'critical':
          score -= 30;
          break;
        case 'high':
          score -= 20;
          break;
        case 'medium':
          score -= 10;
          break;
        case 'low':
          score -= 5;
          break;
      }
    }

    // Deduct points for complexity
    if (complexity.cyclomatic > this.config.maxCyclomaticComplexity) {
      score -= 15;
    }
    if (complexity.cognitive > this.config.maxCognitiveComplexity) {
      score -= 10;
    }
    if (complexity.nestingDepth > this.config.maxNestingDepth) {
      score -= 10;
    }

    // Deduct points for obfuscation
    if (obfuscation.detected) {
      score -= obfuscation.confidence * 0.3; // 0-30 points
    }

    return Math.max(score, 0);
  }

  /**
   * Determine overall threat level
   */
  private determineThreatLevel(threats: SecurityThreat[], score: number): ThreatLevel {
    const criticalThreats = threats.filter(t => t.severity === 'critical').length;
    const highThreats = threats.filter(t => t.severity === 'high').length;
    const mediumThreats = threats.filter(t => t.severity === 'medium').length;

    if (criticalThreats > 0 || score < 30) {
      return 'critical';
    }
    if (highThreats > 2 || score < 50) {
      return 'high';
    }
    if (highThreats > 0 || mediumThreats > 3 || score < 70) {
      return 'medium';
    }
    if (mediumThreats > 0 || score < 90) {
      return 'low';
    }
    return 'none';
  }

  /**
   * Initialize built-in security rules
   */
  private initializeSecurityRules(): SecurityRule[] {
    const rules: SecurityRule[] = [
      {
        id: 'no-prototype-pollution',
        name: 'Prototype Pollution Prevention',
        description: 'Detects potential prototype pollution patterns',
        severity: 'high',
        pattern: /(__proto__|constructor\.prototype|Object\.prototype)/gi,
        message: 'Potential prototype pollution detected',
        suggestion: 'Avoid modifying object prototypes',
      },
      {
        id: 'no-unsafe-regex',
        name: 'Unsafe Regular Expression',
        description: 'Detects potentially unsafe regex patterns',
        severity: 'medium',
        pattern: /(.*\*.*\*|.*\+.*\+|.*\{.*,.*\}.*\{.*,.*\})/,
        message: 'Potentially unsafe regex pattern that could cause ReDoS',
        suggestion: 'Review regex for exponential time complexity',
      },
      {
        id: 'no-hardcoded-secrets',
        name: 'Hardcoded Secrets Detection',
        description: 'Detects potential hardcoded secrets',
        severity: 'high',
        pattern: /(password|secret|key|token)\s*[:=]\s*['"][^'"]{8,}/gi,
        message: 'Potential hardcoded secret detected',
        suggestion: 'Use environment variables or secure vaults for secrets',
      },
    ];

    return [...rules, ...this.config.customRules];
  }

  /**
   * Generate cache key for analysis result
   */
  private generateCacheKey(plugin: IPlugin): string {
    const pluginData = {
      name: plugin.name,
      version: plugin.version,
      execute: plugin.execute?.toString() || '',
      init: plugin.init?.toString() || '',
    };

    const content = JSON.stringify(pluginData);
    return require('crypto').createHash('sha256').update(content).digest('hex');
  }

  /**
   * Create empty complexity metrics
   */
  private createEmptyComplexityMetrics(): ComplexityMetrics {
    return {
      cyclomatic: 0,
      cognitive: 0,
      nestingDepth: 0,
      functionCount: 0,
      variableCount: 0,
      lineCount: 0,
      branchCount: 0,
      loopCount: 0,
    };
  }

  /**
   * Create empty obfuscation analysis
   */
  private createEmptyObfuscationAnalysis(): ObfuscationAnalysis {
    return {
      detected: false,
      confidence: 0,
      patterns: [],
      evidence: {
        minifiedVariables: 0,
        hexStrings: 0,
        unicodeEscapes: 0,
        evalUsage: 0,
        dynamicPropertyAccess: 0,
      },
    };
  }

  /**
   * Add custom security rule
   */
  addCustomRule(rule: SecurityRule): void {
    this.securityRules.push(rule);
    logger.info(`Added custom security rule: ${rule.name}`);
  }

  /**
   * Remove custom security rule
   */
  removeCustomRule(ruleId: string): boolean {
    const initialLength = this.securityRules.length;
    this.securityRules = this.securityRules.filter(rule => rule.id !== ruleId);

    if (this.securityRules.length < initialLength) {
      logger.info(`Removed custom security rule: ${ruleId}`);
      return true;
    }
    return false;
  }

  /**
   * Clear analysis cache
   */
  clearCache(): void {
    this.analysisCache.clear();
    logger.info('Code analysis cache cleared');
  }

  /**
   * Get analysis statistics
   */
  getStats(): object {
    return {
      cacheSize: this.analysisCache.size,
      securityRules: this.securityRules.length,
      config: this.config,
    };
  }

  /**
   * Cleanup resources
   */
  cleanup(): void {
    this.analysisCache.clear();
    logger.info('Code analyzer cleanup completed');
  }
}

/**
 * @fileoverview Emergency plugin termination and response control system
 * @lastmodified 2025-08-27T13:30:00Z
 *
 * Features: Emergency termination, incident response, forensic capture, recovery procedures
 * Main APIs: EmergencyController class for critical security response
 * Constraints: Real-time response, fail-safe mechanisms, forensic preservation
 * Patterns: Circuit breaker, emergency stop, incident response, forensic analysis
 */

import { EventEmitter } from 'events';
import * as crypto from 'crypto';
import * as fs from 'fs/promises';
import * as path from 'path';
import { Worker } from 'worker_threads';
import { logger } from '../../utils/logger';

/**
 * Emergency severity levels
 */
export type EmergencySeverity = 'low' | 'medium' | 'high' | 'critical' | 'catastrophic';

/**
 * Emergency trigger types
 */
export type EmergencyTrigger = 
  | 'resource-exhaustion'
  | 'security-violation'
  | 'malicious-behavior'
  | 'system-compromise'
  | 'data-breach'
  | 'privilege-escalation'
  | 'denial-of-service'
  | 'infinite-loop'
  | 'memory-leak'
  | 'unauthorized-access'
  | 'code-injection'
  | 'manual-trigger';

/**
 * Emergency response action
 */
export type EmergencyAction = 
  | 'terminate'
  | 'isolate'
  | 'quarantine'
  | 'suspend'
  | 'restrict'
  | 'monitor'
  | 'alert'
  | 'backup'
  | 'forensic-capture';

/**
 * Emergency incident data
 */
export interface EmergencyIncident {
  id: string;
  timestamp: Date;
  severity: EmergencySeverity;
  trigger: EmergencyTrigger;
  executionId: string;
  pluginId: string;
  pluginName: string;
  description: string;
  evidence: {
    resourceUsage?: {
      memory: number;
      cpu: number;
      executionTime: number;
    };
    securityViolations?: string[];
    suspiciousActivities?: string[];
    stackTrace?: string;
    networkActivity?: any[];
    fileAccess?: any[];
  };
  context: {
    userId?: string;
    sessionId?: string;
    ipAddress?: string;
    userAgent?: string;
    systemState?: any;
  };
  forensicData?: {
    memoryDump?: string;
    executionLog?: string[];
    networkCapture?: string;
    fileSystemSnapshot?: string;
  };
}

/**
 * Emergency response result
 */
export interface EmergencyResponse {
  incident: EmergencyIncident;
  actionsExecuted: EmergencyActionResult[];
  success: boolean;
  responseTime: number;
  recoverySteps?: string[];
  recommendations?: string[];
}

/**
 * Emergency action execution result
 */
export interface EmergencyActionResult {
  action: EmergencyAction;
  success: boolean;
  executionTime: number;
  details?: string;
  error?: string;
}

/**
 * Emergency controller configuration
 */
export interface EmergencyConfig {
  // Response settings
  enableAutomatedResponse: boolean;
  maxResponseTime: number; // milliseconds
  enableForensicCapture: boolean;
  enableRecoveryProcedures: boolean;

  // Thresholds
  memoryThresholdMB: number;
  cpuThresholdPercent: number;
  executionTimeThresholdMs: number;
  violationThreshold: number;

  // Actions
  defaultActions: {
    [key in EmergencySeverity]: EmergencyAction[];
  };
  customActionHandlers: Map<EmergencyAction, (incident: EmergencyIncident) => Promise<EmergencyActionResult>>;

  // Storage
  forensicStoragePath: string;
  incidentLogPath: string;
  retentionDays: number;

  // Notifications
  enableAlerts: boolean;
  alertEndpoints: string[];
  enableExternalNotifications: boolean;

  // Recovery
  enableAutoRecovery: boolean;
  maxRecoveryAttempts: number;
  recoveryDelay: number;
}

/**
 * Default emergency configuration
 */
export const DEFAULT_EMERGENCY_CONFIG: EmergencyConfig = {
  // Response settings
  enableAutomatedResponse: true,
  maxResponseTime: 5000, // 5 seconds
  enableForensicCapture: true,
  enableRecoveryProcedures: true,

  // Thresholds
  memoryThresholdMB: 1024, // 1GB
  cpuThresholdPercent: 95,
  executionTimeThresholdMs: 60000, // 1 minute
  violationThreshold: 5,

  // Actions
  defaultActions: {
    low: ['monitor', 'alert'],
    medium: ['restrict', 'monitor', 'alert'],
    high: ['isolate', 'forensic-capture', 'alert'],
    critical: ['terminate', 'quarantine', 'forensic-capture', 'alert'],
    catastrophic: ['terminate', 'quarantine', 'forensic-capture', 'alert', 'backup'],
  },
  customActionHandlers: new Map(),

  // Storage
  forensicStoragePath: './plugins/security/forensics',
  incidentLogPath: './plugins/security/incidents.log',
  retentionDays: 90,

  // Notifications
  enableAlerts: true,
  alertEndpoints: [],
  enableExternalNotifications: false,

  // Recovery
  enableAutoRecovery: false,
  maxRecoveryAttempts: 3,
  recoveryDelay: 5000,
};

/**
 * Execution context for emergency tracking
 */
interface ExecutionContext {
  executionId: string;
  pluginId: string;
  pluginName: string;
  startTime: Date;
  worker?: Worker;
  vmContext?: any;
  monitoring: {
    violations: string[];
    resourceUsage: any[];
    networkActivity: any[];
    fileAccess: any[];
  };
}

/**
 * Emergency plugin termination and response control system
 */
export class EmergencyController extends EventEmitter {
  private config: EmergencyConfig;
  private activeExecutions = new Map<string, ExecutionContext>();
  private incidentHistory: EmergencyIncident[] = [];
  private quarantinedPlugins = new Set<string>();
  private emergencyStops = new Set<string>();
  private forensicCaptures = new Map<string, string>(); // executionId -> forensic data path

  constructor(config: Partial<EmergencyConfig> = {}) {
    super();
    this.config = { ...DEFAULT_EMERGENCY_CONFIG, ...config };

    this.initializeDirectories();
    this.setupDefaultActionHandlers();

    logger.info('Emergency controller initialized');
  }

  /**
   * Register active plugin execution
   */
  registerExecution(
    executionId: string,
    pluginId: string,
    pluginName: string,
    worker?: Worker,
    vmContext?: any
  ): void {
    const context: ExecutionContext = {
      executionId,
      pluginId,
      pluginName,
      startTime: new Date(),
      worker,
      vmContext,
      monitoring: {
        violations: [],
        resourceUsage: [],
        networkActivity: [],
        fileAccess: [],
      },
    };

    this.activeExecutions.set(executionId, context);
    logger.debug(`Registered execution for emergency monitoring: ${executionId}`);
  }

  /**
   * Unregister plugin execution
   */
  unregisterExecution(executionId: string): void {
    this.activeExecutions.delete(executionId);
    this.emergencyStops.delete(executionId);
    logger.debug(`Unregistered execution from emergency monitoring: ${executionId}`);
  }

  /**
   * Record security violation for monitoring
   */
  recordViolation(executionId: string, violation: string): void {
    const context = this.activeExecutions.get(executionId);
    if (context) {
      context.monitoring.violations.push(violation);

      // Check if threshold exceeded
      if (context.monitoring.violations.length >= this.config.violationThreshold) {
        this.triggerEmergency(executionId, 'security-violation', 'high', 
          `Security violation threshold exceeded: ${context.monitoring.violations.length} violations`);
      }
    }
  }

  /**
   * Record resource usage for monitoring
   */
  recordResourceUsage(executionId: string, usage: any): void {
    const context = this.activeExecutions.get(executionId);
    if (!context) return;

    context.monitoring.resourceUsage.push(usage);

    // Check emergency thresholds
    if (usage.memory && usage.memory / (1024 * 1024) > this.config.memoryThresholdMB) {
      this.triggerEmergency(executionId, 'resource-exhaustion', 'critical',
        `Memory usage exceeded threshold: ${Math.round(usage.memory / (1024 * 1024))}MB > ${this.config.memoryThresholdMB}MB`);
    }

    if (usage.cpu && usage.cpu > this.config.cpuThresholdPercent) {
      this.triggerEmergency(executionId, 'resource-exhaustion', 'high',
        `CPU usage exceeded threshold: ${usage.cpu}% > ${this.config.cpuThresholdPercent}%`);
    }

    const executionTime = Date.now() - context.startTime.getTime();
    if (executionTime > this.config.executionTimeThresholdMs) {
      this.triggerEmergency(executionId, 'infinite-loop', 'critical',
        `Execution time exceeded threshold: ${executionTime}ms > ${this.config.executionTimeThresholdMs}ms`);
    }
  }

  /**
   * Trigger emergency response
   */
  async triggerEmergency(
    executionId: string,
    trigger: EmergencyTrigger,
    severity: EmergencySeverity,
    description: string,
    additionalEvidence?: any
  ): Promise<EmergencyResponse> {
    const startTime = Date.now();

    // Prevent duplicate emergency triggers
    if (this.emergencyStops.has(executionId)) {
      logger.warn(`Emergency already triggered for ${executionId}`);
      return {
        incident: {} as EmergencyIncident,
        actionsExecuted: [],
        success: false,
        responseTime: 0,
      };
    }

    this.emergencyStops.add(executionId);

    try {
      logger.error(`EMERGENCY TRIGGERED: ${executionId} - ${trigger} (${severity}): ${description}`);

      // Create incident record
      const incident = await this.createIncident(executionId, trigger, severity, description, additionalEvidence);

      // Execute emergency response
      const actions = this.config.defaultActions[severity] || [];
      const actionResults: EmergencyActionResult[] = [];

      for (const action of actions) {
        try {
          const result = await this.executeEmergencyAction(action, incident);
          actionResults.push(result);
        } catch (error: any) {
          actionResults.push({
            action,
            success: false,
            executionTime: 0,
            error: error.message,
          });
        }
      }

      const response: EmergencyResponse = {
        incident,
        actionsExecuted: actionResults,
        success: actionResults.every(result => result.success),
        responseTime: Date.now() - startTime,
      };

      // Add recovery recommendations
      response.recommendations = this.generateRecoveryRecommendations(incident, actionResults);

      // Store incident
      this.incidentHistory.push(incident);
      await this.persistIncident(incident);

      // Emit emergency event
      this.emit('emergency', response);

      logger.error(`Emergency response completed for ${executionId}: ${response.success ? 'SUCCESS' : 'PARTIAL'} (${response.responseTime}ms)`);
      return response;

    } catch (error: any) {
      logger.error(`Emergency response error for ${executionId}: ${error.message}`);
      return {
        incident: {} as EmergencyIncident,
        actionsExecuted: [],
        success: false,
        responseTime: Date.now() - startTime,
      };
    }
  }

  /**
   * Manual emergency trigger
   */
  async manualEmergency(
    executionId: string,
    severity: EmergencySeverity,
    reason: string
  ): Promise<EmergencyResponse> {
    return this.triggerEmergency(executionId, 'manual-trigger', severity, `Manual emergency trigger: ${reason}`);
  }

  /**
   * Quarantine plugin
   */
  async quarantinePlugin(pluginId: string, reason: string): Promise<boolean> {
    try {
      this.quarantinedPlugins.add(pluginId);

      // Terminate all active executions for this plugin
      const activeForPlugin = Array.from(this.activeExecutions.entries())
        .filter(([_, context]) => context.pluginId === pluginId);

      for (const [executionId, context] of activeForPlugin) {
        await this.terminateExecution(context);
      }

      logger.warn(`Plugin quarantined: ${pluginId} - ${reason}`);
      this.emit('quarantine', { pluginId, reason });

      return true;
    } catch (error: any) {
      logger.error(`Failed to quarantine plugin ${pluginId}: ${error.message}`);
      return false;
    }
  }

  /**
   * Check if plugin is quarantined
   */
  isQuarantined(pluginId: string): boolean {
    return this.quarantinedPlugins.has(pluginId);
  }

  /**
   * Release plugin from quarantine
   */
  releaseFromQuarantine(pluginId: string, reason: string): boolean {
    const wasQuarantined = this.quarantinedPlugins.delete(pluginId);
    if (wasQuarantined) {
      logger.info(`Plugin released from quarantine: ${pluginId} - ${reason}`);
      this.emit('quarantineRelease', { pluginId, reason });
    }
    return wasQuarantined;
  }

  /**
   * Get incident history
   */
  getIncidentHistory(limit?: number): EmergencyIncident[] {
    const history = [...this.incidentHistory].reverse(); // Most recent first
    return limit ? history.slice(0, limit) : history;
  }

  /**
   * Get quarantined plugins
   */
  getQuarantinedPlugins(): string[] {
    return Array.from(this.quarantinedPlugins);
  }

  /**
   * Create incident record
   */
  private async createIncident(
    executionId: string,
    trigger: EmergencyTrigger,
    severity: EmergencySeverity,
    description: string,
    additionalEvidence?: any
  ): Promise<EmergencyIncident> {
    const context = this.activeExecutions.get(executionId);
    const incident: EmergencyIncident = {
      id: crypto.randomUUID(),
      timestamp: new Date(),
      severity,
      trigger,
      executionId,
      pluginId: context?.pluginId || 'unknown',
      pluginName: context?.pluginName || 'unknown',
      description,
      evidence: {
        securityViolations: context?.monitoring.violations || [],
        suspiciousActivities: [],
        stackTrace: new Error().stack,
        networkActivity: context?.monitoring.networkActivity || [],
        fileAccess: context?.monitoring.fileAccess || [],
        ...additionalEvidence,
      },
      context: {
        systemState: await this.captureSystemState(),
      },
    };

    // Capture forensic data if enabled
    if (this.config.enableForensicCapture) {
      incident.forensicData = await this.captureForensicData(executionId, context);
    }

    return incident;
  }

  /**
   * Execute emergency action
   */
  private async executeEmergencyAction(
    action: EmergencyAction,
    incident: EmergencyIncident
  ): Promise<EmergencyActionResult> {
    const startTime = Date.now();

    try {
      // Check for custom handler
      const customHandler = this.config.customActionHandlers.get(action);
      if (customHandler) {
        return await customHandler(incident);
      }

      // Execute built-in action
      switch (action) {
        case 'terminate':
          await this.executeTerminate(incident);
          break;

        case 'isolate':
          await this.executeIsolate(incident);
          break;

        case 'quarantine':
          await this.executeQuarantine(incident);
          break;

        case 'suspend':
          await this.executeSuspend(incident);
          break;

        case 'restrict':
          await this.executeRestrict(incident);
          break;

        case 'monitor':
          await this.executeMonitor(incident);
          break;

        case 'alert':
          await this.executeAlert(incident);
          break;

        case 'backup':
          await this.executeBackup(incident);
          break;

        case 'forensic-capture':
          await this.executeForensicCapture(incident);
          break;

        default:
          throw new Error(`Unknown emergency action: ${action}`);
      }

      return {
        action,
        success: true,
        executionTime: Date.now() - startTime,
        details: `Action ${action} executed successfully`,
      };

    } catch (error: any) {
      return {
        action,
        success: false,
        executionTime: Date.now() - startTime,
        error: error.message,
      };
    }
  }

  /**
   * Execute terminate action
   */
  private async executeTerminate(incident: EmergencyIncident): Promise<void> {
    const context = this.activeExecutions.get(incident.executionId);
    if (context) {
      await this.terminateExecution(context);
    }
    logger.warn(`Executed TERMINATE for ${incident.executionId}`);
  }

  /**
   * Execute isolate action
   */
  private async executeIsolate(incident: EmergencyIncident): Promise<void> {
    const context = this.activeExecutions.get(incident.executionId);
    if (context) {
      // Isolate by restricting all permissions and network access
      this.emit('isolate', { executionId: incident.executionId, pluginId: incident.pluginId });
    }
    logger.warn(`Executed ISOLATE for ${incident.executionId}`);
  }

  /**
   * Execute quarantine action
   */
  private async executeQuarantine(incident: EmergencyIncident): Promise<void> {
    await this.quarantinePlugin(incident.pluginId, `Emergency quarantine: ${incident.trigger}`);
  }

  /**
   * Execute suspend action
   */
  private async executeSuspend(incident: EmergencyIncident): Promise<void> {
    // Suspend execution but keep for monitoring
    this.emit('suspend', { executionId: incident.executionId, pluginId: incident.pluginId });
    logger.warn(`Executed SUSPEND for ${incident.executionId}`);
  }

  /**
   * Execute restrict action
   */
  private async executeRestrict(incident: EmergencyIncident): Promise<void> {
    // Apply maximum security restrictions
    this.emit('restrict', { executionId: incident.executionId, pluginId: incident.pluginId });
    logger.warn(`Executed RESTRICT for ${incident.executionId}`);
  }

  /**
   * Execute monitor action
   */
  private async executeMonitor(incident: EmergencyIncident): Promise<void> {
    // Increase monitoring frequency
    this.emit('enhancedMonitoring', { executionId: incident.executionId, pluginId: incident.pluginId });
    logger.info(`Executed MONITOR for ${incident.executionId}`);
  }

  /**
   * Execute alert action
   */
  private async executeAlert(incident: EmergencyIncident): Promise<void> {
    const alert = {
      id: crypto.randomUUID(),
      timestamp: new Date(),
      type: 'emergency',
      severity: incident.severity,
      incident,
    };

    this.emit('alert', alert);

    // External notifications if configured
    if (this.config.enableExternalNotifications) {
      await this.sendExternalNotifications(alert);
    }

    logger.error(`Executed ALERT for ${incident.executionId}`);
  }

  /**
   * Execute backup action
   */
  private async executeBackup(incident: EmergencyIncident): Promise<void> {
    // Backup critical system state and data
    const backupId = crypto.randomUUID();
    await this.createSystemBackup(backupId, incident);
    logger.warn(`Executed BACKUP for ${incident.executionId} (backup ID: ${backupId})`);
  }

  /**
   * Execute forensic capture action
   */
  private async executeForensicCapture(incident: EmergencyIncident): Promise<void> {
    const context = this.activeExecutions.get(incident.executionId);
    if (context) {
      const forensicPath = await this.performForensicCapture(incident.executionId, context);
      this.forensicCaptures.set(incident.executionId, forensicPath);
      logger.warn(`Executed FORENSIC-CAPTURE for ${incident.executionId} (path: ${forensicPath})`);
    }
  }

  /**
   * Terminate execution
   */
  private async terminateExecution(context: ExecutionContext): Promise<void> {
    try {
      if (context.worker) {
        await context.worker.terminate();
        logger.debug(`Terminated worker for ${context.executionId}`);
      }

      if (context.vmContext) {
        // VM contexts cannot be directly terminated, but we can invalidate them
        logger.debug(`Invalidated VM context for ${context.executionId}`);
      }

      this.activeExecutions.delete(context.executionId);
    } catch (error: any) {
      logger.error(`Error terminating execution ${context.executionId}: ${error.message}`);
    }
  }

  /**
   * Capture system state for forensics
   */
  private async captureSystemState(): Promise<any> {
    return {
      timestamp: Date.now(),
      memoryUsage: process.memoryUsage(),
      cpuUsage: process.cpuUsage(),
      uptime: process.uptime(),
      activeExecutions: this.activeExecutions.size,
      quarantinedPlugins: this.quarantinedPlugins.size,
    };
  }

  /**
   * Capture forensic data
   */
  private async captureForensicData(
    executionId: string,
    context?: ExecutionContext
  ): Promise<any> {
    const forensicData: any = {
      executionId,
      timestamp: Date.now(),
      systemState: await this.captureSystemState(),
    };

    if (context) {
      forensicData.pluginContext = {
        pluginId: context.pluginId,
        pluginName: context.pluginName,
        startTime: context.startTime,
        executionTime: Date.now() - context.startTime.getTime(),
        monitoring: context.monitoring,
      };
    }

    return forensicData;
  }

  /**
   * Perform comprehensive forensic capture
   */
  private async performForensicCapture(
    executionId: string,
    context: ExecutionContext
  ): Promise<string> {
    const forensicDir = path.join(this.config.forensicStoragePath, executionId);
    await fs.mkdir(forensicDir, { recursive: true });

    const forensicData = await this.captureForensicData(executionId, context);
    const forensicPath = path.join(forensicDir, 'forensic-data.json');

    await fs.writeFile(forensicPath, JSON.stringify(forensicData, null, 2));
    return forensicPath;
  }

  /**
   * Send external notifications
   */
  private async sendExternalNotifications(alert: any): Promise<void> {
    // This would implement external notification mechanisms
    // For now, just log the alert
    logger.error(`External notification would be sent: ${JSON.stringify(alert)}`);
  }

  /**
   * Create system backup
   */
  private async createSystemBackup(backupId: string, incident: EmergencyIncident): Promise<void> {
    // This would implement system backup procedures
    logger.warn(`System backup created: ${backupId} for incident ${incident.id}`);
  }

  /**
   * Generate recovery recommendations
   */
  private generateRecoveryRecommendations(
    incident: EmergencyIncident,
    actionResults: EmergencyActionResult[]
  ): string[] {
    const recommendations: string[] = [];

    if (incident.trigger === 'resource-exhaustion') {
      recommendations.push('Review and optimize plugin resource usage');
      recommendations.push('Consider increasing resource limits if legitimate');
      recommendations.push('Implement resource usage monitoring');
    }

    if (incident.trigger === 'security-violation') {
      recommendations.push('Conduct security audit of plugin code');
      recommendations.push('Review plugin permissions and access controls');
      recommendations.push('Update security policies and rules');
    }

    if (incident.severity === 'critical' || incident.severity === 'catastrophic') {
      recommendations.push('Conduct thorough incident investigation');
      recommendations.push('Review system security posture');
      recommendations.push('Consider additional security controls');
    }

    const failedActions = actionResults.filter(r => !r.success);
    if (failedActions.length > 0) {
      recommendations.push('Review and fix failed emergency actions');
      recommendations.push('Test emergency response procedures');
    }

    return recommendations;
  }

  /**
   * Persist incident to storage
   */
  private async persistIncident(incident: EmergencyIncident): Promise<void> {
    try {
      const logEntry = `${incident.timestamp.toISOString()}: ${incident.severity.toUpperCase()} - ${incident.trigger} - ${incident.description}\n`;
      await fs.appendFile(this.config.incidentLogPath, logEntry);

      // Also save detailed incident data
      const incidentPath = path.join(
        path.dirname(this.config.incidentLogPath),
        'incidents',
        `${incident.id}.json`
      );

      await fs.mkdir(path.dirname(incidentPath), { recursive: true });
      await fs.writeFile(incidentPath, JSON.stringify(incident, null, 2));

    } catch (error: any) {
      logger.error(`Failed to persist incident ${incident.id}: ${error.message}`);
    }
  }

  /**
   * Initialize required directories
   */
  private async initializeDirectories(): Promise<void> {
    try {
      await fs.mkdir(this.config.forensicStoragePath, { recursive: true });
      await fs.mkdir(path.dirname(this.config.incidentLogPath), { recursive: true });
      await fs.mkdir(path.join(path.dirname(this.config.incidentLogPath), 'incidents'), { recursive: true });
    } catch (error: any) {
      logger.error(`Failed to initialize emergency directories: ${error.message}`);
    }
  }

  /**
   * Setup default action handlers
   */
  private setupDefaultActionHandlers(): void {
    // Custom action handlers can be added here
    // For now, using built-in handlers
  }

  /**
   * Add custom action handler
   */
  addCustomActionHandler(
    action: EmergencyAction,
    handler: (incident: EmergencyIncident) => Promise<EmergencyActionResult>
  ): void {
    this.config.customActionHandlers.set(action, handler);
    logger.info(`Added custom emergency action handler: ${action}`);
  }

  /**
   * Remove custom action handler
   */
  removeCustomActionHandler(action: EmergencyAction): boolean {
    const removed = this.config.customActionHandlers.delete(action);
    if (removed) {
      logger.info(`Removed custom emergency action handler: ${action}`);
    }
    return removed;
  }

  /**
   * Get emergency statistics
   */
  getEmergencyStats(): object {
    const severityCounts = this.incidentHistory.reduce((counts, incident) => {
      counts[incident.severity] = (counts[incident.severity] || 0) + 1;
      return counts;
    }, {} as Record<EmergencySeverity, number>);

    const triggerCounts = this.incidentHistory.reduce((counts, incident) => {
      counts[incident.trigger] = (counts[incident.trigger] || 0) + 1;
      return counts;
    }, {} as Record<EmergencyTrigger, number>);

    return {
      totalIncidents: this.incidentHistory.length,
      activeExecutions: this.activeExecutions.size,
      emergencyStops: this.emergencyStops.size,
      quarantinedPlugins: this.quarantinedPlugins.size,
      forensicCaptures: this.forensicCaptures.size,
      severityDistribution: severityCounts,
      triggerDistribution: triggerCounts,
      config: {
        enableAutomatedResponse: this.config.enableAutomatedResponse,
        enableForensicCapture: this.config.enableForensicCapture,
        maxResponseTime: this.config.maxResponseTime,
      },
    };
  }

  /**
   * Cleanup and dispose resources
   */
  async cleanup(): Promise<void> {
    logger.info('Starting emergency controller cleanup...');

    // Terminate all active executions
    const activeExecutions = Array.from(this.activeExecutions.values());
    for (const context of activeExecutions) {
      await this.terminateExecution(context);
    }

    // Clear all tracking data
    this.activeExecutions.clear();
    this.emergencyStops.clear();
    this.forensicCaptures.clear();

    // Keep incident history and quarantined plugins for persistence

    logger.info('Emergency controller cleanup completed');
  }
}

