/**
 * Security test types
 */
export type SecurityTestType = 
  | 'static-analysis'
  | 'dynamic-analysis'
  | 'penetration-test'
  | 'vulnerability-scan'
  | 'permission-test'
  | 'resource-test'
  | 'sandbox-escape'
  | 'injection-test'
  | 'compliance-check'
  | 'behavioral-analysis';

/**
 * Test severity levels
 */
export type TestSeverity = 'info' | 'low' | 'medium' | 'high' | 'critical';

/**
 * Security test result
 */
export interface SecurityTestResult {
  testId: string;
  testType: SecurityTestType;
  testName: string;
  description: string;
  severity: TestSeverity;
  passed: boolean;
  score: number; // 0-100
  details: {
    findings: SecurityFinding[];
    metrics: TestMetrics;
    evidence?: unknown[];
    recommendations?: string[];
  };
  executionTime: number;
  timestamp: Date;
}

/**
 * Security finding
 */
export interface SecurityFinding {
  id: string;
  type: 'vulnerability' | 'weakness' | 'violation' | 'anomaly';
  severity: TestSeverity;
  title: string;
  description: string;
  location?: {
    file?: string;
    line?: number;
    column?: number;
    function?: string;
  };
  cwe?: string; // Common Weakness Enumeration
  owasp?: string; // OWASP category
  evidence?: unknown[];
  remediation?: string;
  riskScore: number; // 0-100
}

/**
 * Test metrics
 */
export interface TestMetrics {
  totalChecks: number;
  passedChecks: number;
  failedChecks: number;
  skippedChecks: number;
  coverage: number; // 0-100
  timeToComplete: number;
  resourceUsage?: {
    cpu: number;
    memory: number;
  };
}

/**
 * Comprehensive test suite result
 */
export interface SecurityTestSuiteResult {
  suiteId: string;
  pluginId: string;
  pluginName: string;
  timestamp: Date;
  overallScore: number; // 0-100
  riskLevel: 'none' | 'low' | 'medium' | 'high' | 'critical';
  complianceScore: number; // 0-100
