  /**
   * Calculate suite results
   */
  private calculateSuiteResults(
    suiteId: string,
    plugin: IPlugin,
    testResults: SecurityTestResult[],
    executionTime: number
  ): SecurityTestSuiteResult {
    const totalTests = testResults.length;
    const passedTests = testResults.filter(r => r.passed).length;
    const failedTests = totalTests - passedTests;

    const criticalFindings = testResults.reduce((count, r) => 
      count + r.details.findings.filter(f => f.severity === 'critical').length, 0);
    const highFindings = testResults.reduce((count, r) => 
      count + r.details.findings.filter(f => f.severity === 'high').length, 0);
    const mediumFindings = testResults.reduce((count, r) => 
      count + r.details.findings.filter(f => f.severity === 'medium').length, 0);
    const lowFindings = testResults.reduce((count, r) => 
      count + r.details.findings.filter(f => f.severity === 'low').length, 0);

    // Calculate overall score (weighted average)
    const weightedScore = testResults.reduce((sum, result) => {
      const weight = this.getTestWeight(result.testType);
      return sum + (result.score * weight);
    }, 0);
    const totalWeight = testResults.reduce((sum, result) => 
      sum + this.getTestWeight(result.testType), 0);
    const overallScore = totalWeight > 0 ? Math.round(weightedScore / totalWeight) : 0;

    // Determine risk level
    let riskLevel: SecurityTestSuiteResult['riskLevel'];
    if (criticalFindings > 0) riskLevel = 'critical';
    else if (highFindings > 0) riskLevel = 'high';
    else if (mediumFindings > 5) riskLevel = 'medium';
    else if (mediumFindings > 0 || lowFindings > 10) riskLevel = 'low';
    else riskLevel = 'none';

    // Calculate compliance score
    const complianceResult = testResults.find(r => r.testType === 'compliance-check');
    const complianceScore = complianceResult?.score || 0;

    // Generate recommendations
    const recommendations = this.generateSuiteRecommendations(testResults);
    const complianceGaps = this.identifyComplianceGaps(testResults);

    return {
      suiteId,
      pluginId: `${plugin.name}@${plugin.version}`,
      pluginName: plugin.name,
      timestamp: new Date(),
      overallScore,
      riskLevel,
      complianceScore,
      testResults,
      summary: {
        totalTests,
        passedTests,
        failedTests,
        criticalFindings,
        highFindings,
        mediumFindings,
        lowFindings,
        executionTime,
      },
      recommendations,
      complianceGaps,
    };
  }

  /**
   * Get test weight for scoring calculation
   */
  private getTestWeight(testType: SecurityTestType): number {
    const weights: Record<SecurityTestType, number> = {
      'static-analysis': 20,
      'dynamic-analysis': 15,
      'penetration-test': 25,
      'vulnerability-scan': 20,
      'permission-test': 10,
      'resource-test': 5,
      'sandbox-escape': 15,
      'injection-test': 15,
      'compliance-check': 10,
      'behavioral-analysis': 10,
    };

    return weights[testType] || 10;
  }

  /**
   * Calculate risk score from severity
   */
  private calculateRiskScore(severity: TestSeverity): number {
    const scores: Record<TestSeverity, number> = {
      info: 10,
      low: 30,
      medium: 60,
      high: 80,
      critical: 95,
    };

    return scores[severity] || 50;
  }

  /**
   * Create error test result
   */
  private createErrorResult(testType: SecurityTestType, testName: string, errorMessage: string): SecurityTestResult {
    return {
      testId: '',
      testType,
      testName,
      description: 'Security test execution',
      severity: 'high',
      passed: false,
      score: 0,
      details: {
        findings: [{
          id: crypto.randomUUID(),
          type: 'vulnerability',
          severity: 'high',
          title: 'Test Execution Error',
          description: `Test failed with error: ${errorMessage}`,
          riskScore: 70,
        }],
        metrics: {
          totalChecks: 1,
          passedChecks: 0,
          failedChecks: 1,
          skippedChecks: 0,
          coverage: 0,
          timeToComplete: 0,
        },
      },
      executionTime: 0,
      timestamp: new Date(),
    };
  }

  /**
   * Generate suite recommendations
   */
  private generateSuiteRecommendations(testResults: SecurityTestResult[]): string[] {
    const recommendations = new Set<string>();

    for (const result of testResults) {
      if (result.details.recommendations) {
        result.details.recommendations.forEach(rec => recommendations.add(rec));
      }

      // Add generic recommendations based on findings
      const criticalFindings = result.details.findings.filter(f => f.severity === 'critical');
      const highFindings = result.details.findings.filter(f => f.severity === 'high');

      if (criticalFindings.length > 0) {
        recommendations.add('Address all critical security vulnerabilities immediately');
        recommendations.add('Consider blocking plugin deployment until critical issues are resolved');
      }

      if (highFindings.length > 0) {
        recommendations.add('Review and fix high-severity security issues');
        recommendations.add('Implement additional security controls and monitoring');
      }
    }

    return Array.from(recommendations);
  }

  /**
   * Identify compliance gaps
   */
  private identifyComplianceGaps(testResults: SecurityTestResult[]): string[] {
    const gaps: string[] = [];

    const complianceResult = testResults.find(r => r.testType === 'compliance-check');
    if (complianceResult && complianceResult.score < 85) {
      gaps.push('Security compliance below recommended threshold');

      for (const finding of complianceResult.details.findings) {
        gaps.push(finding.description);
      }
    }

    return gaps;
  }

  /**
   * Get test results by suite ID
   */
  getTestResults(suiteId: string): SecurityTestSuiteResult | null {
    return this.testResults.get(suiteId) || null;
  }

  /**
   * Get all test results
   */
  getAllTestResults(): SecurityTestSuiteResult[] {
    return Array.from(this.testResults.values());
  }

  /**
   * Clear test results
   */
  clearTestResults(): void {
    this.testResults.clear();
  }

  /**
   * Get test statistics
   */
  getTestStats(): object {
    return {
      totalSuites: this.testResults.size,
      config: this.config,
      enabledTests: this.config.enabledTests,
    };
  }

  /**
   * Cleanup and dispose resources
   */
  async cleanup(): Promise<void> {
    await this.sandbox.cleanup();
    this.codeAnalyzer.cleanup();
    this.signatureVerifier.cleanup();
    this.behaviorMonitor.cleanup();
    this.permissionManager.cleanup();
    await this.emergencyController.cleanup();
    this.resourceMonitor.cleanup();

    this.testResults.clear();

    logger.info('Security test suite cleanup completed');
  }
}

/**
 * @fileoverview Cryptographic plugin signature verification service
 * @lastmodified 2025-08-27T12:30:00Z
 *
 * Features: Digital signatures, certificate chains, trust management, revocation checking
 * Main APIs: SignatureVerifier class for comprehensive plugin authenticity verification
 * Constraints: Cryptographic security, certificate validation, trust store management
 * Patterns: PKI infrastructure, chain of trust, certificate lifecycle management
 */

import * as crypto from 'crypto';
import * as fs from 'fs/promises';
import * as path from 'path';
import { IPlugin } from '../../types';
import { logger } from '../../utils/logger';

/**
 * Plugin signature metadata
 */
export interface PluginSignature {
  signature: string;
  algorithm: string;
  keyId: string;
  timestamp: number;
  version: string;
  certChain?: string[];
  metadata?: {
    issuer: string;
    subject: string;
    validFrom: number;
    validTo: number;
  };
}

/**
 * Signature verification result
 */
export interface SignatureVerificationResult {
  valid: boolean;
  trustLevel: 'untrusted' | 'basic' | 'verified' | 'trusted' | 'official';
  algorithm?: string;
  keyId?: string;
  issuer?: string;
  timestamp?: number;
  error?: string;
  warnings: string[];
  certificateChain?: CertificateInfo[];
  revoked?: boolean;
  expired?: boolean;
}

/**
 * Certificate information
 */
export interface CertificateInfo {
  subject: string;
  issuer: string;
  serialNumber: string;
  validFrom: Date;
  validTo: Date;
  fingerprint: string;
  publicKey: string;
  keyUsage: string[];
  isCA: boolean;
}

/**
 * Trust store entry
 */
export interface TrustedPublisher {
  keyId: string;
  publicKey: string;
  name: string;
  organization: string;
  trustLevel: 'basic' | 'verified' | 'trusted' | 'official';
  validFrom: Date;
  validTo: Date;
  permissions: string[];
  revoked: boolean;
  metadata?: {
    website?: string;
    contact?: string;
    description?: string;
  };
}

/**
 * Revocation list entry
 */
export interface RevokedCertificate {
  keyId: string;
  serialNumber?: string;
  revocationDate: Date;
  reason: 'unspecified' | 'keyCompromise' | 'caCompromise' | 'affiliationChanged' | 
          'superseded' | 'cessationOfOperation' | 'certificateHold';
  issuer: string;
}

/**
 * Signature verification configuration
 */
export interface SignatureConfig {
  // Verification settings
  requireSignature: boolean;
  allowSelfSigned: boolean;
  allowExpiredCertificates: boolean;
  maxCertChainLength: number;

  // Trust levels
  minimumTrustLevel: 'untrusted' | 'basic' | 'verified' | 'trusted' | 'official';
  requireOfficialSignature: boolean;

  // Paths and stores
  trustStorePath: string;
  revocationListPath: string;

  // Validation settings
  checkRevocation: boolean;
  allowOnlineRevocationCheck: boolean;
  revocationCheckTimeoutMs: number;

  // Algorithm settings
  allowedAlgorithms: string[];
  minimumKeySize: number;

  // Time validation
  allowClockSkewSeconds: number;
  requireTimestamp: boolean;
}

/**
 * Default signature verification configuration
 */
export const DEFAULT_SIGNATURE_CONFIG: SignatureConfig = {
  // Verification settings
  requireSignature: true,
  allowSelfSigned: false,
  allowExpiredCertificates: false,
  maxCertChainLength: 5,

  // Trust levels
  minimumTrustLevel: 'basic',
  requireOfficialSignature: false,

  // Paths
  trustStorePath: './plugins/security/trust-store.json',
  revocationListPath: './plugins/security/revocation-list.json',

  // Validation
  checkRevocation: true,
  allowOnlineRevocationCheck: false,
  revocationCheckTimeoutMs: 5000,

  // Algorithms
  allowedAlgorithms: ['RS256', 'RS384', 'RS512', 'ES256', 'ES384', 'ES512'],
  minimumKeySize: 2048,

  // Time validation
  allowClockSkewSeconds: 300, // 5 minutes
  requireTimestamp: true,
};

/**
 * Cryptographic plugin signature verification service
 */
export class SignatureVerifier {
  private config: SignatureConfig;
  private trustStore = new Map<string, TrustedPublisher>();
  private revocationList = new Map<string, RevokedCertificate>();
  private verificationCache = new Map<string, SignatureVerificationResult>();

  constructor(config: Partial<SignatureConfig> = {}) {
    this.config = { ...DEFAULT_SIGNATURE_CONFIG, ...config };
    logger.info('Plugin signature verifier initialized');
  }

  /**
   * Initialize trust store and revocation list
   */
  async initialize(): Promise<void> {
    try {
      await this.loadTrustStore();
      await this.loadRevocationList();
      logger.info('Signature verifier initialized successfully');
    } catch (error: any) {
      logger.error(`Failed to initialize signature verifier: ${error.message}`);
      throw error;
    }
  }

  /**
   * Verify plugin signature with comprehensive validation
   */
  async verifyPlugin(plugin: IPlugin, signature?: PluginSignature): Promise<SignatureVerificationResult> {
    try {
      // Check if signature is required
      if (this.config.requireSignature && !signature) {
        return {
          valid: false,
          trustLevel: 'untrusted',
          error: 'Plugin signature required but not provided',
          warnings: [],
        };
      }

      if (!signature) {
        return {
          valid: true,
          trustLevel: 'untrusted',
          warnings: ['Plugin not signed - signature verification skipped'],
        };
      }

      // Generate cache key
      const cacheKey = this.generateCacheKey(plugin, signature);
      const cached = this.verificationCache.get(cacheKey);
      if (cached) {
        logger.debug(`Using cached signature verification for plugin ${plugin.name}`);
        return cached;
      }

      logger.info(`Verifying signature for plugin: ${plugin.name}`);

      // Validate signature format and algorithm
      const formatValidation = this.validateSignatureFormat(signature);
      if (!formatValidation.valid) {
        return {
          valid: false,
          trustLevel: 'untrusted',
          error: formatValidation.error,
          warnings: [],
        };
      }

      // Check algorithm whitelist
      if (!this.config.allowedAlgorithms.includes(signature.algorithm)) {
        return {
          valid: false,
          trustLevel: 'untrusted',
          error: `Signature algorithm not allowed: ${signature.algorithm}`,
          warnings: [],
        };
      }

      // Generate plugin fingerprint
      const pluginFingerprint = this.generatePluginFingerprint(plugin);

      // Verify signature
      const cryptoVerification = await this.verifyCryptographicSignature(
        pluginFingerprint,
        signature
      );

      if (!cryptoVerification.valid) {
        const result: SignatureVerificationResult = {
          valid: false,
          trustLevel: 'untrusted',
          error: cryptoVerification.error,
          warnings: cryptoVerification.warnings || [],
        };
        this.verificationCache.set(cacheKey, result);
        return result;
      }

      // Check certificate chain and trust
      const trustValidation = await this.validateTrustChain(signature);

      // Check revocation status
      const revocationCheck = await this.checkRevocationStatus(signature);

      // Validate timestamp
      const timestampValidation = this.validateTimestamp(signature);

      // Combine all validation results
      const result = this.combineValidationResults(
        cryptoVerification,
        trustValidation,
        revocationCheck,
        timestampValidation,
        signature
      );

      // Cache result
      this.verificationCache.set(cacheKey, result);

      logger.info(`Signature verification completed for ${plugin.name}: ${result.valid ? 'VALID' : 'INVALID'} (${result.trustLevel})`);
      return result;

    } catch (error: any) {
      logger.error(`Signature verification error for ${plugin.name}: ${error.message}`);
      return {
        valid: false,
        trustLevel: 'untrusted',
        error: `Verification error: ${error.message}`,
        warnings: [],
      };
    }
  }

  /**
   * Sign a plugin with the provided private key
   */
  async signPlugin(plugin: IPlugin, privateKey: string, keyId: string): Promise<PluginSignature> {
    try {
      logger.info(`Signing plugin: ${plugin.name}`);

      // Generate plugin fingerprint
      const pluginFingerprint = this.generatePluginFingerprint(plugin);

      // Create signature
      const signature = crypto.sign('RSA-SHA256', Buffer.from(pluginFingerprint), {
        key: privateKey,
        padding: crypto.constants.RSA_PKCS1_PSS_PADDING,
      });

      // Get certificate info if available
      const certInfo = this.getCertificateInfo(keyId);

      const pluginSignature: PluginSignature = {
        signature: signature.toString('base64'),
        algorithm: 'RS256',
        keyId,
        timestamp: Date.now(),
        version: '1.0',
        metadata: certInfo ? {
          issuer: certInfo.issuer,
          subject: certInfo.subject,
          validFrom: certInfo.validFrom.getTime(),
          validTo: certInfo.validTo.getTime(),
        } : undefined,
      };

      logger.info(`Plugin signed successfully: ${plugin.name}`);
      return pluginSignature;

    } catch (error: any) {
      logger.error(`Failed to sign plugin ${plugin.name}: ${error.message}`);
      throw new Error(`Plugin signing failed: ${error.message}`);
    }
  }

  /**
   * Add trusted publisher to trust store
   */
  async addTrustedPublisher(publisher: TrustedPublisher): Promise<void> {
    try {
      // Validate publisher data
      this.validateTrustedPublisher(publisher);

      // Add to trust store
      this.trustStore.set(publisher.keyId, publisher);

      // Persist to disk
      await this.saveTrustStore();

      logger.info(`Added trusted publisher: ${publisher.name} (${publisher.keyId})`);
    } catch (error: any) {
      logger.error(`Failed to add trusted publisher: ${error.message}`);
      throw error;
    }
  }

  /**
   * Revoke a certificate
   */
  async revokeCertificate(keyId: string, reason: RevokedCertificate['reason']): Promise<void> {
    try {
      const revokedCert: RevokedCertificate = {
        keyId,
        revocationDate: new Date(),
        reason,
        issuer: 'plugin-security-system',
      };

      this.revocationList.set(keyId, revokedCert);

      // Remove from trust store
      this.trustStore.delete(keyId);

      // Persist changes
      await Promise.all([
        this.saveRevocationList(),
        this.saveTrustStore(),
      ]);

      // Clear verification cache for this key
      this.clearCacheForKey(keyId);

      logger.warn(`Certificate revoked: ${keyId} (${reason})`);
    } catch (error: any) {
      logger.error(`Failed to revoke certificate ${keyId}: ${error.message}`);
      throw error;
    }
  }

  /**
   * Validate signature format
   */
  private validateSignatureFormat(signature: PluginSignature): { valid: boolean; error?: string } {
    if (!signature.signature || typeof signature.signature !== 'string') {
      return { valid: false, error: 'Invalid signature format - missing or invalid signature field' };
    }

    if (!signature.algorithm || typeof signature.algorithm !== 'string') {
      return { valid: false, error: 'Invalid signature format - missing or invalid algorithm field' };
    }

    if (!signature.keyId || typeof signature.keyId !== 'string') {
      return { valid: false, error: 'Invalid signature format - missing or invalid keyId field' };
    }

    if (!signature.timestamp || typeof signature.timestamp !== 'number') {
      return { valid: false, error: 'Invalid signature format - missing or invalid timestamp field' };
    }

    // Validate base64 signature
    try {
      Buffer.from(signature.signature, 'base64');
    } catch {
      return { valid: false, error: 'Invalid signature format - signature is not valid base64' };
    }

    return { valid: true };
  }

  /**
   * Generate plugin fingerprint for signature verification
   */
  private generatePluginFingerprint(plugin: IPlugin): string {
    const pluginData = {
      name: plugin.name,
      version: plugin.version,
      description: plugin.description,
      author: plugin.author,
      // Include function code for verification
      execute: plugin.execute?.toString() || '',
      init: plugin.init?.toString() || '',
      // Include metadata
      timestamp: (plugin as any).timestamp || Date.now(),
    };

    // Create deterministic hash
    const jsonString = JSON.stringify(pluginData, Object.keys(pluginData).sort());
    return crypto.createHash('sha256').update(jsonString).digest('hex');
  }

  /**
   * Verify cryptographic signature
   */
  private async verifyCryptographicSignature(
    data: string,
    signature: PluginSignature
  ): Promise<{ valid: boolean; error?: string; warnings?: string[] }> {
    try {
      // Get public key for verification
      const publicKey = await this.getPublicKey(signature.keyId);
      if (!publicKey) {
        return {
          valid: false,
          error: `Public key not found for keyId: ${signature.keyId}`,
        };
      }

      // Verify signature
      const isValid = crypto.verify(
        'RSA-SHA256',
        Buffer.from(data),
        {
          key: publicKey,
          padding: crypto.constants.RSA_PKCS1_PSS_PADDING,
        },
        Buffer.from(signature.signature, 'base64')
      );

      return {
        valid: isValid,
        error: isValid ? undefined : 'Cryptographic signature verification failed',
      };

    } catch (error: any) {
      return {
        valid: false,
        error: `Signature verification error: ${error.message}`,
      };
    }
  }

  /**
   * Validate trust chain
   */
  private async validateTrustChain(signature: PluginSignature): Promise<{
    trustLevel: SignatureVerificationResult['trustLevel'];
    warnings: string[];
    certificateChain?: CertificateInfo[];
  }> {
    const warnings: string[] = [];

    // Check if key is in trust store
    const trustedPublisher = this.trustStore.get(signature.keyId);

    if (!trustedPublisher) {
      if (this.config.allowSelfSigned) {
        warnings.push('Self-signed certificate - limited trust');
        return { trustLevel: 'basic', warnings };
      }
      return { trustLevel: 'untrusted', warnings: ['Publisher not in trust store'] };
    }

    // Check if publisher is revoked
    if (trustedPublisher.revoked) {
      return { trustLevel: 'untrusted', warnings: ['Publisher has been revoked'] };
    }

    // Check expiration
    if (new Date() > trustedPublisher.validTo) {
      if (!this.config.allowExpiredCertificates) {
        return { trustLevel: 'untrusted', warnings: ['Certificate has expired'] };
      }
      warnings.push('Certificate has expired but allowed by configuration');
    }

    // Check not yet valid
    if (new Date() < trustedPublisher.validFrom) {
      return { trustLevel: 'untrusted', warnings: ['Certificate not yet valid'] };
    }

    return { trustLevel: trustedPublisher.trustLevel, warnings };
  }

  /**
   * Check revocation status
   */
  private async checkRevocationStatus(signature: PluginSignature): Promise<{
    revoked: boolean;
    warnings: string[];
  }> {
    const warnings: string[] = [];

    if (!this.config.checkRevocation) {
      return { revoked: false, warnings };
    }

    // Check local revocation list
    const revokedCert = this.revocationList.get(signature.keyId);
    if (revokedCert) {
      return { revoked: true, warnings: [`Certificate revoked: ${revokedCert.reason}`] };
    }

    // Online revocation checking would go here
    if (this.config.allowOnlineRevocationCheck) {
      warnings.push('Online revocation checking not implemented');
    }

    return { revoked: false, warnings };
  }

  /**
   * Validate timestamp
   */
  private validateTimestamp(signature: PluginSignature): {
    valid: boolean;
    warnings: string[];
  } {
    const warnings: string[] = [];

    if (!this.config.requireTimestamp) {
      return { valid: true, warnings };
    }

    if (!signature.timestamp) {
      return { valid: false, warnings: ['Timestamp required but not provided'] };
    }

    const now = Date.now();
    const clockSkewMs = this.config.allowClockSkewSeconds * 1000;

    // Check if signature is too old or too new
    const timeDiff = Math.abs(now - signature.timestamp);
    if (timeDiff > clockSkewMs) {
      warnings.push(`Timestamp outside allowed clock skew: ${timeDiff}ms`);
      return { valid: false, warnings };
    }

    return { valid: true, warnings };
  }

  /**
   * Combine validation results
   */
  private combineValidationResults(
    crypto: { valid: boolean; error?: string; warnings?: string[] },
    trust: { trustLevel: SignatureVerificationResult['trustLevel']; warnings: string[]; certificateChain?: CertificateInfo[] },
    revocation: { revoked: boolean; warnings: string[] },
    timestamp: { valid: boolean; warnings: string[] },
    signature: PluginSignature
  ): SignatureVerificationResult {
    const allWarnings = [
      ...(crypto.warnings || []),
      ...trust.warnings,
      ...revocation.warnings,
      ...timestamp.warnings,
    ];

    // Signature is valid only if all checks pass
    const isValid = crypto.valid && 
                    !revocation.revoked && 
                    timestamp.valid &&
                    trust.trustLevel !== 'untrusted';

    // Check minimum trust level
    const trustLevelValues = { untrusted: 0, basic: 1, verified: 2, trusted: 3, official: 4 };
    const minTrustValue = trustLevelValues[this.config.minimumTrustLevel];
    const currentTrustValue = trustLevelValues[trust.trustLevel];

    if (currentTrustValue < minTrustValue) {
      return {
        valid: false,
        trustLevel: trust.trustLevel,
        error: `Trust level ${trust.trustLevel} below minimum required ${this.config.minimumTrustLevel}`,
        warnings: allWarnings,
        algorithm: signature.algorithm,
        keyId: signature.keyId,
        timestamp: signature.timestamp,
        certificateChain: trust.certificateChain,
        revoked: revocation.revoked,
        expired: trust.warnings.some(w => w.includes('expired')),
      };
    }

    return {
      valid: isValid,
      trustLevel: trust.trustLevel,
      error: crypto.error,
      warnings: allWarnings,
      algorithm: signature.algorithm,
      keyId: signature.keyId,
      issuer: signature.metadata?.issuer,
      timestamp: signature.timestamp,
      certificateChain: trust.certificateChain,
      revoked: revocation.revoked,
      expired: trust.warnings.some(w => w.includes('expired')),
    };
  }

  /**
   * Generate cache key for verification result
   */
  private generateCacheKey(plugin: IPlugin, signature: PluginSignature): string {
    const pluginHash = this.generatePluginFingerprint(plugin);
    const sigHash = crypto.createHash('sha256')
      .update(`${signature.signature}${signature.keyId}${signature.timestamp}`)
      .digest('hex');
    return `${pluginHash}-${sigHash}`;
  }

  /**
   * Get public key for verification
   */
  private async getPublicKey(keyId: string): Promise<string | null> {
    const trustedPublisher = this.trustStore.get(keyId);
    return trustedPublisher?.publicKey || null;
  }

  /**
   * Get certificate information
   */
  private getCertificateInfo(keyId: string): CertificateInfo | null {
    const trustedPublisher = this.trustStore.get(keyId);
    if (!trustedPublisher) return null;

    return {
      subject: trustedPublisher.name,
      issuer: trustedPublisher.organization,
      serialNumber: keyId,
      validFrom: trustedPublisher.validFrom,
      validTo: trustedPublisher.validTo,
      fingerprint: crypto.createHash('sha256').update(trustedPublisher.publicKey).digest('hex'),
      publicKey: trustedPublisher.publicKey,
      keyUsage: trustedPublisher.permissions,
      isCA: trustedPublisher.trustLevel === 'official',
    };
  }

  /**
   * Validate trusted publisher data
   */
  private validateTrustedPublisher(publisher: TrustedPublisher): void {
    if (!publisher.keyId || !publisher.publicKey || !publisher.name) {
      throw new Error('Trusted publisher missing required fields');
    }

    if (!['basic', 'verified', 'trusted', 'official'].includes(publisher.trustLevel)) {
      throw new Error('Invalid trust level');
    }

    // Validate public key format
    try {
      crypto.createPublicKey(publisher.publicKey);
    } catch (error) {
      throw new Error('Invalid public key format');
    }

    // Validate dates
    if (publisher.validFrom >= publisher.validTo) {
      throw new Error('Invalid validity period');
    }
  }

  /**
   * Load trust store from disk
   */
  private async loadTrustStore(): Promise<void> {
    try {
      const trustStoreData = await fs.readFile(this.config.trustStorePath, 'utf8');
      const publishers = JSON.parse(trustStoreData) as TrustedPublisher[];

      this.trustStore.clear();
      for (const publisher of publishers) {
        // Convert date strings back to Date objects
        publisher.validFrom = new Date(publisher.validFrom);
        publisher.validTo = new Date(publisher.validTo);
        this.trustStore.set(publisher.keyId, publisher);
      }

      logger.info(`Loaded ${publishers.length} trusted publishers`);
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        logger.info('Trust store not found - creating empty trust store');
        await this.createDefaultTrustStore();
      } else {
        logger.error(`Failed to load trust store: ${error.message}`);
        throw error;
      }
    }
  }

  /**
   * Save trust store to disk
   */
  private async saveTrustStore(): Promise<void> {
    try {
      const publishers = Array.from(this.trustStore.values());
      const trustStoreData = JSON.stringify(publishers, null, 2);

      await fs.mkdir(path.dirname(this.config.trustStorePath), { recursive: true });
      await fs.writeFile(this.config.trustStorePath, trustStoreData);

      logger.debug('Trust store saved successfully');
    } catch (error: any) {
      logger.error(`Failed to save trust store: ${error.message}`);
      throw error;
    }
  }

  /**
   * Load revocation list from disk
   */
  private async loadRevocationList(): Promise<void> {
    try {
      const revocationData = await fs.readFile(this.config.revocationListPath, 'utf8');
      const revocations = JSON.parse(revocationData) as RevokedCertificate[];

      this.revocationList.clear();
      for (const revocation of revocations) {
        // Convert date strings back to Date objects
        revocation.revocationDate = new Date(revocation.revocationDate);
        this.revocationList.set(revocation.keyId, revocation);
      }

      logger.info(`Loaded ${revocations.length} revoked certificates`);
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        logger.info('Revocation list not found - creating empty list');
        await this.saveRevocationList();
      } else {
        logger.error(`Failed to load revocation list: ${error.message}`);
        throw error;
      }
    }
  }

  /**
   * Save revocation list to disk
   */
  private async saveRevocationList(): Promise<void> {
    try {
      const revocations = Array.from(this.revocationList.values());
      const revocationData = JSON.stringify(revocations, null, 2);

      await fs.mkdir(path.dirname(this.config.revocationListPath), { recursive: true });
      await fs.writeFile(this.config.revocationListPath, revocationData);

      logger.debug('Revocation list saved successfully');
    } catch (error: any) {
      logger.error(`Failed to save revocation list: ${error.message}`);
      throw error;
    }
  }

  /**
   * Create default trust store with system certificates
   */
  private async createDefaultTrustStore(): Promise<void> {
    // This would include system default trusted publishers
    const defaultPublishers: TrustedPublisher[] = [
      {
        keyId: 'system-default',
        publicKey: '-----BEGIN PUBLIC KEY-----\n...\n-----END PUBLIC KEY-----', // Would be actual key
        name: 'System Default',
        organization: 'Plugin Security System',
        trustLevel: 'official',
        validFrom: new Date(),
        validTo: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000), // 1 year
        permissions: ['*'],
        revoked: false,
        metadata: {
          description: 'Default system certificate for plugin signing',
        },
      },
    ];

    for (const publisher of defaultPublishers) {
      this.trustStore.set(publisher.keyId, publisher);
    }

    await this.saveTrustStore();
    logger.info('Created default trust store');
  }

  /**
   * Clear verification cache for a specific key
   */
  private clearCacheForKey(keyId: string): void {
    const keysToDelete = Array.from(this.verificationCache.keys())
      .filter(key => key.includes(keyId));

    for (const key of keysToDelete) {
      this.verificationCache.delete(key);
    }
  }

  /**
   * Get trust store statistics
   */
  getTrustStoreStats(): object {
    const publishers = Array.from(this.trustStore.values());
    const revoked = Array.from(this.revocationList.values());

    const trustLevelCounts = publishers.reduce((counts, pub) => {
      counts[pub.trustLevel] = (counts[pub.trustLevel] || 0) + 1;
      return counts;
    }, {} as Record<string, number>);

    return {
      totalPublishers: publishers.length,
      revokedCertificates: revoked.length,
      trustLevelDistribution: trustLevelCounts,
      cacheSize: this.verificationCache.size,
    };
  }

  /**
   * Cleanup verification cache and resources
   */
  cleanup(): void {
    this.verificationCache.clear();
    logger.info('Signature verifier cleanup completed');
  }
}

/**
 * @fileoverview Job queue system for optimization tasks with priority scheduling and progress tracking
 * @lastmodified 2025-08-26T16:00:00Z
 *
 * Features: Priority queue, job progress tracking, failure handling, retry mechanism
 * Main APIs: addJob(), processJobs(), getJobStatus(), cancelJob()
 * Constraints: In-memory queue with optional Redis backing
 * Patterns: Producer-consumer, priority queue, job state machine
 */

import { EventEmitter } from 'events';
import { logger } from '../utils/logger';
import { OptimizationPipeline } from '../core/optimization-pipeline';
import { OptimizationCacheService } from '../services/optimization-cache.service';
import { getPromptWizardConfig } from '../config/promptwizard.config';
import { Template } from '../types/index';
import {
  OptimizationRequest,
  OptimizationResult,
  PipelineResult,
} from '../integrations/promptwizard/types';

// Optional Bull Queue support for Redis-backed job queues
interface BullJob {
  id: string;
  data: OptimizationJob;
  progress: (progress: number) => void;
  progress(): number;
  opts: Record<string, unknown>;
}

interface BullQueue {
  add(name: string, data: OptimizationJob, opts?: Record<string, unknown>): Promise<BullJob>;
  process(concurrency: number, processor: (job: BullJob) => Promise<unknown>): void;
  on(event: string, callback: (...args: unknown[]) => void): void;
  getJob(jobId: string): Promise<BullJob | null>;
  removeJobs(pattern: string): Promise<void>;
  clean(grace: number, status: string): Promise<BullJob[]>;
  close(): Promise<void>;
}

let Queue: (new (name: string, opts?: Record<string, unknown>) => BullQueue) | undefined;
// let _Job: unknown; // Commented out as unused
try {
  // eslint-disable-next-line import/no-unresolved
  const bullModule = require('bull');
  Queue = bullModule.default || bullModule.Queue || bullModule;
  // _Job = bullModule.Job; // Commented out as unused
} catch (_error) {
  // Bull not available - fallback to in-memory queue
  Queue = null;
  // _Job = null; // Not used
}

export type JobStatus =
  | 'pending'
  | 'processing'
  | 'completed'
  | 'failed'
  | 'cancelled';
export type JobPriority = 'low' | 'normal' | 'high' | 'urgent';

export interface OptimizationJob {
  jobId: string;
  templateId: string;
  template: Template;
  request: OptimizationRequest;
  priority: JobPriority;
  status: JobStatus;
  progress: number;
  currentStep?: string;
  result?: OptimizationResult;
  error?: string;
  createdAt: Date;
  startedAt?: Date;
  completedAt?: Date;
  retryCount: number;
  maxRetries: number;
  metadata?: Record<string, unknown>;
}

export interface QueueStats {
  totalJobs: number;
  pendingJobs: number;
  processingJobs: number;
  completedJobs: number;
  failedJobs: number;
  cancelledJobs: number;
  averageProcessingTime: number;
  successRate: number;
  activeWorkers: number;
  queueLength: number;
}

export interface QueueConfig {
  maxConcurrency: number;
  defaultPriority: JobPriority;
  maxRetries: number;
  retryDelay: number; // milliseconds
  jobTimeout: number; // milliseconds
  cleanupInterval: number; // milliseconds
  maxJobHistory: number;
  // Bull queue configuration
  useBull?: boolean;
  redisUrl?: string;
  queueName?: string;
  bullOptions?: {
    removeOnComplete?: number;
    removeOnFail?: number;
    attempts?: number;
    backoff?: {
      type: 'fixed' | 'exponential';
      delay: number;
    };
  };
}

export class OptimizationQueue extends EventEmitter {
  private jobs = new Map<string, OptimizationJob>();

  private pendingQueue: OptimizationJob[] = [];

  private processingJobs = new Set<string>();

  private workers: Map<string, Promise<void>> = new Map();

  private optimizationPipeline: OptimizationPipeline;

  // private _cacheService: OptimizationCacheService; // Commented out as unused

  private config: QueueConfig;

  private isProcessing = false;

  private cleanupTimer?: NodeJS.Timeout;

  // Bull queue integration
  private bullQueue?: BullQueue;

  private useBull = false;

  constructor(
    optimizationPipeline: OptimizationPipeline,
    _cacheService: OptimizationCacheService,
    config: Partial<QueueConfig> = {}
  ) {
    super();

    this.optimizationPipeline = optimizationPipeline;
    // this._cacheService = cacheService; // Commented out as unused

    // Get PromptWizard configuration for Redis settings
    const promptwizardConfig = getPromptWizardConfig();

    this.config = {
      maxConcurrency: 3,
      defaultPriority: 'normal',
      maxRetries: 3,
      retryDelay: 5000,
      jobTimeout: 10 * 60 * 1000, // 10 minutes
      cleanupInterval: 60 * 60 * 1000, // 1 hour
      maxJobHistory: 1000,
      // Bull configuration from PromptWizard config
      useBull: Queue !== null && promptwizardConfig.cache.redis?.enabled,
      redisUrl: promptwizardConfig.cache.redis?.url,
      queueName: 'optimization-jobs',
      bullOptions: {
        removeOnComplete: 100,
        removeOnFail: 50,
        attempts: 3,
        backoff: {
          type: 'exponential',
          delay: 2000,
        },
      },
      ...config,
    };

    // Initialize Bull queue if available and configured
    this.initializeBullQueue();

    // Setup periodic cleanup
    this.setupPeriodicCleanup();

    logger.info(
      `Optimization queue initialized - ${JSON.stringify({
        maxConcurrency: this.config.maxConcurrency,
        maxRetries: this.config.maxRetries,
        useBull: this.useBull,
      })}`
    );
  }

  /**
   * Add optimization job to queue
   */
  async addJob(
    templateId: string,
    template: Template,
    request: OptimizationRequest,
    options: {
      priority?: JobPriority;
      maxRetries?: number;
      metadata?: Record<string, unknown>;
    } = {}
  ): Promise<OptimizationJob> {
    const job: OptimizationJob = {
      jobId: this.generateJobId(),
      templateId,
      template,
      request,
      priority: options.priority || this.config.defaultPriority,
      status: 'pending',
      progress: 0,
      createdAt: new Date(),
      retryCount: 0,
      maxRetries: options.maxRetries ?? this.config.maxRetries,
      metadata: options.metadata,
    };

    // Store job
    this.jobs.set(job.jobId, job);

    if (this.useBull && this.bullQueue) {
      // Use Bull queue for Redis-backed processing
      const bullPriority = this.mapPriorityToBull(job.priority);

      try {
        const bullJob = await this.bullQueue.add(
          'optimize',
          {
            templateId,
            template,
            request,
            options,
            jobId: job.jobId,
          },
          {
            priority: bullPriority,
            attempts: job.maxRetries,
            delay: 0,
            jobId: job.jobId,
          }
        );

        logger.info(
          `Optimization job added to Bull queue - ${JSON.stringify({
            jobId: job.jobId,
            bullJobId: bullJob.id,
            templateId,
            priority: job.priority,
          })}`
        );
      } catch (error) {
        logger.warn(
          `Failed to add job to Bull queue, using in-memory fallback: ${error}`
        );
        // Fall back to in-memory queue
        this.insertJobByPriority(job);
        this.startProcessing();
      }
    } else {
      // Use in-memory queue
      this.insertJobByPriority(job);

      logger.info(
        `Optimization job added to queue - ${JSON.stringify({
          jobId: job.jobId,
          templateId,
          priority: job.priority,
          queueLength: this.pendingQueue.length,
        })}`
      );

      // Start processing if not already running
      this.startProcessing();
    }

    this.emit('job:added', job);
    return job;
  }

  /**
   * Get job status
   */
  getJob(jobId: string): OptimizationJob | null {
    return this.jobs.get(jobId) || null;
  }

  /**
   * Cancel job
   */
  async cancelJob(jobId: string): Promise<boolean> {
    const job = this.jobs.get(jobId);
    if (!job) {
      return false;
    }

    if (
      job.status === 'completed' ||
      job.status === 'failed' ||
      job.status === 'cancelled'
    ) {
      return false; // Cannot cancel completed/failed/cancelled jobs
    }

    if (job.status === 'pending') {
      // Remove from pending queue
      const index = this.pendingQueue.findIndex(j => j.jobId === jobId);
      if (index > -1) {
        this.pendingQueue.splice(index, 1);
      }
    }

    if (job.status === 'processing') {
      // Mark for cancellation - the worker will check this flag
      this.processingJobs.delete(jobId);
    }

    job.status = 'cancelled';
    job.completedAt = new Date();

    logger.info(`Optimization job cancelled - ${JSON.stringify({ jobId })}`);
    this.emit('job:cancelled', job);

    return true;
  }

  /**
   * Get queue statistics
   */
  getStats(): QueueStats {
    const totalJobs = this.jobs.size;
    const jobsByStatus = this.getJobsByStatus();

    // Calculate average processing time
    const completedJobs = Array.from(this.jobs.values()).filter(
      job => job.status === 'completed' && job.startedAt && job.completedAt
    );

    const averageProcessingTime =
      completedJobs.length > 0
        ? completedJobs.reduce(
            (sum, job) =>
              sum + (job.completedAt!.getTime() - job.startedAt!.getTime()),
            0
          ) / completedJobs.length
        : 0;

    // Calculate success rate
    const finishedJobs = jobsByStatus.completed + jobsByStatus.failed;
    const successRate =
      finishedJobs > 0 ? jobsByStatus.completed / finishedJobs : 1;

    return {
      totalJobs,
      pendingJobs: jobsByStatus.pending,
      processingJobs: jobsByStatus.processing,
      completedJobs: jobsByStatus.completed,
      failedJobs: jobsByStatus.failed,
      cancelledJobs: jobsByStatus.cancelled,
      averageProcessingTime,
      successRate,
      activeWorkers: this.workers.size,
      queueLength: this.pendingQueue.length,
    };
  }

  /**
   * Clear completed jobs from history
   */
  cleanup(): void {
    const jobsArray = Array.from(this.jobs.values());

    // Sort by completion time (most recent first)
    const completedJobs = jobsArray
      .filter(
        job =>
          job.status === 'completed' ||
          job.status === 'failed' ||
          job.status === 'cancelled'
      )
      .sort(
        (a, b) =>
          (b.completedAt?.getTime() || 0) - (a.completedAt?.getTime() || 0)
      );

    // Keep only the most recent jobs up to maxJobHistory
    const jobsToRemove = completedJobs.slice(this.config.maxJobHistory);

    jobsToRemove.forEach(job => {
      this.jobs.delete(job.jobId);
    });

    if (jobsToRemove.length > 0) {
      logger.info(
        `Queue cleanup completed - ${JSON.stringify({
          jobsRemoved: jobsToRemove.length,
          totalJobs: this.jobs.size,
        })}`
      );
    }
  }

  /**
   * Start job processing
   */
  private async startProcessing(): Promise<void> {
    if (this.isProcessing) {
      return;
    }

    this.isProcessing = true;

    // Start workers up to max concurrency
    while (
      this.workers.size < this.config.maxConcurrency &&
      this.pendingQueue.length > 0
    ) {
      this.startWorker();
    }

    // Check if we should stop processing
    if (this.pendingQueue.length === 0 && this.workers.size === 0) {
      this.isProcessing = false;
    }
  }

  /**
   * Start a worker to process jobs
   */
  private startWorker(): void {
    const workerId = this.generateWorkerId();

    const workerPromise = this.runWorker(workerId);
    this.workers.set(workerId, workerPromise);

    workerPromise
      .catch(error => {
        logger.error(`Worker error - ${JSON.stringify({ workerId, error })}`);
      })
      .finally(() => {
        this.workers.delete(workerId);

        // Start new worker if there are pending jobs
        if (
          this.pendingQueue.length > 0 &&
          this.workers.size < this.config.maxConcurrency
        ) {
          this.startWorker();
        } else if (this.pendingQueue.length === 0 && this.workers.size === 0) {
          this.isProcessing = false;
        }
      });
  }

  /**
   * Run worker to process jobs
   */
  private async runWorker(workerId: string): Promise<void> {
    logger.debug(`Worker started - ${JSON.stringify({ workerId })}`);

    while (this.pendingQueue.length > 0) {
      const job = this.pendingQueue.shift();
      if (!job) break;

      // Check if job was cancelled while waiting
      if (job.status === 'cancelled') {
        continue;
      }

      await this.processJob(job, workerId);

      // Small delay to prevent overwhelming the system
      await this.delay(100);
    }

    logger.debug(`Worker finished - ${JSON.stringify({ workerId })}`);
  }

  /**
   * Process a single job
   */
  private async processJob(
    job: OptimizationJob,
    workerId: string
  ): Promise<void> {
    try {
      // Mark job as processing
      job.status = 'processing';
      job.startedAt = new Date();
      job.progress = 0;
      this.processingJobs.add(job.jobId);

      logger.info(
        `Processing optimization job - ${JSON.stringify({
          jobId: job.jobId,
          workerId,
          templateId: job.templateId,
          priority: job.priority,
        })}`
      );

      this.emit('job:started', job);

      // Set up job timeout
      const timeoutPromise = this.createJobTimeout(job);

      // Set up progress callback
      // Progress callback commented out as unused
      // const _progressCallback = (stage: string, progress: number) => {
      //   job.currentStep = stage;
      //   job.progress = progress;
      //   this.emit('job:progress', job);
      // };

      // Process job with timeout
      const processingPromise = this.optimizationPipeline.process(
        job.templateId,
        job.template,
        job.request
      );

      const result = (await Promise.race([
        processingPromise,
        timeoutPromise,
      ])) as PipelineResult;

      // Clear timeout
      clearTimeout(timeoutPromise as any);

      if (result.success && result.optimizationResult) {
        // Job completed successfully
        job.status = 'completed';
        job.result = result.optimizationResult;
        job.progress = 100;
        job.completedAt = new Date();

        logger.info(
          `Optimization job completed - ${JSON.stringify({
            jobId: job.jobId,
            templateId: job.templateId,
            processingTime:
              job.completedAt.getTime() - job.startedAt!.getTime(),
          })}`
        );

        this.emit('job:completed', job);
      } else {
        // Job failed
        const errorMessage =
          typeof result.error === 'string'
            ? result.error
            : 'Pipeline processing failed';
        throw new Error(errorMessage);
      }
    } catch (error) {
      await this.handleJobError(
        job,
        error instanceof Error ? error : new Error(String(error))
      );
    } finally {
      this.processingJobs.delete(job.jobId);
    }
  }

  /**
   * Handle job error with retry logic
   */
  private async handleJobError(
    job: OptimizationJob,
    error: Error
  ): Promise<void> {
    job.error = error.message;
    job.retryCount += 1;

    logger.warn(
      `Optimization job failed - ${JSON.stringify({
        jobId: job.jobId,
        error: error.message,
        retryCount: job.retryCount,
        maxRetries: job.maxRetries,
      })}`
    );

    if (job.retryCount < job.maxRetries) {
      // Retry job after delay
      job.status = 'pending';
      job.progress = 0;
      job.currentStep = undefined;

      logger.info(
        `Retrying optimization job - ${JSON.stringify({
          jobId: job.jobId,
          retryCount: job.retryCount,
          delay: this.config.retryDelay,
        })}`
      );

      // Add back to queue after delay
      setTimeout(() => {
        this.insertJobByPriority(job);
        this.startProcessing();
      }, this.config.retryDelay);

      this.emit('job:retry', job);
    } else {
      // Max retries reached, mark as failed
      job.status = 'failed';
      job.completedAt = new Date();

      logger.error(
        `Optimization job failed permanently - ${JSON.stringify({
          jobId: job.jobId,
          error: error.message,
          retryCount: job.retryCount,
        })}`
      );

      this.emit('job:failed', job);
    }
  }

  /**
   * Create job timeout promise
   */
  private createJobTimeout(_job: OptimizationJob): Promise<never> {
    return new Promise((_, reject) => {
      setTimeout(() => {
        reject(new Error(`Job timeout after ${this.config.jobTimeout}ms`));
      }, this.config.jobTimeout);
    });
  }

  /**
   * Insert job into pending queue by priority
   */
  private insertJobByPriority(job: OptimizationJob): void {
    const priorityOrder: Record<JobPriority, number> = {
      urgent: 0,
      high: 1,
      normal: 2,
      low: 3,
    };

    const insertIndex = this.pendingQueue.findIndex(
      existingJob =>
        priorityOrder[existingJob.priority] > priorityOrder[job.priority]
    );

    if (insertIndex === -1) {
      this.pendingQueue.push(job);
    } else {
      this.pendingQueue.splice(insertIndex, 0, job);
    }
  }

  /**
   * Get jobs grouped by status
   */
  private getJobsByStatus(): Record<JobStatus, number> {
    const counts: Record<JobStatus, number> = {
      pending: 0,
      processing: 0,
      completed: 0,
      failed: 0,
      cancelled: 0,
    };

    for (const job of this.jobs.values()) {
      counts[job.status] += 1;
    }

    return counts;
  }

  /**
   * Initialize Bull queue if available
   */
  private initializeBullQueue(): void {
    if (!this.config.useBull || !Queue || !this.config.redisUrl) {
      this.useBull = false;
      logger.info(
        'Using in-memory job queue (Bull not available or not configured)'
      );
      return;
    }

    try {
      this.bullQueue = new Queue(this.config.queueName, this.config.redisUrl, {
        defaultJobOptions: {
          removeOnComplete: this.config.bullOptions?.removeOnComplete || 100,
          removeOnFail: this.config.bullOptions?.removeOnFail || 50,
          attempts: this.config.bullOptions?.attempts || this.config.maxRetries,
          backoff: this.config.bullOptions?.backoff || {
            type: 'exponential',
            delay: 2000,
          },
        },
      });

      this.useBull = true;

      // Set up Bull event listeners
      this.bullQueue.on('completed', (job: BullJob, result: unknown) => {
        logger.debug(`Bull job completed: ${job.id}`);
        this.emit('job:completed', { jobId: job.id, result });
      });

      this.bullQueue.on('failed', (job: BullJob, err: Error) => {
        logger.warn(`Bull job failed: ${job.id} - ${err.message}`);
        this.emit('job:failed', { jobId: job.id, error: err.message });
      });

      this.bullQueue.on('progress', (job: BullJob, progress: number) => {
        this.emit('job:progress', { jobId: job.id, progress });
      });

      // Process jobs with Bull
      this.bullQueue.process(this.config.maxConcurrency, async (job: BullJob) =>
        this.processBullJob(job)
      );

      logger.info('Bull queue initialized successfully');
    } catch (error) {
      logger.warn(
        `Failed to initialize Bull queue, falling back to in-memory: ${error}`
      );
      this.useBull = false;
      this.bullQueue = undefined;
    }
  }

  /**
   * Process a Bull job
   */
  private async processBullJob(bullJob: BullJob): Promise<unknown> {
    const { templateId, template, request, options: _options } = bullJob.data;

    try {
      // Update job progress
      await bullJob.progress(10);

      // Process optimization
      const result = await this.optimizationPipeline.process(
        templateId,
        template,
        request
      );

      await bullJob.progress(100);

      if (result.success && result.optimizationResult) {
        return result.optimizationResult;
      }
      throw new Error(
        typeof result.error === 'string'
          ? result.error
          : 'Pipeline processing failed'
      );
    } catch (error) {
      logger.error(`Bull job processing failed: ${error}`);
      throw error;
    }
  }

  /**
   * Setup periodic cleanup
   */
  private setupPeriodicCleanup(): void {
    this.cleanupTimer = setInterval(() => {
      this.cleanup();
    }, this.config.cleanupInterval);
  }

  /**
   * Map job priority to Bull priority (higher number = higher priority in Bull)
   */
  private mapPriorityToBull(priority: JobPriority): number {
    const priorityMap: Record<JobPriority, number> = {
      urgent: 10,
      high: 5,
      normal: 0,
      low: -5,
    };
    return priorityMap[priority];
  }

  /**
   * Generate unique job ID
   */
  private generateJobId(): string {
    return `job_${Date.now()}_${Math.random().toString(36).substring(7)}`;
  }

  /**
   * Generate unique worker ID
   */
  private generateWorkerId(): string {
    return `worker_${Date.now()}_${Math.random().toString(36).substring(7)}`;
  }

  /**
   * Utility delay function
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => {
      setTimeout(resolve, ms);
    });
  }

  /**
   * Cleanup resources
   */
  async shutdown(): Promise<void> {
    logger.info(`Shutting down optimization queue`);

    // Clear cleanup timer
    if (this.cleanupTimer) {
      clearTimeout(this.cleanupTimer);
    }

    // Shutdown Bull queue if using it
    if (this.useBull && this.bullQueue) {
      try {
        await this.bullQueue.close();
        logger.info('Bull queue closed successfully');
      } catch (error) {
        logger.warn(`Error closing Bull queue: ${error}`);
      }
    }

    // Wait for all workers to complete
    await Promise.all(Array.from(this.workers.values()));

    // Cancel all pending jobs
    for (const job of this.pendingQueue) {
      job.status = 'cancelled';
      job.completedAt = new Date();
      this.emit('job:cancelled', job);
    }

    this.pendingQueue = [];
    this.processingJobs.clear();
    this.workers.clear();
    this.isProcessing = false;

    logger.info('Optimization queue shutdown completed');
  }
}

