/**
 * @fileoverview Advanced authorization middleware with resource-level access control
 * @lastmodified 2025-08-27T16:45:00Z
 *
 * Features: Resource-level permissions, policy evaluation, context-aware authorization
 * Main APIs: authorize(), requirePermission(), requireRole(), checkResourceAccess()
 * Constraints: Integrates with RBAC system, supports policy conditions, audit logging
 * Patterns: Authorization middleware, policy evaluation, resource protection, audit trails
 */

import { logger } from '../utils/logger';
import { rbacManager } from './rbac-manager.service';
import { sessionManager } from './session-manager.service';
import { jwtAuthService } from './jwt-auth.service';
import type { 
  AccessContext, 
  PermissionEvaluationResult,
  PolicyCondition 
} from './rbac-manager.service';

export interface AuthorizationRequest {
  headers?: Record<string, string>;
  body?: any;
  params?: Record<string, string>;
  query?: Record<string, string>;
  method?: string;
  path?: string;
  user?: {
    id: string;
    username: string;
    email: string;
    roles: string[];
    permissions: string[];
    sessionId: string;
  };
  sessionInfo?: {
    sessionId: string;
    deviceId: string;
    ipAddress: string;
    userAgent: string;
  };
  resourceId?: string;
  resourceType?: string;
  metadata?: Record<string, any>;
}

export interface AuthorizationResult {
  authorized: boolean;
  user?: AuthorizationRequest['user'];
  session?: any;
  permissions: string[];
  evaluationResult?: PermissionEvaluationResult;
  error?: string;
  auditInfo?: {
    timestamp: Date;
    resource: string;
    action: string;
    granted: boolean;
    reason: string;
  };
}

export interface AuthorizationConfig {
  enableAuditLogging: boolean;
  enableSessionValidation: boolean;
  enableResourceLevelChecks: boolean;
  enablePolicyEvaluation: boolean;
  requireAuthentication: boolean;
  defaultDenyPolicy: boolean;
  rateLimitChecks: boolean;
}

/**
 * Authorization Middleware Service
 */
export class AuthorizationMiddleware {
  private config: AuthorizationConfig;
  private auditLog: Array<{
    timestamp: Date;
    userId: string;
    resource: string;
    action: string;
    granted: boolean;
    reason: string;
    context: any;
  }> = [];

  constructor(config: Partial<AuthorizationConfig> = {}) {
    this.config = {
      enableAuditLogging: true,
      enableSessionValidation: true,
      enableResourceLevelChecks: true,
      enablePolicyEvaluation: true,
      requireAuthentication: true,
      defaultDenyPolicy: true,
      rateLimitChecks: true,
      ...config,
    };
  }

  /**
   * Main authorization middleware
   */
  async authorize(
    permission: string,
    resourceType?: string,
    options: {
      allowOwnership?: boolean;
      requireAll?: boolean;
      conditions?: PolicyCondition[];
    } = {}
  ) {
    return async (request: AuthorizationRequest): Promise<AuthorizationResult> => {
      try {
        const startTime = Date.now();

        // Extract authentication information
        const authResult = await this.extractAuthInfo(request);
        if (!authResult.authorized) {
          return authResult;
        }

        const { user, session } = authResult;

        // Parse permission
        const [resource, action] = permission.split(':');
        if (!resource || !action) {
          return this.createFailureResult('Invalid permission format', resource, action);
        }

        // Create access context
        const context: AccessContext = {
          userId: user!.id,
          resource,
          action,
          resourceId: request.resourceId,
          timestamp: new Date(),
          metadata: {
            ...request.metadata,
            resourceType,
            method: request.method,
            path: request.path,
            sessionId: user!.sessionId,
          },
          clientInfo: request.sessionInfo ? {
            ipAddress: request.sessionInfo.ipAddress,
            userAgent: request.sessionInfo.userAgent,
          } : undefined,
        };

        // Check resource-level permissions
        if (this.config.enableResourceLevelChecks && resourceType && request.resourceId) {
          const resourceAccessResult = await this.checkResourceAccess(
            user!.id,
            resourceType,
            request.resourceId,
            action,
            options
          );

          if (!resourceAccessResult.authorized) {
            await this.logAuthorization(user!.id, resource, action, false, resourceAccessResult.error || 'Resource access denied', context);
            return resourceAccessResult;
          }
        }

        // Evaluate permission with RBAC
        const evaluationResult = await rbacManager.checkPermission(
          user!.id,
          resource,
          action,
          context
        );

        // Check additional conditions
        if (options.conditions && evaluationResult.granted) {
          for (const condition of options.conditions) {
            if (!this.evaluateCondition(condition, context, request)) {
              evaluationResult.granted = false;
              evaluationResult.reason = `Condition failed: ${condition.description || condition.field}`;
              break;
            }
          }
        }

        // Log authorization decision
        if (this.config.enableAuditLogging) {
          await this.logAuthorization(
            user!.id,
            resource,
            action,
            evaluationResult.granted,
            evaluationResult.reason,
            context
          );
        }

        const result: AuthorizationResult = {
          authorized: evaluationResult.granted,
          user,
          session,
          permissions: user!.permissions,
          evaluationResult,
          auditInfo: {
            timestamp: new Date(),
            resource,
            action,
            granted: evaluationResult.granted,
            reason: evaluationResult.reason,
          },
        };

        if (!evaluationResult.granted) {
          result.error = evaluationResult.reason;
        }

        logger.debug('Authorization completed', {
          userId: user!.id,
          permission,
          granted: evaluationResult.granted,
          evaluationTime: Date.now() - startTime,
        });

        return result;
      } catch (error) {
        logger.error('Authorization failed', error as Error);
        return this.createFailureResult('Authorization system error');
      }
    };
  }

  /**
   * Require specific permission
   */
  requirePermission(permission: string, resourceType?: string, options?: {
    allowOwnership?: boolean;
    conditions?: PolicyCondition[];
  }) {
    return this.authorize(permission, resourceType, options);
  }

  /**
   * Require specific role
   */
  requireRole(roleName: string) {
    return async (request: AuthorizationRequest): Promise<AuthorizationResult> => {
      const authResult = await this.extractAuthInfo(request);
      if (!authResult.authorized) {
        return authResult;
      }

      const { user } = authResult;
      const hasRole = user!.roles.includes(roleName);

      if (this.config.enableAuditLogging) {
        await this.logAuthorization(
          user!.id,
          'role',
          'check',
          hasRole,
          hasRole ? `Role ${roleName} granted` : `Role ${roleName} denied`,
          { userId: user!.id, resource: 'role', action: 'check', timestamp: new Date() }
        );
      }

      return {
        ...authResult,
        authorized: hasRole,
        error: hasRole ? undefined : `Role ${roleName} required`,
      };
    };
  }

  /**
   * Require any of the specified roles
   */
  requireAnyRole(roles: string[]) {
    return async (request: AuthorizationRequest): Promise<AuthorizationResult> => {
      const authResult = await this.extractAuthInfo(request);
      if (!authResult.authorized) {
        return authResult;
      }

      const { user } = authResult;
      const hasAnyRole = roles.some(role => user!.roles.includes(role));

      return {
        ...authResult,
        authorized: hasAnyRole,
        error: hasAnyRole ? undefined : `One of roles [${roles.join(', ')}] required`,
      };
    };
  }

  /**
   * Check ownership-based access
   */
  requireOwnership(resourceIdField: string = 'id') {
    return async (request: AuthorizationRequest): Promise<AuthorizationResult> => {
      const authResult = await this.extractAuthInfo(request);
      if (!authResult.authorized) {
        return authResult;
      }

      const { user } = authResult;
      const resourceId = request.params?.[resourceIdField] || request.resourceId;

      if (!resourceId) {
        return this.createFailureResult('Resource ID not found');
      }

      // Get resource ownership (this would typically involve a database lookup)
      const isOwner = await this.checkResourceOwnership(user!.id, resourceId);

      return {
        ...authResult,
        authorized: isOwner,
        error: isOwner ? undefined : 'Resource access denied: ownership required',
      };
    };
  }

  /**
   * Combined authentication and authorization
   */
  protect(
    permission?: string,
    options: {
      resourceType?: string;
      requireOwnership?: boolean;
      allowRoles?: string[];
      conditions?: PolicyCondition[];
    } = {}
  ) {
    return async (request: AuthorizationRequest): Promise<AuthorizationResult> => {
      // First check authentication
      const authResult = await this.extractAuthInfo(request);
      if (!authResult.authorized) {
        return authResult;
      }

      // Check roles if specified
      if (options.allowRoles && options.allowRoles.length > 0) {
        const roleCheck = await this.requireAnyRole(options.allowRoles)(request);
        if (!roleCheck.authorized) {
          return roleCheck;
        }
      }

      // Check ownership if required
      if (options.requireOwnership) {
        const ownershipCheck = await this.requireOwnership()(request);
        if (!ownershipCheck.authorized) {
          return ownershipCheck;
        }
      }

      // Check permission if specified
      if (permission) {
        const permissionCheck = await this.requirePermission(
          permission,
          options.resourceType,
          { conditions: options.conditions }
        )(request);

        return permissionCheck;
      }

      return authResult;
    };
  }

  /**
   * Private helper methods
   */
  private async extractAuthInfo(request: AuthorizationRequest): Promise<AuthorizationResult> {
    try {
      // Check if authentication is required
      if (!this.config.requireAuthentication) {
        return {
          authorized: true,
          permissions: [],
        };
      }

      // Extract JWT token
      const authHeader = request.headers?.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return this.createFailureResult('Missing or invalid authorization header');
      }

      const token = authHeader.substring(7);

      // Verify JWT token
      const tokenResult = await jwtAuthService.verifyToken(token);
      if (!tokenResult.valid || !tokenResult.payload) {
        return this.createFailureResult(tokenResult.error || 'Invalid token');
      }

      const user = {
        id: tokenResult.payload.sub,
        username: tokenResult.payload.username,
        email: tokenResult.payload.email,
        roles: tokenResult.payload.roles,
        permissions: tokenResult.payload.permissions,
        sessionId: tokenResult.payload.sessionId,
      };

      // Validate session if enabled
      let session;
      if (this.config.enableSessionValidation) {
        const sessionValidation = await sessionManager.validateSession(
          tokenResult.payload.sessionId,
          request.sessionInfo ? {
            ipAddress: request.sessionInfo.ipAddress,
            userAgent: request.sessionInfo.userAgent,
          } : undefined
        );

        if (!sessionValidation.valid) {
          return this.createFailureResult(sessionValidation.error || 'Invalid session');
        }

        session = sessionValidation.session;

        // Check for security issues
        if (sessionValidation.securityIssues && sessionValidation.securityIssues.length > 0) {
          logger.warn('Session security issues detected', {
            userId: user.id,
            sessionId: tokenResult.payload.sessionId,
            issues: sessionValidation.securityIssues,
          });

          if (sessionValidation.requiresAction === 'security_check') {
            return this.createFailureResult('Security verification required');
          }
        }
      }

      return {
        authorized: true,
        user,
        session,
        permissions: user.permissions,
      };
    } catch (error) {
      logger.error('Authentication extraction failed', error as Error);
      return this.createFailureResult('Authentication failed');
    }
  }

  private async checkResourceAccess(
    userId: string,
    resourceType: string,
    resourceId: string,
    action: string,
    options: {
      allowOwnership?: boolean;
    } = {}
  ): Promise<AuthorizationResult> {
    try {
      // Check if user has global permission for resource type
      const hasGlobalPermission = await rbacManager.hasPermission(
        userId,
        `${resourceType}:${action}`
      );

      if (hasGlobalPermission) {
        return { authorized: true, permissions: [] };
      }

      // Check ownership if allowed
      if (options.allowOwnership) {
        const isOwner = await this.checkResourceOwnership(userId, resourceId);
        if (isOwner) {
          const hasOwnerPermission = await rbacManager.hasPermission(
            userId,
            `${resourceType}:${action}:own`
          );

          if (hasOwnerPermission) {
            return { authorized: true, permissions: [] };
          }
        }
      }

      return this.createFailureResult('Insufficient permissions for resource');
    } catch (error) {
      logger.error('Resource access check failed', error as Error);
      return this.createFailureResult('Resource access check error');
    }
  }

  private async checkResourceOwnership(userId: string, resourceId: string): Promise<boolean> {
    // This would typically involve database lookups
    // For now, return a mock implementation
    logger.debug('Checking resource ownership', { userId, resourceId });
    return false; // Mock: always deny ownership for security
  }

  private evaluateCondition(
    condition: PolicyCondition,
    context: AccessContext,
    request: AuthorizationRequest
  ): boolean {
    try {
      let fieldValue: any;

      // Get field value from context or request
      switch (condition.type) {
        case 'time':
          fieldValue = new Date().getHours();
          break;
        case 'location':
          fieldValue = context.clientInfo?.location?.country;
          break;
        case 'resource':
          fieldValue = context.resourceId;
          break;
        case 'custom':
          fieldValue = this.getCustomFieldValue(condition.field, context, request);
          break;
        default:
          return false;
      }

      // Evaluate condition
      switch (condition.operator) {
        case 'eq':
          return fieldValue === condition.value;
        case 'ne':
          return fieldValue !== condition.value;
        case 'lt':
          return fieldValue < condition.value;
        case 'le':
          return fieldValue <= condition.value;
        case 'gt':
          return fieldValue > condition.value;
        case 'ge':
          return fieldValue >= condition.value;
        case 'in':
          return Array.isArray(condition.value) && condition.value.includes(fieldValue);
        case 'nin':
          return Array.isArray(condition.value) && !condition.value.includes(fieldValue);
        case 'contains':
          return String(fieldValue).includes(String(condition.value));
        case 'regex':
          return new RegExp(condition.value).test(String(fieldValue));
        default:
          return false;
      }
    } catch (error) {
      logger.error('Condition evaluation failed', error as Error);
      return false;
    }
  }

  private getCustomFieldValue(field: string, context: AccessContext, request: AuthorizationRequest): any {
    // Extract field value from nested objects
    const sources = [context, request, request.body, request.query, request.params];

    for (const source of sources) {
      if (source && typeof source === 'object') {
        const value = this.getNestedValue(source, field);
        if (value !== undefined) {
          return value;
        }
      }
    }

    return undefined;
  }

  private getNestedValue(obj: any, path: string): any {
    return path.split('.').reduce((current, key) => {
      return current && typeof current === 'object' ? current[key] : undefined;
    }, obj);
  }

  private async logAuthorization(
    userId: string,
    resource: string,
    action: string,
    granted: boolean,
    reason: string,
    context: any
  ): Promise<void> {
    const entry = {
      timestamp: new Date(),
      userId,
      resource,
      action,
      granted,
      reason,
      context,
    };

    this.auditLog.push(entry);

    // Limit audit log size
    if (this.auditLog.length > 10000) {
      this.auditLog = this.auditLog.slice(-5000);
    }

    logger.info('Authorization decision logged', {
      userId,
      resource,
      action,
      granted,
      reason,
    });
  }

  private createFailureResult(
    error: string,
    resource?: string,
    action?: string
  ): AuthorizationResult {
    return {
      authorized: false,
      permissions: [],
      error,
      auditInfo: resource && action ? {
        timestamp: new Date(),
        resource,
        action,
        granted: false,
        reason: error,
      } : undefined,
    };
  }

  /**
   * Get authorization statistics
   */
  getStats(): {
    totalChecks: number;
    successfulChecks: number;
    failedChecks: number;
    averageResponseTime: number;
    topResources: Array<{ resource: string; count: number }>;
    topUsers: Array<{ userId: string; count: number }>;
  } {
    const totalChecks = this.auditLog.length;
    const successfulChecks = this.auditLog.filter(entry => entry.granted).length;
    const failedChecks = totalChecks - successfulChecks;

    // Calculate top resources
    const resourceCounts: Record<string, number> = {};
    const userCounts: Record<string, number> = {};

    for (const entry of this.auditLog) {
      resourceCounts[entry.resource] = (resourceCounts[entry.resource] || 0) + 1;
      userCounts[entry.userId] = (userCounts[entry.userId] || 0) + 1;
    }

    const topResources = Object.entries(resourceCounts)
      .map(([resource, count]) => ({ resource, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 10);

    const topUsers = Object.entries(userCounts)
      .map(([userId, count]) => ({ userId, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 10);

    return {
      totalChecks,
      successfulChecks,
      failedChecks,
      averageResponseTime: 0, // Would need to track timing
      topResources,
      topUsers,
    };
  }

  /**
   * Get audit log entries
   */
  getAuditLog(
    options: {
      userId?: string;
      resource?: string;
      action?: string;
      granted?: boolean;
      limit?: number;
      startDate?: Date;
      endDate?: Date;
    } = {}
  ) {
    let filtered = [...this.auditLog];

    if (options.userId) {
      filtered = filtered.filter(entry => entry.userId === options.userId);
    }

    if (options.resource) {
      filtered = filtered.filter(entry => entry.resource === options.resource);
    }

    if (options.action) {
      filtered = filtered.filter(entry => entry.action === options.action);
    }

    if (options.granted !== undefined) {
      filtered = filtered.filter(entry => entry.granted === options.granted);
    }

    if (options.startDate) {
      filtered = filtered.filter(entry => entry.timestamp >= options.startDate!);
    }

    if (options.endDate) {
      filtered = filtered.filter(entry => entry.timestamp <= options.endDate!);
    }

    // Sort by timestamp descending
    filtered.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());

    if (options.limit) {
      filtered = filtered.slice(0, options.limit);
    }

    return filtered;
  }
}

/**
 * Global authorization middleware instance
 */
export const authorizationMiddleware = new AuthorizationMiddleware();

/**
 * Convenience functions for common authorization patterns
 */
export const requireAuth = () => authorizationMiddleware.protect();
export const requirePermission = (permission: string, resourceType?: string) =>
  authorizationMiddleware.requirePermission(permission, resourceType);
export const requireRole = (role: string) => authorizationMiddleware.requireRole(role);
export const requireAnyRole = (roles: string[]) => authorizationMiddleware.requireAnyRole(roles);
export const requireOwnership = (resourceIdField?: string) => 
  authorizationMiddleware.requireOwnership(resourceIdField);

/**
 * Advanced protection with multiple requirements
 */
export const protect = (options: {
  permission?: string;
  resourceType?: string;
  requireOwnership?: boolean;
  allowRoles?: string[];
  conditions?: PolicyCondition[];
} = {}) => authorizationMiddleware.protect(options.permission, options);

/**
 * @fileoverview FIPS 140-2 compliant cryptographic service for enterprise security
 * @lastmodified 2025-08-27T16:00:00Z
 *
 * Features: FIPS-approved algorithms, key management, digital signatures, secure random generation
 * Main APIs: encryptAES256GCM(), generateRSAKeyPair(), signData(), verifySignature()
 * Constraints: Requires FIPS-approved OpenSSL, secure entropy source
 * Patterns: Factory pattern, secure key derivation, authenticated encryption
 */

import * as crypto from 'crypto';
import { logger } from '../utils/logger';

export interface KeyPair {
  publicKey: string;
  privateKey: string;
  keyId: string;
  createdAt: Date;
  expiresAt?: Date;
}

export interface EncryptedPayload {
  data: string;
  iv: string;
  authTag: string;
  algorithm: string;
  keyId?: string;
}

export interface SignedPayload {
  data: string;
  signature: string;
  algorithm: string;
  keyId: string;
  timestamp: number;
}

export interface CryptoConfig {
  defaultAlgorithm?: 'aes-256-gcm' | 'aes-256-ccm';
  keyDerivationIterations?: number;
  rsaKeySize?: 2048 | 3072 | 4096;
  signatureAlgorithm?: 'RSA-PSS' | 'RSA-PKCS1-v1_5';
  hashAlgorithm?: 'sha256' | 'sha384' | 'sha512';
  enableFIPS?: boolean;
}

/**
 * Enterprise-grade cryptographic service implementing FIPS 140-2 standards
 */
export class CryptographicService {
  private readonly config: Required<CryptoConfig>;

  private readonly keyStore = new Map<string, KeyPair>();

  private readonly masterKey: Buffer;

  constructor(config: CryptoConfig = {}) {
    this.config = {
      defaultAlgorithm: config.defaultAlgorithm || 'aes-256-gcm',
      keyDerivationIterations: config.keyDerivationIterations || 100000,
      rsaKeySize: config.rsaKeySize || 4096,
      signatureAlgorithm: config.signatureAlgorithm || 'RSA-PSS',
      hashAlgorithm: config.hashAlgorithm || 'sha384',
      enableFIPS: config.enableFIPS ?? true,
    };

    // Derive master key from environment or generate secure default
    const keyMaterial = process.env.CRYPTO_MASTER_KEY || this.generateSecureKey(32);
    this.masterKey = crypto.scryptSync(keyMaterial, 'fips-salt-2025', 32);

    if (!process.env.CRYPTO_MASTER_KEY) {
      logger.warn('Using generated master key - set CRYPTO_MASTER_KEY for production');
    }

    this.validateFIPSCompliance();
  }

  /**
   * Validate FIPS 140-2 compliance
   */
  private validateFIPSCompliance(): void {
    if (this.config.enableFIPS && !crypto.constants.OPENSSL_VERSION_TEXT?.includes('fips')) {
      logger.warn('FIPS 140-2 mode requested but OpenSSL FIPS module not detected');
    }

    // Validate algorithm support
    const supportedCiphers = crypto.getCiphers();
    if (!supportedCiphers.includes(this.config.defaultAlgorithm)) {
      throw new Error(`Cipher ${this.config.defaultAlgorithm} not supported`);
    }

    logger.info('Cryptographic service initialized with FIPS 140-2 compliance checks');
  }

  /**
   * Generate cryptographically secure random bytes
   */
  generateSecureRandom(length: number): Buffer {
    return crypto.randomBytes(length);
  }

  /**
   * Generate secure random key material
   */
  generateSecureKey(length: number): string {
    return this.generateSecureRandom(length).toString('hex');
  }

  /**
   * Encrypt data using AES-256-GCM (FIPS approved)
   */
  encryptAES256GCM(
    plaintext: string | Buffer,
    associatedData?: Buffer
  ): EncryptedPayload {
    try {
      const algorithm = this.config.defaultAlgorithm;
      const iv = this.generateSecureRandom(16); // 128-bit IV
      const key = this.deriveDataKey('encryption', 32);

      const cipher = crypto.createCipherGCM(algorithm, key, iv);

      if (associatedData) {
        cipher.setAAD(associatedData);
      }

      const inputBuffer = Buffer.isBuffer(plaintext) 
        ? plaintext 
        : Buffer.from(plaintext, 'utf8');

      const encrypted = Buffer.concat([
        cipher.update(inputBuffer),
        cipher.final(),
      ]);

      const authTag = cipher.getAuthTag();

      return {
        data: encrypted.toString('base64'),
        iv: iv.toString('base64'),
        authTag: authTag.toString('base64'),
        algorithm,
      };
    } catch (error) {
      logger.error('AES-256-GCM encryption failed', error as Error);
      throw new Error('Encryption operation failed');
    }
  }

  /**
   * Decrypt data using AES-256-GCM
   */
  decryptAES256GCM(
    encryptedPayload: EncryptedPayload,
    associatedData?: Buffer
  ): Buffer {
    try {
      const { data, iv, authTag, algorithm } = encryptedPayload;
      const key = this.deriveDataKey('encryption', 32);

      const decipher = crypto.createDecipherGCM(
        algorithm,
        key,
        Buffer.from(iv, 'base64')
      );

      decipher.setAuthTag(Buffer.from(authTag, 'base64'));

      if (associatedData) {
        decipher.setAAD(associatedData);
      }

      const decrypted = Buffer.concat([
        decipher.update(Buffer.from(data, 'base64')),
        decipher.final(),
      ]);

      return decrypted;
    } catch (error) {
      logger.error('AES-256-GCM decryption failed', error as Error);
      throw new Error('Decryption operation failed');
    }
  }

  /**
   * Generate RSA key pair (FIPS approved)
   */
  generateRSAKeyPair(keyId?: string): KeyPair {
    try {
      const id = keyId || crypto.randomUUID();
      const keySize = this.config.rsaKeySize;

      const { publicKey, privateKey } = crypto.generateKeyPairSync('rsa', {
        modulusLength: keySize,
        publicKeyEncoding: {
          type: 'spki',
          format: 'pem',
        },
        privateKeyEncoding: {
          type: 'pkcs8',
          format: 'pem',
          cipher: 'aes-256-cbc',
          passphrase: this.deriveKeyPassphrase(id),
        },
      });

      const keyPair: KeyPair = {
        publicKey,
        privateKey,
        keyId: id,
        createdAt: new Date(),
        expiresAt: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000), // 1 year
      };

      this.keyStore.set(id, keyPair);
      logger.info(`RSA-${keySize} key pair generated: ${id}`);

      return keyPair;
    } catch (error) {
      logger.error('RSA key pair generation failed', error as Error);
      throw new Error('Key pair generation failed');
    }
  }

  /**
   * Sign data using RSA-PSS (FIPS approved)
   */
  signData(data: string | Buffer, keyId: string): SignedPayload {
    try {
      const keyPair = this.keyStore.get(keyId);
      if (!keyPair) {
        throw new Error(`Key pair not found: ${keyId}`);
      }

      const inputData = Buffer.isBuffer(data) ? data : Buffer.from(data, 'utf8');
      const passphrase = this.deriveKeyPassphrase(keyId);

      const signature = crypto.sign(this.config.hashAlgorithm, inputData, {
        key: keyPair.privateKey,
        passphrase,
        padding: crypto.constants.RSA_PKCS1_PSS_PADDING,
        saltLength: crypto.constants.RSA_PSS_SALTLEN_DIGEST,
      });

      return {
        data: inputData.toString('base64'),
        signature: signature.toString('base64'),
        algorithm: `${this.config.signatureAlgorithm}-${this.config.hashAlgorithm}`,
        keyId,
        timestamp: Date.now(),
      };
    } catch (error) {
      logger.error('Data signing failed', error as Error);
      throw new Error('Digital signature operation failed');
    }
  }

  /**
   * Verify digital signature
   */
  verifySignature(signedPayload: SignedPayload): boolean {
    try {
      const { data, signature, keyId } = signedPayload;
      const keyPair = this.keyStore.get(keyId);

      if (!keyPair) {
        logger.warn(`Key pair not found for verification: ${keyId}`);
        return false;
      }

      const inputData = Buffer.from(data, 'base64');
      const signatureBuffer = Buffer.from(signature, 'base64');

      const isValid = crypto.verify(
        this.config.hashAlgorithm,
        inputData,
        {
          key: keyPair.publicKey,
          padding: crypto.constants.RSA_PKCS1_PSS_PADDING,
          saltLength: crypto.constants.RSA_PSS_SALTLEN_DIGEST,
        },
        signatureBuffer
      );

      logger.info(`Signature verification result: ${isValid} for key ${keyId}`);
      return isValid;
    } catch (error) {
      logger.error('Signature verification failed', error as Error);
      return false;
    }
  }

  /**
   * Derive key for specific purpose using HKDF
   */
  private deriveDataKey(purpose: string, length: number): Buffer {
    const salt = crypto.createHash('sha256').update(`fips-salt-${purpose}`).digest();
    const info = Buffer.from(`promptwizard-${purpose}-2025`, 'utf8');

    // HKDF-SHA256 key derivation (FIPS approved)
    return crypto.hkdfSync(this.config.hashAlgorithm, this.masterKey, salt, info, length);
  }

  /**
   * Derive passphrase for key encryption
   */
  private deriveKeyPassphrase(keyId: string): string {
    const derived = crypto.hkdfSync(
      'sha256',
      this.masterKey,
      Buffer.from('key-passphrase-salt'),
      Buffer.from(`${keyId}-passphrase`),
      32
    );
    return derived.toString('hex');
  }

  /**
   * Generate secure hash (FIPS approved)
   */
  generateHash(
    data: string | Buffer,
    algorithm: 'sha256' | 'sha384' | 'sha512' = 'sha384'
  ): string {
    const hash = crypto.createHash(algorithm);
    hash.update(data);
    return hash.digest('hex');
  }

  /**
   * Generate HMAC (FIPS approved)
   */
  generateHMAC(
    data: string | Buffer,
    purpose: string,
    algorithm: 'sha256' | 'sha384' | 'sha512' = 'sha384'
  ): string {
    const key = this.deriveDataKey(`hmac-${purpose}`, 32);
    const hmac = crypto.createHmac(algorithm, key);
    hmac.update(data);
    return hmac.digest('hex');
  }

  /**
   * Verify HMAC
   */
  verifyHMAC(
    data: string | Buffer,
    expectedHMAC: string,
    purpose: string,
    algorithm: 'sha256' | 'sha384' | 'sha512' = 'sha384'
  ): boolean {
    try {
      const actualHMAC = this.generateHMAC(data, purpose, algorithm);
      return crypto.timingSafeEqual(
        Buffer.from(expectedHMAC, 'hex'),
        Buffer.from(actualHMAC, 'hex')
      );
    } catch (error) {
      logger.error('HMAC verification failed', error as Error);
      return false;
    }
  }

  /**
   * Rotate key pair
   */
  rotateKeyPair(keyId: string): KeyPair {
    const oldKeyPair = this.keyStore.get(keyId);
    if (oldKeyPair) {
      logger.info(`Rotating key pair: ${keyId}`);
    }

    const newKeyPair = this.generateRSAKeyPair(keyId);
    logger.info(`Key pair rotated successfully: ${keyId}`);

    return newKeyPair;
  }

  /**
   * Get key pair information
   */
  getKeyInfo(keyId: string): Omit<KeyPair, 'privateKey'> | null {
    const keyPair = this.keyStore.get(keyId);
    if (!keyPair) {
      return null;
    }

    const { privateKey, ...publicInfo } = keyPair;
    return publicInfo;
  }

  /**
   * List all key IDs
   */
  listKeys(): string[] {
    return Array.from(this.keyStore.keys());
  }

  /**
   * Remove expired keys
   */
  cleanupExpiredKeys(): number {
    const now = new Date();
    let removedCount = 0;

    for (const [keyId, keyPair] of this.keyStore.entries()) {
      if (keyPair.expiresAt && keyPair.expiresAt < now) {
        this.keyStore.delete(keyId);
        removedCount++;
        logger.info(`Removed expired key: ${keyId}`);
      }
    }

    return removedCount;
  }

  /**
   * Get cryptographic service statistics
   */
  getStats() {
    const activeKeys = this.keyStore.size;
    const expiredKeys = Array.from(this.keyStore.values()).filter(
      key => key.expiresAt && key.expiresAt < new Date()
    ).length;

    return {
      activeKeys,
      expiredKeys,
      fipsEnabled: this.config.enableFIPS,
      defaultAlgorithm: this.config.defaultAlgorithm,
      rsaKeySize: this.config.rsaKeySize,
      signatureAlgorithm: this.config.signatureAlgorithm,
      hashAlgorithm: this.config.hashAlgorithm,
    };
  }
}

/**
 * Global cryptographic service instance
 */
export const cryptoService = new CryptographicService();

/**
 * @fileoverview File encryption service for templates and configurations
 * @lastmodified 2025-08-27T16:30:00Z
 *
 * Features: File-level encryption, metadata protection, integrity verification, key management
 * Main APIs: encryptFile(), decryptFile(), encryptTemplate(), verifyFileIntegrity()
 * Constraints: Requires cryptographic service, proper key management
 * Patterns: Stream encryption, chunked processing, authenticated encryption
 */

import * as fs from 'fs/promises';
import * as crypto from 'crypto';
import * as path from 'path';
import { Transform, pipeline } from 'stream';
import { promisify } from 'util';
import { logger } from '../utils/logger';
import { cryptoService } from './cryptographic.service';

const pipelineAsync = promisify(pipeline);

export interface EncryptedFileMetadata {
  originalPath: string;
  encryptedPath: string;
  algorithm: string;
  keyId: string;
  iv: string;
  authTag: string;
  fileSize: number;
  originalHash: string;
  encryptedAt: Date;
  version: number;
}

export interface TemplateEncryptionConfig {
  encryptTemplates: boolean;
  encryptConfigurations: boolean;
  encryptLogs: boolean;
  keyRotationDays: number;
  compressionEnabled: boolean;
  chunkSize: number;
}

export interface FileEncryptionResult {
  success: boolean;
  encryptedPath?: string;
  metadata?: EncryptedFileMetadata;
  error?: string;
}

/**
 * File encryption service for protecting sensitive templates and configurations
 */
export class FileEncryptionService {
  private readonly config: TemplateEncryptionConfig;

  private readonly metadataStore = new Map<string, EncryptedFileMetadata>();

  private readonly fileKeyId: string;

  constructor(config: Partial<TemplateEncryptionConfig> = {}) {
    this.config = {
      encryptTemplates: config.encryptTemplates ?? true,
      encryptConfigurations: config.encryptConfigurations ?? true,
      encryptLogs: config.encryptLogs ?? false,
      keyRotationDays: config.keyRotationDays || 90,
      compressionEnabled: config.compressionEnabled ?? true,
      chunkSize: config.chunkSize || 64 * 1024, // 64KB chunks
    };

    // Generate or retrieve file encryption key
    this.fileKeyId = this.initializeFileEncryptionKey();
    logger.info('File encryption service initialized');
  }

  /**
   * Encrypt a file with authenticated encryption
   */
  async encryptFile(
    inputPath: string,
    outputPath?: string
  ): Promise<FileEncryptionResult> {
    try {
      const resolvedInputPath = path.resolve(inputPath);
      const resolvedOutputPath = outputPath || `${resolvedInputPath}.encrypted`;

      // Check if file should be encrypted based on config
      if (!this.shouldEncryptFile(resolvedInputPath)) {
        return {
          success: false,
          error: 'File type not configured for encryption',
        };
      }

      // Read file stats
      const stats = await fs.stat(resolvedInputPath);
      if (!stats.isFile()) {
        throw new Error('Input path is not a file');
      }

      // Generate encryption parameters
      const iv = crypto.randomBytes(16);
      const key = await this.deriveFileKey(this.fileKeyId);

      // Calculate original file hash
      const originalContent = await fs.readFile(resolvedInputPath);
      const originalHash = cryptoService.generateHash(originalContent);

      // Encrypt the file
      const cipher = crypto.createCipherGCM('aes-256-gcm', key, iv);
      const encryptedContent = Buffer.concat([
        cipher.update(originalContent),
        cipher.final(),
      ]);
      const authTag = cipher.getAuthTag();

      // Write encrypted file
      await fs.writeFile(resolvedOutputPath, encryptedContent, { mode: 0o600 });

      // Create metadata
      const metadata: EncryptedFileMetadata = {
        originalPath: resolvedInputPath,
        encryptedPath: resolvedOutputPath,
        algorithm: 'aes-256-gcm',
        keyId: this.fileKeyId,
        iv: iv.toString('hex'),
        authTag: authTag.toString('hex'),
        fileSize: stats.size,
        originalHash,
        encryptedAt: new Date(),
        version: 1,
      };

      // Store metadata
      this.metadataStore.set(resolvedOutputPath, metadata);
      await this.persistMetadata(metadata);

      logger.info(`File encrypted successfully: ${resolvedInputPath} -> ${resolvedOutputPath}`);

      return {
        success: true,
        encryptedPath: resolvedOutputPath,
        metadata,
      };
    } catch (error) {
      logger.error('File encryption failed', error as Error);
      return {
        success: false,
        error: (error as Error).message,
      };
    }
  }

  /**
   * Decrypt a file and verify integrity
   */
  async decryptFile(
    encryptedPath: string,
    outputPath?: string
  ): Promise<FileEncryptionResult> {
    try {
      const resolvedEncryptedPath = path.resolve(encryptedPath);
      const metadata = await this.loadMetadata(resolvedEncryptedPath);

      if (!metadata) {
        throw new Error('Metadata not found for encrypted file');
      }

      const resolvedOutputPath = outputPath || metadata.originalPath;

      // Derive decryption key
      const key = await this.deriveFileKey(metadata.keyId);

      // Read encrypted content
      const encryptedContent = await fs.readFile(resolvedEncryptedPath);

      // Decrypt the file
      const iv = Buffer.from(metadata.iv, 'hex');
      const authTag = Buffer.from(metadata.authTag, 'hex');

      const decipher = crypto.createDecipherGCM(metadata.algorithm, key, iv);
      decipher.setAuthTag(authTag);

      const decryptedContent = Buffer.concat([
        decipher.update(encryptedContent),
        decipher.final(),
      ]);

      // Verify file integrity
      const decryptedHash = cryptoService.generateHash(decryptedContent);
      if (decryptedHash !== metadata.originalHash) {
        throw new Error('File integrity verification failed');
      }

      // Write decrypted file
      await fs.writeFile(resolvedOutputPath, decryptedContent, { mode: 0o644 });

      logger.info(`File decrypted successfully: ${resolvedEncryptedPath} -> ${resolvedOutputPath}`);

      return {
        success: true,
        encryptedPath: resolvedOutputPath,
        metadata,
      };
    } catch (error) {
      logger.error('File decryption failed', error as Error);
      return {
        success: false,
        error: (error as Error).message,
      };
    }
  }

  /**
   * Encrypt template files in a directory
   */
  async encryptTemplateDirectory(
    directoryPath: string,
    recursive: boolean = true
  ): Promise<{ encrypted: string[]; failed: string[] }> {
    const encrypted: string[] = [];
    const failed: string[] = [];

    try {
      const files = await this.getTemplateFiles(directoryPath, recursive);

      for (const filePath of files) {
        const result = await this.encryptFile(filePath);
        if (result.success) {
          encrypted.push(filePath);
          // Optionally remove original file after successful encryption
          // await fs.unlink(filePath);
        } else {
          failed.push(filePath);
          logger.error(`Failed to encrypt template: ${filePath} - ${result.error}`);
        }
      }

      logger.info(`Template encryption completed: ${encrypted.length} encrypted, ${failed.length} failed`);
    } catch (error) {
      logger.error('Template directory encryption failed', error as Error);
    }

    return { encrypted, failed };
  }

  /**
   * Decrypt template files in a directory
   */
  async decryptTemplateDirectory(
    directoryPath: string,
    recursive: boolean = true
  ): Promise<{ decrypted: string[]; failed: string[] }> {
    const decrypted: string[] = [];
    const failed: string[] = [];

    try {
      const encryptedFiles = await this.getEncryptedFiles(directoryPath, recursive);

      for (const filePath of encryptedFiles) {
        const result = await this.decryptFile(filePath);
        if (result.success) {
          decrypted.push(filePath);
        } else {
          failed.push(filePath);
          logger.error(`Failed to decrypt template: ${filePath} - ${result.error}`);
        }
      }

      logger.info(`Template decryption completed: ${decrypted.length} decrypted, ${failed.length} failed`);
    } catch (error) {
      logger.error('Template directory decryption failed', error as Error);
    }

    return { decrypted, failed };
  }

  /**
   * Stream encrypt large files in chunks
   */
  async encryptFileStream(
    inputPath: string,
    outputPath: string
  ): Promise<FileEncryptionResult> {
    try {
      const key = await this.deriveFileKey(this.fileKeyId);
      const iv = crypto.randomBytes(16);

      const cipher = crypto.createCipherGCM('aes-256-gcm', key, iv);

      const inputStream = (await import('fs')).createReadStream(inputPath);
      const outputStream = (await import('fs')).createWriteStream(outputPath, { mode: 0o600 });

      // Create encryption transform
      const encryptTransform = new Transform({
        transform(chunk, encoding, callback) {
          callback(null, cipher.update(chunk));
        },
        flush(callback) {
          cipher.final();
          callback();
        },
      });

      await pipelineAsync(inputStream, encryptTransform, outputStream);

      const authTag = cipher.getAuthTag();

      // Append auth tag to file
      await fs.appendFile(outputPath, authTag);

      // Calculate original file hash (for large files, we'll use streaming hash)
      const originalHash = await this.calculateFileHash(inputPath);

      const stats = await fs.stat(inputPath);
      const metadata: EncryptedFileMetadata = {
        originalPath: path.resolve(inputPath),
        encryptedPath: path.resolve(outputPath),
        algorithm: 'aes-256-gcm',
        keyId: this.fileKeyId,
        iv: iv.toString('hex'),
        authTag: authTag.toString('hex'),
        fileSize: stats.size,
        originalHash,
        encryptedAt: new Date(),
        version: 1,
      };

      this.metadataStore.set(path.resolve(outputPath), metadata);
      await this.persistMetadata(metadata);

      logger.info(`Large file encrypted with streaming: ${inputPath} -> ${outputPath}`);

      return {
        success: true,
        encryptedPath: outputPath,
        metadata,
      };
    } catch (error) {
      logger.error('Stream encryption failed', error as Error);
      return {
        success: false,
        error: (error as Error).message,
      };
    }
  }

  /**
   * Verify file integrity without decryption
   */
  async verifyFileIntegrity(encryptedPath: string): Promise<boolean> {
    try {
      const metadata = await this.loadMetadata(encryptedPath);
      if (!metadata) {
        logger.warn(`No metadata found for file: ${encryptedPath}`);
        return false;
      }

      // For now, we'll just verify the metadata exists and file size matches
      const stats = await fs.stat(encryptedPath);
      const expectedSize = metadata.fileSize + 32; // Original size + overhead

      return Math.abs(stats.size - expectedSize) < 100; // Allow some variance
    } catch (error) {
      logger.error('File integrity verification failed', error as Error);
      return false;
    }
  }

  /**
   * Rotate encryption keys for all encrypted files
   */
  async rotateEncryptionKeys(): Promise<{ rotated: string[]; failed: string[] }> {
    const rotated: string[] = [];
    const failed: string[] = [];

    try {
      // Generate new key
      const newKeyId = cryptoService.rotateKeyPair(this.fileKeyId).keyId;

      // Re-encrypt all files with new key
      for (const [encryptedPath, metadata] of this.metadataStore.entries()) {
        try {
          // Decrypt with old key
          const decryptResult = await this.decryptFile(encryptedPath);
          if (!decryptResult.success) {
            failed.push(encryptedPath);
            continue;
          }

          // Re-encrypt with new key
          const tempKeyId = this.fileKeyId;
          (this as any).fileKeyId = newKeyId; // Temporary assignment

          const encryptResult = await this.encryptFile(
            decryptResult.metadata!.originalPath,
            encryptedPath
          );

          (this as any).fileKeyId = tempKeyId; // Restore

          if (encryptResult.success) {
            rotated.push(encryptedPath);
          } else {
            failed.push(encryptedPath);
          }
        } catch (error) {
          logger.error(`Key rotation failed for file: ${encryptedPath}`, error as Error);
          failed.push(encryptedPath);
        }
      }

      logger.info(`Key rotation completed: ${rotated.length} rotated, ${failed.length} failed`);
    } catch (error) {
      logger.error('Key rotation process failed', error as Error);
    }

    return { rotated, failed };
  }

  /**
   * Get encryption statistics
   */
  getEncryptionStats() {
    const now = new Date();
    const metadata = Array.from(this.metadataStore.values());

    return {
      totalEncryptedFiles: metadata.length,
      totalEncryptedSize: metadata.reduce((sum, meta) => sum + meta.fileSize, 0),
      oldestEncryption: metadata.reduce((oldest, meta) => 
        meta.encryptedAt < oldest ? meta.encryptedAt : oldest, now
      ),
      newestEncryption: metadata.reduce((newest, meta) => 
        meta.encryptedAt > newest ? meta.encryptedAt : newest, new Date(0)
      ),
      filesNeedingKeyRotation: metadata.filter(meta => {
        const daysSince = (now.getTime() - meta.encryptedAt.getTime()) / (1000 * 60 * 60 * 24);
        return daysSince >= this.config.keyRotationDays;
      }).length,
      algorithms: [...new Set(metadata.map(meta => meta.algorithm))],
      config: this.config,
    };
  }

  /**
   * Initialize file encryption key
   */
  private initializeFileEncryptionKey(): string {
    // Check if key pair already exists
    const existingKeys = cryptoService.listKeys();
    const fileKeyId = existingKeys.find(id => id.includes('file-encryption'));

    if (fileKeyId) {
      logger.info(`Using existing file encryption key: ${fileKeyId}`);
      return fileKeyId;
    }

    // Generate new key pair for file encryption
    const keyPair = cryptoService.generateRSAKeyPair('file-encryption-' + Date.now());
    logger.info(`Generated new file encryption key: ${keyPair.keyId}`);
    return keyPair.keyId;
  }

  /**
   * Derive encryption key for file operations
   */
  private async deriveFileKey(keyId: string): Promise<Buffer> {
    // For this implementation, we'll use HKDF to derive a file encryption key
    const keyInfo = cryptoService.getKeyInfo(keyId);
    if (!keyInfo) {
      throw new Error(`Key not found: ${keyId}`);
    }

    // Use the key ID as input to HKDF
    const keyMaterial = Buffer.from(keyId, 'utf8');
    return crypto.hkdfSync('sha384', keyMaterial, Buffer.from('file-salt'), Buffer.from('file-encryption'), 32);
  }

  /**
   * Check if file should be encrypted based on configuration
   */
  private shouldEncryptFile(filePath: string): boolean {
    const extension = path.extname(filePath).toLowerCase();
    const basename = path.basename(filePath).toLowerCase();

    if (this.config.encryptTemplates) {
      const templateExtensions = ['.hbs', '.mustache', '.ejs', '.pug', '.twig', '.liquid'];
      if (templateExtensions.includes(extension)) return true;
    }

    if (this.config.encryptConfigurations) {
      const configExtensions = ['.json', '.yaml', '.yml', '.toml', '.ini', '.env'];
      const configFiles = ['config', 'configuration', 'settings', 'options'];

      if (configExtensions.includes(extension)) return true;
      if (configFiles.some(name => basename.includes(name))) return true;
    }

    if (this.config.encryptLogs) {
      const logExtensions = ['.log', '.txt'];
      if (logExtensions.includes(extension) && basename.includes('log')) return true;
    }

    return false;
  }

  /**
   * Get template files in directory
   */
  private async getTemplateFiles(directoryPath: string, recursive: boolean): Promise<string[]> {
    const files: string[] = [];

    try {
      const entries = await fs.readdir(directoryPath, { withFileTypes: true });

      for (const entry of entries) {
        const fullPath = path.join(directoryPath, entry.name);

        if (entry.isDirectory() && recursive) {
          const subFiles = await this.getTemplateFiles(fullPath, recursive);
          files.push(...subFiles);
        } else if (entry.isFile() && this.shouldEncryptFile(fullPath)) {
          files.push(fullPath);
        }
      }
    } catch (error) {
      logger.error(`Failed to read directory: ${directoryPath}`, error as Error);
    }

    return files;
  }

  /**
   * Get encrypted files in directory
   */
  private async getEncryptedFiles(directoryPath: string, recursive: boolean): Promise<string[]> {
    const files: string[] = [];

    try {
      const entries = await fs.readdir(directoryPath, { withFileTypes: true });

      for (const entry of entries) {
        const fullPath = path.join(directoryPath, entry.name);

        if (entry.isDirectory() && recursive) {
          const subFiles = await this.getEncryptedFiles(fullPath, recursive);
          files.push(...subFiles);
        } else if (entry.isFile() && entry.name.endsWith('.encrypted')) {
          files.push(fullPath);
        }
      }
    } catch (error) {
      logger.error(`Failed to read directory: ${directoryPath}`, error as Error);
    }

    return files;
  }

  /**
   * Calculate file hash for integrity verification
   */
  private async calculateFileHash(filePath: string): Promise<string> {
    const content = await fs.readFile(filePath);
    return cryptoService.generateHash(content);
  }

  /**
   * Persist metadata to disk
   */
  private async persistMetadata(metadata: EncryptedFileMetadata): Promise<void> {
    const metadataPath = `${metadata.encryptedPath}.meta`;
    await fs.writeFile(
      metadataPath,
      JSON.stringify(metadata, null, 2),
      { mode: 0o600 }
    );
  }

  /**
   * Load metadata from disk
   */
  private async loadMetadata(encryptedPath: string): Promise<EncryptedFileMetadata | null> {
    try {
      // Check memory first
      const cached = this.metadataStore.get(encryptedPath);
      if (cached) return cached;

      // Load from disk
      const metadataPath = `${encryptedPath}.meta`;
      const content = await fs.readFile(metadataPath, 'utf8');
      const metadata: EncryptedFileMetadata = JSON.parse(content);

      this.metadataStore.set(encryptedPath, metadata);
      return metadata;
    } catch (error) {
      logger.warn(`Failed to load metadata for: ${encryptedPath}`, error as Error);
      return null;
    }
  }
}

/**
 * Global file encryption service instance
 */
export const fileEncryptionService = new FileEncryptionService();

