   */
  getStats(): typeof this.stats & {
    activePolicies: number;
    cacheSize: number;
    cacheHitRate: number;
  } {
    const activePolicies = Array.from(this.policies.values())
      .filter(p => p.status === 'active').length;

    const cacheHitRate = this.stats.totalEvaluations > 0 
      ? this.stats.cacheHits / this.stats.totalEvaluations 
      : 0;

    return {
      ...this.stats,
      activePolicies,
      cacheSize: this.policyCache.size,
      cacheHitRate,
    };
  }
}

/**
 * Global policy engine instance
 */
export const policyEngine = new PolicyEngineService();

/**
 * @fileoverview Comprehensive Role-Based Access Control (RBAC) management system
 * @lastmodified 2025-08-27T16:15:00Z
 *
 * Features: Hierarchical roles, granular permissions, dynamic assignment, policy evaluation
 * Main APIs: createRole(), assignRole(), checkPermission(), evaluatePolicy()
 * Constraints: Follows principle of least privilege, supports role inheritance
 * Patterns: RBAC pattern, policy evaluation, hierarchical permissions, audit logging
 */

import * as crypto from 'crypto';
import { logger } from '../utils/logger';
import { securityService } from '../middleware/security.middleware';

export interface Permission {
  id: string;
  name: string;
  resource: string;
  action: string;
  description: string;
  conditions?: PolicyCondition[];
  metadata?: Record<string, any>;
}

export interface Role {
  id: string;
  name: string;
  displayName: string;
  description: string;
  permissions: string[]; // Permission IDs
  inheritsFrom?: string[]; // Parent role IDs
  metadata?: Record<string, any>;
  isSystemRole: boolean;
  createdAt: Date;
  updatedAt: Date;
}

export interface UserRole {
  userId: string;
  roleId: string;
  assignedAt: Date;
  assignedBy: string;
  expiresAt?: Date;
  isActive: boolean;
  conditions?: PolicyCondition[];
}

export interface PolicyCondition {
  type: 'time' | 'location' | 'resource' | 'custom';
  operator: 'eq' | 'ne' | 'lt' | 'le' | 'gt' | 'ge' | 'in' | 'nin' | 'contains' | 'regex';
  field: string;
  value: any;
  description?: string;
}

export interface AccessContext {
  userId: string;
  resource: string;
  action: string;
  resourceId?: string;
  metadata?: Record<string, any>;
  timestamp: Date;
  clientInfo?: {
    ipAddress: string;
    userAgent: string;
    location?: {
      country: string;
      region: string;
    };
  };
}

export interface PermissionEvaluationResult {
  granted: boolean;
  matchedPermissions: Permission[];
  appliedRoles: Role[];
  conditions: PolicyCondition[];
  reason: string;
  auditInfo: {
    evaluationTime: number;
    rulesEvaluated: number;
    conditionsChecked: number;
  };
}

export interface RBACStats {
  totalRoles: number;
  totalPermissions: number;
  totalUserRoles: number;
  systemRoles: number;
  customRoles: number;
  activeUsers: number;
  permissionEvaluations: number;
  averageEvaluationTime: number;
}

/**
 * Comprehensive RBAC Manager Service
 */
export class RBACManagerService {
  private roles = new Map<string, Role>();
  private permissions = new Map<string, Permission>();
  private userRoles = new Map<string, UserRole[]>(); // userId -> UserRole[]
  private rolePermissionsCache = new Map<string, Set<string>>(); // roleId -> permissions (with inheritance)
  private evaluationStats = {
    totalEvaluations: 0,
    totalTime: 0,
    rulesEvaluated: 0,
    conditionsChecked: 0,
  };

  constructor() {
    this.initializeSystemRoles();
    this.initializeSystemPermissions();

    // Clear caches periodically to prevent memory leaks
    setInterval(() => this.cleanupCaches(), 300000); // Every 5 minutes
  }

  /**
   * Permission Management
   */
  async createPermission(permissionData: Omit<Permission, 'id'>): Promise<Permission> {
    const permission: Permission = {
      id: crypto.randomUUID(),
      ...permissionData,
    };

    this.permissions.set(permission.id, permission);
    this.clearPermissionCaches();

    logger.info('Permission created', {
      permissionId: permission.id,
      name: permission.name,
      resource: permission.resource,
      action: permission.action,
    });

    return permission;
  }

  async updatePermission(permissionId: string, updates: Partial<Permission>): Promise<Permission | null> {
    const permission = this.permissions.get(permissionId);
    if (!permission) {
      return null;
    }

    const updatedPermission = { ...permission, ...updates };
    this.permissions.set(permissionId, updatedPermission);
    this.clearPermissionCaches();

    logger.info('Permission updated', {
      permissionId,
      updates: Object.keys(updates),
    });

    return updatedPermission;
  }

  async deletePermission(permissionId: string): Promise<boolean> {
    if (!this.permissions.has(permissionId)) {
      return false;
    }

    // Remove permission from all roles
    for (const role of this.roles.values()) {
      const permissionIndex = role.permissions.indexOf(permissionId);
      if (permissionIndex > -1) {
        role.permissions.splice(permissionIndex, 1);
        role.updatedAt = new Date();
      }
    }

    this.permissions.delete(permissionId);
    this.clearPermissionCaches();

    logger.info('Permission deleted', { permissionId });
    return true;
  }

  getPermission(permissionId: string): Permission | null {
    return this.permissions.get(permissionId) || null;
  }

  getAllPermissions(): Permission[] {
    return Array.from(this.permissions.values());
  }

  /**
   * Role Management
   */
  async createRole(roleData: Omit<Role, 'id' | 'createdAt' | 'updatedAt'>): Promise<Role> {
    const role: Role = {
      id: crypto.randomUUID(),
      createdAt: new Date(),
      updatedAt: new Date(),
      ...roleData,
    };

    // Validate permissions exist
    for (const permissionId of role.permissions) {
      if (!this.permissions.has(permissionId)) {
        throw new Error(`Permission ${permissionId} not found`);
      }
    }

    // Validate inheritance doesn't create cycles
    if (role.inheritsFrom) {
      this.validateRoleInheritance(role.id, role.inheritsFrom);
    }

    this.roles.set(role.id, role);
    this.clearRoleCache(role.id);

    logger.info('Role created', {
      roleId: role.id,
      name: role.name,
      permissions: role.permissions.length,
      inheritsFrom: role.inheritsFrom,
    });

    return role;
  }

  async updateRole(roleId: string, updates: Partial<Role>): Promise<Role | null> {
    const role = this.roles.get(roleId);
    if (!role) {
      return null;
    }

    // Prevent updating system roles' core properties
    if (role.isSystemRole && (updates.name || updates.isSystemRole === false)) {
      throw new Error('Cannot modify system role core properties');
    }

    const updatedRole = {
      ...role,
      ...updates,
      updatedAt: new Date(),
    };

    // Validate permissions and inheritance
    if (updates.permissions) {
      for (const permissionId of updates.permissions) {
        if (!this.permissions.has(permissionId)) {
          throw new Error(`Permission ${permissionId} not found`);
        }
      }
    }

    if (updates.inheritsFrom) {
      this.validateRoleInheritance(roleId, updates.inheritsFrom);
    }

    this.roles.set(roleId, updatedRole);
    this.clearRoleCache(roleId);

    logger.info('Role updated', {
      roleId,
      updates: Object.keys(updates),
    });

    return updatedRole;
  }

  async deleteRole(roleId: string): Promise<boolean> {
    const role = this.roles.get(roleId);
    if (!role) {
      return false;
    }

    if (role.isSystemRole) {
      throw new Error('Cannot delete system role');
    }

    // Remove role from users
    for (const [userId, userRolesList] of this.userRoles) {
      const filteredRoles = userRolesList.filter(ur => ur.roleId !== roleId);
      if (filteredRoles.length !== userRolesList.length) {
        this.userRoles.set(userId, filteredRoles);
      }
    }

    // Update roles that inherit from this role
    for (const otherRole of this.roles.values()) {
      if (otherRole.inheritsFrom?.includes(roleId)) {
        otherRole.inheritsFrom = otherRole.inheritsFrom.filter(id => id !== roleId);
        otherRole.updatedAt = new Date();
      }
    }

    this.roles.delete(roleId);
    this.clearRoleCache(roleId);

    logger.info('Role deleted', { roleId });
    return true;
  }

  getRole(roleId: string): Role | null {
    return this.roles.get(roleId) || null;
  }

  getAllRoles(): Role[] {
    return Array.from(this.roles.values());
  }

  getRolesByType(isSystemRole: boolean): Role[] {
    return Array.from(this.roles.values()).filter(role => role.isSystemRole === isSystemRole);
  }

  /**
   * User Role Assignment
   */
  async assignRoleToUser(
    userId: string,
    roleId: string,
    assignedBy: string,
    expiresAt?: Date,
    conditions?: PolicyCondition[]
  ): Promise<UserRole> {
    if (!this.roles.has(roleId)) {
      throw new Error('Role not found');
    }

    const userRole: UserRole = {
      userId,
      roleId,
      assignedAt: new Date(),
      assignedBy,
      expiresAt,
      isActive: true,
      conditions,
    };

    if (!this.userRoles.has(userId)) {
      this.userRoles.set(userId, []);
    }

    // Check if role is already assigned
    const userRolesList = this.userRoles.get(userId)!;
    const existingAssignment = userRolesList.find(ur => ur.roleId === roleId);

    if (existingAssignment) {
      // Update existing assignment
      Object.assign(existingAssignment, userRole);
    } else {
      userRolesList.push(userRole);
    }

    logger.info('Role assigned to user', {
      userId,
      roleId,
      assignedBy,
      expiresAt,
      hasConditions: !!conditions?.length,
    });

    return userRole;
  }

  async removeRoleFromUser(userId: string, roleId: string): Promise<boolean> {
    const userRolesList = this.userRoles.get(userId);
    if (!userRolesList) {
      return false;
    }

    const initialLength = userRolesList.length;
    const filteredRoles = userRolesList.filter(ur => ur.roleId !== roleId);

    if (filteredRoles.length < initialLength) {
      this.userRoles.set(userId, filteredRoles);
      logger.info('Role removed from user', { userId, roleId });
      return true;
    }

    return false;
  }

  getUserRoles(userId: string): UserRole[] {
    const userRoles = this.userRoles.get(userId) || [];
    const now = new Date();

    // Filter expired and inactive roles
    return userRoles.filter(ur => {
      if (!ur.isActive) return false;
      if (ur.expiresAt && ur.expiresAt < now) {
        ur.isActive = false;
        return false;
      }
      return true;
    });
  }

  getUsersWithRole(roleId: string): string[] {
    const users: string[] = [];
    for (const [userId, userRoles] of this.userRoles) {
      if (userRoles.some(ur => ur.roleId === roleId && ur.isActive)) {
        users.push(userId);
      }
    }
    return users;
  }

  /**
   * Permission Evaluation
   */
  async checkPermission(
    userId: string,
    resource: string,
    action: string,
    context?: AccessContext
  ): Promise<PermissionEvaluationResult> {
    const startTime = Date.now();
    let rulesEvaluated = 0;
    let conditionsChecked = 0;

    try {
      // Get user roles
      const userRoles = this.getUserRoles(userId);
      if (userRoles.length === 0) {
        return this.createEvaluationResult(false, [], [], [], 'No roles assigned', rulesEvaluated, conditionsChecked, startTime);
      }

      const matchedPermissions: Permission[] = [];
      const appliedRoles: Role[] = [];
      const appliedConditions: PolicyCondition[] = [];

      // Evaluate each role
      for (const userRole of userRoles) {
        const role = this.roles.get(userRole.roleId);
        if (!role) continue;

        appliedRoles.push(role);
        rulesEvaluated++;

        // Check role conditions
        if (userRole.conditions) {
          for (const condition of userRole.conditions) {
            conditionsChecked++;
            if (!this.evaluateCondition(condition, context)) {
              continue; // Skip this role if condition fails
            }
            appliedConditions.push(condition);
          }
        }

        // Get all permissions for this role (including inherited)
        const rolePermissions = this.getRolePermissions(userRole.roleId);

        for (const permissionId of rolePermissions) {
          const permission = this.permissions.get(permissionId);
          if (!permission) continue;

          rulesEvaluated++;

          // Check if permission matches resource and action
          if (this.permissionMatches(permission, resource, action)) {
            // Check permission conditions
            if (permission.conditions) {
              let conditionsPassed = true;
              for (const condition of permission.conditions) {
                conditionsChecked++;
                if (!this.evaluateCondition(condition, context)) {
                  conditionsPassed = false;
                  break;
                }
                appliedConditions.push(condition);
              }
              if (!conditionsPassed) continue;
            }

            matchedPermissions.push(permission);
          }
        }
      }

      // Update stats
      this.evaluationStats.totalEvaluations++;
      this.evaluationStats.totalTime += Date.now() - startTime;
      this.evaluationStats.rulesEvaluated += rulesEvaluated;
      this.evaluationStats.conditionsChecked += conditionsChecked;

      const granted = matchedPermissions.length > 0;
      const reason = granted 
        ? `Access granted via ${matchedPermissions.length} matching permission(s)`
        : 'No matching permissions found';

      return this.createEvaluationResult(
        granted,
        matchedPermissions,
        appliedRoles,
        appliedConditions,
        reason,
        rulesEvaluated,
        conditionsChecked,
        startTime
      );
    } catch (error) {
      logger.error('Permission evaluation failed', error as Error);
      return this.createEvaluationResult(false, [], [], [], 'Evaluation error', rulesEvaluated, conditionsChecked, startTime);
    }
  }

  /**
   * Advanced Permission Checking
   */
  async hasPermission(userId: string, permission: string, resourceId?: string): Promise<boolean> {
    const [resource, action] = permission.split(':');
    const result = await this.checkPermission(userId, resource, action, {
      userId,
      resource,
      action,
      resourceId,
      timestamp: new Date(),
    });
    return result.granted;
  }

  async hasAnyPermission(userId: string, permissions: string[]): Promise<boolean> {
    for (const permission of permissions) {
      if (await this.hasPermission(userId, permission)) {
        return true;
      }
    }
    return false;
  }

  async hasAllPermissions(userId: string, permissions: string[]): Promise<boolean> {
    for (const permission of permissions) {
      if (!(await this.hasPermission(userId, permission))) {
        return false;
      }
    }
    return true;
  }

  /**
   * Role Hierarchy and Inheritance
   */
  private getRolePermissions(roleId: string): Set<string> {
    // Check cache first
    if (this.rolePermissionsCache.has(roleId)) {
      return this.rolePermissionsCache.get(roleId)!;
    }

    const role = this.roles.get(roleId);
    if (!role) {
      return new Set();
    }

    const allPermissions = new Set<string>(role.permissions);

    // Add inherited permissions
    if (role.inheritsFrom) {
      for (const parentRoleId of role.inheritsFrom) {
        const parentPermissions = this.getRolePermissions(parentRoleId);
        for (const permission of parentPermissions) {
          allPermissions.add(permission);
        }
      }
    }

    // Cache result
    this.rolePermissionsCache.set(roleId, allPermissions);
    return allPermissions;
  }

  private validateRoleInheritance(roleId: string, inheritsFrom: string[]): void {
    const visited = new Set<string>();
    const stack = [...inheritsFrom];

    while (stack.length > 0) {
      const currentRoleId = stack.pop()!;

      if (currentRoleId === roleId) {
        throw new Error('Circular role inheritance detected');
      }

      if (visited.has(currentRoleId)) {
        continue;
      }
      visited.add(currentRoleId);

      const role = this.roles.get(currentRoleId);
      if (role?.inheritsFrom) {
        stack.push(...role.inheritsFrom);
      }
    }
  }

  /**
   * Condition Evaluation
   */
  private evaluateCondition(condition: PolicyCondition, context?: AccessContext): boolean {
    if (!context) return true;

    try {
      const fieldValue = this.getContextFieldValue(condition.field, context);
      const conditionValue = condition.value;

      switch (condition.operator) {
        case 'eq':
          return fieldValue === conditionValue;
        case 'ne':
          return fieldValue !== conditionValue;
        case 'lt':
          return fieldValue < conditionValue;
        case 'le':
          return fieldValue <= conditionValue;
        case 'gt':
          return fieldValue > conditionValue;
        case 'ge':
          return fieldValue >= conditionValue;
        case 'in':
          return Array.isArray(conditionValue) && conditionValue.includes(fieldValue);
        case 'nin':
          return Array.isArray(conditionValue) && !conditionValue.includes(fieldValue);
        case 'contains':
          return String(fieldValue).includes(String(conditionValue));
        case 'regex':
          return new RegExp(conditionValue).test(String(fieldValue));
        default:
          logger.warn('Unknown condition operator', { operator: condition.operator });
          return false;
      }
    } catch (error) {
      logger.error('Condition evaluation failed', error as Error);
      return false;
    }
  }

  private getContextFieldValue(field: string, context: AccessContext): any {
    const parts = field.split('.');
    let value: any = context;

    for (const part of parts) {
      if (value && typeof value === 'object' && part in value) {
        value = value[part];
      } else {
        return undefined;
      }
    }

    return value;
  }

  private permissionMatches(permission: Permission, resource: string, action: string): boolean {
    // Exact match
    if (permission.resource === resource && permission.action === action) {
      return true;
    }

    // Wildcard match
    if (permission.resource === '*' || permission.action === '*') {
      return true;
    }

    // Pattern match (basic)
    const resourceMatch = permission.resource.endsWith('*') 
      ? resource.startsWith(permission.resource.slice(0, -1))
      : permission.resource === resource;

    const actionMatch = permission.action.endsWith('*')
      ? action.startsWith(permission.action.slice(0, -1))
      : permission.action === action;

    return resourceMatch && actionMatch;
  }

  /**
   * Utility Methods
   */
  private createEvaluationResult(
    granted: boolean,
    permissions: Permission[],
    roles: Role[],
    conditions: PolicyCondition[],
    reason: string,
    rulesEvaluated: number,
    conditionsChecked: number,
    startTime: number
  ): PermissionEvaluationResult {
    return {
      granted,
      matchedPermissions: permissions,
      appliedRoles: roles,
      conditions,
      reason,
      auditInfo: {
        evaluationTime: Date.now() - startTime,
        rulesEvaluated,
        conditionsChecked,
      },
    };
  }

  private clearPermissionCaches(): void {
    this.rolePermissionsCache.clear();
  }

  private clearRoleCache(roleId: string): void {
    this.rolePermissionsCache.delete(roleId);

    // Clear cache for roles that inherit from this role
    for (const role of this.roles.values()) {
      if (role.inheritsFrom?.includes(roleId)) {
        this.rolePermissionsCache.delete(role.id);
      }
    }
  }

  private cleanupCaches(): void {
    if (this.rolePermissionsCache.size > 1000) {
      this.rolePermissionsCache.clear();
      logger.info('Role permissions cache cleared due to size limit');
    }
  }

  /**
   * Statistics and Monitoring
   */
  getStats(): RBACStats {
    const totalUsers = this.userRoles.size;
    const activeUsers = Array.from(this.userRoles.values())
      .filter(userRoles => userRoles.some(ur => ur.isActive)).length;

    return {
      totalRoles: this.roles.size,
      totalPermissions: this.permissions.size,
      totalUserRoles: Array.from(this.userRoles.values())
        .reduce((sum, userRoles) => sum + userRoles.length, 0),
      systemRoles: Array.from(this.roles.values()).filter(r => r.isSystemRole).length,
      customRoles: Array.from(this.roles.values()).filter(r => !r.isSystemRole).length,
      activeUsers,
      permissionEvaluations: this.evaluationStats.totalEvaluations,
      averageEvaluationTime: this.evaluationStats.totalEvaluations > 0 
        ? this.evaluationStats.totalTime / this.evaluationStats.totalEvaluations 
        : 0,
    };
  }

  /**
   * Initialize System Roles and Permissions
   */
  private initializeSystemRoles(): void {
    const systemRoles = [
      {
        id: 'system-admin',
        name: 'system-admin',
        displayName: 'System Administrator',
        description: 'Full system access with all permissions',
        permissions: [], // Will be populated after permissions are created
        isSystemRole: true,
      },
      {
        id: 'admin',
        name: 'admin',
        displayName: 'Administrator',
        description: 'Administrative access to most system features',
        permissions: [],
        isSystemRole: true,
      },
      {
        id: 'editor',
        name: 'editor',
        displayName: 'Content Editor',
        description: 'Can create, read, update, and delete content',
        permissions: [],
        isSystemRole: true,
      },
      {
        id: 'viewer',
        name: 'viewer',
        displayName: 'Viewer',
        description: 'Read-only access to content',
        permissions: [],
        isSystemRole: true,
      },
      {
        id: 'guest',
        name: 'guest',
        displayName: 'Guest User',
        description: 'Limited access for unauthenticated users',
        permissions: [],
        isSystemRole: true,
      },
    ];

    for (const roleData of systemRoles) {
      const role: Role = {
        ...roleData,
        createdAt: new Date(),
        updatedAt: new Date(),
      };
      this.roles.set(role.id, role);
    }
  }

  private initializeSystemPermissions(): void {
    const systemPermissions = [
      // Template permissions
      { resource: 'templates', action: 'create', description: 'Create new templates' },
      { resource: 'templates', action: 'read', description: 'View templates' },
      { resource: 'templates', action: 'update', description: 'Edit templates' },
      { resource: 'templates', action: 'delete', description: 'Delete templates' },
      { resource: 'templates', action: 'publish', description: 'Publish templates to marketplace' },

      // Marketplace permissions
      { resource: 'marketplace', action: 'browse', description: 'Browse marketplace' },
      { resource: 'marketplace', action: 'install', description: 'Install templates from marketplace' },
      { resource: 'marketplace', action: 'publish', description: 'Publish to marketplace' },
      { resource: 'marketplace', action: 'moderate', description: 'Moderate marketplace content' },

      // User management permissions
      { resource: 'users', action: 'create', description: 'Create user accounts' },
      { resource: 'users', action: 'read', description: 'View user information' },
      { resource: 'users', action: 'update', description: 'Update user accounts' },
      { resource: 'users', action: 'delete', description: 'Delete user accounts' },
      { resource: 'users', action: 'manage-roles', description: 'Assign and manage user roles' },

      // System permissions
      { resource: 'system', action: 'admin', description: 'Full system administration' },
      { resource: 'system', action: 'configure', description: 'Configure system settings' },
      { resource: 'system', action: 'audit', description: 'View audit logs and security reports' },
      { resource: 'system', action: 'monitor', description: 'Monitor system health and performance' },

      // Plugin permissions
      { resource: 'plugins', action: 'install', description: 'Install plugins' },
      { resource: 'plugins', action: 'configure', description: 'Configure plugins' },
      { resource: 'plugins', action: 'develop', description: 'Develop and test plugins' },
    ];

    for (const permData of systemPermissions) {
      const permission: Permission = {
        id: crypto.randomUUID(),
        name: `${permData.resource}:${permData.action}`,
        ...permData,
      };
      this.permissions.set(permission.id, permission);
    }

    // Assign permissions to system roles
    this.assignSystemPermissions();
  }

  private assignSystemPermissions(): void {
    const allPermissions = Array.from(this.permissions.keys());

    // System Admin - all permissions
    const systemAdmin = this.roles.get('system-admin')!;
    systemAdmin.permissions = [...allPermissions];

    // Admin - most permissions except system admin
    const admin = this.roles.get('admin')!;
    admin.permissions = Array.from(this.permissions.values())
      .filter(p => !p.name.includes('system:admin'))
      .map(p => p.id);

    // Editor - content management permissions
    const editor = this.roles.get('editor')!;
    editor.permissions = Array.from(this.permissions.values())
      .filter(p => p.resource === 'templates' || p.resource === 'marketplace' && p.action !== 'moderate')
      .map(p => p.id);

    // Viewer - read-only permissions
    const viewer = this.roles.get('viewer')!;
    viewer.permissions = Array.from(this.permissions.values())
      .filter(p => p.action === 'read' || p.action === 'browse')
      .map(p => p.id);

    // Guest - very limited permissions
    const guest = this.roles.get('guest')!;
    guest.permissions = Array.from(this.permissions.values())
      .filter(p => p.resource === 'marketplace' && p.action === 'browse')
      .map(p => p.id);
  }
}

/**
 * Global RBAC manager instance
 */
export const rbacManager = new RBACManagerService();

/**
 * @fileoverview Role-based rate limiting with dynamic adjustment and security controls
 * @lastmodified 2025-08-27T17:00:00Z
 *
 * Features: Role-based limits, dynamic adjustment, burst handling, security monitoring
 * Main APIs: RoleBasedRateLimiter, checkRateLimit(), adjustLimits(), monitorAbuse()
 * Constraints: Integrates with RBAC system, enforces per-user and per-role limits
 * Patterns: Rate limiting, role-based policies, abuse detection, adaptive limits
 */

import { EventEmitter } from 'events';
import { logger } from '../utils/logger';
import { rbacManager } from './rbac-manager.service';
import { RateLimiter, RateLimitConfig, RateLimitResult, IRateLimitStore, MemoryRateLimitStore } from '../middleware/rate-limiter';

export interface RoleBasedRateLimitConfig {
  // Role-based limits (role -> config)
  roleLimits: Record<string, RateLimitConfig>;

  // Global fallback limits
  defaultLimits: RateLimitConfig;

  // Emergency limits for security incidents
  emergencyLimits: RateLimitConfig;

  // Per-user limits (overrides role limits)
  userLimits?: Record<string, RateLimitConfig>;

  // Dynamic adjustment settings
  enableDynamicAdjustment: boolean;
  adjustmentFactor: number; // Multiplier for dynamic changes
  adjustmentWindow: number; // Time window for adjustment decisions

  // Abuse detection
  enableAbuseDetection: boolean;
  abuseThreshold: number; // Number of limit violations before abuse detection
  abuseTimeWindow: number; // Time window for abuse detection
  abusePenaltyMultiplier: number; // Penalty factor for abusers

  // Emergency controls
  enableEmergencyMode: boolean;
  emergencyThreshold: number; // System-wide rate before emergency mode
  emergencyDuration: number; // How long emergency mode lasts

  // Monitoring
  enableRealTimeMonitoring: boolean;
  monitoringInterval: number;
  alertThresholds: {
    warningPercent: number; // % of limit before warning
    criticalPercent: number; // % of limit before critical alert
  };
}

export interface RateLimitContext {
  userId: string;
  userRoles: string[];
  resource: string;
  action: string;
  priority: 'low' | 'medium' | 'high' | 'critical';
  clientInfo: {
    ipAddress: string;
    userAgent: string;
  };
  metadata?: Record<string, any>;
}

export interface RoleBasedRateLimitResult extends RateLimitResult {
  appliedConfig: RateLimitConfig;
  appliedRole?: string;
  dynamicAdjustment?: {
    originalLimit: number;
    adjustedLimit: number;
    reason: string;
  };
  securityFlags: {
    suspiciousActivity: boolean;
    abuseDetected: boolean;
    emergencyMode: boolean;
  };
}

export interface AbuseRecord {
  userId: string;
  violations: number;
  firstViolation: Date;
  lastViolation: Date;
  penaltyLevel: number;
  ipAddresses: Set<string>;
  resources: Set<string>;
}

/**
 * Role-Based Rate Limiter with Security Features
 */
export class RoleBasedRateLimiter extends EventEmitter {
  private config: RoleBasedRateLimitConfig;
  private rateLimiters = new Map<string, RateLimiter>(); // key -> RateLimiter
  private abuseRecords = new Map<string, AbuseRecord>();
  private emergencyModeActive = false;
  private emergencyModeEnd?: Date;
  private monitoringTimer?: ReturnType<typeof setInterval>;

  // Statistics
  private stats = {
    totalRequests: 0,
    blockedRequests: 0,
    emergencyBlocks: 0,
    abuseDetections: 0,
    dynamicAdjustments: 0,
  };

  constructor(config: Partial<RoleBasedRateLimitConfig> = {}) {
    super();

    this.config = {
      roleLimits: {
        'system-admin': {
          windowMs: 60 * 1000,
          maxRequests: 10000, // Very high limit for system admin
          algorithm: 'token-bucket',
        },
        'admin': {
          windowMs: 60 * 1000,
          maxRequests: 5000,
          algorithm: 'token-bucket',
        },
        'editor': {
          windowMs: 60 * 1000,
          maxRequests: 1000,
          algorithm: 'sliding-window',
        },
        'viewer': {
          windowMs: 60 * 1000,
          maxRequests: 500,
          algorithm: 'sliding-window',
        },
        'guest': {
          windowMs: 60 * 1000,
          maxRequests: 100,
          algorithm: 'fixed-window',
        },
      },
      defaultLimits: {
        windowMs: 60 * 1000,
        maxRequests: 100,
        algorithm: 'sliding-window',
      },
      emergencyLimits: {
        windowMs: 60 * 1000,
        maxRequests: 10, // Very restrictive during emergency
        algorithm: 'leaky-bucket',
      },
      enableDynamicAdjustment: true,
      adjustmentFactor: 0.5,
      adjustmentWindow: 300000, // 5 minutes
      enableAbuseDetection: true,
      abuseThreshold: 10,
      abuseTimeWindow: 600000, // 10 minutes
      abusePenaltyMultiplier: 0.1,
      enableEmergencyMode: true,
      emergencyThreshold: 10000, // requests per minute system-wide
      emergencyDuration: 300000, // 5 minutes
      enableRealTimeMonitoring: true,
      monitoringInterval: 30000, // 30 seconds
      alertThresholds: {
        warningPercent: 80,
        criticalPercent: 95,
      },
      ...config,
    };

    if (this.config.enableRealTimeMonitoring) {
      this.startMonitoring();
    }

    // Cleanup abuse records periodically
    setInterval(() => this.cleanupAbuseRecords(), 3600000); // Every hour
  }

  /**
   * Check rate limit with role-based and dynamic adjustments
   */
  async checkRateLimit(context: RateLimitContext): Promise<RoleBasedRateLimitResult> {
    try {
      this.stats.totalRequests++;

      // Check emergency mode first
      if (this.emergencyModeActive) {
        if (this.emergencyModeEnd && new Date() > this.emergencyModeEnd) {
          this.emergencyModeActive = false;
          this.emergencyModeEnd = undefined;
          this.emit('emergencyModeDeactivated');
          logger.info('Emergency mode deactivated');
        } else {
          return this.handleEmergencyMode(context);
        }
      }

      // Check for abuse
      if (this.config.enableAbuseDetection) {
        const abuseCheck = this.checkAbuse(context.userId, context.clientInfo.ipAddress, context.resource);
        if (abuseCheck.isAbusive) {
          this.stats.blockedRequests++;
          return this.createAbuseResult(context, abuseCheck.penaltyLevel);
        }
      }

      // Determine effective rate limit configuration
      const effectiveConfig = await this.getEffectiveConfig(context);

      // Get or create rate limiter for this configuration
      const rateLimiterKey = this.getRateLimiterKey(context, effectiveConfig);
      let rateLimiter = this.rateLimiters.get(rateLimiterKey);

      if (!rateLimiter) {
        rateLimiter = new RateLimiter(effectiveConfig);
        this.rateLimiters.set(rateLimiterKey, rateLimiter);
      }

      // Check rate limit
      const identifier = this.createIdentifier(context);
      const result = await rateLimiter.checkLimit(identifier);

      // Handle rate limit violation
      if (!result.allowed) {
        this.stats.blockedRequests++;
        this.handleRateLimitViolation(context, result);
      }

      // Create role-based result
      const roleBasedResult: RoleBasedRateLimitResult = {
        ...result,
        appliedConfig: effectiveConfig,
        appliedRole: this.getAppliedRole(context.userRoles),
        securityFlags: {
          suspiciousActivity: false,
          abuseDetected: false,
          emergencyMode: this.emergencyModeActive,
        },
      };

      // Apply dynamic adjustments if enabled
      if (this.config.enableDynamicAdjustment && !result.allowed) {
        const adjustment = await this.considerDynamicAdjustment(context, result);
        if (adjustment) {
          roleBasedResult.dynamicAdjustment = adjustment;
          this.stats.dynamicAdjustments++;
        }
      }

      this.emit('rateLimitCheck', {
        userId: context.userId,
        resource: context.resource,
        allowed: result.allowed,
        remaining: result.remaining,
      });

      return roleBasedResult;
    } catch (error) {
      logger.error('Rate limit check failed', error as Error);

      // Fail securely - deny the request
      this.stats.blockedRequests++;
      return {
        allowed: false,
        remaining: 0,
        resetTime: Date.now() + this.config.defaultLimits.windowMs,
        error: 'Rate limit check failed',
        appliedConfig: this.config.defaultLimits,
        securityFlags: {
          suspiciousActivity: false,
          abuseDetected: false,
          emergencyMode: false,
        },
      };
    }
  }

  /**
   * Manually adjust rate limits for a user or role
   */
  async adjustUserLimit(
    userId: string,
    adjustment: Partial<RateLimitConfig>,
    duration?: number
  ): Promise<void> {
    if (!this.config.userLimits) {
      this.config.userLimits = {};
    }

    // Get current user limits or default
    const currentLimits = this.config.userLimits[userId] || this.config.defaultLimits;
    this.config.userLimits[userId] = { ...currentLimits, ...adjustment };

    logger.info('User rate limit adjusted', {
      userId,
      adjustment,
      duration,
    });

    // Remove adjustment after duration
    if (duration) {
      setTimeout(() => {
        if (this.config.userLimits && this.config.userLimits[userId]) {
          delete this.config.userLimits[userId];
          logger.info('User rate limit adjustment expired', { userId });
        }
      }, duration);
    }

    this.emit('limitAdjusted', { userId, adjustment });
  }

  /**
   * Enable emergency mode system-wide
   */
  async enableEmergencyMode(duration?: number): Promise<void> {
    this.emergencyModeActive = true;
    this.emergencyModeEnd = duration ? new Date(Date.now() + duration) : undefined;

    logger.warn('Emergency mode activated', {
      duration: duration ? `${duration}ms` : 'indefinite',
      emergencyLimits: this.config.emergencyLimits,
    });

    this.emit('emergencyModeActivated', { duration });

    if (duration) {
      setTimeout(() => {
        this.emergencyModeActive = false;
        this.emergencyModeEnd = undefined;
        this.emit('emergencyModeDeactivated');
        logger.info('Emergency mode deactivated automatically');
      }, duration);
    }
  }

  /**
   * Disable emergency mode
   */
  async disableEmergencyMode(): Promise<void> {
    this.emergencyModeActive = false;
    this.emergencyModeEnd = undefined;

    logger.info('Emergency mode manually deactivated');
    this.emit('emergencyModeDeactivated');
  }

  /**
   * Get rate limiting statistics
   */
  getStats(): typeof this.stats & {
    activeRateLimiters: number;
    activeAbuseRecords: number;
    emergencyModeActive: boolean;
    configuredRoles: string[];
  } {
    return {
      ...this.stats,
      activeRateLimiters: this.rateLimiters.size,
      activeAbuseRecords: this.abuseRecords.size,
      emergencyModeActive: this.emergencyModeActive,
      configuredRoles: Object.keys(this.config.roleLimits),
    };
  }

  /**
   * Get abuse records for monitoring
   */
  getAbuseRecords(): AbuseRecord[] {
    return Array.from(this.abuseRecords.values()).map(record => ({
      ...record,
      ipAddresses: Array.from(record.ipAddresses),
      resources: Array.from(record.resources),
    })) as any;
  }

  /**
   * Private helper methods
   */
  private async getEffectiveConfig(context: RateLimitContext): Promise<RateLimitConfig> {
    // Check user-specific limits first
    if (this.config.userLimits?.[context.userId]) {
      return this.config.userLimits[context.userId];
    }

    // Find highest priority role and use its limits
    const appliedRole = this.getAppliedRole(context.userRoles);
    if (appliedRole && this.config.roleLimits[appliedRole]) {
      return this.config.roleLimits[appliedRole];
    }

    // Fall back to default limits
    return this.config.defaultLimits;
  }

  private getAppliedRole(userRoles: string[]): string | undefined {
    // Priority order for roles (highest to lowest)
    const rolePriority = ['system-admin', 'admin', 'editor', 'viewer', 'guest'];

    for (const role of rolePriority) {
      if (userRoles.includes(role)) {
        return role;
      }
    }

    return undefined;
  }

  private getRateLimiterKey(context: RateLimitContext, config: RateLimitConfig): string {
    return `${config.algorithm}_${config.windowMs}_${config.maxRequests}_${context.resource}`;
  }

  private createIdentifier(context: RateLimitContext): string {
    // Create hierarchical identifier: user -> IP -> resource
    return `user:${context.userId}:ip:${context.clientInfo.ipAddress}:resource:${context.resource}`;
  }

  private checkAbuse(userId: string, ipAddress: string, resource: string): {
    isAbusive: boolean;
    penaltyLevel: number;
  } {
    const abuseRecord = this.abuseRecords.get(userId);
    if (!abuseRecord) {
      return { isAbusive: false, penaltyLevel: 0 };
    }

    const now = new Date();
    const timeWindow = now.getTime() - this.config.abuseTimeWindow;

    // Check if violations are within the time window
    if (abuseRecord.lastViolation.getTime() < timeWindow) {
      // Reset abuse record if outside time window
      this.abuseRecords.delete(userId);
      return { isAbusive: false, penaltyLevel: 0 };
    }

    // Check abuse threshold
    const isAbusive = abuseRecord.violations >= this.config.abuseThreshold;
    return {
      isAbusive,
      penaltyLevel: abuseRecord.penaltyLevel,
    };
  }

  private handleRateLimitViolation(context: RateLimitContext, result: RateLimitResult): void {
    // Record abuse
    let abuseRecord = this.abuseRecords.get(context.userId);
    if (!abuseRecord) {
      abuseRecord = {
        userId: context.userId,
        violations: 0,
        firstViolation: new Date(),
        lastViolation: new Date(),
        penaltyLevel: 1,
        ipAddresses: new Set(),
        resources: new Set(),
      };
      this.abuseRecords.set(context.userId, abuseRecord);
    }

    abuseRecord.violations++;
    abuseRecord.lastViolation = new Date();
    abuseRecord.ipAddresses.add(context.clientInfo.ipAddress);
    abuseRecord.resources.add(context.resource);

    // Increase penalty level
    if (abuseRecord.violations % this.config.abuseThreshold === 0) {
      abuseRecord.penaltyLevel++;
      this.stats.abuseDetections++;

      this.emit('abuseDetected', {
        userId: context.userId,
        violations: abuseRecord.violations,
        penaltyLevel: abuseRecord.penaltyLevel,
      });

      logger.warn('Abuse detected', {
        userId: context.userId,
        violations: abuseRecord.violations,
        penaltyLevel: abuseRecord.penaltyLevel,
        ipAddress: context.clientInfo.ipAddress,
        resource: context.resource,
      });
    }

    // Check for emergency mode activation
    if (this.config.enableEmergencyMode && this.stats.totalRequests > this.config.emergencyThreshold) {
      this.enableEmergencyMode(this.config.emergencyDuration);
    }
  }

  private async considerDynamicAdjustment(
    context: RateLimitContext,
    result: RateLimitResult
  ): Promise<RoleBasedRateLimitResult['dynamicAdjustment']> {
    // Simple dynamic adjustment logic
    if (context.priority === 'critical' || context.priority === 'high') {
      const originalLimit = this.config.roleLimits[this.getAppliedRole(context.userRoles) || 'guest']?.maxRequests || 100;
      const adjustedLimit = Math.floor(originalLimit * (1 + this.config.adjustmentFactor));

      return {
        originalLimit,
        adjustedLimit,
        reason: `Priority ${context.priority} request adjustment`,
      };
    }

    return undefined;
  }

  private handleEmergencyMode(context: RateLimitContext): RoleBasedRateLimitResult {
    this.stats.emergencyBlocks++;

    return {
      allowed: false,
      remaining: 0,
      resetTime: this.emergencyModeEnd?.getTime() || (Date.now() + this.config.emergencyDuration),
      retryAfter: this.emergencyModeEnd ? 
        Math.max(0, this.emergencyModeEnd.getTime() - Date.now()) : 
        this.config.emergencyDuration,
      error: 'System in emergency mode - rate limiting enforced',
      appliedConfig: this.config.emergencyLimits,
      securityFlags: {
        suspiciousActivity: false,
        abuseDetected: false,
        emergencyMode: true,
      },
    };
  }

  private createAbuseResult(context: RateLimitContext, penaltyLevel: number): RoleBasedRateLimitResult {
    const penalizedLimit = Math.max(1, Math.floor(
      this.config.defaultLimits.maxRequests * Math.pow(this.config.abusePenaltyMultiplier, penaltyLevel)
    ));

    return {
      allowed: false,
      remaining: 0,
      resetTime: Date.now() + this.config.abuseTimeWindow,
      retryAfter: this.config.abuseTimeWindow,
      error: `Rate limit violation - penalty level ${penaltyLevel}`,
      appliedConfig: {
        ...this.config.defaultLimits,
        maxRequests: penalizedLimit,
      },
      securityFlags: {
        suspiciousActivity: true,
        abuseDetected: true,
        emergencyMode: false,
      },
    };
  }

  private cleanupAbuseRecords(): void {
    const now = Date.now();
    const cutoff = now - (this.config.abuseTimeWindow * 2); // Keep records for 2x the abuse window

    for (const [userId, record] of this.abuseRecords) {
      if (record.lastViolation.getTime() < cutoff) {
        this.abuseRecords.delete(userId);
      }
    }
  }

  private startMonitoring(): void {
    this.monitoringTimer = setInterval(() => {
      this.performMonitoringChecks();
    }, this.config.monitoringInterval);
  }

  private performMonitoringChecks(): void {
    const stats = this.getStats();

    // Calculate rate percentages
    const totalRate = this.stats.totalRequests / (this.config.monitoringInterval / 1000); // per second
    const blockRate = this.stats.blockedRequests / this.stats.totalRequests * 100;

    // Emit monitoring events
    this.emit('monitoring', {
      stats,
      rates: {
        total: totalRate,
        blocked: blockRate,
      },
    });

    // Check alert thresholds
    if (blockRate > this.config.alertThresholds.criticalPercent) {
      this.emit('criticalAlert', { blockRate, stats });
    } else if (blockRate > this.config.alertThresholds.warningPercent) {
      this.emit('warningAlert', { blockRate, stats });
    }

    // Reset interval stats
    this.stats.totalRequests = 0;
    this.stats.blockedRequests = 0;
  }

  /**
   * Cleanup resources
   */
  dispose(): void {
    if (this.monitoringTimer) {
      clearInterval(this.monitoringTimer);
    }

    for (const rateLimiter of this.rateLimiters.values()) {
      rateLimiter.dispose();
    }

    this.rateLimiters.clear();
    this.abuseRecords.clear();
  }
}

/**
 * Global role-based rate limiter instance
 */
export const roleBasedRateLimiter = new RoleBasedRateLimiter();

/**
 * Middleware function for role-based rate limiting
 */
export function createRoleBasedRateLimitMiddleware(
  resource: string,
  action: string = 'access',
  priority: RateLimitContext['priority'] = 'medium'
) {
  return async (request: {
    user?: { id: string; roles: string[] };
    headers?: Record<string, string>;
    ip?: string;
    metadata?: Record<string, any>;
  }) => {
    if (!request.user) {
      throw new Error('User information required for rate limiting');
    }

    const context: RateLimitContext = {
      userId: request.user.id,
      userRoles: request.user.roles,
      resource,
      action,
      priority,
      clientInfo: {
        ipAddress: request.ip || 'unknown',
        userAgent: request.headers?.['user-agent'] || 'unknown',
      },
      metadata: request.metadata,
    };

    const result = await roleBasedRateLimiter.checkRateLimit(context);

    if (!result.allowed) {
      const error = new Error(result.error || 'Rate limit exceeded');
      (error as any).rateLimitInfo = result;
      throw error;
    }

    return request;
  };
}

/**
 * @fileoverview Enterprise secrets management with vault integration and encryption
 * @lastmodified 2025-08-27T16:15:00Z
 *
 * Features: Encrypted secret storage, automatic rotation, environment-based config, audit logging
 * Main APIs: setSecret(), getSecret(), rotateSecret(), rotateAllSecrets()
 * Constraints: Requires VAULT_TOKEN, VAULT_URL, or local encrypted storage
 * Patterns: Vault pattern, encryption at rest, audit trail, automatic expiration
 */

import * as crypto from 'crypto';
import * as fs from 'fs/promises';
import * as path from 'path';
import { logger } from '../utils/logger';
import { cryptoService } from './cryptographic.service';

export interface SecretMetadata {
  id: string;
  name: string;
  version: number;
  createdAt: Date;
  updatedAt: Date;
  expiresAt?: Date;
  rotationPolicy?: RotationPolicy;
  tags: string[];
  encrypted: boolean;
}

export interface RotationPolicy {
  enabled: boolean;
  intervalDays: number;
  reminderDays: number;
  autoRotate: boolean;
}

export interface VaultConfig {
  provider: 'local' | 'hashicorp' | 'aws' | 'azure';
  encryptionEnabled: boolean;
  vaultUrl?: string;
  token?: string;
  storePath?: string;
  defaultRotationPolicy?: RotationPolicy;
}

export interface SecretEntry {
  metadata: SecretMetadata;
  value: string;
  checksum: string;
}

export interface AuditEvent {
  timestamp: Date;
  operation: 'CREATE' | 'READ' | 'UPDATE' | 'DELETE' | 'ROTATE';
  secretId: string;
  userId?: string;
  source: string;
  success: boolean;
  error?: string;
}

/**
 * Enterprise-grade secrets management with vault integration
 */
export class SecretsVaultService {
  private readonly config: Required<VaultConfig>;

  private readonly secrets = new Map<string, SecretEntry>();

  private readonly auditLog: AuditEvent[] = [];

  private readonly storePath: string;

  private isInitialized = false;

  constructor(config: Partial<VaultConfig> = {}) {
    this.config = {
      provider: config.provider || 'local',
      encryptionEnabled: config.encryptionEnabled ?? true,
      vaultUrl: config.vaultUrl || process.env.VAULT_URL || '',
      token: config.token || process.env.VAULT_TOKEN || '',
      storePath: config.storePath || path.join(process.cwd(), '.vault'),
      defaultRotationPolicy: config.defaultRotationPolicy || {
        enabled: true,
        intervalDays: 90,
        reminderDays: 7,
        autoRotate: false,
      },
    };

    this.storePath = this.config.storePath;
    this.validateConfiguration();
  }

  /**
   * Initialize the secrets vault
   */
  async initialize(): Promise<void> {
    try {
      await this.ensureStorageDirectory();
      await this.loadSecretsFromStorage();
      this.setupRotationScheduler();
      this.isInitialized = true;
      logger.info(`Secrets vault initialized with ${this.config.provider} provider`);
    } catch (error) {
      logger.error('Failed to initialize secrets vault', error as Error);
      throw new Error('Secrets vault initialization failed');
    }
  }

  /**
   * Store a secret with metadata and encryption
   */
  async setSecret(
    name: string,
    value: string,
    options: {
      expiresAt?: Date;
      rotationPolicy?: Partial<RotationPolicy>;
      tags?: string[];
      userId?: string;
    } = {}
  ): Promise<string> {
    this.ensureInitialized();

    try {
      const secretId = this.generateSecretId(name);
      const now = new Date();

      // Encrypt the secret value if encryption is enabled
      let encryptedValue = value;
      let encrypted = false;

      if (this.config.encryptionEnabled) {
        const encryptedPayload = cryptoService.encryptAES256GCM(
          value,
          Buffer.from(`secret-${secretId}`)
        );
        encryptedValue = JSON.stringify(encryptedPayload);
        encrypted = true;
      }

      const metadata: SecretMetadata = {
        id: secretId,
        name,
        version: this.getNextVersion(name),
        createdAt: now,
        updatedAt: now,
        expiresAt: options.expiresAt,
        rotationPolicy: {
          ...this.config.defaultRotationPolicy,
          ...options.rotationPolicy,
        },
        tags: options.tags || [],
        encrypted,
      };

      const checksum = cryptoService.generateHMAC(encryptedValue, `secret-${secretId}`);

      const secretEntry: SecretEntry = {
        metadata,
        value: encryptedValue,
        checksum,
      };

      this.secrets.set(secretId, secretEntry);
      await this.persistSecret(secretEntry);

      this.logAuditEvent('CREATE', secretId, options.userId, true);
      logger.info(`Secret stored: ${name} (${secretId})`);

      return secretId;
    } catch (error) {
      this.logAuditEvent('CREATE', name, options.userId, false, (error as Error).message);
      logger.error('Failed to store secret', error as Error);
      throw new Error('Failed to store secret');
    }
  }

  /**
   * Retrieve and decrypt a secret
   */
  async getSecret(secretId: string, userId?: string): Promise<string | null> {
    this.ensureInitialized();

    try {
      const secretEntry = this.secrets.get(secretId);
      if (!secretEntry) {
        this.logAuditEvent('READ', secretId, userId, false, 'Secret not found');
        return null;
      }

      // Check if secret has expired
      if (secretEntry.metadata.expiresAt && secretEntry.metadata.expiresAt < new Date()) {
        this.logAuditEvent('READ', secretId, userId, false, 'Secret expired');
        logger.warn(`Attempted to access expired secret: ${secretId}`);
        return null;
      }

      // Verify checksum
      const expectedChecksum = cryptoService.generateHMAC(
        secretEntry.value,
        `secret-${secretId}`
      );
      if (expectedChecksum !== secretEntry.checksum) {
        this.logAuditEvent('read', secretId, userId, false, 'Checksum validation failed');
        logger.error(`Checksum validation failed for secret: ${secretId}`);
        throw new Error('Secret integrity check failed');
      }

      let decryptedValue = secretEntry.value;

      if (secretEntry.metadata.encrypted) {
        try {
          const encryptedPayload = JSON.parse(secretEntry.value);
          const decryptedBuffer = cryptoService.decryptAES256GCM(
            encryptedPayload,
            Buffer.from(`secret-${secretId}`)
          );
          decryptedValue = decryptedBuffer.toString('utf8');
        } catch (decryptError) {
          this.logAuditEvent('read', secretId, userId, false, 'Decryption failed');
          logger.error(`Failed to decrypt secret: ${secretId}`, decryptError as Error);
          throw new Error('Failed to decrypt secret');
        }
      }

      this.logAuditEvent('READ', secretId, userId, true);
      return decryptedValue;
    } catch (error) {
      this.logAuditEvent('READ', secretId, userId, false, (error as Error).message);
      logger.error('Failed to retrieve secret', error as Error);
      throw error;
    }
  }

  /**
   * Rotate a secret with a new value
   */
  async rotateSecret(
    secretId: string,
    newValue: string,
    userId?: string
  ): Promise<boolean> {
    this.ensureInitialized();

    try {
      const secretEntry = this.secrets.get(secretId);
      if (!secretEntry) {
        this.logAuditEvent('ROTATE', secretId, userId, false, 'Secret not found');
        return false;
      }

      const now = new Date();
      const updatedMetadata = {
        ...secretEntry.metadata,
        version: secretEntry.metadata.version + 1,
        updatedAt: now,
      };

      // Encrypt new value
      let encryptedValue = newValue;
      if (this.config.encryptionEnabled) {
        const encryptedPayload = cryptoService.encryptAES256GCM(
          newValue,
          Buffer.from(`secret-${secretId}`)
        );
        encryptedValue = JSON.stringify(encryptedPayload);
      }

      const checksum = cryptoService.generateHMAC(encryptedValue, `secret-${secretId}`);

      const updatedEntry: SecretEntry = {
        metadata: updatedMetadata,
        value: encryptedValue,
        checksum,
      };

      this.secrets.set(secretId, updatedEntry);
      await this.persistSecret(updatedEntry);

      this.logAuditEvent('ROTATE', secretId, userId, true);
      logger.info(`Secret rotated: ${secretEntry.metadata.name} (${secretId})`);

      return true;
    } catch (error) {
      this.logAuditEvent('ROTATE', secretId, userId, false, (error as Error).message);
      logger.error('Failed to rotate secret', error as Error);
      return false;
    }
  }

  /**
   * Delete a secret
   */
  async deleteSecret(secretId: string, userId?: string): Promise<boolean> {
    this.ensureInitialized();

    try {
      const secretEntry = this.secrets.get(secretId);
      if (!secretEntry) {
        this.logAuditEvent('DELETE', secretId, userId, false, 'Secret not found');
        return false;
      }

      this.secrets.delete(secretId);
      await this.removeSecretFromStorage(secretId);

      this.logAuditEvent('DELETE', secretId, userId, true);
      logger.info(`Secret deleted: ${secretEntry.metadata.name} (${secretId})`);

      return true;
    } catch (error) {
      this.logAuditEvent('DELETE', secretId, userId, false, (error as Error).message);
      logger.error('Failed to delete secret', error as Error);
      return false;
    }
  }

  /**
   * List all secrets (metadata only)
   */
  listSecrets(tags?: string[]): SecretMetadata[] {
    this.ensureInitialized();

    const secrets = Array.from(this.secrets.values()).map(entry => entry.metadata);

    if (tags && tags.length > 0) {
      return secrets.filter(metadata =>
        tags.some(tag => metadata.tags.includes(tag))
      );
    }

    return secrets;
  }

  /**
   * Get secrets that need rotation
   */
  getSecretsNeedingRotation(): SecretMetadata[] {
    this.ensureInitialized();

    const now = new Date();
    return Array.from(this.secrets.values())
      .map(entry => entry.metadata)
      .filter(metadata => {
        if (!metadata.rotationPolicy?.enabled) return false;

        const daysSinceUpdate = Math.floor(
          (now.getTime() - metadata.updatedAt.getTime()) / (1000 * 60 * 60 * 24)
        );

        return daysSinceUpdate >= metadata.rotationPolicy.intervalDays;
      });
  }

  /**
   * Auto-rotate secrets based on policies
   */
  async rotateAllSecrets(userId?: string): Promise<{ rotated: number; failed: number }> {
    this.ensureInitialized();

    const secretsToRotate = this.getSecretsNeedingRotation().filter(
      metadata => metadata.rotationPolicy?.autoRotate
    );

    let rotated = 0;
    let failed = 0;

    for (const metadata of secretsToRotate) {
      try {
        // Generate new secure value
        const newValue = cryptoService.generateSecureKey(32);
        const success = await this.rotateSecret(metadata.id, newValue, userId);

        if (success) {
          rotated++;
        } else {
          failed++;
        }
      } catch (error) {
        logger.error(`Auto-rotation failed for ${metadata.id}`, error as Error);
        failed++;
      }
    }

    logger.info(`Auto-rotation completed: ${rotated} rotated, ${failed} failed`);
    return { rotated, failed };
  }

  /**
   * Get audit log entries
   */
  getAuditLog(
    options: {
      secretId?: string;
      userId?: string;
      operation?: AuditEvent['operation'];
      limit?: number;
    } = {}
  ): AuditEvent[] {
    let filtered = this.auditLog;

    if (options.secretId) {
      filtered = filtered.filter(event => event.secretId === options.secretId);
    }

    if (options.userId) {
      filtered = filtered.filter(event => event.userId === options.userId);
    }

    if (options.operation) {
      filtered = filtered.filter(event => event.operation === options.operation);
    }

    if (options.limit) {
      filtered = filtered.slice(-options.limit);
    }

    return filtered.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * Get vault statistics
   */
  getVaultStats() {
    this.ensureInitialized();

    const now = new Date();
    const secrets = Array.from(this.secrets.values());

    return {
      totalSecrets: secrets.length,
      encryptedSecrets: secrets.filter(s => s.metadata.encrypted).length,
      expiredSecrets: secrets.filter(s => 
        s.metadata.expiresAt && s.metadata.expiresAt < now
      ).length,
      secretsNeedingRotation: this.getSecretsNeedingRotation().length,
      totalAuditEvents: this.auditLog.length,
      provider: this.config.provider,
      encryptionEnabled: this.config.encryptionEnabled,
    };
  }

  /**
   * Validate configuration
   */
  private validateConfiguration(): void {
    if (this.config.provider === 'hashicorp' && !this.config.vaultUrl) {
      throw new Error('Vault URL required for HashiCorp Vault provider');
    }

    if (this.config.provider !== 'local' && !this.config.token) {
      logger.warn('No vault token configured - some operations may fail');
    }
  }

  /**
   * Ensure vault is initialized
   */
  private ensureInitialized(): void {
    if (!this.isInitialized) {
      throw new Error('Secrets vault not initialized - call initialize() first');
    }
  }

  /**
   * Generate unique secret ID
   */
  private generateSecretId(name: string): string {
    const timestamp = Date.now().toString(36);
    const random = crypto.randomBytes(8).toString('hex');
    const nameHash = crypto.createHash('sha256').update(name).digest('hex').slice(0, 8);
    return `${nameHash}-${timestamp}-${random}`;
  }

  /**
   * Get next version number for a secret name
   */
  private getNextVersion(name: string): number {
    const existingVersions = Array.from(this.secrets.values())
      .filter(entry => entry.metadata.name === name)
      .map(entry => entry.metadata.version);

    return existingVersions.length > 0 ? Math.max(...existingVersions) + 1 : 1;
  }

  /**
   * Log audit event
   */
  private logAuditEvent(
    operation: AuditEvent['operation'],
    secretId: string,
    userId?: string,
    success: boolean = true,
    error?: string
  ): void {
    const event: AuditEvent = {
      timestamp: new Date(),
      operation,
      secretId,
      userId,
      source: 'SecretsVaultService',
      success,
      error,
    };

    this.auditLog.push(event);

    // Keep audit log size manageable
    if (this.auditLog.length > 10000) {
      this.auditLog.splice(0, 1000);
    }
  }

  /**
   * Ensure storage directory exists
   */
  private async ensureStorageDirectory(): Promise<void> {
    try {
      await fs.mkdir(this.storePath, { recursive: true, mode: 0o700 });
    } catch (error) {
      logger.error('Failed to create vault storage directory', error as Error);
      throw error;
    }
  }

  /**
   * Load secrets from storage
   */
  private async loadSecretsFromStorage(): Promise<void> {
    try {
      const files = await fs.readdir(this.storePath);

      for (const file of files) {
        if (file.endsWith('.secret')) {
          const filePath = path.join(this.storePath, file);
          const content = await fs.readFile(filePath, 'utf8');
          const secretEntry: SecretEntry = JSON.parse(content);
          this.secrets.set(secretEntry.metadata.id, secretEntry);
        }
      }

      logger.info(`Loaded ${this.secrets.size} secrets from storage`);
    } catch (error) {
      logger.warn('Failed to load secrets from storage', error as Error);
      // Continue with empty vault
    }
  }

  /**
   * Persist secret to storage
   */
  private async persistSecret(secretEntry: SecretEntry): Promise<void> {
    const filename = `${secretEntry.metadata.id}.secret`;
    const filePath = path.join(this.storePath, filename);

    try {
      await fs.writeFile(
        filePath,
        JSON.stringify(secretEntry, null, 2),
        { mode: 0o600 }
      );
    } catch (error) {
      logger.error('Failed to persist secret to storage', error as Error);
      throw error;
    }
  }

  /**
   * Remove secret from storage
   */
  private async removeSecretFromStorage(secretId: string): Promise<void> {
    const filename = `${secretId}.secret`;
    const filePath = path.join(this.storePath, filename);

    try {
      await fs.unlink(filePath);
    } catch (error) {
      logger.warn('Failed to remove secret from storage', error as Error);
      // Continue - secret already removed from memory
    }
  }

  /**
   * Setup automatic rotation scheduler
   */
  private setupRotationScheduler(): void {
    // Run rotation check every hour
    setInterval(async () => {
      try {
        const stats = await this.rotateAllSecrets('system');
        if (stats.rotated > 0 || stats.failed > 0) {
          logger.info(`Scheduled rotation: ${stats.rotated} rotated, ${stats.failed} failed`);
        }
      } catch (error) {
        logger.error('Scheduled rotation failed', error as Error);
      }
    }, 60 * 60 * 1000); // 1 hour
  }
}

/**
 * Global secrets vault instance
 */
export const secretsVault = new SecretsVaultService();

/**
 * @fileoverview Comprehensive security audit utilities for ongoing validation monitoring
 * @lastmodified 2025-08-27T17:00:00Z
 *
 * Features: Security auditing, threat monitoring, compliance checking, vulnerability scanning
 * Main APIs: SecurityAuditor.audit(), generateSecurityReport(), monitorThreats()
 * Constraints: All security events must be logged and monitored
 * Patterns: Auditing pattern, security monitoring, compliance validation
 */

import { z } from 'zod';
import { 
  EnhancedValidator, 
  SecurityValidationResult,
  ValidationContext,
  customValidators 
} from '../validation/schemas';
import { apiValidationMiddleware } from '../middleware/api-validation.middleware';
import { fileUploadValidationMiddleware } from '../middleware/file-upload-validation.middleware';
import { templateSanitizer } from '../core/template-sanitizer';
import { secureDatabaseAdapter } from '../database/secure-database-adapter';
import { logger } from '../utils/logger';

/**
 * Security audit configuration
 */
export interface SecurityAuditConfig {
  enableRealTimeMonitoring: boolean;
  auditInterval: number;
  maxAuditLogSize: number;
  threatThresholds: {
    low: number;
    medium: number;
    high: number;
    critical: number;
  };
  complianceStandards: string[];
  enableAutomatedResponse: boolean;
  notificationWebhooks: string[];
}

/**
 * Security audit result
 */
export interface SecurityAuditResult {
  timestamp: Date;
  overallScore: number;
  maxScore: number;
  threatLevel: 'low' | 'medium' | 'high' | 'critical';
  categories: {
    inputValidation: SecurityCategoryResult;
    dataProtection: SecurityCategoryResult;
    accessControl: SecurityCategoryResult;
    systemIntegrity: SecurityCategoryResult;
    auditLogging: SecurityCategoryResult;
  };
  recommendations: SecurityRecommendation[];
  compliance: ComplianceResult[];
  summary: string;
}

/**
 * Security category result
 */
export interface SecurityCategoryResult {
  score: number;
  maxScore: number;
  passed: boolean;
  issues: SecurityIssue[];
  checks: SecurityCheck[];
}

/**
 * Security issue
 */
export interface SecurityIssue {
  id: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  title: string;
  description: string;
  category: string;
  recommendation: string;
  cweId?: string;
  cvssScore?: number;
  affectedComponents: string[];
  remediationEffort: 'low' | 'medium' | 'high';
}

/**
 * Security check
 */
export interface SecurityCheck {
