  /**
   * Calculate overall threat level
   */
  private calculateThreatLevel(score: number, maxScore: number): 'low' | 'medium' | 'high' | 'critical' {
    const percentage = (score / maxScore) * 100;

    if (percentage >= 90) return 'low';
    if (percentage >= 80) return 'medium';
    if (percentage >= 70) return 'high';
    return 'critical';
  }

  /**
   * Generate security recommendations
   */
  private generateRecommendations(auditResult: SecurityAuditResult): SecurityRecommendation[] {
    const recommendations: SecurityRecommendation[] = [];

    // Analyze each category for recommendations
    Object.entries(auditResult.categories).forEach(([category, result]) => {
      if (result.score < result.maxScore * 0.9) {
        result.issues.forEach(issue => {
          recommendations.push({
            priority: issue.severity,
            title: `Address ${issue.title}`,
            description: issue.description,
            category: issue.category,
            implementation: issue.recommendation,
            effort: issue.remediationEffort,
            impact: issue.severity === 'critical' ? 'high' : 'medium',
          });
        });
      }
    });

    return recommendations.sort((a, b) => {
      const priorityOrder = { critical: 4, high: 3, medium: 2, low: 1 };
      return priorityOrder[b.priority] - priorityOrder[a.priority];
    });
  }

  /**
   * Check compliance against standards
   */
  private async checkCompliance(auditResult: SecurityAuditResult): Promise<ComplianceResult[]> {
    const compliance: ComplianceResult[] = [];

    // OWASP ASVS compliance check
    if (this.config.complianceStandards.includes('OWASP-ASVS-4.0')) {
      compliance.push(await this.checkOwaspAsvs(auditResult));
    }

    // NIST CSF compliance check
    if (this.config.complianceStandards.includes('NIST-CSF-1.1')) {
      compliance.push(await this.checkNistCsf(auditResult));
    }

    return compliance;
  }

  /**
   * Check OWASP ASVS compliance
   */
  private async checkOwaspAsvs(auditResult: SecurityAuditResult): Promise<ComplianceResult> {
    const requirements: ComplianceRequirement[] = [
      {
        id: 'V5.1.1',
        title: 'Input validation performed on trusted service layer',
        status: auditResult.categories.inputValidation.passed ? 'compliant' : 'non-compliant',
        evidence: 'API validation middleware with comprehensive input validation',
        gaps: auditResult.categories.inputValidation.passed ? [] : ['Input validation needs enhancement'],
      },
      {
        id: 'V7.1.1',
        title: 'Application does not log credentials or payment details',
        status: 'compliant',
        evidence: 'No credential logging found in audit',
        gaps: [],
      },
      {
        id: 'V14.2.1',
        title: 'All cryptographic modules fail securely and errors handled properly',
        status: auditResult.categories.dataProtection.passed ? 'compliant' : 'partial',
        evidence: 'Encryption service with error handling',
        gaps: auditResult.categories.dataProtection.passed ? [] : ['Cryptographic error handling needs review'],
      },
    ];

    const compliantCount = requirements.filter(r => r.status === 'compliant').length;
    const score = (compliantCount / requirements.length) * 100;

    return {
      standard: 'OWASP ASVS',
      version: '4.0',
      compliant: score >= 80,
      score: Math.round(score),
      maxScore: 100,
      requirements,
    };
  }

