/**
 * Refactored auto-optimization coordinator
 *
 * This replaces the monolithic AutoOptimizeManager with a lean coordinator
 * that orchestrates focused services, following single responsibility principle.
 *
 * Services coordinated:
 * - FileWatcherService: Monitors file changes
 * - JobProcessorService: Manages job queue and processing
 * - NotificationService: Handles desktop notifications
 * - UnifiedOptimizationService: Performs optimizations
 */
export class AutoOptimizeCoordinator extends EventEmitter {
  private fileWatcher: FileWatcherService;

  private jobProcessor: JobProcessorService;

  private notificationService: NotificationService;

  private optimizationService: UnifiedOptimizationService;

  private options: AutoOptimizeCoordinatorOptions;

  private isEnabled: boolean = false;

  constructor(
    options: AutoOptimizeCoordinatorOptions,
    services?: {
      fileWatcher?: FileWatcherService;
      jobProcessor?: JobProcessorService;
      notificationService?: NotificationService;
      optimizationService?: UnifiedOptimizationService;
    }
  ) {
    super();

    this.options = options;

    // Initialize services with dependency injection
    this.fileWatcher =
      services?.fileWatcher ||
      new FileWatcherService({
        watchPatterns: options.watchPatterns,
        debounceMs: options.debounceMs,
      });

    this.jobProcessor =
      services?.jobProcessor ||
      new JobProcessorService({
        maxConcurrentJobs: options.maxConcurrentJobs,
        maxRetryAttempts: options.maxRetryAttempts,
        retryBackoffMs: 5000,
        jobTimeoutMs: options.jobTimeoutMs,
      });

    this.notificationService =
      services?.notificationService ||
      new NotificationService({
        enabled: options.notifications,
      });

    this.optimizationService =
      services?.optimizationService || this.createOptimizationService();

    this.setupEventHandlers();
  }

  /**
   * Initialize and start auto-optimization
   */
  async start(): Promise<void> {
    if (this.isEnabled) {
      logger.warn('Auto-optimize coordinator is already running');
      return;
    }

    try {
      logger.info('Initializing auto-optimize coordinator...');

      // Check PromptWizard availability
      const availability = await checkPromptWizardAvailability();
      if (!availability.available) {
        logger.warn('PromptWizard not available for auto-optimization', {
          issues: availability.issues,
          recommendations: availability.recommendations,
        });
      }

      // Start services
      await this.fileWatcher.start();
      await this.jobProcessor.start();

      this.isEnabled = true;

      logger.info('Auto-optimization enabled', {
        watchPatterns: this.options.watchPatterns,
        targetModels: this.options.targetModels,
        maxConcurrentJobs: this.options.maxConcurrentJobs,
      });

      // Send start notification
      await this.notificationService.sendAutoOptimizationEnabled();

      this.emit('started', this.getStatus());
    } catch (error) {
      logger.error('Failed to start auto-optimization', error as Error);
      throw error;
    }
  }

  /**
   * Stop auto-optimization
   */
  async stop(graceful: boolean = true): Promise<void> {
    if (!this.isEnabled) {
      logger.warn('Auto-optimize coordinator is not running');
      return;
    }

    try {
      logger.info('Stopping auto-optimize coordinator...');

      // Stop services
      await this.fileWatcher.stop();
      await this.jobProcessor.stop(graceful);

      this.isEnabled = false;

      logger.info('Auto-optimization disabled');

      // Send stop notification
      await this.notificationService.sendAutoOptimizationDisabled();

      this.emit('stopped', this.getStatus());
    } catch (error) {
      logger.error('Failed to stop auto-optimization', error as Error);
      throw error;
    }
  }

  /**
   * Get current status
   */
  getStatus(): AutoOptimizeStatus {
    const jobStats = this.jobProcessor.getStats();

    return {
      enabled: this.isEnabled,
      activeJobs: jobStats.activeJobs,
      queuedJobs: jobStats.queuedJobs,
      totalProcessed: jobStats.totalProcessed,
      successRate:
        jobStats.totalProcessed > 0
          ? (jobStats.successCount / jobStats.totalProcessed) * 100
          : 0,
      options: { ...this.options },
    };
  }

  /**
   * Update configuration options
   */
  updateOptions(newOptions: Partial<AutoOptimizeCoordinatorOptions>): void {
    this.options = { ...this.options, ...newOptions };

    // Update service configurations
    if (newOptions.watchPatterns || newOptions.debounceMs) {
      // File watcher options changed - restart may be needed
      if (this.isEnabled) {
        logger.info(
          'File watcher configuration changed - restart may be required for full effect'
        );
      }
    }

    if (
      newOptions.maxConcurrentJobs ||
      newOptions.maxRetryAttempts ||
      newOptions.jobTimeoutMs
    ) {
      this.jobProcessor.updateOptions({
        maxConcurrentJobs: this.options.maxConcurrentJobs,
        maxRetryAttempts: this.options.maxRetryAttempts,
        jobTimeoutMs: this.options.jobTimeoutMs,
      });
    }

    if (newOptions.notifications !== undefined) {
      if (newOptions.notifications) {
        this.notificationService.enable();
      } else {
        this.notificationService.disable();
      }
    }

    logger.info('Auto-optimize options updated', newOptions);
    this.emit('options-updated', this.options);
  }

  /**
   * Manually trigger optimization for a specific file
   */
  async optimizeFile(
    templatePath: string,
    priority: number = 10
  ): Promise<OptimizationJob> {
    const job = this.jobProcessor.addJob(templatePath, priority);
    logger.info('Manual optimization triggered', {
      jobId: job.id,
      templatePath,
    });
    return job;
  }

  /**
   * Get processing statistics
   */
  getStatistics(): {
    processor: ReturnType<JobProcessorService['getStats']>;
    notifications: ReturnType<NotificationService['getStats']>;
    optimization: ReturnType<UnifiedOptimizationService['getStatistics']>;
  } {
    return {
      processor: this.jobProcessor.getStats(),
      notifications: this.notificationService.getStats(),
      optimization: this.optimizationService.getStatistics(),
    };
  }

  // Private methods

  private createOptimizationService(): UnifiedOptimizationService {
    const promptWizardConfig = getPromptWizardConfig();

    return new UnifiedOptimizationService({
      promptWizard: {
        enabled: true,
        serviceUrl: promptWizardConfig.connection.serviceUrl,
        timeout: promptWizardConfig.connection.timeout,
        retries: promptWizardConfig.connection.retries,
      },
      cache: {
        maxSize: 1000,
        ttlMs: 86400000, // 24 hours
        useRedis: false,
      },
      queue: {
        maxConcurrent: this.options.maxConcurrentJobs,
        retryAttempts: this.options.maxRetryAttempts,
        backoffMs: 5000,
      },
      defaults: {
        targetModel: this.options.targetModels[0] || 'gpt-4',
        mutateRefineIterations:
          promptWizardConfig.optimization.mutateRefineIterations,
        fewShotCount: promptWizardConfig.optimization.fewShotCount,
        generateReasoning: promptWizardConfig.optimization.generateReasoning,
      },
    });
  }

  private setupEventHandlers(): void {
    // File watcher events
    this.fileWatcher.on('file-change', (event: FileChangeEvent) => {
      this.handleFileChange(event);
    });

    this.fileWatcher.on('error', (error: Error) => {
      logger.error('File watcher error', error);
      this.emit('file-watcher-error', error);
    });

    // Job processor events
    this.jobProcessor.on('process-job', (job: OptimizationJob) => {
      this.handleJobProcessing(job);
    });

    this.jobProcessor.on('job-completed', (job: OptimizationJob) => {
      this.handleJobCompleted(job);
    });

    this.jobProcessor.on('job-failed', (job: OptimizationJob) => {
      this.handleJobFailed(job);
    });

    this.jobProcessor.on('job-retry', (job: OptimizationJob) => {
      logger.info('Job scheduled for retry', {
        jobId: job.id,
        templatePath: job.templatePath,
        retryCount: job.retryCount,
      });
    });
  }

  private handleFileChange(event: FileChangeEvent): void {
    logger.debug('File change detected', {
      filePath: event.filePath,
      eventType: event.eventType,
    });

    if (event.eventType === 'unlink') {
      // Don't optimize deleted files
      return;
    }

    // Add optimization job for changed file
    const job = this.jobProcessor.addJob(event.absolutePath, 5); // Medium priority

    this.emit('file-queued', {
      filePath: event.absolutePath,
      jobId: job.id,
      eventType: event.eventType,
    });
  }

  private async handleJobProcessing(job: OptimizationJob): Promise<void> {
    try {
      // Send start notification
      await this.notificationService.sendOptimizationStarted(job.templatePath);

      // Perform optimization
      const result = await this.optimizationService.optimize(job.templatePath, {
        targetModel: this.options.targetModels[0] as any,
        generateReasoning: true,
      });

      // Job completed successfully
      job.result = result;
      this.jobProcessor.emit('job-process-completed', result);
    } catch (error) {
      // Job failed
      this.jobProcessor.emit('job-process-failed', error);
    }
  }

  private async handleJobCompleted(job: OptimizationJob): Promise<void> {
    const result = job.result as any;

    // Extract metrics if available
    let tokenReduction: number | undefined;
    if (result?.result?.metrics?.tokenReduction) {
      tokenReduction = result.result.metrics.tokenReduction;
    }

    // Send completion notification
    await this.notificationService.sendOptimizationCompleted(
      job.templatePath,
      tokenReduction
    );

    this.emit('optimization-completed', {
      jobId: job.id,
      templatePath: job.templatePath,
      result: job.result,
    });

    logger.info('Template optimization completed', {
      jobId: job.id,
      templatePath: job.templatePath,
      tokenReduction,
    });
  }

  private async handleJobFailed(job: OptimizationJob): Promise<void> {
    // Send failure notification
    await this.notificationService.sendOptimizationFailed(
      job.templatePath,
      job.error || 'Unknown error'
    );

    this.emit('optimization-failed', {
      jobId: job.id,
      templatePath: job.templatePath,
      error: job.error,
    });

    logger.error('Template optimization failed', new Error(job.error), {
      jobId: job.id,
      templatePath: job.templatePath,
    });
  }

  /**
   * Get default coordinator options
   */
  static getDefaultOptions(): AutoOptimizeCoordinatorOptions {
    const config = ConfigManager.getInstance();

    return {
      enabled: config.get<boolean>('promptwizard.autoOptimize', false),
      watchPatterns: [
        '**/*.prompt',
        '**/*.md',
        'templates/**/*',
        'prompts/**/*',
      ],
      debounceMs: 2000,
      targetModels: ['gpt-4', 'claude-3-sonnet'],
      notifications: true,
      maxConcurrentJobs: 3,
      saveOptimized: true,
      minConfidence: 0.7,
      maxRetryAttempts: 2,
      jobTimeoutMs: 30000, // 30 seconds
    };
  }
}

/**
 * Global coordinator instance
 */
export const autoOptimizeCoordinator = new AutoOptimizeCoordinator(
  AutoOptimizeCoordinator.getDefaultOptions()
);

/**
 * @fileoverview Job processor service for auto-optimization
 * @lastmodified 2025-08-27T04:55:00Z
 *
 * Features: Job queue management, concurrent processing, job status tracking
 * Main APIs: addJob(), start(), stop(), getStats()
 * Constraints: Single responsibility - only handles job processing
 * Patterns: Queue pattern, worker pool, event emitter
 */

import { EventEmitter } from 'events';
import { logger } from '../../utils/logger';

export interface OptimizationJob {
  id: string;
  templatePath: string;
  status: 'pending' | 'processing' | 'completed' | 'failed';
  startTime: Date;
  completedTime?: Date;
  result?: unknown;
  error?: string;
  priority: number;
  retryCount: number;
}

export interface JobProcessorOptions {
  /** Maximum concurrent jobs */
  maxConcurrentJobs: number;
  /** Maximum retry attempts for failed jobs */
  maxRetryAttempts: number;
  /** Backoff delay between retries (ms) */
  retryBackoffMs: number;
  /** Job timeout (ms) */
  jobTimeoutMs: number;
}

export interface JobProcessorStats {
  totalProcessed: number;
  successCount: number;
  failureCount: number;
  averageProcessingTime: number;
  activeJobs: number;
  queuedJobs: number;
  retryCount: number;
}

/**
 * Job processor service focused solely on managing optimization job queue
 *
 * This service handles the job processing logic that was previously
 * embedded in AutoOptimizeManager, following single responsibility principle.
 */
export class JobProcessorService extends EventEmitter {
  private jobQueue: OptimizationJob[] = [];

  private activeJobs: Map<string, OptimizationJob> = new Map();

  private options: JobProcessorOptions;

  private isProcessing: boolean = false;

  private processorInterval: NodeJS.Timeout | null = null;

  private stats: JobProcessorStats = {
    totalProcessed: 0,
    successCount: 0,
    failureCount: 0,
    averageProcessingTime: 0,
    activeJobs: 0,
    queuedJobs: 0,
    retryCount: 0,
  };

  constructor(options: JobProcessorOptions) {
    super();
    this.options = options;
  }

  /**
   * Add a job to the processing queue
   */
  addJob(templatePath: string, priority: number = 0): OptimizationJob {
    const job: OptimizationJob = {
      id: this.generateJobId(),
      templatePath,
      status: 'pending',
      startTime: new Date(),
      priority,
      retryCount: 0,
    };

    // Insert job in priority order (higher priority first)
    let inserted = false;
    for (let i = 0; i < this.jobQueue.length; i++) {
      if (this.jobQueue[i].priority < priority) {
        this.jobQueue.splice(i, 0, job);
        inserted = true;
        break;
      }
    }

    if (!inserted) {
      this.jobQueue.push(job);
    }

    this.updateStats();

    logger.info('Job added to processing queue', {
      jobId: job.id,
      templatePath,
      priority,
      queueLength: this.jobQueue.length,
    });

    this.emit('job-added', job);
    return job;
  }

  /**
   * Start job processing
   */
  async start(): Promise<void> {
    if (this.isProcessing) {
      logger.warn('Job processor is already running');
      return;
    }

    this.isProcessing = true;
    this.startJobProcessor();

    logger.info('Job processor started', {
      maxConcurrentJobs: this.options.maxConcurrentJobs,
      maxRetryAttempts: this.options.maxRetryAttempts,
    });

    this.emit('processor-started');
  }

  /**
   * Stop job processing
   */
  async stop(graceful: boolean = true): Promise<void> {
    if (!this.isProcessing) {
      logger.warn('Job processor is not running');
      return;
    }

    this.isProcessing = false;

    if (this.processorInterval) {
      clearInterval(this.processorInterval);
      this.processorInterval = null;
    }

    if (graceful) {
      // Wait for active jobs to complete
      await this.waitForActiveJobsToComplete(30000); // 30 second timeout
    } else {
      // Cancel active jobs
      this.activeJobs.forEach(job => {
        job.status = 'failed';
        job.error = 'Job cancelled due to processor shutdown';
        job.completedTime = new Date();
        this.emit('job-failed', job);
      });
      this.activeJobs.clear();
    }

    this.updateStats();

    logger.info('Job processor stopped', { graceful });
    this.emit('processor-stopped');
  }

  /**
   * Get job by ID
   */
  getJob(jobId: string): OptimizationJob | null {
    // Check active jobs
    const activeJob = this.activeJobs.get(jobId);
    if (activeJob) {
      return activeJob;
    }

    // Check queued jobs
    return this.jobQueue.find(job => job.id === jobId) || null;
  }

  /**
   * Get all jobs (active and queued)
   */
  getAllJobs(): OptimizationJob[] {
    return [...Array.from(this.activeJobs.values()), ...this.jobQueue];
  }

  /**
   * Cancel a job
   */
  cancelJob(jobId: string): boolean {
    // Try to remove from queue first
    const queueIndex = this.jobQueue.findIndex(job => job.id === jobId);
    if (queueIndex !== -1) {
      const job = this.jobQueue.splice(queueIndex, 1)[0];
      job.status = 'failed';
      job.error = 'Job cancelled';
      job.completedTime = new Date();

      this.updateStats();
      this.emit('job-cancelled', job);

      logger.info('Job cancelled from queue', { jobId });
      return true;
    }

    // Try to cancel active job
    const activeJob = this.activeJobs.get(jobId);
    if (activeJob) {
      activeJob.status = 'failed';
      activeJob.error = 'Job cancelled';
      activeJob.completedTime = new Date();

      this.emit('job-cancelled', activeJob);
      logger.info('Active job marked for cancellation', { jobId });
      return true;
    }

    return false;
  }

  /**
   * Clear the job queue
   */
  clearQueue(): number {
    const clearedCount = this.jobQueue.length;

    this.jobQueue.forEach(job => {
      job.status = 'failed';
      job.error = 'Queue cleared';
      job.completedTime = new Date();
      this.emit('job-cancelled', job);
    });

    this.jobQueue = [];
    this.updateStats();

    logger.info('Job queue cleared', { clearedCount });
    return clearedCount;
  }

  /**
   * Get current statistics
   */
  getStats(): JobProcessorStats {
    return { ...this.stats };
  }

  /**
   * Update processor options
   */
  updateOptions(options: Partial<JobProcessorOptions>): void {
    this.options = { ...this.options, ...options };
    logger.info('Job processor options updated', options);
  }

  // Private methods

  private startJobProcessor(): void {
    this.processorInterval = setInterval(async () => {
      await this.processJobs();
    }, 1000) as NodeJS.Timeout; // Check every second
  }

  private async processJobs(): Promise<void> {
    if (!this.isProcessing) {
      return;
    }

    // Process jobs if we have capacity and jobs in queue
    while (
      this.activeJobs.size < this.options.maxConcurrentJobs &&
      this.jobQueue.length > 0
    ) {
      const job = this.jobQueue.shift()!;
      this.activeJobs.set(job.id, job);
      this.updateStats();

      // Process job in background
      this.processJob(job).catch(error => {
        logger.error('Unexpected error in job processing', error as Error, {
          jobId: job.id,
        });
      });
    }
  }

  private async processJob(job: OptimizationJob): Promise<void> {
    try {
      job.status = 'processing';
      const startTime = Date.now();

      logger.info('Processing optimization job', {
        jobId: job.id,
        templatePath: job.templatePath,
        retryCount: job.retryCount,
      });

      this.emit('job-started', job);

      // Set timeout for job
      const timeoutPromise = new Promise((_, reject) => {
        setTimeout(() => {
          reject(new Error('Job timeout'));
        }, this.options.jobTimeoutMs);
      });

      // Emit job processing event (external processor handles the actual work)
      const processingPromise = new Promise((resolve, reject) => {
        const onJobCompleted = (result: unknown) => {
          this.removeAllListeners('job-process-completed');
          this.removeAllListeners('job-process-failed');
          resolve(result);
        };

        const onJobFailed = (error: Error) => {
          this.removeAllListeners('job-process-completed');
          this.removeAllListeners('job-process-failed');
          reject(error);
        };

        this.once('job-process-completed', onJobCompleted);
        this.once('job-process-failed', onJobFailed);

        // Emit the job for external processing
        this.emit('process-job', job);
      });

      // Race between processing and timeout
      const result = await Promise.race([processingPromise, timeoutPromise]);

      // Job completed successfully
      job.status = 'completed';
      job.result = result;
      job.completedTime = new Date();

      const processingTime = Date.now() - startTime;
      this.updateJobStats(true, processingTime);

      logger.info('Optimization job completed', {
        jobId: job.id,
        processingTime: `${processingTime}ms`,
      });

      this.emit('job-completed', job);
    } catch (error) {
      // Job failed
      const errorMessage = (error as Error).message;

      if (
        job.retryCount < this.options.maxRetryAttempts &&
        errorMessage !== 'Job timeout'
      ) {
        // Retry the job
        job.retryCount += 1;
        job.status = 'pending';

        // Add back to queue with delay
        setTimeout(() => {
          this.jobQueue.unshift(job); // Add to front for priority
          this.updateStats();
        }, this.options.retryBackoffMs);

        this.stats.retryCount += 1;

        logger.info('Job scheduled for retry', {
          jobId: job.id,
          retryCount: job.retryCount,
          maxRetries: this.options.maxRetryAttempts,
        });

        this.emit('job-retry', job);
      } else {
        // Job failed permanently
        job.status = 'failed';
        job.error = errorMessage;
        job.completedTime = new Date();

        this.updateJobStats(false, 0);

        logger.error('Optimization job failed permanently', error as Error, {
          jobId: job.id,
          retryCount: job.retryCount,
        });

        this.emit('job-failed', job);
      }
    } finally {
      // Remove from active jobs if not retrying
      if (job.status !== 'pending') {
        this.activeJobs.delete(job.id);
        this.updateStats();
      }
    }
  }

  private async waitForActiveJobsToComplete(timeoutMs: number): Promise<void> {
    const startTime = Date.now();

    while (this.activeJobs.size > 0 && Date.now() - startTime < timeoutMs) {
      await new Promise(resolve => setTimeout(resolve, 1000));
    }

    if (this.activeJobs.size > 0) {
      logger.warn(`${this.activeJobs.size} jobs still active after timeout`);
    }
  }

  private updateStats(): void {
    this.stats.activeJobs = this.activeJobs.size;
    this.stats.queuedJobs = this.jobQueue.length;
  }

  private updateJobStats(success: boolean, processingTime: number): void {
    this.stats.totalProcessed += 1;

    if (success) {
      this.stats.successCount += 1;

      // Update average processing time
      if (processingTime > 0) {
        this.stats.averageProcessingTime =
          (this.stats.averageProcessingTime * (this.stats.successCount - 1) +
            processingTime) /
          this.stats.successCount;
      }
    } else {
      this.stats.failureCount += 1;
    }
  }

  private generateJobId(): string {
    return `job_${Date.now()}_${Math.random().toString(36).substring(2, 9)}`;
  }
}

/**
 * @fileoverview Notification service for auto-optimization
 * @lastmodified 2025-08-27T05:00:00Z
 *
 * Features: Desktop notifications, notification history, platform-specific implementations
 * Main APIs: send(), enable(), disable(), getHistory()
 * Constraints: Single responsibility - only handles notifications
 * Patterns: Strategy pattern for different platforms, event tracking
 */

import * as os from 'os';
import * as child_process from 'child_process';
import { logger } from '../../utils/logger';

export interface NotificationOptions {
  /** Notification title */
  title: string;
  /** Notification message */
  message: string;
  /** Notification icon (platform-specific) */
  icon?: string;
  /** Notification sound (platform-specific) */
  sound?: boolean;
  /** Auto-dismiss timeout (ms) */
  timeout?: number;
}

export interface NotificationHistory {
  id: string;
  title: string;
  message: string;
  timestamp: Date;
  platform: string;
  success: boolean;
  error?: string;
}

/**
 * Notification service focused solely on sending desktop notifications
 *
 * This service handles the notification logic that was previously
 * embedded in AutoOptimizeManager, following single responsibility principle.
 */
export class NotificationService {
  private enabled: boolean = true;

  private history: NotificationHistory[] = [];

  private maxHistorySize: number = 100;

  private platform: string;

  constructor(options: { enabled?: boolean; maxHistorySize?: number } = {}) {
    this.enabled = options.enabled ?? true;
    this.maxHistorySize = options.maxHistorySize ?? 100;
    this.platform = os.platform();
  }

  /**
   * Send a notification
   */
  async send(options: NotificationOptions): Promise<boolean> {
    if (!this.enabled) {
      logger.debug('Notifications disabled, skipping notification', options);
      return false;
    }

    const notificationId = this.generateNotificationId();

    try {
      await this.sendPlatformNotification(options);

      this.addToHistory({
        id: notificationId,
        title: options.title,
        message: options.message,
        timestamp: new Date(),
        platform: this.platform,
        success: true,
      });

      logger.debug('Notification sent successfully', {
        id: notificationId,
        title: options.title,
        platform: this.platform,
      });

      return true;
    } catch (error) {
      const errorMessage = (error as Error).message;

      this.addToHistory({
        id: notificationId,
        title: options.title,
        message: options.message,
        timestamp: new Date(),
        platform: this.platform,
        success: false,
        error: errorMessage,
      });

      logger.error('Failed to send notification', error as Error, {
        id: notificationId,
        title: options.title,
      });

      return false;
    }
  }

  /**
   * Send optimization started notification
   */
  async sendOptimizationStarted(templatePath: string): Promise<boolean> {
    return this.send({
      title: 'Optimization Started',
      message: `Optimizing template: ${this.getTemplateDisplayName(templatePath)}`,
      sound: false,
      timeout: 3000,
    });
  }

  /**
   * Send optimization completed notification
   */
  async sendOptimizationCompleted(
    templatePath: string,
    tokenReduction?: number
  ): Promise<boolean> {
    const reductionText = tokenReduction
      ? ` (${tokenReduction.toFixed(1)}% token reduction)`
      : '';

    return this.send({
      title: 'Optimization Completed',
      message: `Template optimized: ${this.getTemplateDisplayName(templatePath)}${reductionText}`,
      sound: true,
      timeout: 5000,
    });
  }

  /**
   * Send optimization failed notification
   */
  async sendOptimizationFailed(
    templatePath: string,
    error: string
  ): Promise<boolean> {
    return this.send({
      title: 'Optimization Failed',
      message: `Failed to optimize ${this.getTemplateDisplayName(templatePath)}: ${error}`,
      sound: true,
      timeout: 10000,
    });
  }

  /**
   * Send batch optimization completed notification
   */
  async sendBatchCompleted(
    totalFiles: number,
    successCount: number,
    failureCount: number
  ): Promise<boolean> {
    return this.send({
      title: 'Batch Optimization Completed',
      message: `Processed ${totalFiles} templates: ${successCount} successful, ${failureCount} failed`,
      sound: true,
      timeout: 8000,
    });
  }

  /**
   * Send auto-optimization enabled notification
   */
  async sendAutoOptimizationEnabled(): Promise<boolean> {
    return this.send({
      title: 'Auto-Optimization Enabled',
      message: 'Templates will be optimized automatically on save',
      sound: false,
      timeout: 4000,
    });
  }

  /**
   * Send auto-optimization disabled notification
   */
  async sendAutoOptimizationDisabled(): Promise<boolean> {
    return this.send({
      title: 'Auto-Optimization Disabled',
      message: 'Templates will no longer be optimized automatically',
      sound: false,
      timeout: 4000,
    });
  }

  /**
   * Enable notifications
   */
  enable(): void {
    this.enabled = true;
    logger.info('Notifications enabled');
  }

  /**
   * Disable notifications
   */
  disable(): void {
    this.enabled = false;
    logger.info('Notifications disabled');
  }

  /**
   * Check if notifications are enabled
   */
  isEnabled(): boolean {
    return this.enabled;
  }

  /**
   * Get notification history
   */
  getHistory(limit?: number): NotificationHistory[] {
    const history = [...this.history].reverse(); // Most recent first
    return limit ? history.slice(0, limit) : history;
  }

  /**
   * Clear notification history
   */
  clearHistory(): void {
    this.history = [];
    logger.info('Notification history cleared');
  }

  /**
   * Get notification statistics
   */
  getStats(): {
    total: number;
    successful: number;
    failed: number;
    platforms: Record<string, number>;
  } {
    const stats = {
      total: this.history.length,
      successful: 0,
      failed: 0,
      platforms: {} as Record<string, number>,
    };

    this.history.forEach(notification => {
      if (notification.success) {
        stats.successful += 1;
      } else {
        stats.failed += 1;
      }

      stats.platforms[notification.platform] =
        (stats.platforms[notification.platform] || 0) + 1;
    });

    return stats;
  }

  // Private methods

  private async sendPlatformNotification(
    options: NotificationOptions
  ): Promise<void> {
    switch (this.platform) {
      case 'darwin':
        await this.sendMacOSNotification(options);
        break;

      case 'linux':
        await this.sendLinuxNotification(options);
        break;

      case 'win32':
        await this.sendWindowsNotification(options);
        break;

      default:
        throw new Error(`Unsupported platform: ${this.platform}`);
    }
  }

  private async sendMacOSNotification(
    options: NotificationOptions
  ): Promise<void> {
    const { title, message, sound = false } = options;

    const soundArg = sound ? '' : 'with no sound';
    const script = `display notification "${message}" with title "${title}" ${soundArg}`;

    return new Promise((resolve, reject) => {
      child_process.exec(
        `osascript -e '${script}'`,
        (error, _stdout, _stderr) => {
          if (error) {
            reject(new Error(`macOS notification failed: ${error.message}`));
          } else {
            resolve();
          }
        }
      );
    });
  }

  private async sendLinuxNotification(
    options: NotificationOptions
  ): Promise<void> {
    const { title, message, timeout = 5000 } = options;

    return new Promise((resolve, reject) => {
      child_process.exec(
        `notify-send "${title}" "${message}" --expire-time=${timeout}`,
        (error, _stdout, _stderr) => {
          if (error) {
            reject(new Error(`Linux notification failed: ${error.message}`));
          } else {
            resolve();
          }
        }
      );
    });
  }

  private async sendWindowsNotification(
    options: NotificationOptions
  ): Promise<void> {
    // Windows notifications require more complex setup or third-party tools
    // For now, we'll just log the notification
    logger.info(`Windows Notification: ${options.title} - ${options.message}`);

    // In a real implementation, you might use:
    // - Windows Toast Notification API via PowerShell
    // - node-notifier library
    // - Electron's notification API if running in Electron

    // For demonstration, we'll simulate success
    await new Promise(resolve => setTimeout(resolve, 100));
  }

  private addToHistory(notification: NotificationHistory): void {
    this.history.push(notification);

    // Maintain history size limit
    if (this.history.length > this.maxHistorySize) {
      this.history = this.history.slice(-this.maxHistorySize);
    }
  }

  private generateNotificationId(): string {
    return `notif_${Date.now()}_${Math.random().toString(36).substring(2, 6)}`;
  }

  private getTemplateDisplayName(templatePath: string): string {
    const parts = templatePath.split('/');
    return parts[parts.length - 1] || templatePath;
  }
}

/**
 * @fileoverview LRU Cache service for template caching and performance optimization
 * @lastmodified 2025-08-26T11:31:10Z
 *
 * Features: LRU caching with TTL, size limits, and performance metrics
 * Main APIs: get(), set(), clear(), getStats()
 * Constraints: Configurable max size and TTL, memory-based storage
 * Patterns: Singleton pattern, async operations, metric tracking
 */

import { LRUCache } from 'lru-cache';
import { createHash } from 'crypto';

/**
 * Configuration options for cache behavior and performance tuning
 * Supports both memory-based caching and optional file system persistence
 */
export interface CacheOptions {
  /** Maximum number of items to store in cache */
  maxSize?: number;
  /** Time-to-live in milliseconds for cached items */
  maxAge?: number;
  /** Alias for maxAge (maintained for backward compatibility) */
  ttl?: number;
  /** Custom function to calculate item size for memory management */
  sizeCalculation?: <T>(value: T, key: string) => number;
  /** Whether to update item age on cache hits */
  updateAgeOnGet?: boolean;
  /** Directory path for persistent cache storage on file system */
  cacheDir?: string;
}

/**
 * Comprehensive cache performance and usage statistics
 * Provides metrics for monitoring cache effectiveness and optimization
 */
export interface CacheStats {
  /** Number of successful cache retrievals */
  hits: number;
  /** Number of cache misses (item not found) */
  misses: number;
  /** Number of items added to cache */
  sets: number;
  /** Number of items removed from cache */
  deletes: number;
  /** Number of complete cache clears */
  clears: number;
  /** Cache hit ratio as decimal (0.0 to 1.0) */
  hitRate: number;
  /** Current number of items in cache */
  size: number;
  /** Maximum cache capacity */
  maxSize: number;
}

/**
 * High-performance LRU cache service with TTL and persistence support
 *
 * This service provides a comprehensive caching solution that combines:
 * - LRU (Least Recently Used) eviction policy for memory efficiency
 * - TTL (Time-to-Live) support for automatic expiration
 * - Optional file system persistence for cache durability
 * - Detailed performance metrics and statistics tracking
 * - Generic type support for type-safe caching operations
 *
 * The cache is built on the proven lru-cache library and extends it with
 * additional features like size calculation, persistence, and comprehensive
 * monitoring capabilities.
 *
 * @template T - Type of objects to be cached (must extend object)
 * @example
 * ```typescript
 * // Create a cache for API responses
 * const apiCache = new CacheService<ApiResponse>({
 *   maxSize: 200,
 *   maxAge: 5 * 60 * 1000, // 5 minutes
 *   updateAgeOnGet: true
 * });
 *
 * // Cache an API response
 * await apiCache.set('user:123', userResponse);
 *
 * // Retrieve cached response
 * const cached = await apiCache.get('user:123');
 * if (cached) {
 *   console.log('Using cached response');
 * }
 *
 * // Get cache performance stats
 * const stats = apiCache.getStats();
 * console.log(`Hit rate: ${(stats.hitRate * 100).toFixed(1)}%`);
 * ```
 *
 * @see {@link CacheOptions} for configuration options
 * @see {@link CacheStats} for performance metrics
 */
export class CacheService<T extends object = Record<string, unknown>> {
  private cache: LRUCache<string, T>;

  private stats: Omit<CacheStats, 'hitRate' | 'size' | 'maxSize'>;

  private cacheDir?: string;

  private readonly defaultOptions: CacheOptions = {
    maxSize: 100,
    maxAge: 1000 * 60 * 60, // 1 hour default TTL
    updateAgeOnGet: true,
  };

  /**
   * Creates a new CacheService instance with specified configuration
   *
   * Initializes the LRU cache with provided options, sets up statistics
   * tracking, and configures optional file system persistence. The
   * constructor merges provided options with sensible defaults.
   *
   * @param options - Cache configuration options
   * @param options.maxSize - Maximum items (default: 100)
   * @param options.maxAge - TTL in milliseconds (default: 1 hour)
   * @param options.ttl - Alternative TTL specification
   * @param options.sizeCalculation - Custom size calculation function
   * @param options.updateAgeOnGet - Update age on access (default: true)
   * @param options.cacheDir - Persistence directory path
   *
   * @example
   * ```typescript
   * // Create cache with custom configuration
   * const cache = new CacheService({
   *   maxSize: 500,
   *   maxAge: 30 * 60 * 1000, // 30 minutes
   *   cacheDir: '/tmp/mycache',
   *   sizeCalculation: (value, key) => {
   *     return JSON.stringify(value).length + key.length;
   *   }
   * });
   * ```
   */
  constructor(options?: CacheOptions) {
    const finalOptions = { ...this.defaultOptions, ...options };

    // Handle ttl option alias (backward compatibility)
    if (options?.ttl !== undefined) {
      finalOptions.maxAge = options.ttl;
    }

    // Store cacheDir option for persistence configuration
    this.cacheDir = finalOptions.cacheDir;

    const cacheConfig: {
      max: number;
      ttl: number;
      updateAgeOnGet?: boolean;
      sizeCalculation?: <V>(value: V, key: string) => number;
      maxSize?: number;
    } = {
      max: finalOptions.maxSize!,
      ttl: finalOptions.maxAge!,
      updateAgeOnGet: finalOptions.updateAgeOnGet,
    };

    // Only add sizeCalculation if provided
    if (finalOptions.sizeCalculation) {
      cacheConfig.sizeCalculation = finalOptions.sizeCalculation;
      cacheConfig.maxSize = Number.MAX_SAFE_INTEGER; // Required when using sizeCalculation
    }

    this.cache = new LRUCache<string, T>(cacheConfig);

    this.stats = {
      hits: 0,
      misses: 0,
      sets: 0,
      deletes: 0,
      clears: 0,
    };
  }

  /**
   * Retrieve a value from the cache by key
   *
   * Attempts to retrieve a cached value and updates cache statistics.
   * If the item exists and hasn't expired, it's returned and marked as
   * recently accessed. Cache hits and misses are tracked for performance
   * monitoring.
   *
   * @param key - Unique identifier for the cached item
   * @returns Promise resolving to cached value or undefined if not found/expired
   *
   * @example
   * ```typescript
   * const userProfile = await cache.get('user:123');
   * if (userProfile) {
   *   console.log('Cache hit! Using cached profile');
   *   return userProfile;
   * }
   * console.log('Cache miss, fetching from database');
   * ```
   */
  async get(key: string): Promise<T | undefined> {
    const value = this.cache.get(key);

    if (value !== undefined) {
      this.stats.hits += 1;
    } else {
      this.stats.misses += 1;
    }

    return value;
  }

  /**
   * Get a cached value or compute and cache it if not present
   *
   * This convenience method implements the cache-aside pattern by first
   * checking the cache for an existing value. If not found, it executes
   * the provided compute function, caches the result, and returns it.
   * This pattern is ideal for expensive computations or API calls.
   *
   * @param key - Cache key to check/store under
   * @param computeFn - Function to compute value if cache miss occurs
   * @returns Promise resolving to cached or newly computed value
   *
   * @example
   * ```typescript
   * const expensiveData = await cache.getOrCompute('computation:xyz', async () => {
   *   console.log('Cache miss - performing expensive computation');
   *   const result = await performExpensiveOperation();
   *   return result;
   * });
   *
   * // Subsequent calls will use cached result
   * const cachedData = await cache.getOrCompute('computation:xyz', async () => {
   *   console.log('This will not execute due to cache hit');
   *   return null; // This won't be called
   * });
   * ```
   */
  async getOrCompute(key: string, computeFn: () => Promise<T>): Promise<T> {
    const cached = await this.get(key);
    if (cached !== undefined) {
      return cached;
    }

    const value = await computeFn();
    await this.set(key, value);
    return value;
  }

  /**
   * Store a value in the cache with optional custom TTL
   *
   * Adds or updates a cache entry with the specified key and value.
   * An optional TTL can override the default cache TTL for this specific
   * entry. The operation updates cache statistics and handles LRU eviction
   * if the cache is at capacity.
   *
   * @param key - Unique identifier for the cache entry
   * @param value - Object to be cached (must extend object type)
   * @param ttl - Optional TTL in milliseconds (overrides default)
   *
   * @example
   * ```typescript
   * // Set with default TTL
   * await cache.set('user:456', userObject);
   *
   * // Set with custom TTL (10 minutes)
   * await cache.set('temp:data', temporaryData, 10 * 60 * 1000);
   *
   * // Set critical data with longer TTL (2 hours)
   * await cache.set('config:app', configData, 2 * 60 * 60 * 1000);
   * ```
   */
  async set(key: string, value: T, ttl?: number): Promise<void> {
    const options = ttl ? { ttl } : undefined;
    this.cache.set(key, value, options);
    this.stats.sets += 1;
  }

  /**
   * Remove a specific entry from the cache
   *
   * Deletes the cache entry associated with the specified key and updates
   * deletion statistics. This is useful for invalidating specific cache
   * entries when the underlying data changes.
   *
   * @param key - Key of the cache entry to remove
   * @returns Promise resolving to true if item was deleted, false if not found
   *
   * @example
   * ```typescript
   * // Remove user profile after update
   * const deleted = await cache.delete('user:123');
   * if (deleted) {
   *   console.log('User profile cache invalidated');
   * }
   *
   * // Cleanup temporary cache entries
   * const tempKeys = ['temp:1', 'temp:2', 'temp:3'];
   * for (const key of tempKeys) {
   *   await cache.delete(key);
   * }
   * ```
   */
  async delete(key: string): Promise<boolean> {
    const deleted = this.cache.delete(key);
    if (deleted) {
      this.stats.deletes += 1;
    }
    return deleted;
  }

  /**
   * Check if a key exists in the cache without retrieving the value
   *
   * Determines whether a cache entry exists for the specified key without
   * affecting cache statistics (unlike get()). This is useful for conditional
   * logic that depends on cache presence without triggering hits/misses.
   *
   * @param key - Key to check for existence
   * @returns Promise resolving to true if key exists, false otherwise
   *
   * @example
   * ```typescript
   * if (await cache.has('user:789')) {
   *   console.log('User data is cached');
   *   const userData = await cache.get('user:789');
   * } else {
   *   console.log('User data not in cache, will fetch from database');
   * }
   * ```
   */
  async has(key: string): Promise<boolean> {
    return this.cache.has(key);
  }

  /**
   * Clear all entries from the cache
   *
   * Removes all cached items and updates clear statistics. This operation
   * is useful for cache invalidation during system updates, testing, or
   * when memory needs to be freed. Use with caution in production as it
   * will cause cache misses for all subsequent requests.
   *
   * @example
   * ```typescript
   * // Clear cache during system maintenance
   * await cache.clear();
   * console.log('All cache entries cleared');
   *
   * // Verify cache is empty
   * console.log(`Cache size after clear: ${cache.size}`);
   * ```
   */
  async clear(): Promise<void> {
    this.cache.clear();
    this.stats.clears += 1;
  }

  /**
   * Get comprehensive cache performance statistics
   *
   * Returns detailed metrics about cache performance including hit/miss
   * ratios, operation counts, and current cache utilization. These statistics
   * are valuable for monitoring cache effectiveness and optimizing cache
   * configuration.
   *
   * @returns Object containing detailed cache statistics
   * @returns returns.hits - Number of successful cache retrievals
   * @returns returns.misses - Number of cache misses
   * @returns returns.hitRate - Hit ratio (0.0 to 1.0) for performance assessment
   * @returns returns.size - Current number of cached items
   * @returns returns.maxSize - Maximum cache capacity
   *
   * @example
   * ```typescript
   * const stats = cache.getStats();
   * console.log(`Cache Performance Report:`);
   * console.log(`- Hit Rate: ${(stats.hitRate * 100).toFixed(2)}%`);
   * console.log(`- Utilization: ${stats.size}/${stats.maxSize} (${(stats.size/stats.maxSize*100).toFixed(1)}%)`);
   * console.log(`- Operations: ${stats.hits} hits, ${stats.misses} misses`);
   * console.log(`- Maintenance: ${stats.sets} sets, ${stats.deletes} deletes, ${stats.clears} clears`);
   *
   * if (stats.hitRate < 0.7) {
   *   console.warn('Low cache hit rate - consider increasing cache size or TTL');
   * }
   * ```
   */
  getStats(): CacheStats {
    const total = this.stats.hits + this.stats.misses;
    const hitRate = total > 0 ? this.stats.hits / total : 0;

    return {
      ...this.stats,
      hitRate: Math.round(hitRate * 100) / 100,
      size: this.cache.size,
      maxSize: this.cache.max,
    };
  }

  /**
   * Reset all cache statistics to zero
   *
   * Clears all accumulated statistics including hits, misses, and operation
   * counts while preserving cached data. This is useful for performance
   * testing, monitoring periods, or when you want to start fresh statistics
   * tracking without clearing the cache contents.
   *
   * @example
   * ```typescript
   * // Reset stats before performance test
   * cache.resetStats();
   *
   * // Run performance test
   * await runCachePerformanceTest();
   *
   * // Check test results
   * const testStats = cache.getStats();
   * console.log(`Test hit rate: ${testStats.hitRate}`);
   * ```
   */
  resetStats(): void {
    this.stats = {
      hits: 0,
      misses: 0,
      sets: 0,
      deletes: 0,
      clears: 0,
    };
  }

  /**
   * Generate a deterministic cache key from multiple components
   *
   * Creates a SHA-256 hash from the provided parts to ensure consistent
   * key generation for complex cache keys. This method handles objects,
   * strings, numbers, and other data types by serializing them appropriately.
   *
   * @param parts - Variable number of components to combine into cache key
   * @returns SHA-256 hash string suitable for use as cache key
   *
   * @example
   * ```typescript
   * // Generate key from multiple components
   * const cacheKey = CacheService.generateKey(
   *   'user',
   *   userId,
   *   { includeProjects: true, activeOnly: false },
   *   'v2'
   * );
   *
   * // Use generated key for caching
   * await cache.set(cacheKey, userData);
   *
   * // Same components will generate same key
   * const sameKey = CacheService.generateKey(
   *   'user',
   *   userId,
   *   { includeProjects: true, activeOnly: false },
   *   'v2'
   * );
   * console.log(cacheKey === sameKey); // true
   * ```
   */
  static generateKey(...parts: unknown[]): string {
    const combined = parts
      .map(p => (typeof p === 'object' ? JSON.stringify(p) : String(p)))
      .join(':');

    return createHash('sha256').update(combined).digest('hex');
  }

  /**
   * Get all cache keys currently stored in the cache
   *
   * Returns an array of all cache keys. This method is useful for debugging,
   * cache inspection, and bulk operations. Note that for large caches, this
   * operation may be expensive as it iterates through all cache entries.
   *
   * @returns Array of all cache keys as strings
   *
   * @example
   * ```typescript
   * // List all cached keys
   * const keys = cache.keys();
   * console.log(`Cached keys: ${keys.join(', ')}`);
   *
   * // Find user-related cache entries
   * const userKeys = keys.filter(key => key.startsWith('user:'));
   * console.log(`User cache entries: ${userKeys.length}`);
   *
   * // Bulk invalidation by pattern
   * const tempKeys = keys.filter(key => key.startsWith('temp:'));
   * for (const key of tempKeys) {
   *   await cache.delete(key);
   * }
   * ```
   */
  keys(): string[] {
    return Array.from(this.cache.keys());
  }

  /**
   * Calculate total cache size in bytes including memory and file system storage
   *
   * Computes the total memory footprint of the cache by calculating the size
   * of all cached keys and values. If file system persistence is enabled,
   * includes the size of persisted cache files. This method is useful for
   * memory management and cache optimization.
   *
   * The calculation includes:
   * - UTF-8 byte size of all cache keys
   * - JSON serialization size of all cached values
   * - File system storage size (if cacheDir is configured)
   *
   * @returns Promise resolving to total cache size in bytes
   *
   * @example
   * ```typescript
   * const sizeBytes = await cache.getSize();
   * const sizeKB = (sizeBytes / 1024).toFixed(2);
   * const sizeMB = (sizeBytes / (1024 * 1024)).toFixed(2);
   *
   * console.log(`Cache size: ${sizeBytes} bytes (${sizeKB} KB, ${sizeMB} MB)`);
   *
   * // Monitor cache size growth
   * if (sizeBytes > 100 * 1024 * 1024) { // 100 MB
   *   console.warn('Cache size exceeds 100MB - consider cleanup');
   *   await cache.clear();
   * }
   * ```
   */
  async getSize(): Promise<number> {
    let totalSize = 0;

    // Calculate in-memory cache size
    for (const [key, value] of this.cache.entries()) {
      const keySize = Buffer.byteLength(key, 'utf8');
      const valueSize = Buffer.byteLength(JSON.stringify(value), 'utf8');
      totalSize += keySize + valueSize;
    }

    // If cacheDir is specified, add file system cache size
    if (this.cacheDir) {
      try {
        const fs = await import('fs');
        const path = await import('path');

        if (fs.existsSync(this.cacheDir)) {
          const files = fs.readdirSync(this.cacheDir, { withFileTypes: true });

          for (const file of files) {
            if (file.isFile()) {
              const filePath = path.join(this.cacheDir, file.name);
              const stats = fs.statSync(filePath);
              totalSize += stats.size;
            }
          }
        }
      } catch (_error) {
        // Silently ignore filesystem errors - return in-memory size only
      }
    }

    return totalSize;
  }

  /**
   * Get the current number of items in the cache
   *
   * Returns the count of cached entries currently stored. This is a
   * synchronous operation that provides quick access to cache utilization
   * information without the overhead of calculating byte sizes.
   *
   * @returns Current number of cached items
   *
   * @example
   * ```typescript
   * console.log(`Cache contains ${cache.size} items`);
   *
   * // Check if cache is approaching capacity
   * const stats = cache.getStats();
   * const utilizationPercent = (cache.size / stats.maxSize) * 100;
   * if (utilizationPercent > 80) {
   *   console.warn(`Cache utilization high: ${utilizationPercent.toFixed(1)}%`);
   * }
   * ```
   */
  get size(): number {
    return this.cache.size;
  }
}

/**
 * Singleton cache manager for managing multiple named cache instances
 *
 * The CacheManager provides a centralized way to create, access, and manage
 * multiple cache instances with different configurations. This is particularly
 * useful for applications that need separate caches for different data types
 * or use cases (e.g., API responses, user sessions, computed results).
 *
 * Features:
 * - Singleton pattern ensures single instance per cache name
 * - Lazy initialization of cache instances
 * - Centralized cache clearing and statistics collection
 * - Memory-efficient instance reuse
 *
 * @example
 * ```typescript
 * // Get or create named cache instances
 * const userCache = CacheManager.getCache('users', {
 *   maxSize: 500,
 *   maxAge: 15 * 60 * 1000 // 15 minutes
 * });
 *
 * const apiCache = CacheManager.getCache('api', {
 *   maxSize: 1000,
 *   maxAge: 5 * 60 * 1000 // 5 minutes
 * });
 *
 * // Use the caches independently
 * await userCache.set('user:123', userData);
 * await apiCache.set('endpoint:/users', apiResponse);
 *
 * // Get global statistics
 * const allStats = CacheManager.getGlobalStats();
 * console.log('All cache statistics:', allStats);
 *
 * // Clear all caches
 * CacheManager.clearAll();
 * ```
 */
export class CacheManager {
  private static instances = new Map<string, CacheService>();

  /**
   * Get or create a named cache instance
   *
   * Returns an existing cache instance if one exists for the given name,
   * or creates a new one with the provided options. This ensures singleton
   * behavior per cache name while allowing different configurations.
   *
   * @param name - Unique name for the cache instance
   * @param options - Configuration options (only used if creating new instance)
   * @returns Cache service instance for the specified name
   *
   * @example
   * ```typescript
   * // First call creates the cache with specified options
   * const cache1 = CacheManager.getCache('myCache', {
   *   maxSize: 100,
   *   maxAge: 60000
   * });
   *
   * // Second call returns the same instance, options ignored
   * const cache2 = CacheManager.getCache('myCache', {
   *   maxSize: 200 // This will be ignored
   * });
   *
   * console.log(cache1 === cache2); // true
   * ```
   */
  static getCache(name: string, options?: CacheOptions): CacheService {
    if (!this.instances.has(name)) {
      this.instances.set(name, new CacheService(options));
    }
    return this.instances.get(name)!;
  }

  /**
   * Clear all managed cache instances and reset the manager
   *
   * Clears the contents of all managed cache instances and removes them
   * from the manager's registry. This is useful for testing, memory cleanup,
   * or application shutdown scenarios.
   *
   * @example
   * ```typescript
   * // Clear all caches during application shutdown
   * process.on('SIGTERM', () => {
   *   console.log('Clearing all caches before shutdown');
   *   CacheManager.clearAll();
   *   process.exit(0);
   * });
   *
   * // Clear caches during testing
   * afterEach(() => {
   *   CacheManager.clearAll();
   * });
   * ```
   */
  static clearAll(): void {
    for (const cache of this.instances.values()) {
      cache.clear();
    }
    // Also clear the instances map to reset state completely
    this.instances.clear();
  }

  /**
   * Get performance statistics for all managed cache instances
   *
   * Collects and returns statistics from all managed cache instances,
   * providing a comprehensive view of cache performance across the application.
   * This is valuable for monitoring, debugging, and performance optimization.
   *
   * @returns Object mapping cache names to their respective statistics
   *
   * @example
   * ```typescript
   * const globalStats = CacheManager.getGlobalStats();
   *
   * // Display statistics for all caches
   * Object.entries(globalStats).forEach(([name, stats]) => {
   *   console.log(`Cache '${name}':`);
   *   console.log(`  Hit rate: ${(stats.hitRate * 100).toFixed(2)}%`);
   *   console.log(`  Size: ${stats.size}/${stats.maxSize}`);
   *   console.log(`  Operations: ${stats.hits + stats.misses} total`);
   * });
   *
   * // Find poorly performing caches
   * const poorCaches = Object.entries(globalStats)
   *   .filter(([_, stats]) => stats.hitRate < 0.5)
   *   .map(([name]) => name);
   *
   * if (poorCaches.length > 0) {
   *   console.warn(`Low-performing caches: ${poorCaches.join(', ')}`);
   * }
   * ```
   */
  static getGlobalStats(): Record<string, CacheStats> {
    const stats: Record<string, CacheStats> = {};
    for (const [name, cache] of this.instances.entries()) {
      stats[name] = cache.getStats();
    }
    return stats;
  }
}

