export interface OptimizedTemplateServiceConfig {
  enableOptimization: boolean;
  autoOptimizeNewTemplates: boolean;
  enableFeedbackLoop: boolean;
  optimizationCache: {
    ttl: number;
    maxSize: number;
  };
}

/**
 * Service for managing optimized templates with PromptWizard integration
 */
export class OptimizedTemplateService extends EventEmitter {
  private readonly config: OptimizedTemplateServiceConfig;

  private readonly templateService: TemplateService;

  private readonly optimizationPipeline?: OptimizationPipeline;

  private readonly optimizationQueue: OptimizationQueue;

  private readonly optimizedTemplateCache: CacheService<OptimizedTemplate>;

  private readonly promptWizardService: PromptWizardService;

  private readonly cacheService: CacheService<OptimizationResult>;

  private settings: OptimizationSettings;

  constructor(config: Partial<OptimizedTemplateServiceConfig> = {}) {
    super();

    this.config = {
      enableOptimization: true,
      autoOptimizeNewTemplates: false,
      enableFeedbackLoop: true,
      optimizationCache: {
        ttl: 24 * 60 * 60 * 1000, // 24 hours
        maxSize: 100,
      },
      ...config,
    };

    this.templateService = new TemplateService({});

    // Initialize services - properly typed implementations
    this.promptWizardService = new PromptWizardService();
    this.cacheService = new CacheService<OptimizationResult>({ maxSize: 100, ttl: 3600000 });

    // Note: OptimizationPipeline needs proper service instances - simplified for now
    // this.optimizationPipeline = new OptimizationPipeline(promptService, this.templateService, {});
    // Initialize OptimizationQueue with dependency injection pattern
    // Note: Full service dependencies would be injected in production
    this.optimizationQueue = new OptimizationQueue(
      this.promptWizardService,
      this.cacheService
    );

    // Initialize cache
    this.optimizedTemplateCache = new CacheService<OptimizedTemplate>({
      maxSize: this.config.optimizationCache.maxSize,
      ttl: this.config.optimizationCache.ttl,
    });

    // Load optimization settings
    this.settings = this.loadOptimizationSettings();

    logger.info(
      `Optimized template service initialized with optimization: ${this.config.enableOptimization}`
    );
  }

  /**
   * Optimize a template using PromptWizard
   */
  async optimizeTemplate(
    templateId: string,
    context?: Partial<OptimizationContext>,
    options: {
      forceReoptimization?: boolean;
      async?: boolean;
      priority?: 'low' | 'normal' | 'high' | 'urgent';
    } = {}
  ): Promise<OptimizedTemplate | OptimizationJob> {
    if (!this.config.enableOptimization) {
      throw new Error('Template optimization is disabled');
    }

    // Load template
    const serviceTemplate = await this.templateService.loadTemplate(templateId);
    if (!serviceTemplate) {
      throw new Error(`Template not found: ${templateId}`);
    }

    const template = convertServiceToIndexTemplate(serviceTemplate);

    // Check if optimization is needed
    if (!options.forceReoptimization) {
      const existingOptimized = await this.getOptimizedTemplate(templateId);
      if (
        existingOptimized &&
        this.shouldUseExistingOptimization(existingOptimized)
      ) {
        logger.info(`Using existing optimized template for ${templateId}`);
        return existingOptimized;
      }
    }

    // Prepare optimization context
    const optimizationContext: OptimizationContext = {
      templateId,
      targetModel: this.settings.global.defaultModel,
      task: template.description || 'Optimize template for better performance',
      preferences: context?.preferences,
      metadata: context?.metadata,
      ...context,
    };

    // Convert context to request format
    const optimizationRequest = this.convertContextToRequest(
      template,
      optimizationContext
    );

    if (options.async) {
      // Queue optimization job
      const job = await this.optimizationQueue.addJob(
        templateId,
        template,
        optimizationRequest,
        {
          priority: options.priority || 'normal',
        }
      );

      this.emit('optimization:queued', { templateId, jobId: job.jobId });
      return job;
    }
    // Process immediately (simplified for now - would use actual pipeline)
    if (!this.optimizationPipeline) {
      throw new Error('Optimization pipeline not initialized');
    }

    const result = await this.optimizationPipeline.process(
      templateId,
      template,
      optimizationRequest
    );

    if (result.success && result.data) {
      const optimizedTemplate = await this.createOptimizedTemplate(
        template,
        result.data,
        optimizationContext
      );

      await this.saveOptimizedTemplate(optimizedTemplate);
      return optimizedTemplate;
    }
    throw new Error(result.error?.message || 'Optimization pipeline failed');
  }

  /**
   * Get optimized version of a template
   */
  async getOptimizedTemplate(
    templateId: string
  ): Promise<OptimizedTemplate | null> {
    // Check cache first
    const cached = await this.optimizedTemplateCache.get(templateId);
    if (cached !== undefined) {
      return cached || null;
    }

    // Try loading from disk
    try {
      const optimizedTemplate =
        await this.loadOptimizedTemplateFromDisk(templateId);
      if (optimizedTemplate) {
        this.optimizedTemplateCache.set(templateId, optimizedTemplate);
        return optimizedTemplate;
      }
    } catch (_error) {
      logger.debug(`No optimized template found on disk for ${templateId}`);
    }

    return null;
  }

  /**
   * Compare original and optimized templates
   */
  async compareTemplates(
    originalId: string,
    optimizedId: string
  ): Promise<TemplateComparison> {
    const original = await this.templateService.loadTemplate(originalId);
    const optimized = await this.getOptimizedTemplate(optimizedId);

    if (!original || !optimized) {
      throw new Error('Templates not found for comparison');
    }

    return this.generateTemplateComparison(
      convertServiceToIndexTemplate(original),
      optimized
    );
  }

  /**
   * Batch optimize multiple templates
   */
  async batchOptimize(
    templateIds: string[],
    context?: Partial<OptimizationContext>,
    options: {
      concurrency?: number;
      failFast?: boolean;
      priority?: 'low' | 'normal' | 'high' | 'urgent';
    } = {}
  ): Promise<OptimizationBatch> {
    const batchId = this.generateBatchId();
    const jobs: OptimizationJob[] = [];

    logger.info(
      `Starting batch optimization ${batchId} for ${templateIds.length} templates`
    );

    // Create optimization jobs
    for (const templateId of templateIds) {
      try {
        const job = (await this.optimizeTemplate(templateId, context, {
          priority: options.priority,
          async: true,
        })) as OptimizationJob;

        jobs.push(job);
      } catch (error) {
        if (options.failFast) {
          throw error;
        }

        logger.warn(
          `Failed to create optimization job in batch ${batchId} for template ${templateId}: ${error}`
        );
      }
    }

    const batch: OptimizationBatch = {
      batchId,
      templateIds,
      status: jobs.length > 0 ? 'processing' : 'failed',
      jobs,
      config: {
        concurrency: options.concurrency || 3,
        failFast: options.failFast || false,
        context: context || {
          templateId: '',
          targetModel: 'gpt-4',
          task: 'Default optimization task',
        },
      },
      createdAt: new Date(),
      // settings commented out as not in OptimizationBatch interface
      // settings: {
      //   concurrency: options.concurrency || 3,
      //   failFast: options.failFast || false,
      //   priority: options.priority || 'normal',
      // },
    };

    return batch;
  }

  /**
   * Update optimization settings
   */
  async updateOptimizationSettings(
    updates: Partial<OptimizationSettings>
  ): Promise<void> {
    this.settings = {
      ...this.settings,
      ...updates,
    };

    await this.saveOptimizationSettings(this.settings);
    this.emit('settings:updated', this.settings);
    logger.info('Optimization settings updated');
  }

  /**
   * Get current optimization settings
   */
  getOptimizationSettings(): OptimizationSettings {
    return { ...this.settings };
  }

  /**
   * Check if template can be optimized
   */
  canOptimize(template: Template): { canOptimize: boolean; reasons: string[] } {
    const reasons: string[] = [];

    // Get primary content
    const primaryContent =
      template.content || template.files?.[0]?.source || '';

    if (!primaryContent || primaryContent.trim().length === 0) {
      reasons.push('Template has no content to optimize');
    }

    if (primaryContent.length < 50) {
      reasons.push('Template content is too short to optimize');
    }

    if (primaryContent.length > 50000) {
      reasons.push('Template content is too long to optimize');
    }

    return {
      canOptimize: reasons.length === 0,
      reasons,
    };
  }

  /**
   * Create optimized template from optimization result
   */
  private async createOptimizedTemplate(
    original: Template,
    result: OptimizationResult,
    context: OptimizationContext
  ): Promise<OptimizedTemplate> {
    const optimizationId = this.generateOptimizationId();

    const optimizationHistory: OptimizationHistory = {
      optimizationId,
      timestamp: new Date(),
      version: '1.0.0',
      context,
      metrics: result.metrics,
      originalContent:
        (original.files?.[0] as TemplateFile)?.content ||
        original.description ||
        '',
      optimizedContent: result.optimizedPrompt,
      method: 'promptwizard',
      success: true,
    };

    const optimizedTemplate: OptimizedTemplate = {
      ...original,
      isOptimized: true,
      originalTemplateId: original.name,
      optimizationMetrics: result.metrics,
      currentOptimizationMetrics: result.metrics,
      optimizationHistory: [optimizationHistory],
      optimizationContext: context,
      optimizationLevel: 'basic',
      // Update the content in files if they exist, otherwise add content property
      files:
        original.files && original.files.length > 0
          ? original.files.map((file, index) =>
              index === 0 ? { ...file, content: result.optimizedPrompt } : file
            )
          : [
              {
                path: `${original.name}.optimized.md`,
                source: `${original.name}.optimized.md`,
                destination: `${original.name}.optimized.md`,
                content: result.optimizedPrompt,
                name: original.name,
              } as TemplateFile,
            ],
    };

    return optimizedTemplate;
  }

  /**
   * Save optimized template to disk
   */
  private async saveOptimizedTemplate(
    template: OptimizedTemplate
  ): Promise<void> {
    const optimizedDir = path.join(process.cwd(), '.optimized-templates');
    await fs.mkdir(optimizedDir, { recursive: true });

    const templatePath = path.join(
      optimizedDir,
      `${template.originalTemplateId}.optimized.json`
    );

    await fs.writeFile(templatePath, JSON.stringify(template, null, 2), 'utf8');

    // Cache it
    this.optimizedTemplateCache.set(template.originalTemplateId!, template);
    logger.debug(
      `Optimized template saved: ${template.originalTemplateId} to ${templatePath}`
    );
  }

  /**
   * Load optimized template from disk
   */
  private async loadOptimizedTemplateFromDisk(
    templateId: string
  ): Promise<OptimizedTemplate | null> {
    const optimizedDir = path.join(process.cwd(), '.optimized-templates');
    const templatePath = path.join(
      optimizedDir,
      `${templateId}.optimized.json`
    );

    try {
      const content = await fs.readFile(templatePath, 'utf8');
      const template = JSON.parse(content) as OptimizedTemplate;
      return template;
    } catch (_error) {
      return null;
    }
  }

  /**
   * Generate template comparison
   */
  private generateTemplateComparison(
    original: Template,
    optimized: OptimizedTemplate
  ): TemplateComparison {
    const originalContent =
      (original.files?.[0] as TemplateFile)?.content ||
      original.description ||
      '';
    const optimizedContent =
      (optimized.files?.[0] as TemplateFile)?.content ||
      optimized.description ||
      '';

    const comparison: TemplateComparison = {
      original,
      optimized,
      improvements: {
        tokenReduction: optimized.optimizationMetrics.tokenReduction,
        costSavings: optimized.optimizationMetrics.costReduction,
        qualityImprovement:
          optimized.optimizationMetrics.qualityImprovement || 0,
        complexityReduction:
          optimized.optimizationMetrics.complexityReduction || 0,
      },
      analysis: {
        contentChanges: this.analyzeContentChanges(
          originalContent,
          optimizedContent
        ),
        structuralChanges: this.analyzeStructuralChanges(original, optimized),
        variableChanges: this.analyzeVariableChanges(original, optimized),
      },
      comparison: {
        overallImprovement: optimized.optimizationMetrics.accuracyImprovement,
        metrics: optimized.optimizationMetrics,
        contentDiff: {
          additions: this.findContentDifferences(
            originalContent,
            optimizedContent,
            'additions'
          ),
          deletions: this.findContentDifferences(
            originalContent,
            optimizedContent,
            'deletions'
          ),
          modifications: this.findContentModifications(
            originalContent,
            optimizedContent
          ),
        },
        structuralChanges: {
          variablesAdded: Object.keys(optimized.variables || {}).filter(
            v => !Object.keys(original.variables || {}).includes(v)
          ),
          variablesRemoved: Object.keys(original.variables || {}).filter(
            v => !Object.keys(optimized.variables || {}).includes(v)
          ),
          sectionsReorganized:
            (original.files?.length || 0) !== (optimized.files?.length || 0),
          logicSimplified: optimizedContent.length < originalContent.length,
        },
        qualityAssessment: {
          clarity: optimized.optimizationMetrics.readabilityImprovement || 0,
          conciseness: optimized.optimizationMetrics.tokenReduction,
          completeness: optimized.optimizationMetrics.qualityImprovement || 0,
          accuracy: optimized.optimizationMetrics.accuracyImprovement,
        },
      },
      recommendation: this.generateRecommendation(
        optimized.optimizationMetrics
      ),
    };

    return comparison;
  }

  /**
   * Check if existing optimization should be used
   */
  private shouldUseExistingOptimization(optimized: OptimizedTemplate): boolean {
    const lastOptimization =
      optimized.optimizationHistory[optimized.optimizationHistory.length - 1];
    const age = Date.now() - lastOptimization.timestamp.getTime();
    const maxAge =
      this.settings.global.maxOptimizationAge || 7 * 24 * 60 * 60 * 1000; // 7 days

    return age < maxAge;
  }

  // Helper methods for content analysis
  private analyzeContentChanges(original: string, optimized: string): string[] {
    const changes: string[] = [];

    if (original.length !== optimized.length) {
      changes.push(
        `Content length changed from ${original.length} to ${optimized.length} characters`
      );
    }

    if (optimized.length < original.length) {
      changes.push('Content was shortened and condensed');
    } else if (optimized.length > original.length) {
      changes.push('Content was expanded with additional details');
    }

    return changes;
  }

  private analyzeStructuralChanges(
    original: Template,
    optimized: OptimizedTemplate
  ): string[] {
    const changes: string[] = [];

    const origLen = original.files?.length || 0;
    const optLen = optimized.files?.length || 0;
    if (origLen !== optLen) {
      changes.push(`File count changed from ${origLen} to ${optLen}`);
    }

    const origCmdLen = original.commands?.length || 0;
    const optCmdLen = optimized.commands?.length || 0;
    if (origCmdLen !== optCmdLen) {
      changes.push(`Command count changed from ${origCmdLen} to ${optCmdLen}`);
    }

    return changes;
  }

  private analyzeVariableChanges(
    original: Template,
    optimized: OptimizedTemplate
  ): string[] {
    const originalVars = Object.keys(original.variables || {});
    const optimizedVars = Object.keys(optimized.variables || {});
    const changes: string[] = [];

    const added = optimizedVars.filter(v => !originalVars.includes(v));
    const removed = originalVars.filter(v => !optimizedVars.includes(v));

    if (added.length > 0) {
      changes.push(`Variables added: ${added.join(', ')}`);
    }

    if (removed.length > 0) {
      changes.push(`Variables removed: ${removed.join(', ')}`);
    }

    return changes;
  }

  private findContentDifferences(
    original: string,
    comparison: string,
    type: 'additions' | 'deletions'
  ): string[] {
    const originalLines = original.split('\n');
    const comparisonLines = comparison.split('\n');

    if (type === 'additions') {
      return comparisonLines.filter(line => !originalLines.includes(line));
    }
    return originalLines.filter(line => !comparisonLines.includes(line));
  }

  private findContentModifications(
    original: string,
    comparison: string
  ): Array<{ original: string; modified: string }> {
    const originalLines = original.split('\n');
    const comparisonLines = comparison.split('\n');
    const modifications: Array<{ original: string; modified: string }> = [];

    const maxLines = Math.max(originalLines.length, comparisonLines.length);
    for (let i = 0; i < maxLines; i++) {
      const origLine = originalLines[i] || '';
      const compLine = comparisonLines[i] || '';

      if (origLine !== compLine && origLine.length > 0 && compLine.length > 0) {
        modifications.push({ original: origLine, modified: compLine });
      }
    }

    return modifications;
  }

  private generateRecommendation(metrics: OptimizationMetrics): {
    useOptimized: boolean;
    confidence: number;
    reasons: string[];
    warnings?: string[];
  } {
    const reasons: string[] = [];
    const warnings: string[] = [];
    let useOptimized = false;
    let confidence = 0.5;

    if (metrics.accuracyImprovement > 0.1) {
      reasons.push('Significant accuracy improvement detected');
      useOptimized = true;
      confidence += 0.2;
    }

    if (metrics.tokenReduction > 0.2) {
      reasons.push('Substantial token reduction achieved');
      useOptimized = true;
      confidence += 0.15;
    }

    if (metrics.costReduction > 1.5) {
      reasons.push('Cost reduction benefits');
      useOptimized = true;
      confidence += 0.1;
    }

    if (metrics.confidence && metrics.confidence < 0.7) {
      warnings.push('Low optimization confidence - manual review recommended');
      confidence -= 0.2;
    }

    return {
      useOptimized,
      confidence: Math.max(0, Math.min(1, confidence)),
      reasons,
      warnings: warnings.length > 0 ? warnings : undefined,
    };
  }

  private loadOptimizationSettings(): OptimizationSettings {
    return {
      global: {
        enabled: true,
        defaultModel: 'gpt-4',
        maxOptimizationAge: 7 * 24 * 60 * 60 * 1000,
        autoOptimizeNewTemplates: false,
      },
      categorySettings: {},
      qualityThresholds: {
        minimumImprovement: 0.1,
        maximumDegradation: 0.05,
        confidenceThreshold: 0.7,
      },
      feedbackSettings: {
        enableFeedbackLoop: true,
        reoptimizationThreshold: 2.5,
        feedbackWeight: 0.3,
      },
      performance: {
        targetTokenReduction: 0.2,
        targetQualityImprovement: 0.1,
      },
      feedback: {
        enableAutoReoptimization: true,
        reoptimizationThreshold: 2.5,
      },
      advanced: {
        enableABTesting: false,
        maxOptimizationHistory: 10,
        enableExperimentalFeatures: false,
      },
    };
  }

  private async saveOptimizationSettings(
    _settings: OptimizationSettings
  ): Promise<void> {
    // Implementation would save to config file
  }

  private generateOptimizationId(): string {
    return `opt_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateBatchId(): string {
    return `batch_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Convert OptimizationContext to OptimizationRequest format
   */
  private convertContextToRequest(
    template: Template,
    context: OptimizationContext
  ): OptimizationRequest {
    // Convert targetModel to valid values
    const validTargetModels: Array<
      | 'gpt-4'
      | 'gpt-3.5-turbo'
      | 'claude-3-opus'
      | 'claude-3-sonnet'
      | 'gemini-pro'
    > = [
      'gpt-4',
      'gpt-3.5-turbo',
      'claude-3-opus',
      'claude-3-sonnet',
      'gemini-pro',
    ];

    const targetModel = validTargetModels.includes(context.targetModel as any)
      ? (context.targetModel as any)
      : 'gpt-4';

    return {
      prompt: template.content || template.description || '',
      task: context.task,
      targetModel,
      metadata: {
        templateId: context.templateId,
        templateName: template.name,
        version: template.version,
        author: template.author,
      },
      ...context.preferences,
    };
  }
}

/**
 * @fileoverview Service for managing prompt optimization workflows with PromptWizard
 * @lastmodified 2025-08-26T11:31:10Z
 *
 * Features: Batch optimization, queue management, result caching, feedback loops
 * Main APIs: optimizeTemplate(), batchOptimize(), getOptimizationStatus(), sendFeedback()
 * Constraints: Requires PromptWizard service running, Redis for caching
 * Patterns: Queue-based async processing, LRU cache, event-driven updates
 */

import { EventEmitter } from 'events';
import { LRUCache } from 'lru-cache';
import { logger } from '../utils/logger';
import {
  PromptWizardClient,
  OptimizationConfig,
  QualityScore,
  PromptComparison,
} from '../integrations/promptwizard';
import { TemplateService } from './template.service';
import { Template, TemplateFile } from '../types';
import { CacheService } from './cache.service';
import { OptimizationCacheService } from './optimization-cache.service';
import {
  OptimizationQueue,
  OptimizationJob as QueueJob,
} from '../queues/optimization-queue';
import { OptimizationPipeline } from '../core/optimization-pipeline';

/**
 * Configuration options for optimization requests
 * Controls caching, priority, and timeout behavior
 */
export interface OptimizationOptions {
  /** Skip cache lookup and force fresh optimization */
  skipCache?: boolean;
  /** Processing priority level affecting queue order */
  priority?: 'low' | 'normal' | 'high' | 'critical';
  /** Maximum number of retry attempts on failure */
  maxRetries?: number;
  /** Request timeout in milliseconds */
  timeoutMs?: number;
}

/**
 * Request structure for single template optimization
 * Contains template data and optimization parameters
 */
export interface OptimizationRequest {
  /** Unique identifier for the template */
  templateId: string;
  /** Template object containing content and metadata */
  template: Template;
  /** Optimization configuration overrides */
  config?: Partial<OptimizationConfig>;
  /** Request-specific options for caching and priority */
  options?: OptimizationOptions;
}

/**
 * Result object containing optimization outcomes and metrics
 * Provides before/after comparison and performance data
 */
export interface OptimizationResult {
  /** Unique identifier for this optimization request */
  requestId: string;
  /** Template identifier that was optimized */
  templateId: string;
  /** Original template before optimization */
  originalTemplate: Template;
  /** Optimized template with improvements */
  optimizedTemplate: Template;
  /** Performance metrics from the optimization process */
  metrics: {
    /** Percentage reduction in token count */
    tokenReduction: number;
    /** Improvement in accuracy score */
    accuracyImprovement: number;
    /** Time taken for optimization in milliseconds */
    optimizationTime: number;
    /** Number of API calls made during optimization */
    apiCalls: number;
  };
  /** Quality assessment of the optimized prompt */
  qualityScore: QualityScore;
  /** Detailed comparison between original and optimized versions */
  comparison: PromptComparison;
  /** Timestamp when optimization completed */
  timestamp: Date;
}

/**
 * Request structure for batch template optimization
 * Allows processing multiple templates with shared configuration
 */
export interface BatchOptimizationRequest {
  /** Array of templates to optimize with their identifiers */
  templates: Array<{
    /** Unique identifier for the template */
    id: string;
    /** Template object to be optimized */
    template: Template;
  }>;
  /** Shared optimization configuration for all templates */
  config?: Partial<OptimizationConfig>;
  /** Shared options for all templates in the batch */
  options?: OptimizationOptions;
}

/**
 * Result object for batch optimization operations
 * Contains aggregated results and error information
 */
export interface BatchOptimizationResult {
  /** Unique identifier for this batch operation */
  batchId: string;
  /** Total number of templates processed */
  total: number;
  /** Number of successfully optimized templates */
  successful: number;
  /** Number of templates that failed optimization */
  failed: number;
  /** Array of successful optimization results */
  results: OptimizationResult[];
  /** Array of errors for failed templates */
  errors: Array<{
    /** Template ID that failed */
    templateId: string;
    /** Error message describing the failure */
    error: string;
  }>;
  /** Timestamp when batch operation completed */
  timestamp: Date;
}

/**
 * Service for managing prompt optimization workflows with PromptWizard integration
 *
 * This service provides a comprehensive prompt optimization solution that handles:
 * - Single and batch template optimization
 * - Queue-based asynchronous processing with priority levels
 * - Result caching with LRU and distributed cache support
 * - Real-time progress tracking through event emission
 * - Feedback collection for continuous improvement
 *
 * The service uses an event-driven architecture to provide real-time updates
 * on optimization progress and integrates with both in-memory and distributed
 * caching systems for improved performance.
 *
 * @extends EventEmitter
 * @example
 * ```typescript
 * const optimizationService = new PromptOptimizationService(
 *   promptWizardClient,
 *   templateService,
 *   cacheService
 * );
 *
 * // Single template optimization
 * const result = await optimizationService.optimizeTemplate({
 *   templateId: 'template-123',
 *   template: myTemplate,
 *   config: {
 *     targetModel: 'gpt-4',
 *     mutateRefineIterations: 3
 *   },
 *   options: {
 *     priority: 'high',
 *     skipCache: false
 *   }
 * });
 *
 * // Listen for progress updates
 * optimizationService.on('optimization:progress', (data) => {
 *   console.log(`Progress: ${data.progress}% - ${data.currentStep}`);
 * });
 * ```
 *
 * @see {@link OptimizationRequest} for request structure
 * @see {@link OptimizationResult} for result structure
 * @see {@link BatchOptimizationRequest} for batch operations
 */
export class PromptOptimizationService extends EventEmitter {
  private client: PromptWizardClient;

  private cacheService: CacheService;

  private optimizationCacheService: OptimizationCacheService;

  private optimizationQueue: OptimizationQueue;

  private optimizationPipeline: OptimizationPipeline;

  private resultCache: LRUCache<string, OptimizationResult>;

  private templateService: TemplateService;

  /**
   * Creates a new PromptOptimizationService instance
   *
   * Initializes the optimization service with all necessary dependencies
   * and sets up event listeners for queue operations. The service automatically
   * configures an LRU cache for results and establishes connections to the
   * optimization pipeline and cache services.
   *
   * @param client - PromptWizard client for API communication
   * @param _templateService - Template service (kept for interface compatibility)
   * @param cacheService - Cache service for distributed caching
   * @param optimizationPipeline - Optional custom optimization pipeline
   *
   * @example
   * ```typescript
   * const service = new PromptOptimizationService(
   *   new PromptWizardClient({ apiKey: 'key' }),
   *   templateService,
   *   new CacheService({ maxSize: 200 })
   * );
   * ```
   */
  constructor(
    client: PromptWizardClient,
    templateService: TemplateService,
    cacheService: CacheService,
    optimizationPipeline?: OptimizationPipeline
  ) {
    super();
    this.client = client;
    this.cacheService = cacheService;
    this.templateService = templateService;

    // Initialize optimization cache service
    this.optimizationCacheService = new OptimizationCacheService();

    // Initialize optimization pipeline (create a basic one if not provided)
    this.optimizationPipeline =
      optimizationPipeline ||
      new OptimizationPipeline(
        this, // Use self as optimization service
        this.templateService,
        this.cacheService
      );

    // Initialize optimization queue
    this.optimizationQueue = new OptimizationQueue(
      this.optimizationPipeline,
      this.optimizationCacheService
    );

    // Initialize LRU cache for optimization results
    this.resultCache = new LRUCache<string, OptimizationResult>({
      max: 100, // Maximum 100 cached results
      ttl: 1000 * 60 * 60 * 24, // 24 hour TTL
      allowStale: false,
      updateAgeOnGet: true,
      updateAgeOnHas: true,
    });

    // Set up event listeners for queue events
    this.setupQueueEventListeners();
  }

  /**
   * Set up event listeners for optimization queue events
   *
   * Configures event forwarding from the optimization queue to this service,
   * allowing clients to listen for job lifecycle events including queuing,
   * processing, progress updates, completion, and failures.
   *
   * Events emitted:
   * - optimization:queued - When job is added to queue
   * - optimization:started - When job processing begins
   * - optimization:progress - When job reports progress updates
   * - optimization:completed - When job completes successfully
   * - optimization:failed - When job fails with error
   * - optimization:retry - When job is retried after failure
   *
   * @private
   */
  private setupQueueEventListeners(): void {
    this.optimizationQueue.on('job:added', job => {
      this.emit('optimization:queued', {
        templateId: job.templateId,
        jobId: job.jobId,
        priority: job.priority,
      });
    });

    this.optimizationQueue.on('job:started', job => {
      this.emit('optimization:started', {
        templateId: job.templateId,
        jobId: job.jobId,
      });
    });

    this.optimizationQueue.on('job:progress', job => {
      this.emit('optimization:progress', {
        templateId: job.templateId,
        jobId: job.jobId,
        progress: job.progress,
        currentStep: job.currentStep,
      });
    });

    this.optimizationQueue.on('job:completed', job => {
      if (job.result) {
        this.emit('optimization:completed', job.result);
      }
    });

    this.optimizationQueue.on('job:failed', job => {
      this.emit('optimization:failed', {
        templateId: job.templateId,
        jobId: job.jobId,
        error: job.error,
      });
    });

    this.optimizationQueue.on('job:retry', job => {
      this.emit('optimization:retry', {
        templateId: job.templateId,
        jobId: job.jobId,
        retryCount: job.retryCount,
      });
    });
  }

  /**
   * Optimize a single template using the queue-based processing system
   *
   * This method processes a single template optimization request through the
   * following workflow:
   * 1. Check optimization cache for existing results (unless skipCache is true)
   * 2. Queue the optimization job with appropriate priority
   * 3. Wait for job completion with configurable timeout
   * 4. Return optimization result with metrics and comparisons
   *
   * The method supports both cached and fresh optimizations, priority-based
   * queue processing, and comprehensive error handling with retry mechanisms.
   *
   * @param request - The optimization request containing template and config
   * @returns Promise resolving to optimization result with metrics
   *
   * @throws {Error} When optimization times out or fails after retries
   * @throws {Error} When template or configuration is invalid
   *
   * @example
   * ```typescript
   * const result = await service.optimizeTemplate({
   *   templateId: 'user-onboarding-v1',
   *   template: {
   *     name: 'User Onboarding',
   *     content: 'Welcome new user, please complete...'
   *   },
   *   config: {
   *     targetModel: 'gpt-4',
   *     mutateRefineIterations: 5,
   *     generateReasoning: true
   *   },
   *   options: {
   *     priority: 'high',
   *     timeoutMs: 300000, // 5 minutes
   *     maxRetries: 3
   *   }
   * });
   *
   * console.log(`Token reduction: ${result.metrics.tokenReduction}%`);
   * console.log(`Quality score: ${result.qualityScore.overall}`);
   * ```
   *
   * @see {@link OptimizationRequest} for request structure
   * @see {@link OptimizationResult} for result structure
   */
  async optimizeTemplate(
    request: OptimizationRequest
  ): Promise<OptimizationResult> {
    const startTime = Date.now();

    // Check optimization cache first unless explicitly skipped
    if (!request.options?.skipCache) {
      try {
        const optimizationConfig: OptimizationConfig = {
          task: request.template.name,
          prompt:
            request.template.content ||
            (request.template.files?.[0] as TemplateFile)?.content ||
            request.template.description ||
            '',
          targetModel: request.config?.targetModel || 'gpt-4',
          mutateRefineIterations: request.config?.mutateRefineIterations || 3,
          fewShotCount: request.config?.fewShotCount || 5,
          generateReasoning: request.config?.generateReasoning ?? true,
          ...request.config,
        };

        const cached =
          await this.optimizationCacheService.get(optimizationConfig);
        if (cached) {
          logger.info(
            `Using cached optimization for template: ${request.templateId}`
          );

          // Convert cached result to OptimizationResult format
          const result: OptimizationResult = {
            requestId: this.generateRequestId(),
            templateId: request.templateId,
            originalTemplate: request.template,
            optimizedTemplate: {
              ...request.template,
              content: cached.optimizedPrompt,
            },
            metrics: {
              tokenReduction: cached.metrics?.tokenReduction || 0,
              accuracyImprovement: cached.metrics?.accuracyImprovement || 0,
              optimizationTime: Date.now() - startTime,
              apiCalls: cached.metrics?.apiCallsUsed || 0,
            },
            qualityScore: (typeof cached.qualityScore === 'object'
              ? cached.qualityScore
              : {
                  overall: cached.qualityScore || 80,
                  confidence: 0.8,
                  metrics: {
                    clarity: 80,
                    taskAlignment: 80,
                    tokenEfficiency: 80,
                  },
                  suggestions: [],
                }) as QualityScore,
            comparison:
              cached.comparison ||
              this.createDefaultComparison(
                (request as any).prompt || 'No prompt available'
              ),
            timestamp: new Date(),
          };

          return result;
        }
      } catch (error) {
        logger.warn(`Cache lookup failed: ${error}`);
      }
    }

    // Add job to optimization queue
    logger.info(
      `Queueing optimization job for template: ${request.templateId}`
    );

    const job = await this.optimizationQueue.addJob(
      request.templateId,
      request.template,
      {
        task: request.template.name,
        prompt:
          request.template.content ||
          (request.template.files?.[0] as any)?.content ||
          request.template.description ||
          '',
        targetModel: request.config?.targetModel || 'gpt-4',
        mutateRefineIterations: request.config?.mutateRefineIterations || 3,
        fewShotCount: request.config?.fewShotCount || 5,
        generateReasoning: request.config?.generateReasoning ?? true,
        ...request.config,
      },
      {
        priority:
          request.options?.priority === 'critical'
            ? 'urgent'
            : request.options?.priority === 'high'
              ? 'high'
              : request.options?.priority === 'low'
                ? 'low'
                : 'normal',
        maxRetries: request.options?.maxRetries,
        metadata: {
          originalRequestId: this.generateRequestId(),
          startTime,
        },
      }
    );

    // For now, return a promise that resolves when the job completes
    // In a real implementation, you might want to return the job ID
    // and provide a separate method to get results
    return new Promise((resolve, reject) => {
      const timeout = setTimeout(
        () => {
          reject(
            new Error(
              `Optimization timeout for template: ${request.templateId}`
            )
          );
        },
        request.options?.timeoutMs || 10 * 60 * 1000
      ); // 10 minutes default

      // Forward declaration
      let onCompleted: (completedJob: QueueJob) => void;

      const onFailed = (failedJob: QueueJob): void => {
        if (failedJob.jobId === job.jobId) {
          clearTimeout(timeout);
          this.optimizationQueue.off('job:completed', onCompleted);
          this.optimizationQueue.off('job:failed', onFailed);
          reject(
            new Error(
              typeof failedJob.error === 'string'
                ? failedJob.error
                : 'Job failed'
            )
          );
        }
      };

      onCompleted = (completedJob: QueueJob): void => {
        if (completedJob.jobId === job.jobId) {
          clearTimeout(timeout);
          this.optimizationQueue.off('job:completed', onCompleted);
          this.optimizationQueue.off('job:failed', onFailed);

          if (completedJob.result) {
            // Transform QueueJob result to OptimizationResult format
            const optimizationResult: OptimizationResult = {
              requestId: completedJob.jobId,
              templateId: completedJob.templateId,
              originalTemplate: completedJob.template,
              optimizedTemplate:
                typeof completedJob.result.optimizedTemplate === 'object' &&
                completedJob.result.optimizedTemplate !== null
                  ? (completedJob.result.optimizedTemplate as Template)
                  : completedJob.template,
              metrics: {
                tokenReduction:
                  completedJob.result.metrics?.tokenReduction || 0,
                accuracyImprovement:
                  completedJob.result.metrics?.accuracyImprovement || 0,
                optimizationTime: Date.now() - completedJob.createdAt.getTime(),
                apiCalls: completedJob.result.metrics?.apiCallsUsed || 0,
              },
              qualityScore:
                typeof completedJob.result.qualityScore === 'object' &&
                completedJob.result.qualityScore !== null
                  ? (completedJob.result.qualityScore as QualityScore)
                  : {
                      overall: 50,
                      metrics: {
                        clarity: 50,
                        taskAlignment: 50,
                        tokenEfficiency: 50,
                      },
                      suggestions: ['No quality score available'],
                      confidence: 0.5,
                    },
              comparison:
                completedJob.result.comparison ||
                this.createDefaultComparison(
                  (request as any).prompt || 'No prompt available'
                ),
              timestamp: new Date(),
            };
            resolve(optimizationResult);
          } else {
            reject(new Error('Job completed but no result available'));
          }
        }
      };

      this.optimizationQueue.on('job:completed', onCompleted);
      this.optimizationQueue.on('job:failed', onFailed);
    });
  }

  /**
   * Optimize multiple templates in a batch operation
   *
   * Processes multiple templates concurrently with controlled concurrency
   * to prevent resource exhaustion. The method applies the same configuration
   * and options to all templates in the batch, providing efficient bulk
   * optimization capabilities.
   *
   * Features:
   * - Concurrent processing with configurable concurrency (default: 3)
   * - Shared configuration applied to all templates
   * - Individual error handling per template
   * - Progress tracking through events
   * - Comprehensive result aggregation
   *
   * @param request - Batch optimization request with templates and config
   * @returns Promise resolving to aggregated batch results
   *
   * @throws {Error} When batch configuration is invalid
   *
   * @example
   * ```typescript
   * const batchResult = await service.batchOptimize({
   *   templates: [
   *     { id: 'template-1', template: template1 },
   *     { id: 'template-2', template: template2 },
   *     { id: 'template-3', template: template3 }
   *   ],
   *   config: {
   *     targetModel: 'gpt-4',
   *     mutateRefineIterations: 3
   *   },
   *   options: {
   *     priority: 'normal',
   *     timeoutMs: 600000 // 10 minutes per template
   *   }
   * });
   *
   * console.log(`Successfully optimized: ${batchResult.successful}/${batchResult.total}`);
   * batchResult.errors.forEach(error => {
   *   console.error(`Failed ${error.templateId}: ${error.error}`);
   * });
   * ```
   *
   * @see {@link BatchOptimizationRequest} for request structure
   * @see {@link BatchOptimizationResult} for result structure
   */
  async batchOptimize(
    request: BatchOptimizationRequest
  ): Promise<BatchOptimizationResult> {
    logger.info(
      `Starting batch optimization for ${request.templates.length} templates`
    );
    this.emit('batch:started', { count: request.templates.length });

    const batchId = this.generateBatchId();
    const results: OptimizationResult[] = [];
    const errors: Array<{ templateId: string; error: string }> = [];

    // Process templates in parallel with concurrency control
    const concurrency = 3; // Process 3 templates at a time
    for (let i = 0; i < request.templates.length; i += concurrency) {
      const batch = request.templates.slice(i, i + concurrency);
      const promises = batch.map(async ({ id, template }) => {
        try {
          const result = await this.optimizeTemplate({
            templateId: id,
            template,
            config: request.config,
            options: request.options,
          });
          results.push(result);
        } catch (error) {
          errors.push({
            templateId: id,
            error: error instanceof Error ? error.message : String(error),
          });
        }
      });
      await Promise.all(promises);
    }

    const batchResult: BatchOptimizationResult = {
      batchId,
      total: request.templates.length,
      successful: results.length,
      failed: errors.length,
      results,
      errors,
      timestamp: new Date(),
    };

    logger.info(
      `Batch optimization completed: ${batchResult.successful}/${batchResult.total} successful`
    );
    this.emit('batch:completed', batchResult);

    return batchResult;
  }

  /**
   * Get the current status of an optimization job
   *
   * Retrieves the current status of an optimization job by checking both
   * the local queue and the remote PromptWizard service. This method provides
   * real-time visibility into job progress, current processing step, and
   * any errors that may have occurred.
   *
   * @param jobId - Unique identifier of the optimization job
   * @returns Promise resolving to job status or null if not found
   *
   * @example
   * ```typescript
   * const status = await service.getOptimizationStatus('job-123');
   * if (status) {
   *   console.log(`Status: ${status.status}`);
   *   console.log(`Progress: ${status.progress}%`);
   *   console.log(`Current step: ${status.currentStep}`);
   * } else {
   *   console.log('Job not found');
   * }
   * ```
   *
   * @see {@link OptimizationJob} for job structure
   */
  async getOptimizationStatus(jobId: string): Promise<QueueJob | null> {
    const job = this.optimizationQueue.getJob(jobId);
    if (!job) {
      // Check if job exists in the Python service
      try {
        await this.client.getJobStatus(jobId);
        // Convert to QueueJob format or return null if no meaningful conversion
        return null;
      } catch (error) {
        logger.warn(`Failed to get job status from client: ${error}`);
        return null;
      }
    }
    return job;
  }

  /**
   * Send feedback for an optimization result to improve future optimization quality
   *
   * Collects user feedback on optimization results and uses this information
   * to improve future optimizations. Low ratings automatically trigger
   * re-optimization with adjusted parameters for better results.
   *
   * The feedback system helps train the optimization algorithms and provides
   * valuable insights for continuous improvement of prompt optimization quality.
   *
   * @param templateId - Identifier of the template that was optimized
   * @param feedback - Feedback data including rating and preferences
   * @param feedback.rating - Rating from 1-5 (1=poor, 5=excellent)
   * @param feedback.comments - Optional detailed feedback comments
   * @param feedback.preferredVersion - Whether user prefers original or optimized version
   *
   * @throws {Error} When optimization result is not found for feedback
   *
   * @example
   * ```typescript
   * // Provide positive feedback
   * await service.sendFeedback('template-123', {
   *   rating: 5,
   *   comments: 'Excellent optimization, much clearer and more concise',
   *   preferredVersion: 'optimized'
   * });
   *
   * // Provide negative feedback (triggers re-optimization)
   * await service.sendFeedback('template-456', {
   *   rating: 2,
   *   comments: 'Lost important context, too aggressive optimization',
   *   preferredVersion: 'original'
   * });
   * ```
   *
   * @see {@link OptimizationResult} for related result structure
   */
  async sendFeedback(
    templateId: string,
    feedback: {
      rating: number; // 1-5
      comments?: string;
      preferredVersion: 'original' | 'optimized';
    }
  ): Promise<void> {
    logger.info(`Sending feedback for template: ${templateId}`);

    // Get the cached optimization result
    const cacheKey = `feedback:${templateId}`;
    const result = this.resultCache.get(cacheKey);

    if (!result) {
      throw new Error('Optimization result not found for feedback');
    }

    // Note: PromptWizard client doesn't have sendFeedback method
    // This would need to be implemented in the client

    // Trigger re-optimization if rating is low
    if (feedback.rating <= 2 && feedback.preferredVersion === 'original') {
      logger.info(
        `Low rating detected, scheduling re-optimization for: ${templateId}`
      );
      this.emit('reoptimization:scheduled', { templateId });

      // Schedule re-optimization with adjusted parameters
      await this.optimizeTemplate({
        templateId,
        template: result.originalTemplate,
        config: {
          mutateRefineIterations: 5, // More iterations for better results
          generateReasoning: true,
          fewShotCount: 10, // More examples
        },
        options: {
          skipCache: true, // Force fresh optimization
          priority: 'high',
        },
      });
    }

    logger.info(`Feedback processed for template: ${templateId}`);
  }

  /**
   * Clear all optimization caches to free memory and force fresh optimizations
   *
   * Clears both the in-memory LRU cache and the distributed optimization cache,
   * ensuring that all future requests will perform fresh optimizations rather
   * than using cached results. This is useful for testing, debugging, or when
   * optimization algorithms have been updated.
   *
   * @example
   * ```typescript
   * // Clear cache before running fresh optimizations
   * await service.clearCache();
   * console.log('All caches cleared');
   * ```
   */
  async clearCache(): Promise<void> {
    // Clear both old LRU cache and new optimization cache
    this.resultCache.clear();
    await this.optimizationCacheService.clear();
    logger.info('Optimization cache cleared');
  }

