  .string()
  .url('Must be a valid URL')
  .refine(val => patterns.url.test(val), 'Only HTTP/HTTPS URLs allowed')
  .refine(val => !val.includes('..'), 'URL contains path traversal')
  .refine(val => val.length < 2000, 'URL too long');

